INSERT INTO `creature_text` VALUES (58646, 3, 0, 'Make sure to tend those scallions. After that, nothing to do but wait until tomorrow when they\'ll be nice and ripe.', 12, 0, 100, 0, 0, 0, 68209, 219672, 219672, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` VALUES (58646, 3, 0, 'There we go! Ripe for the pickin\'s!', 12, 0, 100, 0, 0, 0, 59371, 105672, 105672, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` VALUES (58646, 3, 0, 'Those cabbages should be ready any minute now... wait for it...', 12, 0, 100, 0, 0, 0, 59370, 97938, 97938, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` VALUES (58646, 3, 0, 'Привет, Джина! Не ожидал, что ты придешь.', 12, 0, 100, 3, 0, 0, 67916, 5339000, 5339000, 0, 'Фермер Юн to Player');
INSERT INTO `creature_text` VALUES (58646, 3, 0, 'Ухаживай за этим луком-шалотом как следует. А затем подожди один день, преждем чем урожай созреет.', 12, 0, 100, 0, 0, 0, 68209, 4932000, 4932000, 0, 'Фермер Юн to Player');
INSERT INTO `creature_text` VALUES (58646, 4, 0, 'Mmm, that\'s mighty tasty, $n. I wouldn\'t mind another round of that... or two... hic!', 12, 0, 100, 0, 0, 0, 65913, 0, 0, 0, 'Farmer Yoon to player');
INSERT INTO `creature_text` VALUES (58646, 5, 0, 'Hmmm, it sounds like the Kunzen hozen are causing quite a lot of trouble for Mr. Mudclaw. Maybe we can help.', 12, 0, 100, 0, 0, 0, 67904, 0, 0, 0, 'Фермер Юн to Player');
INSERT INTO `creature_text` VALUES (58664, 2, 0, 'ЭТО НЕВОЗМОЖНО!', 14, 0, 100, 0, 0, 29456, 99092, 0, 0, 0, 'Филактерия инструктора Ледяное Сердце');
INSERT INTO `creature_text` VALUES (58666, 0, 0, 'Кор, зачем ты это сделал? Месть не стоит того, чтобы ради нее умирать.', 12, 0, 100, 0, 0, 0, 57500, 0, 0, 0, 'Служитель солнца Дезко to Первопроходец Брожай');
INSERT INTO `creature_text` VALUES (58666, 0, 0, 'Кор!.. Не-е-ет!!!', 14, 0, 100, 0, 0, 0, 57440, 0, 0, 0, 'Служитель солнца Дезко to Player');
INSERT INTO `creature_text` VALUES (58666, 0, 0, 'Старый эльф, мы оба многое испытали. Похорони своих погибших.', 12, 0, 100, 0, 0, 0, 58097, 0, 0, 0, 'Служитель солнца Дезко to Первопроходец Брожай');
INSERT INTO `creature_text` VALUES (58676, 3, 0, 'Тебе не скрыться. Орден уничтожит всех, кто носит метку Плети.', 12, 0, 100, 0, 0, 0, 2626, 95000, 95000, 0, 'Защитник из Алого ордена to Player');
INSERT INTO `creature_text` VALUES (58683, 0, 0, 'You carry the taint of the Scourge.  Prepare to enter the Twisting Nether.', 12, 0, 100, 0, 0, 0, 2625, 0, 0, 0, 'Scarlet Myrmidon to Player');
INSERT INTO `creature_text` VALUES (58683, 1, 0, 'The Scourge is upon you! Now you will go to the Twisting Nether!', 12, 0, 100, 0, 0, 0, 2625, 0, 0, 0, 'Мирмидон Алого ордена to Player');
INSERT INTO `creature_text` VALUES (58683, 1, 0, 'Тебе не скрыться. Орден уничтожит всех, кто носит метку Плети.', 12, 0, 100, 0, 0, 0, 2626, 0, 0, 0, 'Мирмидон Алого ордена to Player');
INSERT INTO `creature_text` VALUES (58683, 3, 0, 'Свет приговаривает всех, кто служит прибежищем зла. Сейчас ты умрешь!', 12, 0, 100, 0, 0, 0, 2627, 95000, 95000, 0, 'Мирмидон Алого ордена to Player');
INSERT INTO `creature_text` VALUES (58684, 1, 0, 'Тебе не скрыться. Орден уничтожит всех, кто носит метку Плети.', 12, 0, 100, 0, 0, 0, 2626, 0, 0, 0, 'Рассекатель Алого ордена to Player');
INSERT INTO `creature_text` VALUES (58684, 3, 0, 'Алый орден сокрушит зло и изгонит его из этих земель!', 12, 0, 100, 0, 0, 0, 2628, 74000, 74000, 0, 'Рассекатель Алого ордена to Player');
INSERT INTO `creature_text` VALUES (58685, 3, 0, 'Свет приговаривает всех, кто служит прибежищем зла. Сейчас ты умрешь!', 12, 0, 100, 0, 0, 0, 2627, 129000, 129000, 0, 'Проповедница Алого ордена to Player');
INSERT INTO `creature_text` VALUES (58704, 3, 0, 'Отлично сработано!', 12, 0, 100, 0, 0, 0, 138076, 14629000, 14629000, 0, 'Келари Легкая Поступь to Player');
INSERT INTO `creature_text` VALUES (58705, 3, 0, 'Какая досада, я сама хотела их съесть! Но раз они нужны Элле, так тому и быть. Вот, бери все!', 12, 0, 100, 0, 0, 0, 65906, 38377000, 38377000, 0, 'Рыба Тростниковая Шкура to Player');
INSERT INTO `creature_text` VALUES (58705, 3, 0, 'Ты $gсам:сама; $gприготовил:приготовила; блюдо из двух рыб?', 12, 0, 100, 1, 0, 0, 59080, 9472000, 9472000, 0, 'Рыба Тростниковая Шкура to Player');
INSERT INTO `creature_text` VALUES (58706, 0, 0, 'Now that we\'re acquaintances, $n, let me give you a tip. Folks \'round here can help spruce up your farm, but you gotta become friends. Unfortunately, you\'re too green to help them out or give gifts yet, so get stronger first!', 12, 0, 100, 0, 0, 0, 67881, 0, 0, 0, 'Gina Mudclaw to Player');
INSERT INTO `creature_text` VALUES (58710, 0, 0, 'Wow! Thish ish shome amazing shtuff! Where can I get shome more?', 12, 0, 100, 0, 0, 0, 65911, 0, 0, 0, 'Jogu the Drunk to player');
INSERT INTO `creature_text` VALUES (58716, 3, 0, 'Свежий хлеб!', 12, 0, 100, 61, 0, 0, 64665, 4903000, 5554000, 0, 'Цзянь Железная Лапа to Player');
INSERT INTO `creature_text` VALUES (58717, 0, 0, 'Any time, neighbor. Any time.', 12, 0, 50, 0, 0, 0, 59144, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (58717, 1, 0, 'We\'ll need at least 8 apples. And a few handfuls of craneberries - 50 should do it.', 12, 0, 50, 0, 0, 0, 59145, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (58717, 2, 0, 'Hmmm, this is excellent! EXCELLENT! This is the best stuff I\'ve tasted in a long time. Who brewed it?', 12, 0, 100, 0, 0, 0, 65912, 0, 0, 0, 'Bobo Ironpaw to player');
INSERT INTO `creature_text` VALUES (58718, 0, 0, 'First packet of seeds is on the house! Come back to me anytime you want to buy more seeds, ya hear?', 12, 0, 100, 1, 0, 0, 67826, 0, 0, 0, 'Smart text');
INSERT INTO `creature_text` VALUES (58722, 0, 0, 'I ... will not let you ....', 14, 0, 100, 0, 0, 29464, 58423, 0, 0, 0, 'Лилиан Восс to Player');
INSERT INTO `creature_text` VALUES (58722, 1, 0, 'I can\'t ... resist ...', 12, 0, 100, 0, 0, 29465, 58424, 0, 0, 0, 'Лилиан Восс to 0');
INSERT INTO `creature_text` VALUES (58722, 2, 0, 'Убейте меня! Пока он не...', 14, 0, 100, 0, 0, 29463, 58425, 0, 0, 0, 'Лилиан Восс to 0');
INSERT INTO `creature_text` VALUES (58722, 3, 0, 'УМРИ, НЕКРОМАНТ!', 14, 0, 100, 0, 0, 29466, 58527, 0, 0, 0, 'Лилиан Восс to 0');
INSERT INTO `creature_text` VALUES (58722, 4, 0, 'Оставьте меня... Дайте мне умереть...', 12, 0, 100, 0, 0, 29467, 58528, 0, 0, 0, 'Лилиан Восс to 0');
INSERT INTO `creature_text` VALUES (58723, 3, 0, 'Take over for me... uh... what\'s your name? $n? Thank you, $n. I need a break.', 12, 0, 100, 0, 0, 0, 57502, 2203, 2203, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` VALUES (58723, 3, 0, 'You can find me in my house if you need me.', 12, 0, 100, 0, 0, 0, 57503, 5422, 5422, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` VALUES (58729, 3, 0, 'Come on boys, let\'s leave him to his \"farming.\"  Ho ho ho!', 12, 0, 100, 11, 0, 0, 57518, 77843, 77843, 0, 'Haohan Mudclaw to 58730');
INSERT INTO `creature_text` VALUES (58729, 3, 0, 'Do as you please, city boy, but I wager you won\'t last a week. I mean, just look at this dump!', 12, 0, 100, 0, 0, 0, 57515, 68156, 68156, 0, 'Haohan Mudclaw to Farmer Yoon');
INSERT INTO `creature_text` VALUES (58729, 3, 0, 'Hey, Yoon! Come out here!', 12, 0, 100, 5, 0, 0, 57511, 9015, 9015, 0, 'Haohan Mudclaw to Player');
INSERT INTO `creature_text` VALUES (58729, 3, 0, 'Let\'s get one thing straight. You may be Old Yoon\'s cub, but you\'re still a citypaw.', 12, 0, 100, 274, 0, 0, 57513, 34937, 34937, 0, 'Haohan Mudclaw to Farmer Yoon');
INSERT INTO `creature_text` VALUES (58729, 3, 0, 'So, you\'ve taken over the ranch for good, eh?', 12, 0, 100, 1, 0, 0, 57512, 24812, 24812, 0, 'Haohan Mudclaw to Farmer Yoon');
INSERT INTO `creature_text` VALUES (58729, 3, 0, 'You even know how to run a farm? Know what, I\'ll save you the trouble. Sell me this here piece of land and go back where you came from.', 12, 0, 100, 25, 0, 0, 57514, 40672, 40672, 0, 'Haohan Mudclaw to Farmer Yoon');
INSERT INTO `creature_text` VALUES (58729, 3, 0, 'You? Join the Tillers? Ha! You\'d have to prove your farming skills to all five members of our Council. They would never vote for you!', 12, 0, 100, 0, 0, 0, 57520, 54375, 54375, 0, 'Haohan Mudclaw to Farmer Yoon');
INSERT INTO `creature_text` VALUES (58730, 3, 0, 'B-but it\'s my family farm. I can\'t just sell it.', 12, 0, 100, 1, 0, 0, 57524, 46281, 46281, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` VALUES (58730, 3, 0, 'Besides, once I\'ve gotten the hang of this, I want to be a Tiller like the rest of you!', 12, 0, 100, 0, 0, 0, 57523, 51953, 51953, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` VALUES (58730, 3, 0, 'Hey, do I hear someone outside?', 12, 0, 100, 0, 0, 0, 57526, 1328, 1328, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` VALUES (58730, 3, 0, 'Never say never, my grandpa always said. I\'m going to give it my best shot.', 12, 0, 100, 0, 0, 0, 57525, 62468, 62468, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` VALUES (58730, 3, 0, 'Oh, oh my! Mr. Mudclaw! I didn\'t expect such an important visitor.', 12, 0, 100, 21, 0, 0, 57519, 14281, 14281, 0, 'Farmer Yoon to Haohan Mudclaw');
INSERT INTO `creature_text` VALUES (58730, 3, 0, 'Yessir! My grandfather left me this farm. Just moved in the other day. Nice to meet you, neighbor!', 12, 0, 100, 3, 0, 0, 57522, 30468, 30468, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` VALUES (58730, 3, 0, 'You are THE Haohan Mudclaw, right? Head of the Tiller\'s Union? They are the master farmers in all of Pandaria!', 12, 0, 100, 1, 0, 0, 57521, 19968, 19968, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` VALUES (58733, 3, 0, 'Two days, tops!', 12, 0, 100, 0, 0, 0, 57528, 77031, 77031, 0, 'Mung-Mung to Haohan Mudclaw');
INSERT INTO `creature_text` VALUES (58734, 3, 0, 'I give \'im five days.', 12, 0, 100, 0, 0, 0, 57527, 73812, 73812, 0, 'Farmer Fung to Haohan Mudclaw');
INSERT INTO `creature_text` VALUES (58739, 3, 0, '|TInterface\\Icons\\inv_misc_pocketwatch_01:20|t Дополнительная задача: Победите Борохалу Разрушителя!', 41, 0, 100, 0, 0, 0, 74209, 55000, 55000, 0, 'Борохала Разрушитель to Player');
INSERT INTO `creature_text` VALUES (58739, 4, 0, '%s просит подкрепления.', 41, 0, 100, 53, 0, 16209, 59399, 20000, 36000, 0, 'Борохала Разрушитель');
INSERT INTO `creature_text` VALUES (58740, 0, 0, 'I will fly you! ..', 12, 0, 100, 0, 0, 16209, 59118, 0, 0, 0, 'Хмелевар Бланш to Хмелевар Бланш');
INSERT INTO `creature_text` VALUES (58740, 0, 0, 'Некогда отдыхать. Скорее! Надо им помочь!', 12, 0, 100, 25, 0, 0, 59314, 0, 0, 0, 'Хмелевар Бланш to 58916');
INSERT INTO `creature_text` VALUES (58740, 3, 0, '%s останавливается на дороге, тяжело дыша.', 16, 0, 100, 0, 0, 0, 58254, 308000, 308000, 0, 'Хмелевар Бланш to 58916');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Время для геройства!', 12, 0, 100, 4, 0, 0, 59353, 362000, 362000, 0, 'Хмелевар Бланш to Player');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Все готовы? Пошли!', 12, 0, 100, 1, 0, 0, 58239, 93000, 93000, 0, 'Хмелевар Бланш to 58916');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Гроза начинается! Готовьтесь.', 12, 0, 100, 5, 0, 0, 58142, 8000, 8000, 0, 'Хмелевар Бланш to Player');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Дайте знать, когда соберетесь идти внутрь...', 12, 0, 100, 1, 0, 14991, 59352, 356000, 356000, 0, 'Хмелевар Бланш to 58916');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Если бочонки загорятся, вам придется их тушить.', 12, 0, 100, 1, 0, 0, 58143, 12000, 12000, 0, 'Хмелевар Бланш to Player');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'И у меня как раз припасен отличный напиток для такого случая. По первой кружке – за мой счет. Спасибо, друзья!', 12, 0, 100, 1, 0, 0, 59403, 436000, 436000, 0, 'Хмелевар Бланш to Player');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Идти домой необязательно, но оставаться тут вы не можете.', 12, 0, 100, 25, 0, 16209, 59355, 370000, 370000, 0, 'Хмелевар Бланш to Player');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Ой, а гроза-то получилась сильнее, чем я думала! Будьте осторожнее!', 12, 0, 100, 5, 0, 0, 58146, 53000, 53000, 0, 'Хмелевар Бланш to Player');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Они карабкаются наверх! Похоже, дело дрянь.', 12, 0, 100, 1, 0, 0, 58250, 164000, 164000, 0, 'Хмелевар Бланш to 58916');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Отдохните немного, а я приготовлю бочонок.', 12, 0, 100, 1, 0, 0, 58238, 81000, 81000, 0, 'Хмелевар Бланш to Бочка с отваром');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Ох ты, опять сауроки. Вечно они вылезают, когда начинается дождь!', 12, 0, 100, 5, 0, 0, 58240, 106000, 106000, 0, 'Хмелевар Бланш to 58916');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Получилось! А теперь отнесем его в монастырь...', 12, 0, 100, 4, 0, 0, 57811, 76000, 76000, 0, 'Хмелевар Бланш to Бочка с отваром');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Попробуй освежающий эликсир Бланш, он восстанавливает здоровье и ману!', 41, 0, 100, 0, 0, 0, 61946, 58000, 58000, 0, 'Хмелевар Бланш to Player');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Последний шанс, червяки вы гнилозубые!', 12, 0, 100, 15, 0, 0, 59354, 366000, 366000, 0, 'Хмелевар Бланш to Player');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Посматривайте назад! Да не на мой зад. Оглянитесь!', 12, 0, 100, 5, 0, 0, 58253, 241000, 241000, 0, 'Хмелевар Бланш to 58916');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Сауроки? Что они здесь забыли? Не подпускайте их!', 12, 0, 100, 5, 0, 0, 58145, 39000, 39000, 0, 'Хмелевар Бланш to Player');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Следите, куда бьет молния!', 12, 0, 100, 5, 0, 0, 58144, 24000, 24000, 0, 'Хмелевар Бланш to Player');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Смотрите под ноги! Камни мокрые из-за дождя.', 12, 0, 100, 1, 0, 0, 58252, 216000, 216000, 0, 'Хмелевар Бланш to 58916');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Там, впереди! На убежище напали!', 12, 0, 100, 5, 0, 0, 59313, 326000, 326000, 0, 'Хмелевар Бланш to 58916');
INSERT INTO `creature_text` VALUES (58740, 3, 0, 'Фу-ух! Надо отдышаться. Давай-ка чуток пере... Что?', 12, 0, 100, 1, 0, 0, 59312, 320000, 320000, 0, 'Хмелевар Бланш to 58916');
INSERT INTO `creature_text` VALUES (58740, 4, 0, 'Возможно, это поможет...', 12, 0, 100, 0, 0, 0, 59116, 11000, 11000, 0, 'Хмелевар Бланш to Хмелевар Бланш');
INSERT INTO `creature_text` VALUES (58740, 4, 0, 'Даже не думайте умирать!', 12, 0, 100, 0, 0, 0, 59119, 10000, 26000, 0, 'Хмелевар Бланш to Хмелевар Бланш');
INSERT INTO `creature_text` VALUES (58740, 4, 0, 'Осторожнее!', 12, 0, 100, 0, 0, 0, 86422, 12000, 12000, 0, 'Хмелевар Бланш to Хмелевар Бланш');
INSERT INTO `creature_text` VALUES (58740, 4, 0, 'Получилось! Укрытие Громовой Лапы спасено!', 12, 0, 100, 4, 0, 0, 59402, 22000, 22000, 0, 'Хмелевар Бланш to Player');
INSERT INTO `creature_text` VALUES (58745, 1, 0, 'And now for the moment of truth.', 12, 0, 100, 0, 0, 0, 57893, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58745, 2, 0, 'Aaaaaaaaahhh!', 14, 0, 100, 0, 0, 0, 57894, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58745, 3, 0, 'That... hurt...', 12, 0, 100, 0, 0, 0, 57895, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58745, 4, 0, 'I spent so long trying to find a way to extend my life that I forgot to live it.', 12, 0, 100, 0, 0, 0, 57760, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58745, 5, 0, 'And now this...folly...would cost my daughter her life.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58745, 6, 0, 'This water may not create life, but at least it will let me transfer it.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58745, 7, 0, 'I love you, my little Lyalia.', 12, 0, 100, 0, 0, 0, 57763, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58769, 4, 0, '|TInterface\\Icons\\inv_misc_monsterfang_02.blp:20|t %s вонзает в жертву|cFFFF0000|Hspell:126187|h[злобные челюсти]|h|r! Убить!', 41, 0, 100, 0, 0, 0, 65075, 145000, 145000, 126187, 'Злобнохап to Player');
INSERT INTO `creature_text` VALUES (58769, 4, 0, '|TInterface\\Icons\\spell_shadow_corpseexplode.blp:20|t %s слишком много ест, начинается |cFFFF0000|Hspell:126126|h[рвота]|h|r!', 41, 0, 100, 0, 0, 0, 65073, 88000, 88000, 126101, 'Злобнохап');
INSERT INTO `creature_text` VALUES (58777, 1, 0, 'Избегайте или уничтожайте тотемы бурь Бальдрука.', 41, 0, 100, 0, 0, 0, 58811, 0, 0, 0, 'Бальдрук');
INSERT INTO `creature_text` VALUES (58777, 3, 0, 'Корабли горят! Все в гавань!', 14, 0, 100, 0, 0, 30565, 64859, 3000, 3000, 0, 'Бальдрук');
INSERT INTO `creature_text` VALUES (58787, 3, 0, 'Не попадайтесь на прицел Большой Бессе.', 41, 0, 100, 0, 0, 0, 58810, 15000, 15000, 0, 'Большая Бесса');
INSERT INTO `creature_text` VALUES (58821, 1, 0, 'F... father? No! Not for me! No for this!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58826, 0, 0, 'The blistering heat must have dulled my senses... mirages! All of you!', 14, 0, 100, 0, 0, 27748, 57623, 0, 0, 0, 'Entering combat of Zao Sunseeker');
INSERT INTO `creature_text` VALUES (58826, 1, 0, 'I will put an end to this!', 14, 0, 100, 0, 0, 27747, 57748, 0, 0, 0, 'Цзао Искатель Солнца to Солнце');
INSERT INTO `creature_text` VALUES (58870, 0, 0, 'Переходим к плану Б. Ваша задача – продвинуться вглубь острова Терамор.', 12, 0, 100, 1, 0, 0, 64684, 0, 0, 0, 'Капитан Дрок to Player');
INSERT INTO `creature_text` VALUES (58870, 0, 0, 'Посейте панику в их рядах, пока мы ждем подкрепления. Адский Крик следит за вами!', 12, 0, 100, 66, 0, 0, 64830, 0, 0, 0, 'Капитан Дрок to Player');
INSERT INTO `creature_text` VALUES (58870, 0, 0, 'Случилось немыслимое: мы потерпели поражение при осаде Терамора.', 12, 0, 100, 5, 0, 0, 64678, 0, 0, 0, 'Капитан Дрок to Player');
INSERT INTO `creature_text` VALUES (58870, 0, 0, 'Сожгите их корабли. Убейте распорядителя полетов. Выведите из строя их боевые машины. И спасите нашего шпиона!', 12, 0, 100, 25, 0, 0, 64826, 0, 0, 0, 'Капитан Дрок to Player');
INSERT INTO `creature_text` VALUES (58875, 3, 0, 'I feel her will is weakening! Her soul burns so brightly.', 12, 0, 100, 6, 0, 27492, 58505, 77000, 77000, 0, 'Темный магистр Гандлинг to Лилиан Восс');
INSERT INTO `creature_text` VALUES (58875, 3, 0, 'А теперь обрати свои рунные клинки против гостей и принеси мне их кости!', 14, 0, 100, 6, 0, 27487, 58422, 71000, 71000, 0, 'Темный магистр Гандлинг to Player');
INSERT INTO `creature_text` VALUES (58875, 3, 0, 'Лилиан, настало время для твоего превращения.', 12, 0, 100, 6, 0, 27491, 58513, 80000, 80000, 0, 'Темный магистр Гандлинг to Лилиан Восс');
INSERT INTO `creature_text` VALUES (58875, 3, 0, 'Пойдем, Лилиан, твоя судьба ждет тебя.', 12, 0, 100, 0, 0, 27485, 58199, 222000, 222000, 0, 'Темный магистр Гандлинг to Player');
INSERT INTO `creature_text` VALUES (58875, 3, 0, 'Похоже, у нас гости! Ха-ха-ха! Твои друзья, Лилиан?', 12, 0, 100, 6, 0, 27479, 58193, 193000, 193000, 0, 'Темный магистр Гандлинг to Player');
INSERT INTO `creature_text` VALUES (58875, 3, 0, 'Смерть одних призывателей лишь делает других сильнее!', 12, 0, 100, 153, 0, 27480, 58194, 230000, 230000, 0, 'Темный магистр Гандлинг to Player');
INSERT INTO `creature_text` VALUES (58875, 3, 0, 'Ты забыла, девочка? Я темный магистр! Я повелеваю нежитью!', 14, 0, 100, 6, 0, 27486, 58421, 55000, 55000, 0, 'Темный магистр Гандлинг to Player');
INSERT INTO `creature_text` VALUES (58875, 3, 0, 'Ты тоже красива, Лилиан. Давно ли ты заглядывала в зеркало?', 12, 0, 100, 396, 0, 27483, 58197, 249000, 249000, 0, 'Темный магистр Гандлинг to Player');
INSERT INTO `creature_text` VALUES (58875, 3, 0, 'Что с тобой, Лилиан? Ты устала?', 12, 0, 100, 153, 0, 27480, 58195, 231000, 231000, 0, 'Темный магистр Гандлинг to Player');
INSERT INTO `creature_text` VALUES (58875, 3, 0, 'Что?!', 14, 0, 100, 6, 0, 27489, 125725, 127000, 127000, 0, 'Темный магистр Гандлинг to Лилиан Восс');
INSERT INTO `creature_text` VALUES (58875, 3, 0, 'Это великий дар, которым тебя наградила судьба. Прими его.', 12, 0, 100, 5, 0, 27484, 58198, 212000, 212000, 0, 'Темный магистр Гандлинг to Player');
INSERT INTO `creature_text` VALUES (58875, 4, 0, 'Кости! Ах ты тварь!', 14, 0, 100, 6, 0, 27490, 58530, 4000, 4000, 0, 'Темный магистр Гандлинг to Лилиан Восс');
INSERT INTO `creature_text` VALUES (58875, 4, 0, 'Сплетение костей, плоти и душ... Это так красиво.', 12, 0, 100, 396, 0, 27482, 58196, 9000, 9000, 0, 'Темный магистр Гандлинг to Player');
INSERT INTO `creature_text` VALUES (58898, 0, 0, 'Not the face!', 12, 0, 100, 0, 0, 0, 58009, 0, 0, 0, 'Vigilant Watchman to Player');
INSERT INTO `creature_text` VALUES (58898, 0, 0, 'This is bad.', 12, 0, 100, 0, 0, 0, 73445, 0, 0, 0, 'Vigilant Watchman to Player');
INSERT INTO `creature_text` VALUES (58898, 3, 0, 'А-а-а!', 12, 0, 100, 0, 0, 0, 135125, 24000, 24000, 0, 'Бдительный часовой to Player');
INSERT INTO `creature_text` VALUES (58898, 4, 0, 'Кто это сделал? А ну, признавайтесь! Кто это был?', 12, 0, 100, 0, 0, 0, 58010, 4000, 4000, 0, 'Бдительный часовой to Player');
INSERT INTO `creature_text` VALUES (58898, 4, 0, 'Только не лицо!', 12, 0, 100, 0, 0, 29512, 58009, 8000, 8000, 111902, 'Бдительный часовой to Player');
INSERT INTO `creature_text` VALUES (58898, 4, 0, 'Это плохо...', 12, 0, 100, 0, 0, 29511, 58008, 9000, 9000, 111902, 'Бдительный часовой to Player');
INSERT INTO `creature_text` VALUES (58949, 4, 0, '|TInterface\\Icons\\spell_fire_soulburn.blp:20|t %s произносит |cFFFF0000|Hspell:126288|h[Истребление]|h|r!', 42, 0, 100, 0, 0, 0, 65345, 6000, 6000, 0, 'Бай-Цзинь Мясник to Player');
