INSERT INTO `creature_text` VALUES (58951, 3, 0, '|cFFFF8000Оранжевая|r сфера возвращена!', 41, 0, 100, 0, 0, 9431, 62285, 3565, 3565, 0, 'Trigger to Player');
INSERT INTO `creature_text` VALUES (58951, 3, 0, 'Альянс победил!', 36, 0, 100, 0, 0, 0, 66442, 690886, 690886, 0, NULL);
INSERT INTO `creature_text` VALUES (58951, 3, 0, 'Орда победила!', 36, 0, 100, 0, 0, 0, 66441, 387458, 387458, 0, NULL);
INSERT INTO `creature_text` VALUES (58955, 0, 0, 'I must get to Stoneplow! The mantid will attack soon.', 12, 0, 0, 1, 0, 0, 57490, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58955, 0, 1, 'Thank you, hero.', 12, 0, 0, 1, 0, 0, 57489, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58955, 0, 2, 'Thank you, stranger.', 12, 0, 0, 1, 0, 0, 91890, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58955, 0, 3, 'The mogu ambushed us while we were trying to retreat from the mantid.', 12, 0, 0, 1, 0, 0, 57491, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58959, 0, 0, 'Эй, $n! Помнишь меня? Ладно, ладно... Эй, послушай... Мы совсем не обязаны помогать этому парню...', 12, 0, 100, 0, 0, 0, 69547, 0, 0, 0, 'Кеппико to Player');
INSERT INTO `creature_text` VALUES (58959, 1, 0, 'В этом месте наверняка есть, чем поживиться... Ну же, $gначальник:начальница;! Тебе же этого хочется...', 12, 0, 100, 0, 0, 0, 69548, 0, 0, 0, 'Кеппико to Player');
INSERT INTO `creature_text` VALUES (58961, 0, 0, 'Кто смеет бросать мне вызов?', 12, 0, 100, 0, 0, 0, 82437, 0, 0, 0, 'Батаарский яунгол to Player');
INSERT INTO `creature_text` VALUES (58968, 0, 0, 'How can I give up when heroes still fight at my side?', 12, 0, 100, 0, 0, 0, 57817, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58968, 0, 1, 'I... I don\'t know what came over me.', 12, 0, 100, 0, 0, 0, 57812, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58968, 0, 2, 'It seems we haven\'t lost yet.', 12, 0, 100, 0, 0, 0, 57818, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58968, 0, 3, 'It was... so dark...', 12, 0, 100, 0, 0, 0, 57819, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58968, 0, 4, 'No, I cannot let it end like this. I have to keep fighting!', 12, 0, 100, 0, 0, 0, 57816, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58968, 0, 5, 'Of course. We must not lose hope.', 12, 0, 100, 0, 0, 0, 57813, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58968, 0, 6, 'So they CAN die... I have to stay focused.', 12, 0, 100, 0, 0, 0, 57814, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58968, 0, 7, 'Thank you, I had lost sight of the Way.', 12, 0, 100, 0, 0, 0, 57815, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (58969, 3, 0, 'I ... I don’t know what came over me.', 12, 0, 100, 0, 0, 0, 57812, 297000, 297000, 0, 'Отчаявшийся послушник to Player');
INSERT INTO `creature_text` VALUES (58969, 3, 0, 'Разве могу я сдаться, когда столько героев стоят со мной плечом к плечу?', 12, 0, 100, 0, 0, 0, 57817, 469000, 469000, 0, 'Отчаявшийся послушник to Player');
INSERT INTO `creature_text` VALUES (58969, 3, 0, 'Спасибо, ведь я почти перестал следовать Пути.', 12, 0, 100, 0, 0, 0, 57815, 259000, 259000, 0, 'Отчаявшийся послушник to Player');
INSERT INTO `creature_text` VALUES (58975, 0, 0, 'И что теперь, служитель солнца?', 12, 0, 100, 0, 0, 0, 58095, 0, 0, 0, 'Хранитель знаний Ваэлдрин to Первопроходец Брожай');
INSERT INTO `creature_text` VALUES (58975, 0, 0, 'Лиалия! НЕ-Е-ЕТ!', 14, 0, 100, 0, 0, 0, 57635, 0, 0, 0, 'Хранитель знаний Ваэлдрин to Player');
INSERT INTO `creature_text` VALUES (58975, 0, 0, 'Согласен.', 12, 0, 100, 0, 0, 0, 140029, 0, 0, 0, 'Хранитель знаний Ваэлдрин to Первопроходец Брожай');
INSERT INTO `creature_text` VALUES (58978, 3, 0, '$p, помоги... Мне... стрела попала в колено.', 12, 0, 100, 0, 0, 0, 58039, 73000, 73000, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` VALUES (58978, 3, 0, 'I am injured ... Watch out!', 14, 0, 100, 0, 0, 0, 58003, 54000, 54000, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` VALUES (58978, 3, 0, 'Берегись!', 14, 0, 100, 0, 0, 0, 143886, 51000, 51000, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` VALUES (58978, 3, 0, 'Еще сауроки!', 12, 0, 100, 25, 0, 0, 58041, 89000, 89000, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` VALUES (58978, 3, 0, 'Лагерь уже близко. Мы сможем сами добраться.', 12, 0, 100, 0, 0, 0, 58043, 125000, 125000, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` VALUES (58978, 3, 0, 'Мое колено! Прости, я не могу сражаться.', 12, 0, 100, 0, 0, 0, 58042, 91000, 91000, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` VALUES (58978, 3, 0, 'Не отходите от меня. И будьте начеку.', 12, 0, 100, 0, 0, 0, 57917, 27000, 27000, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` VALUES (58978, 3, 0, 'Неправильный ответ.', 12, 0, 100, 0, 0, 0, 57915, 14000, 14000, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` VALUES (58978, 3, 0, 'Похоже, нам придется продвигаться вслепую. Хорошо, что ты с нами.', 12, 0, 100, 1, 0, 0, 57916, 22000, 22000, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` VALUES (58978, 3, 0, 'Спасибо. Не беспокойся обо мне, нам нужно двигаться дальше.', 12, 0, 100, 1, 0, 0, 58040, 80000, 80000, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` VALUES (58978, 3, 0, 'Стоп... Не нравится мне это...', 12, 0, 100, 0, 0, 0, 57943, 46000, 46000, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` VALUES (58978, 3, 0, 'Это твой последний шанс, ящерица. Говори, где ваши патрули?!', 12, 0, 100, 0, 0, 0, 57914, 2000, 2000, 0, 'Коро Странник Туманов to Player');
INSERT INTO `creature_text` VALUES (59019, 3, 0, '$gОн:Она;, кажется, $gсильный:сильная;. Может, у $gнего:нее; хватит силы прогнать яунголов.', 12, 0, 100, 273, 0, 0, 58429, 1562, 1562, 0, 'Житель деревни Бинан to Player');
INSERT INTO `creature_text` VALUES (59021, 0, 0, 'Никто не устоит против огня яунголов!', 12, 0, 100, 0, 0, 0, 64509, 0, 0, 0, 'Батаарский заклинатель пламени to Player');
INSERT INTO `creature_text` VALUES (59037, 4, 0, 'Кун-Динь набросился на вас! Сражайтесь! |TInterface\\Icons\\ability_warrior_secondwind.blp:20|t', 42, 0, 100, 0, 0, 0, 59814, 5000, 5000, 116504, 'Кун-Динь to Player');
INSERT INTO `creature_text` VALUES (59040, 3, 0, 'Ты что тут делаешь?! Ты что, не знаешь, что яунголы начали вторжение на вершину Кунь-Лай?', 12, 0, 100, 0, 0, 0, 57973, 2000, 2000, 0, 'Куньлайский беженец to Player');
INSERT INTO `creature_text` VALUES (59055, 3, 0, 'Может быть, $gон:она; герой, пришедший из-за туманов, чтобы спасти нас?', 12, 0, 100, 0, 0, 0, 58012, 6000, 6000, 0, 'Куньлайский беженец to Player');
INSERT INTO `creature_text` VALUES (59058, 0, 0, '$n, ты $gпринес:принесла; реликвии как раз вовремя. Старейшина готов говорить с водой.', 12, 0, 100, 0, 0, 0, 58104, 0, 0, 0, 'Хранитель жемчужин Фуцзинь to Player');
INSERT INTO `creature_text` VALUES (59058, 0, 0, 'Как же! Тьфу! Много тысяч лет назад племя Жемчужного Плавника правило всей Пандарией, а сейчас от него осталась только эта деревня. И все потому, что мы поверили чужакам... хозенам.', 12, 0, 100, 0, 0, 0, 58055, 0, 0, 0, 'Хранитель жемчужин Фуцзинь to Player');
INSERT INTO `creature_text` VALUES (59067, 3, 0, 'I have nothing to do with it. It’s all Guqgut, the leader of the Artful Tail tribe.', 12, 0, 100, 0, 0, 30629, 58014, 14000, 14000, 0, 'Здоровяк Кажаши to Салли \"Рассольный\" Маклири');
INSERT INTO `creature_text` VALUES (59067, 3, 0, 'Мы не любим, когда в нашу деревню заходят чужаки.', 12, 0, 100, 0, 0, 30630, 58015, 36000, 36000, 0, 'Здоровяк Кажаши to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (59067, 3, 0, 'Погоди! Я чувствую присутствие чужаков. Хозены? А ну выходите!', 12, 0, 100, 0, 0, 30628, 58013, 3000, 3000, 0, 'Здоровяк Кажаши to Player');
INSERT INTO `creature_text` VALUES (59067, 3, 0, 'Пока вы можете остаться, но когда раны Тейлора затянутся, вам придется уйти.', 12, 0, 100, 1, 0, 30631, 58016, 41000, 41000, 0, 'Здоровяк Кажаши to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (59067, 3, 0, 'Только без резких движений.', 12, 0, 100, 1, 0, 30633, 61289, 29000, 29000, 0, 'Здоровяк Кажаши to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (59068, 3, 0, 'У нас нет выбора. Убери оружие, воин, и мы сделаем то же самое.', 12, 0, 100, 0, 0, 27707, 58017, 23000, 23000, 0, 'Релл Ночной Ветер to Мишка');
INSERT INTO `creature_text` VALUES (59069, 3, 0, 'Что ты сделал с адмиралом?', 12, 0, 100, 0, 0, 27847, 58020, 10000, 10000, 0, 'Салли \"Рассольный\" Маклири to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (59070, 3, 0, 'Командир, вы думаете, мы можем ему верить?', 12, 0, 100, 0, 0, 27660, 58019, 21000, 21000, 0, 'Мишка to Здоровяк Кажаши');
INSERT INTO `creature_text` VALUES (59073, 0, 0, 'Добро пожаловать в деревню Бинан, |3-6($c). Умоляю, помоги нам.', 12, 0, 100, 20, 0, 0, 58440, 0, 0, 0, 'Мэр Ежевичный Посох to Player');
INSERT INTO `creature_text` VALUES (59073, 3, 0, 'Welcome to Binan Village, $c. I beg you... please help us.', 12, 0, 100, 20, 0, 0, 58440, 44343, 44343, 114224, 'Mayor Bramblestaff to Player');
INSERT INTO `creature_text` VALUES (59073, 3, 0, 'You have saved Binan Village, $n. Ring the gong so all shall know!', 12, 0, 100, 0, 0, 0, 58467, 886000, 886000, 0, 'Mayor Bramblestaff to Player');
INSERT INTO `creature_text` VALUES (59073, 3, 0, 'Ты $gспас:спасла; деревню Бинан, $n. Ударьте в гонг, чтобы все знали об этом!', 12, 0, 100, 0, 0, 0, 58467, 1405000, 1405000, 0, 'Мэр Ежевичный Посох to Player');
INSERT INTO `creature_text` VALUES (59074, 3, 0, 'Это... неужели получилось?!', 12, 0, 100, 6, 0, 28293, 59305, 648125, 648125, 0, 'Дядюшка Гао to Player');
INSERT INTO `creature_text` VALUES (59075, 0, 0, 'Интересно, есть ли что-нибудь, чего этот лекарь не умеет?', 12, 0, 100, 1, 0, 28030, 63210, 0, 0, 0, 'Хмелевар-предок to Булькающий бражный хмелементаль');
INSERT INTO `creature_text` VALUES (59075, 1, 0, 'Эй... эй, ТЫ! Это НАШИ... летающие... пивные монстры?', 14, 0, 100, 1, 0, 27510, 63137, 0, 0, 0, 'Хмелевар-предок to Тучный бражный хмелементаль');
INSERT INTO `creature_text` VALUES (59076, 3, 0, 'Looks like you got more than enough. Quick, $n, I think I can see the yaungol reinforcements massing on the horizon.', 12, 0, 100, 396, 0, 0, 58510, 662469, 662469, 114256, 'Commander Hsieh to Player');
INSERT INTO `creature_text` VALUES (59076, 3, 0, 'Думаю, этого более чем достаточно. Скорее, $n, я уже вижу, как на горизонте собираются новые отряды яунголов.', 12, 0, 100, 396, 0, 0, 58510, 3000000, 3000000, 0, 'Командир Се to Player');
INSERT INTO `creature_text` VALUES (59077, 0, 0, '$c, приветствую тебя. Твои друзья внутри. Но сначала могу я попросить кое о чем?', 12, 0, 100, 1, 0, 0, 58493, 0, 0, 0, 'Аптекарь Чэн to Player');
INSERT INTO `creature_text` VALUES (59077, 3, 0, 'Greetings, $c. Your people are inside. But first, may I ask a favor?', 12, 0, 100, 1, 0, 0, 58493, 34625, 34625, 114244, 'Apothecary Cheng to Player');
INSERT INTO `creature_text` VALUES (59080, 0, 0, 'Раз вы не хотите учиться, придется вас заставить.', 14, 0, 100, 0, 0, 27496, 63346, 0, 0, 0, 'Темный магистр Гандлинг to Player');
INSERT INTO `creature_text` VALUES (59080, 1, 0, 'Начнем урок!', 14, 0, 100, 0, 0, 27477, 58099, 0, 0, 0, 'Темный магистр Гандлинг to 0');
INSERT INTO `creature_text` VALUES (59080, 2, 0, '|TInterface\\Icons\\inv_misc_book_01.blp:20|t%s преподает |3-2($n) |cFFFF0000|Hspell:113395|h[суровый урок]|h|r!', 41, 0, 100, 0, 0, 0, 58103, 0, 0, 0, 'Темный магистр Гандлинг to Player');
INSERT INTO `creature_text` VALUES (59080, 3, 0, 'Занятия... окончены.', 14, 0, 100, 0, 0, 27478, 58102, 0, 0, 0, 'Темный магистр Гандлинг to Player');
INSERT INTO `creature_text` VALUES (59083, 0, 0, 'Fools! Fight harder! Do not let these puny pandaren defeat you!', 14, 0, 100, 0, 0, 0, 62838, 0, 0, 0, 'Bataari Fire-Warrior to Player');
INSERT INTO `creature_text` VALUES (59083, 0, 0, 'Глупцы! Сражайтесь! Не дайте этим жалким пандаренам победить вас!', 14, 0, 100, 0, 0, 0, 62838, 0, 0, 0, 'Батаарский огненный воин to Player');
INSERT INTO `creature_text` VALUES (59083, 3, 0, 'I will crush you!', 12, 0, 100, 0, 0, 0, 117447, 582000, 582000, 0, 'Батаарский огненный воин');
INSERT INTO `creature_text` VALUES (59098, 3, 0, 'Х-х-хe-хe-хе! Ты сдохнешь в грязи!', 12, 0, 100, 0, 0, 0, 58069, 7000, 7000, 0, 'Налетчик Речного Клинка to Player');
INSERT INTO `creature_text` VALUES (59113, 0, 0, 'A chittering sound echoes across the trees, then fades...', 41, 0, 100, 0, 0, 0, 80416, 0, 0, 0, 'Generic Bunny to Player');
