INSERT INTO `creature_text` VALUES (60680, 3, 0, 'Смотри, смотри, красотка! $gДобрый:Добрая:r; |3-6($r) $gпринес:принесла:r; тебе кафу!', 12, 0, 100, 0, 0, 0, 60177, 2000, 2000, 0, 'Племянник Берберри to Player');
INSERT INTO `creature_text` VALUES (60683, 3, 0, 'Ты $gвыбрал:выбрала; не ту сторону в битве, |3-6($r). Сторону свирепых тварей!', 14, 0, 100, 0, 0, 0, 60288, 60000, 60000, 0, 'Дмонг Наруук to Player');
INSERT INTO `creature_text` VALUES (60683, 4, 0, 'Нет! Я не могу сосредоточиться на факеле!', 14, 0, 100, 0, 0, 0, 60180, 29000, 29000, 0, 'Дмонг Наруук');
INSERT INTO `creature_text` VALUES (60683, 4, 0, 'Нужно... вернуть... контроль...', 14, 0, 100, 0, 0, 0, 60181, 33000, 33000, 0, 'Дмонг Наруук');
INSERT INTO `creature_text` VALUES (60684, 3, 0, 'I have not forgotten! You forgot that among these prisoners is my husband.', 12, 0, 100, 1, 0, 0, 60310, 32000, 32000, 0, 'Суна Беззвучный Удар to Бань Медвежье Сердце');
INSERT INTO `creature_text` VALUES (60684, 3, 0, 'И зачем только он оставил тебя командующим? Ты хочешь, чтобы мы спрятались под одеяло и ждали, когда яунголы постучатся в дверь?', 12, 0, 100, 0, 0, 0, 60349, 262000, 262000, 0, 'Суна Беззвучный Удар to Бань Медвежье Сердце');
INSERT INTO `creature_text` VALUES (60684, 3, 0, 'Мы уже вынудили их перейти к обороне. Их становится все меньше и меньше.', 12, 0, 100, 1, 0, 0, 60309, 2000, 2000, 0, 'Суна Беззвучный Удар to Бань Медвежье Сердце');
INSERT INTO `creature_text` VALUES (60684, 3, 0, 'Но там же Линь!', 12, 0, 100, 0, 0, 0, 60329, 276000, 276000, 0, 'Суна Беззвучный Удар to Бань Медвежье Сердце');
INSERT INTO `creature_text` VALUES (60684, 3, 0, 'Нужно атаковать сейчас, Бань, пока их внимание сосредоточено на подступах к Кунь-Лай.', 12, 0, 100, 0, 0, 0, 60327, 238000, 238000, 0, 'Суна Беззвучный Удар');
INSERT INTO `creature_text` VALUES (60684, 3, 0, 'Ты забыл, что у них в плену мой муж? У нас нет времени на раздумья!', 12, 0, 100, 0, 0, 0, 60328, 249000, 249000, 0, 'Суна Беззвучный Удар to Бань Медвежье Сердце');
INSERT INTO `creature_text` VALUES (60687, 3, 0, 'И нас тоже. Разве ты не видела, сколько у нас раненых? Разве ты забыла, скольких забрали в плен?', 12, 0, 100, 6, 0, 0, 60313, 6000, 6000, 0, 'Бань Медвежье Сердце to Суна Беззвучный Удар');
INSERT INTO `creature_text` VALUES (60688, 3, 0, 'Успокойтесь оба. У нас много дел, и спасение Линя – одна из самых главных задач.', 12, 0, 100, 1, 0, 0, 60315, 36000, 36000, 0, 'Тажань Чжу to Суна Беззвучный Удар');
INSERT INTO `creature_text` VALUES (60689, 3, 0, 'Не может быть: |3-6($c), $gкоторый:которая;, похоже, знает, что делать на поле битвы.', 12, 0, 100, 0, 0, 0, 60355, 121000, 121000, 0, 'Защитник Лун-Ин to Player');
INSERT INTO `creature_text` VALUES (60689, 3, 0, 'Не стесняйся, |3-6($c). Яунголов хватит на всех!', 12, 0, 100, 0, 0, 0, 60357, 144000, 144000, 0, 'Защитник Лун-Ин to Player');
INSERT INTO `creature_text` VALUES (60689, 3, 0, 'Прибыло подкрепление! Ну же, |3-6($r), приди нам на помощь!', 12, 0, 100, 0, 0, 0, 60354, 365000, 365000, 0, 'Защитник Лун-Ин to Player');
INSERT INTO `creature_text` VALUES (60689, 4, 0, 'Пора тебе показаться, |3-6($r)!', 12, 0, 100, 0, 0, 0, 60353, 251000, 251000, 0, 'Защитник Лун-Ин to Player');
INSERT INTO `creature_text` VALUES (60693, 3, 0, 'Они всю наживку откусывают, а крючок не берут.', 12, 0, 100, 0, 0, 0, 60202, 351000, 351000, 0, 'Рыболов с верфи');
INSERT INTO `creature_text` VALUES (60694, 3, 0, 'Куда катится мир! Уже даже умереть нельзя, чтобы к тебе не цеплялись крабы.', 12, 0, 100, 0, 0, 0, 63524, 3021000, 3021000, 0, 'Мудрец Ляо');
