INSERT INTO `creature_text` VALUES (60701, 0, 0, 'Скоро вы поймете, почему мои слуги боятся теней!', 14, 0, 100, 0, 0, 0, 62976, 0, 0, 0, 'Цзыань Беспросветная Тьма to Player');
