INSERT INTO `creature_text` VALUES (60701, 2, 0, 'Тьма придет за вами, а вместе с ней и смерть.', 14, 0, 100, 0, 0, 0, 62981, 0, 0, 0, 'Цзыань Беспросветная Тьма to 0');
INSERT INTO `creature_text` VALUES (60701, 5, 0, 'Мрак... окружает меня!', 14, 0, 100, 0, 0, 0, 62977, 0, 0, 0, 'Цзыань Беспросветная Тьма to Цзыань Беспросветная Тьма');
INSERT INTO `creature_text` VALUES (60708, 0, 0, 'Вы разгневали императора! Я ПРИГОВАРИВАЮ ВАС К СМЕРТИ!', 14, 0, 100, 0, 0, 0, 62947, 0, 0, 0, 'Мын Безумный to Player');
