INSERT INTO `creature_text` VALUES (60729, 0, 0, 'Если мы позволим Цзи сделать это, Шэнь-Цзынь Су может погибнуть! За мной, $p! Быстрее!', 12, 0, 100, 5, 0, 27412, 55140, 0, 0, 0, 'Аиса Воспевающая Облака');
