INSERT INTO `creature_text` VALUES (60734, 4, 0, 'Гол... госс...', 12, 0, 100, 0, 0, 0, 60289, 8000, 8000, 0, 'Голгосс to Player');
INSERT INTO `creature_text` VALUES (60735, 3, 0, 'Где твои подхалимы, великий вождь? На этот раз они тебе не помогут.', 12, 0, 100, 0, 0, 0, 60260, 1320000, 1320000, 0, 'Катак Побежденный to Цзюн Дук');
