INSERT INTO `creature_text` VALUES (60741, 3, 0, 'Ты предлагаешь просто сидеть и ждать, пока нас осенит? Шэнь-Цзынь Су может погибнуть, если мы не взорвем корабль!', 12, 0, 100, 6, 0, 27330, 60191, 26000, 26000, 0, 'Цзи Огненная Лапа to Аиса Воспевающая Облака');
