INSERT INTO `creature_text` VALUES (60763, 0, 0, '$gВидел:видела;, как яунголы сражались сдруг с другом? Кажется, их проблемы серьезнее, чем мы думали.', 12, 0, 100, 2, 0, 0, 60234, 0, 0, 0, 'Стрелок Лун-Ин to Player');
