INSERT INTO `creature_text` VALUES (60888, 0, 0, 'I tolerate your presence only out of respect for our new allies. Do not test my patience.', 12, 0, 100, 0, 0, 28117, 60359, 0, 0, 0, 'Корга Крепкая Грива');
INSERT INTO `creature_text` VALUES (60889, 0, 0, 'Отойди, таурен, или снова окажешься в цепях.', 12, 0, 100, 15, 0, 27522, 60623, 0, 0, 0, 'Делора Львиное Сердце');
INSERT INTO `creature_text` VALUES (60900, 0, 0, 'Давай-ка разыщем этого инженера. Может быть, убрать занозу будет не так уж и сложно.', 12, 0, 100, 396, 0, 27335, 60379, 0, 0, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` VALUES (60901, 1, 0, 'NOOO!', 12, 0, 100, 0, 0, 0, 60381, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (60901, 2, 0, 'Lin, heart of my heart! No, please, NO! It cannot be!', 12, 0, 100, 0, 0, 0, 60382, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (60901, 3, 0, 'If only I had come sooner! If only Ban had listened to me!', 12, 0, 100, 0, 0, 0, 60383, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (60901, 4, 0, 'Shhhhhh, my sweet darling, sleep now and be at peace.', 12, 0, 100, 0, 0, 0, 60384, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (60902, 3, 0, 'Видишь ли, своим поступком ты $gподдержал:поддержала; самую ценную для нас, пандаренов, истину.', 12, 0, 100, 0, 0, 0, 60371, 2000, 2000, 0, 'Мудрец Ляо to Player');
INSERT INTO `creature_text` VALUES (60951, 0, 0, 'Насколько я понимаю, в этом бою каждый был сам за себя. Зачем яунголам убивать друг друга просто так?', 12, 0, 100, 16, 0, 0, 60475, 0, 0, 0, 'Сяо Ту to Player');
INSERT INTO `creature_text` VALUES (60951, 0, 0, 'Они боролись за еду. И вместо того чтобы делиться, кто-то ее просто испортил. Но зачем?', 12, 0, 100, 16, 0, 0, 60474, 0, 0, 0, 'Сяо Ту to Player');
INSERT INTO `creature_text` VALUES (60951, 0, 0, 'Смотри-ка, вещи Суны! Должно быть, она решила расположиться здесь на ночлег. Но где она сама?', 12, 0, 100, 16, 0, 0, 60700, 0, 0, 0, 'Сяо Ту to Player');
INSERT INTO `creature_text` VALUES (60951, 0, 0, 'Это вождь яунголов. Его убили, как и всех остальных. Похоже, в последнем бою он не сумел сохранить порядок.', 12, 0, 100, 16, 0, 0, 60476, 0, 0, 0, 'Сяо Ту to Player');
INSERT INTO `creature_text` VALUES (60968, 0, 0, 'Ты ищешь оружие грозового бога? Выйди вперед, шаман.', 12, 0, 100, 0, 0, 60621, 102458, 0, 0, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (60968, 1, 0, 'Многие жаждали завладеть артефактами Хранителя бурь. Но испытаний не прошел никто. Иди же и повстречайся со своей судьбой!', 12, 0, 100, 0, 0, 60624, 102356, 0, 0, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (60968, 2, 0, 'Первый бой – испытание силы. Победителем выйдет только один. Скажи, когда приготовишься начать.', 12, 0, 100, 0, 0, 60612, 102465, 0, 0, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (60968, 3, 0, 'Шаман победил! Первое испытание окончено.', 12, 0, 100, 0, 0, 60619, 102472, 0, 0, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (60968, 4, 0, 'Настоящий герой никогда не сражается в одиночку. Следующее испытание – работа в команде. Выбери себе напарника.', 12, 0, 100, 0, 0, 60626, 102475, 0, 0, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (60968, 5, 0, 'Помните! Чтобы победить, вы должны действовать как одна команда. 3... 2... 1... В бой!', 12, 0, 100, 0, 0, 60633, 102483, 0, 0, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (60968, 6, 0, 'Поздравляю! Второе испытание пройдено.', 12, 0, 100, 0, 0, 60644, 102495, 0, 0, 0, 'Сюэнь to 976');
INSERT INTO `creature_text` VALUES (60968, 7, 0, 'Твое мастерство несомненно. Теперь тебя ждет последнее испытание, которое еще никому не удавалось пройти.', 12, 0, 100, 0, 0, 60648, 102499, 0, 0, 0, 'Сюэнь to 976');
INSERT INTO `creature_text` VALUES (60968, 8, 0, 'Оружие Хранителя бурь так могущественно, что даже Властелин Грома не посмел прикоснуться к нему.', 12, 0, 100, 0, 0, 60649, 102500, 0, 0, 0, 'Сюэнь to 976');
INSERT INTO `creature_text` VALUES (60968, 9, 0, 'Кулак Ра-дена станет твоей последней проверкой. Возьми его – и если ты выживешь, это оружие будет служить тебе верой и правдой.', 12, 0, 100, 0, 0, 60650, 102501, 0, 0, 0, 'Сюэнь to 976');
INSERT INTO `creature_text` VALUES (60968, 10, 0, 'Что-то не так... Здесь кто-то чужой...', 12, 0, 100, 0, 0, 60661, 102520, 0, 0, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (60968, 11, 0, 'Тебе удалось всех нас спасти...', 12, 0, 100, 0, 0, 60608, 102545, 0, 0, 0, 'Сюэнь to 976');
INSERT INTO `creature_text` VALUES (60968, 12, 0, 'Больше испытаний не требуется, шаман. Оружие Хранителя бурь нашло себе $gнового хозяина:новую хозяйку;.', 12, 0, 100, 0, 0, 60609, 102547, 0, 0, 0, 'Сюэнь to 976');
INSERT INTO `creature_text` VALUES (60968, 13, 0, 'Ступай же, последователь Служителей Земли, я благословляю тебя. Азероту нужно отразить удар, и такие герои, как ты, должны принять вызов.', 12, 0, 100, 0, 0, 60611, 109902, 0, 0, 0, 'Сюэнь to 976');
INSERT INTO `creature_text` VALUES (60970, 3, 0, 'Ну, теперь мы хотя бы знаем, что Андуин жив. Отлично, $n. А сейчас поговори со старейшиной Лушанем. Он уже справлялся о тебе.', 12, 0, 100, 0, 0, 0, 61205, 17000, 17000, 0, 'Адмирал Тейлор to Player');
INSERT INTO `creature_text` VALUES (60973, 0, 0, 'I\'\'ve never performed this ritual. I don\'\'t know what will happen, exactly.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (60973, 1, 0, 'It\'\'s been passed down from ancient times, but none of our people have needed to use it since then.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (60973, 2, 0, 'Let us see if this is sufficient to cleanse the corruption from my people\'\'s souls.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (60973, 3, 0, 'It worked! It wo-', 12, 0, 100, 0, 0, 0, 60999, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (60973, 4, 0, 'Wh... what is happening why is the ritual affecting her?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
