INSERT INTO `creature_text` VALUES (60978, 1, 0, 'Wow that hurt! Well fought, friend.', 12, 0, 100, 1, 0, 0, 60640, 0, 0, 0, 'EvEndText text');
INSERT INTO `creature_text` VALUES (60979, 0, 0, 'Ken-Ken\'s got this!', 12, 0, 100, 1, 0, 0, 60739, 0, 0, 0, 'EvStartText text');
