INSERT INTO `creature_text` VALUES (61050, 3, 0, 'Во мне было... столько ненависти...', 12, 0, 100, 2, 0, 0, 60570, 63000, 63000, 0, 'Обезумевший следопыт Шадо-Пан to Испепеляющая ненависть');
INSERT INTO `creature_text` VALUES (61050, 3, 0, 'Ч-что это было?', 12, 0, 100, 2, 0, 0, 60569, 288000, 288000, 0, 'Обезумевший следопыт Шадо-Пан to Испепеляющая ненависть');
INSERT INTO `creature_text` VALUES (61050, 3, 0, 'Что? Как странно, я чувствую покой...', 12, 0, 100, 2, 0, 0, 60572, 166000, 166000, 0, 'Обезумевший следопыт Шадо-Пан to Испепеляющая ненависть');
INSERT INTO `creature_text` VALUES (61050, 4, 0, 'I ... what happened? I am now much calmer.', 12, 0, 100, 2, 0, 0, 60575, 20000, 20000, 0, 'Обезумевший следопыт Шадо-Пан to Испепеляющая ненависть');
INSERT INTO `creature_text` VALUES (61050, 4, 0, 'Неужели мной завладели ша? До этой минуты мне это и в голову не приходило...', 12, 0, 100, 2, 0, 0, 60576, 23000, 23000, 0, 'Обезумевший следопыт Шадо-Пан to Испепеляющая ненависть');
INSERT INTO `creature_text` VALUES (61050, 4, 0, 'Спасибо, $gмой избавитель:моя избавительница;. Без тебя мне не удалось бы избежать цепких лап ша.', 12, 0, 100, 2, 0, 0, 60573, 23000, 23000, 0, 'Обезумевший следопыт Шадо-Пан to Испепеляющая ненависть');
INSERT INTO `creature_text` VALUES (61050, 4, 0, 'Так это были... Ша? Мне так хотелось разорвать всех на куски!', 12, 0, 100, 2, 0, 0, 60574, 20000, 20000, 0, 'Обезумевший следопыт Шадо-Пан to Испепеляющая ненависть');
INSERT INTO `creature_text` VALUES (61055, 0, 0, 'Has Ban sent you to kill me, then? Just like he killed my husband?', 12, 0, 100, 0, 0, 0, 60643, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61055, 1, 0, 'I see now... my hatred... consumed me. Tell Ban... all is... forgiven.', 12, 0, 100, 0, 0, 0, 60646, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61065, 0, 0, 'I\'m starting a ritual. Get ready!', 12, 0, 100, 0, 0, 0, 61017, 0, 0, 0, 'Говорящий с водой Гожай to Источник порчи');
INSERT INTO `creature_text` VALUES (61065, 0, 0, 'Друг, я возвращаюсь к беженцам. Найди нас там, чтобы мы смогли как следует тебя отблагодарить.', 12, 0, 100, 0, 0, 0, 61104, 0, 0, 0, 'Говорящий с водой Гожай to Player');
INSERT INTO `creature_text` VALUES (61065, 0, 0, 'Если уничтожить взрывную ненависть, то его щит исчезнет! Быстрее!', 12, 0, 100, 5, 0, 0, 68558, 0, 0, 0, 'Говорящий с водой Гожай to Шай Ху');
INSERT INTO `creature_text` VALUES (61065, 0, 0, 'Он мертв! Получилось!', 14, 0, 100, 0, 0, 0, 61103, 0, 0, 0, 'Говорящий с водой Гожай to Player');
INSERT INTO `creature_text` VALUES (61065, 0, 0, 'Пора, друг мой. Приготовься к бою!', 12, 0, 100, 0, 0, 0, 61018, 0, 0, 0, 'Говорящий с водой Гожай to Источник порчи');
INSERT INTO `creature_text` VALUES (61066, 3, 0, 'Где часовые на северной стороне? Я не увидел ни одного, когда подъезжал к лагерю. Ты что-то расслабился, Гао-Жань.', 12, 0, 100, 1, 0, 0, 61754, 7000, 48000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (61067, 3, 0, 'Благодарю вас, господин Чжу. Удачной охоты!', 12, 0, 100, 1, 0, 0, 61771, 3441000, 3441000, 0, 'Гао-Жань Закаленный to Player');
INSERT INTO `creature_text` VALUES (61067, 3, 0, 'Простите, господин Чжу, но это не так. На наших плечах лежит слишком много. Нас атакуют на всех фронтах, мои бойцы истощены, и у меня нет ни одного свободного воина.', 12, 0, 100, 1, 0, 0, 61755, 17000, 58000, 0, 'Гао-Жань Закаленный to Player');
INSERT INTO `creature_text` VALUES (61069, 1, 0, 'Твоя ярость! Мне придает сил твоя ярость!', 14, 0, 100, 5, 0, 0, 60581, 0, 0, 0, 'Шай Ху');
INSERT INTO `creature_text` VALUES (61069, 3, 0, 'Нет! Не может быть! Ты меня не остановишь!', 14, 0, 100, 0, 0, 0, 60583, 0, 12000, 0, 'Шай Ху');
INSERT INTO `creature_text` VALUES (61069, 4, 0, 'I ... feel something. Is that ... calm?', 12, 0, 100, 0, 0, 0, 60584, 54000, 54000, 0, 'Шай Ху to Player');
INSERT INTO `creature_text` VALUES (61069, 4, 0, 'Ваши ничтожные атаки тщетны!', 14, 0, 100, 0, 0, 0, 68520, 34000, 34000, 0, 'Шай Ху');
INSERT INTO `creature_text` VALUES (61069, 4, 0, 'Г-р-р! Ярость заполняет меня!', 14, 0, 100, 0, 0, 0, 68521, 187000, 187000, 0, 'Шай Ху to Взрывная ненависть');
INSERT INTO `creature_text` VALUES (61092, 0, 0, 'Ненависть переполняет вас!', 42, 0, 100, 0, 0, 0, 60579, 0, 0, 0, 'Испепеляющая ненависть to Player');
INSERT INTO `creature_text` VALUES (61093, 1, 0, 'I will NEVER yield!', 14, 0, 100, 0, 0, 0, 63768, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61093, 2, 0, 'Thank you, $n. You have saved us all.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61093, 3, 0, 'Remember, it is at the darkest moment that light shines the brightest.', 14, 0, 100, 0, 0, 0, 63898, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61093, 4, 0, 'Well done, heroes.', 14, 0, 100, 0, 0, 0, 63899, 0, 0, 0, '');
