INSERT INTO `creature_text` VALUES (61131, 3, 0, 'Ло-кал...', 12, 0, 100, 0, 0, 0, 60638, 438000, 438000, 0, 'Хранитель тайн Могу\'шан to Player');
INSERT INTO `creature_text` VALUES (61131, 3, 0, 'Туро фон Курос!', 12, 0, 100, 0, 0, 0, 60635, 490000, 490000, 0, 'Хранитель тайн Могу\'шан to Player');
INSERT INTO `creature_text` VALUES (61140, 0, 0, 'Bah! Bested by de likes of you! What a shame for a Zandalari Priest ta endure.', 14, 0, 100, 15, 0, 29082, 60653, 0, 0, 0, 'Gara\'jal the Spiritbinder to Gara\'jal the Spiritbinder');
INSERT INTO `creature_text` VALUES (61140, 3, 0, '...а мне все равно. Забирайте свои побрякушки! Пригодятся.', 14, 0, 100, 14, 0, 29083, 60654, 14000, 14000, 0, 'Душелов Гара\'джал to Душелов Гара\'джал');
INSERT INTO `creature_text` VALUES (61140, 3, 0, 'А я пойду искать новый приют для моей души.', 14, 0, 100, 1, 0, 29084, 60655, 20000, 20000, 0, 'Душелов Гара\'джал to Душелов Гара\'джал');
INSERT INTO `creature_text` VALUES (61140, 3, 0, 'Смерть – это порог, а время – окно. Я еще вернусь! Ха-ха-ха!', 14, 0, 100, 0, 0, 29085, 60656, 26000, 26000, 0, 'Душелов Гара\'джал to Душелов Гара\'джал');
INSERT INTO `creature_text` VALUES (61140, 3, 0, 'Ха! Вы посмотрите, кто меня победил! Какой позор для зандаларского жреца.', 14, 0, 100, 15, 0, 0, 60653, 5000, 5000, 0, 'Душелов Гара\'джал to Душелов Гара\'джал');
INSERT INTO `creature_text` VALUES (61161, 3, 0, 'I said. This yak is terrible. We will not leave this temple of yaks until it is cured or until we are replaced.', 12, 0, 100, 0, 0, 0, 61116, 152000, 152000, 0, 'Синескок to Player');
INSERT INTO `creature_text` VALUES (61161, 3, 0, 'Видишь? Плохой як! Ну-ка еще, |3-6($r). Попробуй покормить его.', 12, 0, 100, 0, 0, 0, 61110, 103000, 103000, 0, 'Синескок to Player');
INSERT INTO `creature_text` VALUES (61161, 3, 0, 'Как видишь, это дефективный як. Подойди, |3-6($r). Попробуй его погладить.', 12, 0, 100, 0, 0, 0, 61108, 10000, 10000, 0, 'Синескок to Player');
INSERT INTO `creature_text` VALUES (61161, 3, 0, 'Понятно. А можно хотя бы позаботиться о моем? Он даже седока не хочет нести.', 12, 0, 100, 0, 0, 0, 61114, 144000, 144000, 0, 'Синескок to Player');
INSERT INTO `creature_text` VALUES (61162, 0, 0, 'Брат Синескок... Ты чрезмерно настойчив. И ты плохо ведешь себя по отношению к |3-2($r).', 12, 0, 100, 0, 0, 0, 61115, 0, 0, 0, 'Брат Каменная Ноша to Player');
INSERT INTO `creature_text` VALUES (61218, 3, 0, '%s протягивает вам пару садовых ножниц и указывает на большое лохматое дерево.', 16, 0, 100, 25, 0, 0, 62628, 13000, 13000, 125433, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61218, 3, 0, 'I am one of the keepers of the history of my people. My instrument is the word.', 12, 0, 100, 0, 0, 31788, 62633, 46000, 46000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61218, 3, 0, 'Well Met! Чем я могу тебе помочь?', 12, 0, 100, 404, 0, 0, 63377, 143000, 143000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61218, 3, 0, 'А, вот и они, мои малышки. Видишь, как просто завести здесь новых друзей?', 12, 0, 100, 404, 0, 31795, 63374, 121000, 121000, 125433, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61218, 3, 0, 'Ах, ясно – это один из великих воинов Альянса? На вашем гербе красуется лев.', 12, 0, 100, 1, 0, 31793, 63372, 89000, 89000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61218, 3, 0, 'в изгибах ствола дерева, в ярких красках восхода, даже в безупречных движениях воина с мечом.', 12, 0, 100, 273, 0, 0, 62647, 80000, 80000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61218, 3, 0, 'Делай осторожные надрезы... Пусть дерево само решит, как ему расти.', 12, 0, 100, 1, 0, 31786, 62630, 15000, 15000, 125433, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61218, 3, 0, 'Значит, вы стойкие и свирепые люди. Но помни: лев убивает только, когда голоден.', 12, 0, 100, 1, 0, 0, 63373, 97000, 97000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61218, 3, 0, 'Многие отрезают все, что им не нравится, но это убивает растение. Пандарены так не поступают.', 12, 0, 100, 274, 0, 0, 62631, 19000, 19000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61218, 3, 0, 'Мои поручения почти закончились. Иди, я покажу тебе свою коллекцию экзотических птиц. Я даже разрешу тебе покормить их.', 12, 0, 100, 0, 0, 31794, 62654, 107000, 107000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61218, 3, 0, 'Нет, нет, молчи. Пусть за тебя говорит твой рисунок. Мы, пандарены, ценим красоту во всем, что нас окружает:', 12, 0, 100, 404, 0, 31789, 62646, 71000, 71000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61218, 3, 0, 'Пойдем со мной, и мы будем учиться друг у друга. Твой народ любит сады?', 12, 0, 100, 0, 0, 31785, 62625, 2000, 2000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61218, 3, 0, 'Помни, что не всех в Пандарии можно завоевать острием меча.', 12, 0, 100, 1, 0, 0, 63375, 130000, 130000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61218, 3, 0, 'Похоже... мои птицы полюбили тебя. Я последую их примеру.', 12, 0, 100, 4, 0, 31796, 63376, 139000, 139000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61218, 3, 0, 'Так-так. Кто бы мог подумать, что здесь скрывается такая красота! Теперь я вижу, что этот твой \"Альянс\", меняя мир, действует аккуратно. Любопытно...', 12, 0, 100, 4, 0, 31787, 62632, 32000, 32000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61218, 3, 0, 'Увы и ах, мне не хватает художественного дара. Подскажи мне, как украсить эту стену?', 12, 0, 100, 274, 0, 0, 62634, 56000, 56000, 125434, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61243, 0, 0, 'I hear something...', 14, 0, 100, 0, 0, 32495, 63254, 0, 0, 0, 'INTRO');
INSERT INTO `creature_text` VALUES (61243, 1, 0, 'Search his corpse!', 14, 0, 100, 0, 0, 32496, 63257, 0, 0, 0, 'killing a player');
INSERT INTO `creature_text` VALUES (61243, 2, 0, 'Slay them!', 14, 0, 100, 0, 0, 32497, 63258, 0, 0, 0, 'SPELL');
INSERT INTO `creature_text` VALUES (61243, 3, 0, 'Stop them!', 14, 0, 100, 0, 0, 32488, 63255, 0, 0, 0, 'AGGRO');
INSERT INTO `creature_text` VALUES (61243, 4, 0, 'Such a waste...', 14, 0, 100, 0, 0, 32494, 63256, 0, 0, 0, 'DEATH');
INSERT INTO `creature_text` VALUES (61252, 4, 0, 'Спасибо, $p. Ты неплохо сражаешься для $gтого:той;, кто не состоит в нашем ордене. С остальным я справлюсь сам.', 12, 0, 100, 0, 0, 0, 61270, 9000, 9000, 0, 'Страж Шадо-Пан to Player');
INSERT INTO `creature_text` VALUES (61260, 3, 0, 'Давай, чемпик! Порычай Рок Рок!', 12, 0, 100, 1, 0, 0, 60743, 16000, 16000, 0, 'Тренер Рок Рок');
INSERT INTO `creature_text` VALUES (61260, 3, 0, 'Нет! Нет-нет-нет-нет-нет! Борец надо вставай! Вставай, шнуропс!', 12, 0, 100, 0, 0, 29027, 60906, 50000, 50000, 0, 'Тренер Рок Рок to Борец');
INSERT INTO `creature_text` VALUES (61289, 3, 0, 'Старая Шляпа – самый лучший тренер. Если $p послушает меня, то будет удачкой!', 12, 0, 100, 0, 0, 0, 60777, 36000, 36000, 113262, 'Старая Шляпа to Player');
INSERT INTO `creature_text` VALUES (61297, 3, 0, 'Гробница Завоевателей находится прямо под нами. Иди по пыльной тропинке и выйдешь ко входу. Поторопись!', 12, 0, 100, 0, 0, 0, 66969, 11000, 11000, 0, 'Образ хранителя истории Чо to Player');
INSERT INTO `creature_text` VALUES (61303, 0, 0, 'Death to the Shado-Pan! Death to all Pandaren!', 14, 0, 100, 0, 0, 0, 60730, 0, 0, 0, 'Kobai');
INSERT INTO `creature_text` VALUES (61303, 0, 0, 'Вперед, яунголы, шагом марш! Всем трусам – смерть!', 14, 0, 100, 0, 0, 0, 60731, 0, 0, 0, 'Кобай');
INSERT INTO `creature_text` VALUES (61303, 0, 0, 'Принесите мне их головы!', 14, 0, 100, 0, 0, 0, 84388, 0, 0, 0, 'Кобай');
INSERT INTO `creature_text` VALUES (61303, 3, 0, 'Смерть шадо-панам! Смерть всем пандаренам!', 14, 0, 100, 0, 0, 0, 60730, 1000, 1000, 0, 'Кобай');
INSERT INTO `creature_text` VALUES (61303, 4, 0, 'Нет! Что... происходит...', 14, 0, 100, 0, 0, 0, 62831, 3000, 3000, 0, 'Кобай to Player');
INSERT INTO `creature_text` VALUES (61305, 1, 0, 'Clever, hiding out up here - using the elements to shield your tracks. But my quilen hounds could sense the Tongue even here.', 12, 0, 100, 0, 0, 0, 60716, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61306, 4, 0, '%s is trying to escape in a panic!', 16, 0, 100, 0, 0, 0, 1150, 18000, 18000, 0, 'Яо to Завоеватель Цзуань');
INSERT INTO `creature_text` VALUES (61307, 4, 0, '%s is trying to escape in a panic!', 16, 0, 100, 0, 0, 0, 1150, 18000, 18000, 0, 'Ячи to Завоеватель Цзуань');
INSERT INTO `creature_text` VALUES (61315, 0, 0, 'Готово. Я ничего не упустил?', 12, 0, 100, 0, 0, 0, 60718, 0, 0, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61315, 0, 0, 'Прикрой меня – уверен, ты справишься со всем, что может мне помешать.', 12, 0, 100, 0, 0, 0, 60714, 0, 0, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61315, 0, 0, 'Сотворение чар займет всего минутку, но я буду в полном трансе.', 12, 0, 100, 0, 0, 0, 60713, 0, 0, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61333, 0, 0, '%s falls into a frenzy!', 16, 0, 100, 0, 0, 0, 30726, 0, 0, 0, 'Злобная ярость');
INSERT INTO `creature_text` VALUES (61348, 0, 0, 'Тажань Чжу!', 14, 0, 100, 0, 0, 38144, 74993, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (61348, 1, 0, 'Тише. Тише. Я отведу тебя к лекарям.', 12, 0, 100, 0, 0, 38145, 74994, 0, 0, 0, 'Тажань Чжу');
INSERT INTO `creature_text` VALUES (61348, 2, 0, 'Я найду помощь! Удерживайте ша как можно дольше!', 14, 0, 100, 0, 0, 38146, 74995, 0, 0, 0, 'Тажань Чжу');
INSERT INTO `creature_text` VALUES (61348, 3, 0, 'Герои! Вы живы. Я привел подмогу.', 14, 0, 100, 0, 0, 38147, 74996, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (61371, 3, 0, 'Честное слово, $p – лучший боец, которого я когда-либо видел. $GОн:Она; сможет помочь вашей деревне подготовиться к битве.', 12, 0, 100, 1, 0, 0, 63278, 17000, 17000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61381, 0, 0, 'I was starting to wonder how long I would be out here.', 12, 0, 100, 0, 0, 0, 61326, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61381, 0, 1, 'My poor old bones were starting to get stiff out here.', 12, 0, 100, 0, 0, 0, 61327, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61381, 0, 2, 'Those Zandalari will regret attacking Zouchin!', 12, 0, 100, 0, 0, 0, 61328, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61381, 0, 3, 'I\'m grateful to you, friend.', 12, 0, 100, 0, 0, 0, 61330, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61390, 1, 0, 'There\'s nothing here. Let\'s keep looking.', 12, 0, 100, 0, 0, 0, 60803, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61390, 2, 0, 'Another stone... and it matches the markings on the others we found.', 12, 0, 100, 0, 0, 0, 61940, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61398, 0, 0, 'Кто тут? Еще один отряд пришел за моей короной? Какие-то они мелкие и слабые.', 14, 0, 100, 0, 0, 28355, 61048, 0, 0, 0, 'Синь Мастер Боя to Молниеносный клинок клана Гуртан');
INSERT INTO `creature_text` VALUES (61398, 1, 0, 'Вы не первые, кто бросает мне вызов. И не последние.', 14, 0, 100, 0, 0, 28349, 61047, 0, 0, 0, 'Синь Мастер Боя to 0');
INSERT INTO `creature_text` VALUES (61398, 2, 0, 'Полюбуйтесь моим арсеналом, слабаки!', 14, 0, 100, 0, 0, 28350, 61055, 0, 0, 0, 'Синь Мастер Боя to 0');
INSERT INTO `creature_text` VALUES (61398, 3, 0, '%s приводит в действие ловушку |cFFFF0404|Hspell:119374|h[Топор вихря]|h|r!', 41, 0, 100, 0, 0, 28350, 63555, 0, 0, 0, 'Синь Мастер Боя to 0');
INSERT INTO `creature_text` VALUES (61398, 4, 0, '%s приводит в действие ловушку |cFFFF0404|Hspell:119311|h[Шквал клинков]|h|r!', 41, 0, 100, 0, 0, 28351, 63551, 0, 0, 0, 'Синь Мастер Боя to 0');
INSERT INTO `creature_text` VALUES (61398, 5, 0, 'Моя мощь безгранична!', 14, 0, 100, 0, 0, 28351, 91273, 0, 0, 0, 'Синь Мастер Боя to 0');
INSERT INTO `creature_text` VALUES (61398, 6, 0, 'Хватит играть в игрушки! Сейчас вам будет больно, трусы!', 14, 0, 100, 0, 0, 28360, 61058, 0, 0, 0, 'Синь Мастер Боя to 0');
