INSERT INTO `creature_text` VALUES (61398, 8, 0, 'Падай на колени, трус!', 14, 0, 100, 0, 0, 28358, 61070, 0, 0, 0, 'Синь Мастер Боя to Player');
INSERT INTO `creature_text` VALUES (61398, 9, 0, 'I\'m invincible!', 14, 0, 100, 0, 0, 28356, 61178, 0, 0, 0, 'Синь Мастер Боя to 0');
INSERT INTO `creature_text` VALUES (61398, 10, 0, '%s издает победный вой.', 16, 0, 100, 0, 0, 28356, 61180, 0, 0, 0, 'Синь Мастер Боя to 0');
INSERT INTO `creature_text` VALUES (61398, 27, 0, 'И это... по-вашему... бой?', 14, 0, 100, 0, 0, 28352, 61065, 0, 0, 0, 'Синь Мастер Боя to Player');
INSERT INTO `creature_text` VALUES (61398, 28, 0, '%s плюет кровью вам в лицо.', 16, 0, 100, 0, 0, 28352, 61059, 0, 0, 0, 'Синь Мастер Боя to Player');
INSERT INTO `creature_text` VALUES (61399, 0, 0, 'Глинтрок-разведчик предупреждает об опасности!', 41, 0, 100, 0, 0, 0, 60810, 0, 0, 0, 'Глинтрок-разведчик');
INSERT INTO `creature_text` VALUES (61408, 0, 0, '|TInterface\\Icons\\ability_warrior_charge.blp:20|t Адарогг останавливает взгляд на |cFFFF0000|3-5($n)|r и begins to cast a spell |cFFFF0000|Hspell:119299|h[Инферно]|h|r!', 41, 0, 100, 0, 0, 0, 60884, 0, 0, 0, 'Адарогг to Player');
INSERT INTO `creature_text` VALUES (61412, 0, 0, 'Сила темного шамана поразит тебя!', 14, 0, 100, 0, 0, 0, 61305, 0, 0, 0, 'Темный шаман Корантал to Player');
INSERT INTO `creature_text` VALUES (61412, 1, 0, '|TInterface\\Icons\\spell_shadow_shadowfury:20|tТемный шаман Корантал призывает |r|cFF9E09DE|Hspell:119971|h[Темный шторм]|h|r!', 41, 0, 100, 0, 0, 0, 61139, 0, 0, 0, 'Темный шаман Корантал to 0');
INSERT INTO `creature_text` VALUES (61412, 2, 0, 'Моя смерть... ничего... не значит...', 14, 0, 100, 0, 0, 0, 61306, 0, 0, 0, 'Темный шаман Корантал to Player');
INSERT INTO `creature_text` VALUES (61417, 0, 0, 'You\'re my hero!', 12, 0, 100, 0, 0, 0, 61330, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61417, 0, 1, 'So... sore...', 12, 0, 100, 0, 0, 0, 61328, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61417, 0, 2, 'You do us a great service friend.', 12, 0, 100, 0, 0, 0, 61326, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61417, 0, 3, 'Thanks to you I\'ll be rested when those barbarians return.', 12, 0, 100, 0, 0, 0, 61327, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61434, 1, 0, 'За императрицу!', 12, 0, 100, 0, 0, 0, 63398, 0, 0, 0, 'Сик\'тик - боец авангарда to Player');
INSERT INTO `creature_text` VALUES (61434, 1, 0, 'Пандария склонится перед императрицей!', 12, 0, 100, 0, 0, 0, 63392, 0, 0, 0, 'Сик\'тик - боец авангарда to Player');
INSERT INTO `creature_text` VALUES (61434, 3, 0, 'Нюцзао падет!', 12, 0, 100, 0, 0, 0, 63389, 92000, 92000, 0, 'Сик\'тик - боец авангарда to Player');
INSERT INTO `creature_text` VALUES (61434, 3, 0, 'Храм будет нашим!', 12, 0, 100, 0, 0, 0, 63390, 108000, 108000, 0, 'Сик\'тик - боец авангарда to Player');
INSERT INTO `creature_text` VALUES (61442, 0, 0, 'Clan Gurthan will show our King and the rest of you power-hungry imposters why we are the rightful ones at his side!', 12, 0, 100, 0, 0, 27932, 61077, 0, 0, 0, 'aggro');
INSERT INTO `creature_text` VALUES (61442, 1, 0, 'We will never surrender our right to rule the destiny of our people!', 12, 0, 100, 0, 0, 27933, 61077, 0, 0, 0, 'defeated');
INSERT INTO `creature_text` VALUES (61442, 2, 0, 'The glory of Gurthan!', 12, 0, 100, 0, 0, 27942, 61077, 0, 0, 0, 'killing a player');
INSERT INTO `creature_text` VALUES (61442, 3, 0, 'Who allowed these outsiders in our halls!? Only Clan Harthak or Clan Korgesh would stoop to such treachery!', 12, 0, 100, 0, 0, 27940, 61077, 0, 0, 0, 'outro');
INSERT INTO `creature_text` VALUES (61442, 4, 0, 'A well-scripted exchange, but we are not fooled! Your clans are clearly in league with one another! For the glory of our King, our empire, we will put you all down!', 12, 0, 100, 0, 0, 27941, 61077, 0, 0, 0, 'outro2');
INSERT INTO `creature_text` VALUES (61442, 5, 0, '|TInterface\\Icons\\spell_shaman_earthquake.blp:20|tКуай Бесчеловечный пускает |cFFFF0000|Hspell:119922|h[ударную волну]|h|r!', 41, 0, 100, 0, 0, 0, 65534, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61444, 0, 0, 'Our clan holds true to the ways set forth by the ancients. We deserve the king\'s favor above all others!', 12, 0, 100, 0, 0, 27950, 61562, 0, 0, 0, 'intro1');
INSERT INTO `creature_text` VALUES (61444, 1, 0, 'Clan Harthak will show all why they are the truest of Mogu!', 12, 0, 100, 0, 0, 27947, 60934, 0, 0, 0, 'aggro');
INSERT INTO `creature_text` VALUES (61444, 2, 0, 'Our clan is the true clan! No interloper can change that!', 12, 0, 100, 0, 0, 27948, 60944, 0, 0, 0, 'defeated');
INSERT INTO `creature_text` VALUES (61444, 3, 0, 'This is the might of Harthak!', 12, 0, 100, 0, 0, 27957, 63315, 0, 0, 0, 'killing a player');
INSERT INTO `creature_text` VALUES (61444, 4, 0, 'Only the dogs of clan Gurthan would point fingers at others for their own failures! Your clan is the most desperate! It must have been you!', 12, 0, 100, 0, 0, 27955, 61072, 0, 0, 0, 'outro');
INSERT INTO `creature_text` VALUES (61444, 5, 0, 'Silence, you motherless mu\'shan herders! I\'d blame your clan, but your skulls are too thick to figure out a child\'z puzzle box, let alone how to usurp power.', 12, 0, 100, 0, 0, 27956, 61076, 0, 0, 0, 'outro02');
INSERT INTO `creature_text` VALUES (61445, 0, 0, 'Clan Kargesh will demonstrate why only the strong deserve to stand at our King\'s side!', 12, 0, 100, 0, 0, 28246, 60933, 0, 0, 0, 'Aggro');
INSERT INTO `creature_text` VALUES (61445, 1, 0, 'Impossible! Our might is the greatest in all the empire!', 12, 0, 100, 0, 0, 28247, 60945, 0, 0, 0, 'Defeated');
INSERT INTO `creature_text` VALUES (61445, 2, 0, 'For Korgesh!', 12, 0, 100, 0, 0, 28256, 63316, 0, 0, 0, 'Killing a player');
INSERT INTO `creature_text` VALUES (61445, 3, 0, 'Clan Gurthan is right! Only the honorous Clan Harthak could bring in a lesser race to do their bidding! You must be traitors!', 12, 0, 100, 0, 0, 28254, 61074, 0, 0, 0, 'outro');
INSERT INTO `creature_text` VALUES (61445, 4, 0, 'Clan Korgesh will not suffer these insults! We will have your heads to offer to the King as proof of our might soon enough!', 12, 0, 100, 0, 0, 28255, 61078, 0, 0, 0, 'outro2');
INSERT INTO `creature_text` VALUES (61454, 3, 0, 'Сокруши захватчиков и сбрось их со стены!', 12, 0, 100, 0, 0, 0, 61500, 38000, 38000, 0, 'Суна Беззвучный Удар to Player');
INSERT INTO `creature_text` VALUES (61472, 3, 0, 'А, так гораздо лучше. Повязки были таким тугими!', 12, 0, 100, 0, 0, 0, 53558, 10000, 10000, 0, 'Связанный фермер Сладкого Ветерка to Связанный фермер Сладкого Ветерка');
INSERT INTO `creature_text` VALUES (61472, 3, 0, 'Большое спасибо!', 12, 0, 100, 0, 0, 0, 53554, 9000, 9000, 0, 'Связанный фермер Сладкого Ветерка to Связанный фермер Сладкого Ветерка');
INSERT INTO `creature_text` VALUES (61472, 3, 0, 'Когда все закончится, я вернусь в столицу.', 12, 0, 100, 0, 0, 0, 53559, 19000, 19000, 0, 'Связанный фермер Сладкого Ветерка to Связанный фермер Сладкого Ветерка');
INSERT INTO `creature_text` VALUES (61472, 3, 0, 'Кто бы мог подумать, что с нашим садом может случиться такое.', 12, 0, 100, 0, 0, 0, 53557, 16000, 16000, 0, 'Связанный фермер Сладкого Ветерка to Связанный фермер Сладкого Ветерка');
INSERT INTO `creature_text` VALUES (61472, 3, 0, 'Надеюсь, сад не погиб...', 12, 0, 100, 0, 0, 0, 53555, 10000, 10000, 0, 'Связанный фермер Сладкого Ветерка to Связанный фермер Сладкого Ветерка');
INSERT INTO `creature_text` VALUES (61472, 3, 0, 'Наконец-то я свободна! За это нужно выпить...', 12, 0, 100, 0, 0, 0, 53556, 55000, 55000, 0, 'Связанный фермер Сладкого Ветерка to Связанный фермер Сладкого Ветерка');
INSERT INTO `creature_text` VALUES (61472, 3, 0, 'Спасибо! Мне страшно даже подумать о том, что они собирались с нами сделать.', 12, 0, 100, 0, 0, 0, 53561, 20000, 20000, 0, 'Связанный фермер Сладкого Ветерка to Связанный фермер Сладкого Ветерка');
INSERT INTO `creature_text` VALUES (61472, 3, 0, 'У меня все хорошо! Помоги остальным!', 12, 0, 100, 0, 0, 0, 53560, 14000, 14000, 0, 'Связанный фермер Сладкого Ветерка to Связанный фермер Сладкого Ветерка');
INSERT INTO `creature_text` VALUES (61485, 0, 0, 'Войска Нюцзао атакуют нас с тыла!', 14, 0, 100, 0, 0, 30279, 61499, 0, 0, 0, 'Генерал Па\'валак to Сик\'тик - танцующий с клинками');
INSERT INTO `creature_text` VALUES (61485, 1, 0, 'Этот храм падет! Вам не остановить мою армию!', 14, 0, 100, 0, 0, 30277, 61505, 0, 0, 0, 'Генерал Па\'валак to Player');
INSERT INTO `creature_text` VALUES (61485, 2, 0, 'Сложите оружие – или умрите!', 14, 0, 100, 0, 0, 30283, 61509, 0, 0, 0, 'Генерал Па\'валак to 0');
INSERT INTO `creature_text` VALUES (61485, 3, 0, 'Генерал Па\'валак вызывает подкрепление!', 41, 0, 100, 0, 0, 30283, 61020, 0, 0, 0, 'Генерал Па\'валак to 0');
INSERT INTO `creature_text` VALUES (61485, 6, 0, 'I let you down, Empress ...', 14, 0, 100, 0, 0, 30284, 65897, 0, 0, 0, 'Генерал Па\'валак to Player');
INSERT INTO `creature_text` VALUES (61490, 3, 0, 'We were on our way to the Tiger Temple when you caught us. Lucky for you our healer was napping... apparently.', 12, 0, 100, 0, 0, 0, 61372, 40250, 40250, 0, 'Tankiss to Ruqin Infantry');
INSERT INTO `creature_text` VALUES (61490, 3, 0, 'Нас трое, но оторвать тебе голову я могу и один.', 12, 0, 100, 0, 0, 0, 61371, 179000, 179000, 0, 'Танкисс to Пехотинец Жу-Цзинь');
INSERT INTO `creature_text` VALUES (61490, 3, 0, 'Не хочешь ли кое-что \"попробовать\"? Подойди-ка поближе...', 12, 0, 100, 0, 0, 0, 61373, 119000, 119000, 0, 'Танкисс to Пехотинец Жу-Цзинь');
INSERT INTO `creature_text` VALUES (61492, 0, 0, 'Спасибо, $gбрат:сестра;! Не беспокойся, это пустяковая царапина. У меня теперь открылось второе дыхание!', 12, 0, 100, 0, 0, 0, 61006, 0, 0, 0, 'Серьезно раненный пилот to Player');
INSERT INTO `creature_text` VALUES (61492, 1, 0, 'Приятно встретить еще $gодного собрата:одну сестру; из Альянса! Думал, что мне уже крышка.', 12, 0, 100, 0, 0, 0, 61007, 0, 0, 0, 'Смертельно раненный пилот to Player');
INSERT INTO `creature_text` VALUES (61492, 2, 0, 'I really decided that I won’t get out. I owe you my life.', 12, 0, 100, 0, 0, 0, 61003, 0, 0, 0, 'Раненый пилот to Player');
INSERT INTO `creature_text` VALUES (61492, 4, 0, 'Я все еще жив! Ха-ха! Вот как мы утерли нос ордынцам в лагере Гаррош\'ар. Хорошенько мы им поддали.', 12, 0, 100, 0, 0, 0, 53197, 0, 0, 0, 'Раненый пилот to Player');
INSERT INTO `creature_text` VALUES (61492, 5, 0, 'Благодарю за спасение. А то было совсем туго.', 12, 0, 100, 0, 0, 0, 53196, 0, 0, 0, 'Смертельно раненный пилот to Player');
INSERT INTO `creature_text` VALUES (61496, 3, 0, 'I almost fooled myself, believing that Zouchin was not in danger from the outside.', 12, 0, 100, 1, 0, 0, 63650, 41000, 1227000, 0, 'Дошу Сгибающий Сталь to Player');
INSERT INTO `creature_text` VALUES (61496, 3, 0, 'Когда-то я был великим бойцом. Будем надеяться, что с возрастом я не стал слабаком.', 12, 0, 100, 1, 0, 0, 63652, 106000, 106000, 0, 'Дошу Сгибающий Сталь to Player');
INSERT INTO `creature_text` VALUES (61504, 3, 0, 'Ветер сегодня хорош.', 12, 0, 100, 1, 0, 0, 59837, 14000, 14000, 0, 'Коротышка Чистый Воздушный Змей to Player');
INSERT INTO `creature_text` VALUES (61524, 3, 0, 'Новости даже лучше, чем я надеялся услышать. Скорей бы уснуть в теплой кровати.', 12, 0, 100, 396, 0, 0, 60997, 2000, 2000, 0, 'Разведчик Вэй-чин to Player');
INSERT INTO `creature_text` VALUES (61525, 0, 0, 'В первую очередь нужно думать о рое. Да помогут нам предки, если богомолы решат прорваться через заставу.', 12, 0, 100, 66, 0, 0, 60995, 0, 0, 0, 'Разведчик Цзай-гань to Player');
INSERT INTO `creature_text` VALUES (61526, 0, 0, 'Лучше, чем еда, может быть только отдых. Я думала, придется здесь еще поторчать.', 12, 0, 100, 396, 0, 0, 60998, 0, 0, 0, 'Разведчица Ин to Player');
INSERT INTO `creature_text` VALUES (61527, 3, 0, 'Пусть тот, кто сменит меня, знает, что полчища богомолов пытались проскользнуть по главному каналу. Это непростой пост.', 12, 0, 100, 66, 0, 0, 60996, 2000, 2000, 0, 'Разведчица Лун to Player');
INSERT INTO `creature_text` VALUES (61534, 0, 0, '...а теперь В АТАКУ!', 14, 0, 100, 0, 0, 0, 62192, 0, 0, 0, 'Шоми');
INSERT INTO `creature_text` VALUES (61534, 0, 0, 'Бойцовский дух сейчас как надает тебе по морде!', 14, 0, 100, 0, 0, 0, 62179, 0, 0, 0, 'Шоми');
INSERT INTO `creature_text` VALUES (61534, 0, 0, 'Будете знать, как лезть в мою деревню!', 14, 0, 100, 0, 0, 0, 62177, 0, 0, 0, 'Шоми');
INSERT INTO `creature_text` VALUES (61534, 0, 0, 'В руинах наверняка живут призраки. Хотя я в них не верю. Но мне ведь и в зандаларов не верилось.', 12, 0, 100, 1, 0, 0, 62189, 0, 0, 0, 'Шоми to Player');
INSERT INTO `creature_text` VALUES (61534, 0, 0, 'Надо двигаться быстро, как... Горный поток!', 14, 0, 100, 0, 0, 0, 62178, 0, 0, 0, 'Шоми');
INSERT INTO `creature_text` VALUES (61534, 0, 0, 'Отец уж слишком меня оберегает. Никогда ничего не разрешает!', 12, 0, 100, 0, 0, 0, 62181, 0, 0, 0, 'Шоми to Шоми');
INSERT INTO `creature_text` VALUES (61534, 0, 0, 'Отлично! Сразу к делу!', 12, 0, 100, 1, 0, 0, 61836, 0, 0, 0, 'Шоми to Player');
INSERT INTO `creature_text` VALUES (61534, 0, 0, 'Спасибо тебе за тренировку, $n. Надеюсь, когда-нибудь я стану бойцом не хуже тебя!', 12, 0, 100, 2, 0, 0, 62204, 0, 0, 0, 'Шоми to Player');
INSERT INTO `creature_text` VALUES (61534, 0, 0, 'У меня все получится. Я знаю, что получится...', 14, 0, 100, 0, 0, 0, 62176, 0, 0, 0, 'Шоми');
INSERT INTO `creature_text` VALUES (61534, 0, 0, 'Я вижу, ты никогда никогда не выпускаешь цели из виду. Вот что значит концентрация!', 14, 0, 100, 0, 0, 0, 62174, 0, 0, 0, 'Шоми');
INSERT INTO `creature_text` VALUES (61541, 3, 0, 'Властелин Грома жив! Наши империи снова едины...', 12, 0, 100, 4, 0, 0, 68516, 32000, 32000, 0, 'Пророк Хар\'зул');
INSERT INTO `creature_text` VALUES (61541, 3, 0, 'Все только начинается... Мы не собираемся ложиться и умирать...', 12, 0, 100, 0, 0, 0, 68784, 75000, 75000, 0, 'Пророк Хар\'зул');
INSERT INTO `creature_text` VALUES (61541, 3, 0, 'Встаньте повыше, чтобы избежать Темного тумана.', 41, 0, 100, 4, 0, 0, 65275, 32000, 32000, 0, 'Пророк Хар\'зул');
INSERT INTO `creature_text` VALUES (61541, 3, 0, 'Нам нужны эти земли! Катаклизм уничтожил наш дом.', 12, 0, 100, 0, 0, 0, 68517, 52000, 52000, 0, 'Пророк Хар\'зул');
INSERT INTO `creature_text` VALUES (61541, 3, 0, 'Скормлю тебя воронам!', 12, 0, 100, 0, 0, 0, 64715, 38000, 81000, 0, 'Пророк Хар\'зул');
INSERT INTO `creature_text` VALUES (61545, 3, 0, 'Друзья говорят, что я тормоз, но я бегаю быстрее их всех!', 12, 0, 100, 0, 0, 0, 63929, 4250, 4250, 0, 'Коа to Player');
INSERT INTO `creature_text` VALUES (61549, 3, 0, 'Когда в одном месте столько кланов, драки не избежать.', 12, 0, 100, 274, 0, 0, 61319, 31000, 31000, 0, 'Гуртанский задира');
INSERT INTO `creature_text` VALUES (61551, 3, 0, 'I heard that the king will make us fight each other. You might think this is a difficult task.', 12, 0, 100, 11, 0, 0, 61318, 47906, 47906, 0, 'Рубака клана Каргеш');
INSERT INTO `creature_text` VALUES (61557, 1, 0, 'Дукеры вытворять омерзюшные штуки!', 12, 0, 100, 0, 0, 0, 57974, 0, 0, 0, 'Древолаз из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` VALUES (61557, 3, 0, 'Брюкни их в джункс!', 12, 0, 100, 0, 0, 0, 57975, 36000, 36000, 0, 'Древолаз из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` VALUES (61557, 3, 0, 'Гукгут говорит – твоя умирай-давай!', 12, 0, 100, 0, 0, 0, 57972, 46000, 46000, 0, 'Древолаз из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` VALUES (61557, 3, 0, 'Не крадай больших грязней!', 12, 0, 100, 0, 0, 0, 57971, 4590000, 4590000, 0, 'Древолаз из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` VALUES (61557, 4, 0, 'Трахтель!', 12, 0, 100, 0, 0, 0, 57983, 8000, 8000, 0, 'Древолаз из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` VALUES (61557, 4, 0, 'Ук!', 12, 0, 100, 0, 0, 0, 63114, 5000, 5000, 0, 'Древолаз из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` VALUES (61562, 0, 0, 'Дукеры!', 12, 0, 100, 37, 0, 0, 57980, 0, 0, 0, 'Древолаз из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` VALUES (61562, 0, 0, 'Трахтель!', 12, 0, 100, 153, 0, 0, 57983, 0, 0, 0, 'Древолаз из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` VALUES (61562, 1, 0, 'Гукгут говорит – твоя умирай-давай!', 12, 0, 100, 37, 0, 0, 57972, 0, 0, 0, 'Древолаз из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` VALUES (61562, 1, 0, 'Мерзянские грязни убеганцы!', 12, 0, 100, 0, 0, 0, 57977, 0, 0, 0, 'Древолаз из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` VALUES (61562, 3, 0, 'Бортельный дукер!', 12, 0, 100, 406, 0, 0, 58275, 4195000, 4195000, 0, 'Древолаз из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` VALUES (61562, 3, 0, 'Дукеры вытворять омерзюшные штуки!', 12, 0, 100, 4, 0, 0, 57974, 2205000, 2205000, 0, 'Древолаз из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` VALUES (61562, 3, 0, 'Не крадай больших грязней!', 12, 0, 100, 406, 0, 0, 57971, 53000, 53000, 0, 'Древолаз из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` VALUES (61562, 3, 0, 'Ой-ой!.. Вождь будет недоволен...', 12, 0, 100, 11, 0, 0, 57970, 1867000, 1867000, 0, 'Древолаз из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` VALUES (61563, 0, 0, 'Не стоило тебе покидать Жуткие Пустоши!', 12, 0, 100, 0, 0, 0, 61449, 0, 0, 0, 'Таоши');
INSERT INTO `creature_text` VALUES (61563, 0, 0, 'Одним врагом меньше.', 12, 0, 100, 0, 0, 0, 61027, 0, 0, 0, 'Таоши');
INSERT INTO `creature_text` VALUES (61563, 0, 0, 'Отлично! Встретимся у восточного входа на баррикаду. Нам нужно найти брешь в обороне и закрыть ее.', 12, 0, 100, 2, 0, 0, 61091, 0, 0, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (61563, 0, 0, 'Смертельное попадание.', 12, 0, 100, 0, 0, 0, 61443, 0, 0, 0, 'Таоши');
INSERT INTO `creature_text` VALUES (61563, 0, 0, 'Ты научишься нас бояться, богомол.', 12, 0, 100, 0, 0, 0, 61447, 0, 0, 0, 'Таоши');
INSERT INTO `creature_text` VALUES (61563, 0, 0, 'У баррикады тебе могло повезти больше, богомол.', 12, 0, 100, 0, 0, 0, 61445, 0, 0, 0, 'Таоши');
INSERT INTO `creature_text` VALUES (61563, 3, 0, 'Они даже не осознают, что за нашими баррикадами их ждет верная смерть.', 12, 0, 100, 0, 0, 0, 61446, 5000, 5000, 0, 'Таоши');
INSERT INTO `creature_text` VALUES (61563, 4, 0, 'С твоей королевой будет то же самое!', 12, 0, 100, 0, 0, 0, 61448, 2000, 2000, 0, 'Таоши');
INSERT INTO `creature_text` VALUES (61566, 0, 0, 'Gorai lives? I will go find him. Thank you, friend!', 12, 0, 0, 1, 0, 0, 61054, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (61566, 0, 1, 'Thank you, friend... I\'ll make my way to the south!', 12, 0, 0, 1, 0, 0, 61053, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (61566, 0, 2, 'You are very brave... thank you!', 12, 0, 0, 1, 0, 0, 61056, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (61567, 0, 0, 'Надеюсь, им удастся дойти сюда. Тогда все увидят, какова моя сила.', 14, 0, 100, 0, 0, 29392, 66443, 0, 0, 0, 'Визирь Цзинь\'бак to Сик\'тик - янтарный прядильщик');
INSERT INTO `creature_text` VALUES (61567, 1, 0, 'Ха! Вы еще пожалеете, что вообще пришли сюда.', 14, 0, 100, 0, 0, 29390, 66444, 0, 0, 0, 'Визирь Цзинь\'бак to Player');
INSERT INTO `creature_text` VALUES (61567, 2, 0, 'Но... я же... кто теперь будет?..', 14, 0, 100, 0, 0, 29391, 66445, 0, 0, 0, 'Визирь Цзинь\'бак to Player');
INSERT INTO `creature_text` VALUES (61567, 3, 0, '|cFFFE9A2EDrops drops|r begin to stand out from the tree!', 41, 0, 100, 0, 0, 0, 61182, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61567, 4, 0, 'Allow me to show you the power of amber alchemy...', 14, 0, 100, 0, 0, 29395, 66448, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61567, 5, 0, 'Your carved bones will decorate the weapons of our warriors!', 14, 0, 100, 0, 0, 29393, 66446, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61567, 5, 1, 'Did you think you stood a chance?!', 14, 0, 100, 0, 0, 29394, 66447, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61571, 0, 0, 'Таким, как ты, не место в нашей долине.', 12, 0, 100, 0, 0, 0, 64088, 0, 0, 0, 'Императорский пожинатель душ to Player');
INSERT INTO `creature_text` VALUES (61571, 0, 0, 'Ты покоришься или умрешь!', 12, 0, 100, 0, 0, 0, 64093, 0, 0, 0, 'Императорский пожинатель душ to Player');
INSERT INTO `creature_text` VALUES (61571, 3, 0, 'Дело не в том, что ты сделал. Тебе здесь не рады, Чэнь \"Буйный Портер\".', 12, 0, 100, 0, 0, 0, 64089, 284000, 284000, 0, 'Императорский пожинатель душ to Player');
INSERT INTO `creature_text` VALUES (61571, 3, 0, 'Как ты смеешь входить в долину!', 12, 0, 100, 0, 0, 0, 64085, 2969000, 2969000, 0, 'Императорский пожинатель душ to Player');
INSERT INTO `creature_text` VALUES (61571, 3, 0, 'Начнем с тебя, потом умрут остальные.', 12, 0, 100, 0, 0, 0, 64090, 8152000, 8152000, 0, 'Императорский пожинатель душ to Player');
INSERT INTO `creature_text` VALUES (61571, 3, 0, 'Стыдись своей слабости!', 12, 0, 100, 0, 0, 0, 64094, 19000, 19000, 0, 'Императорский пожинатель душ to Player');
INSERT INTO `creature_text` VALUES (61571, 3, 0, 'Ты заплатишь за свою дерзость!', 12, 0, 100, 0, 0, 0, 64084, 6000, 6000, 0, 'Императорский пожинатель душ to Player');
INSERT INTO `creature_text` VALUES (61572, 3, 0, 'Да здравствует $n!', 14, 0, 100, 4, 0, 0, 61107, 654000, 654000, 0, 'Беженец из клана Чернильной Жабры to Player');
INSERT INTO `creature_text` VALUES (61580, 0, 0, 'Подойди, |3-6($r). Я хочу поговорить с тобой. Нашу тайну больше нельзя скрывать.', 12, 0, 100, 0, 0, 0, 61987, 0, 0, 0, 'Старейшина Ого to Player');
INSERT INTO `creature_text` VALUES (61582, 0, 0, 'I have never performed this ritual. I don’t know what exactly will happen.', 12, 0, 100, 0, 0, 0, 60991, 0, 0, 0, 'Говорящий с водой Гожай to Player');
INSERT INTO `creature_text` VALUES (61582, 0, 0, 'Давай посмотрим, хватит ли этого, чтобы очистить души моего народа от скверны.', 12, 0, 100, 0, 0, 0, 60993, 0, 0, 0, 'Говорящий с водой Гожай to Player');
INSERT INTO `creature_text` VALUES (61582, 0, 0, 'Когда я начал ритуал, из тебя выскочила тварь. И сдохла от рук |3-1($p)!', 12, 0, 100, 0, 0, 0, 61011, 0, 0, 0, 'Говорящий с водой Гожай to Мастер клинков Мэй');
INSERT INTO `creature_text` VALUES (61582, 0, 0, 'Сработало! Срабо...', 12, 0, 100, 0, 0, 0, 60999, 0, 0, 0, 'Говорящий с водой Гожай to Player');
INSERT INTO `creature_text` VALUES (61582, 0, 0, 'Что это?!', 12, 0, 100, 0, 0, 0, 105272, 0, 0, 0, 'Говорящий с водой Гожай to Мастер клинков Мэй');
INSERT INTO `creature_text` VALUES (61582, 0, 0, 'Что... Что происходит? Почему на нее действует ритуал?', 12, 0, 100, 0, 0, 0, 61000, 0, 0, 0, 'Говорящий с водой Гожай to Loremaster Staff Bunny Event Version');
INSERT INTO `creature_text` VALUES (61582, 0, 0, 'Эта... штука, похоже, и управляла жителями деревни! Теперь я знаю, что делать.', 12, 0, 100, 0, 0, 0, 61008, 0, 0, 0, 'Говорящий с водой Гожай to Мастер клинков Мэй');
INSERT INTO `creature_text` VALUES (61582, 0, 0, 'Это очень древний ритуал, но за все это время он ни разу не пригодился моему народу.', 12, 0, 100, 0, 0, 0, 60992, 0, 0, 0, 'Говорящий с водой Гожай to Player');
INSERT INTO `creature_text` VALUES (61585, 3, 0, 'Кажется, ты не понимаешь, как ведутся дела в храме яков.', 12, 0, 100, 0, 0, 0, 61111, 107000, 107000, 0, 'Сторож яков Киана to Player');
INSERT INTO `creature_text` VALUES (61585, 3, 0, 'Попытайся понять, груммель. Это храм Нюцзао, великого черного быка. Мы его слуги и хранители этого храма. Мы не \"заменяем яков\".', 12, 0, 100, 0, 0, 0, 61113, 115000, 125000, 0, 'Сторож яков Киана to Player');
INSERT INTO `creature_text` VALUES (61585, 3, 0, 'Хорошо. У озера к северу отсюда пасется много диких яков. Можете \"заменить\" своего яка на одного из них.', 12, 0, 100, 0, 0, 0, 61117, 159000, 159000, 0, 'Сторож яков Киана to Player');
INSERT INTO `creature_text` VALUES (61597, 3, 0, 'Превосходно! Провозглашаю тебя победителем в Состязании Силы! Пусть враги трепещут перед тобой.', 14, 0, 100, 1, 0, 29026, 61101, 7000, 7000, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (61616, 3, 0, 'Видишь, брат Каменная Ноша? Настойчивость окупается.', 12, 0, 100, 0, 0, 0, 61118, 4000, 4000, 0, 'Синескок to Player');
INSERT INTO `creature_text` VALUES (61617, 3, 0, 'Но... мы могли найти диких яков еще в Кунь-Лай...', 12, 0, 100, 0, 0, 0, 61119, 9000, 9000, 0, 'Брат Каменная Ноша to Player');
INSERT INTO `creature_text` VALUES (61620, 0, 0, 'Спасибо, конечно, за спасение, но мы по-прежнему со всех сторон окружены богомолами.', 12, 0, 100, 0, 0, 31505, 61584, 0, 0, 0, 'Ян Железный Коготь to Сик\'тик-страж');
INSERT INTO `creature_text` VALUES (61620, 1, 0, 'Только посмотрите. Их слишком много. Мы не справимся.', 12, 0, 100, 0, 0, 31506, 61591, 0, 0, 0, 'Ян Железный Коготь to Сик\'тик-страж');
INSERT INTO `creature_text` VALUES (61620, 2, 0, 'Хм-м... У меня появилась идея. Это может сработать, но мне понадобится помощь!', 12, 0, 100, 0, 0, 31507, 62580, 0, 0, 0, 'Ян Железный Коготь to Сик\'тик-страж');
INSERT INTO `creature_text` VALUES (61620, 3, 0, 'Обратим оружие этих богомолов против них самих!', 12, 0, 100, 0, 0, 31508, 62581, 0, 0, 0, 'Ян Железный Коготь to Сик\'тик-страж');
INSERT INTO `creature_text` VALUES (61620, 4, 0, 'Ли! Ло! Нам нужен сок!', 12, 0, 100, 0, 0, 31509, 62582, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61620, 5, 0, 'Ну, начнем веселье?', 12, 0, 100, 0, 0, 31510, 62583, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61620, 6, 0, 'Мы раздавим вас, как жуков!', 14, 0, 100, 0, 0, 31514, 66224, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61620, 7, 0, 'Большой отряд |cFF00dc29сик\'тиков-роевиков|r вступает в битву.', 41, 0, 100, 0, 0, 30265, 62318, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61620, 8, 0, 'Роевики взбираются по склону! Используй смолу, чтобы их замедлить!', 12, 0, 100, 0, 0, 31511, 62586, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61620, 9, 0, 'Желтокрыл приближается. Берегитесь!', 12, 0, 100, 0, 0, 31503, 62023, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61620, 10, 0, 'Большой отряд |cFFB600B8сик\'тиков-разрушителей|r вступает в бой.', 41, 0, 100, 0, 0, 30266, 62578, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61620, 11, 0, 'У подрывников бомбы! Прямой – и они взлетят на воздух!', 12, 0, 100, 0, 0, 31504, 62025, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61620, 12, 0, 'Большой отряд |cFF00dc29сик\'тиков-роевиков|r и |cFF815d18сик\'тиков-воинов|r вступает в битву.', 41, 0, 100, 0, 0, 30267, 62622, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61620, 13, 0, 'Воин! Сосредоточьте огонь на нем!', 12, 0, 100, 0, 0, 31512, 62168, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61620, 14, 0, 'Большой отряд |cFFB600B8сик\'тиков-разрушителей|r и |cFF815d18сик\'тиков-воинов|r вступает в бой.', 41, 0, 100, 0, 0, 30268, 62623, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61620, 15, 0, 'По-моему, было весело!', 12, 0, 100, 0, 0, 31497, 62551, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61620, 16, 0, 'Ли, Ло! Спуститесь к нашим друзьям!', 12, 0, 100, 0, 0, 31498, 62734, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61620, 17, 0, 'Ворота заперты... но думаю, я смогу их открыть.', 12, 0, 100, 0, 0, 31499, 62735, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61620, 18, 0, 'Дайте-ка подумать... запал на 30 секунд...', 12, 0, 100, 0, 0, 31500, 62736, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61620, 19, 0, 'Что? Я сказал \"тридцать?\" Я имел в виду три!', 12, 0, 100, 0, 0, 31501, 62737, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61620, 20, 0, 'Быстрее! Вы должны остановить осаду!', 12, 0, 100, 0, 0, 31502, 62738, 0, 0, 0, 'Ян Железный Коготь to Player');
INSERT INTO `creature_text` VALUES (61621, 0, 0, 'Отлично, $n. Я готов идти.', 12, 0, 100, 0, 0, 0, 61135, 0, 0, 0, 'У-Пэн to Player');
INSERT INTO `creature_text` VALUES (61634, 0, 0, 'Глупцы! Вы смеете противостоять армии богомолов? Вы быстро умрете!', 14, 0, 100, 0, 0, 30264, 61296, 0, 0, 0, 'Командир Во\'цзак to Player');
INSERT INTO `creature_text` VALUES (61634, 1, 0, 'Роевики, атакуйте! Обглодайте их до костей!', 14, 0, 100, 0, 0, 30265, 61297, 0, 0, 0, 'Командир Во\'цзак to Player');
INSERT INTO `creature_text` VALUES (61634, 2, 0, 'Начать ковровую бомбардировку платформы! Прислать Желтокрыла!', 14, 0, 100, 0, 0, 30269, 61299, 0, 0, 0, 'Командир Во\'цзак to Player');
INSERT INTO `creature_text` VALUES (61634, 3, 0, 'Разрушители! Пусть здесь останется лишь куча пепла!', 14, 0, 100, 0, 0, 30266, 62587, 0, 0, 0, 'Командир Во\'цзак to Player');
INSERT INTO `creature_text` VALUES (61634, 4, 0, 'Ну где же он?! Все вы бесполезны... Проклятье!', 14, 0, 100, 0, 0, 30272, 66200, 0, 0, 0, 'Командир Во\'цзак to Player');
INSERT INTO `creature_text` VALUES (61634, 5, 0, 'Воины, раздавите этих ничтожеств! Уничтожьте их!', 14, 0, 100, 0, 0, 30267, 62588, 0, 0, 0, 'Командир Во\'цзак to Player');
INSERT INTO `creature_text` VALUES (61634, 7, 0, 'Все в атаку! Убейте этих трусов, из-за них мы выглядим, как идиоты!', 14, 0, 100, 0, 0, 30268, 62589, 0, 0, 0, 'Командир Во\'цзак to Player');
INSERT INTO `creature_text` VALUES (61634, 8, 0, 'Убитые заслуживали смерти! Вы просто убрали самых слабых!', 14, 0, 100, 0, 0, 30262, 66496, 0, 0, 0, 'Командир Во\'цзак to Player');
INSERT INTO `creature_text` VALUES (61634, 9, 0, 'Что ж, значит, мне придется разбираться самому...', 14, 0, 100, 0, 0, 30263, 66206, 0, 0, 0, 'Командир Во\'цзак to Player');
INSERT INTO `creature_text` VALUES (61634, 10, 0, '%s начинает приближаться к платформе!', 41, 0, 100, 0, 0, 0, 62579, 0, 0, 0, 'Командир Во\'цзак to Командир Во\'цзак');
INSERT INTO `creature_text` VALUES (61634, 11, 0, 'Это... была... ловушка...', 14, 0, 100, 0, 0, 30273, 66201, 0, 0, 0, 'Командир Во\'цзак to Player');
INSERT INTO `creature_text` VALUES (61636, 0, 0, 'Ah, the Illusionary Banana-Tosser! It will rapidly toss bananas toward you. Move to avoid them. Prevent it from running away from you, if you can.', 14, 0, 100, 0, 0, 38496, 75855, 0, 0, 0, 'Trial Master Rotun to Proving Grounds');
INSERT INTO `creature_text` VALUES (61636, 0, 0, 'An Illusionary Banshee! Kill it, quickly, before it explodes!', 14, 0, 100, 0, 0, 38491, 75860, 0, 0, 0, 'Trial Master Rotun to Proving Grounds');
INSERT INTO `creature_text` VALUES (61636, 0, 0, 'As before, defeat each wave of illusions before time runs out. This time, you will encounter more difficult illusions.', 14, 0, 100, 0, 0, 38553, 75848, 0, 0, 0, 'Trial Master Rotun to Player');
INSERT INTO `creature_text` VALUES (61636, 0, 0, 'Prepare yourself for the most difficult illusions. Defeat them before time expires, and success will be yours.', 14, 0, 100, 0, 0, 38552, 75849, 0, 0, 0, 'Trial Master Rotun to Player');
INSERT INTO `creature_text` VALUES (61636, 0, 0, 'Success! Hah, well done. See me for your next trial.', 14, 0, 100, 0, 0, 38554, 75851, 0, 0, 0, 'Trial Master Rotun to Proving Grounds');
INSERT INTO `creature_text` VALUES (61636, 0, 0, 'The Illusionary Amber-Weaver conjures globs of amber that float toward you and explode on any contact. Lead the amber globs into other illusions.', 14, 0, 100, 0, 0, 38497, 75854, 0, 0, 0, 'Trial Master Rotun to Proving Grounds');
INSERT INTO `creature_text` VALUES (61636, 0, 0, 'The Illusionary Mystic will heal any ally that is significantly wounded. Interrupt its healing spells.', 14, 0, 100, 0, 0, 38495, 75856, 0, 0, 0, 'Trial Master Rotun to Proving Grounds');
INSERT INTO `creature_text` VALUES (61636, 0, 0, 'Иллюзорная банши! Убей ее прежде, чем она взорвется!', 14, 0, 100, 0, 0, 38491, 75860, 0, 0, 0, 'Мастер испытаний Ротун to Арена испытаний');
INSERT INTO `creature_text` VALUES (61636, 0, 0, 'О, иллюзорный ша! Прибереги свои самые мощные удары на момент, когда падет барьер.', 14, 0, 100, 0, 0, 38493, 75858, 0, 0, 0, 'Мастер испытаний Ротун to Арена испытаний');
INSERT INTO `creature_text` VALUES (61636, 0, 0, 'Это испытание не кончается, но становится сложнее. Насколько вы хороши?', 14, 0, 100, 0, 0, 38501, 75850, 0, 0, 0, 'Мастер испытаний Ротун to Player');
INSERT INTO `creature_text` VALUES (61636, 3, 0, 'A master of defense, the Illusionary Guardian deflects all attacks from the front. Stay behind it.', 14, 0, 100, 0, 0, 38494, 75857, 263797, 393031, 0, 'Trial Master Rotun to Proving Grounds');
INSERT INTO `creature_text` VALUES (61636, 3, 0, 'Illusionary Varmints come in packs, and are harmless. Use attacks that affect multiple opponents to kill them quickly.', 14, 0, 100, 0, 0, 38498, 75853, 448031, 448031, 0, 'Trial Master Rotun to Proving Grounds');
INSERT INTO `creature_text` VALUES (61636, 3, 0, 'This Illusionary Slayer will test your ability to damage your opponent. Do not worry; it cannot harm you.', 12, 0, 100, 0, 0, 38492, 75859, 233797, 363031, 0, 'Trial Master Rotun to Proving Grounds');
INSERT INTO `creature_text` VALUES (61636, 3, 0, 'Welcome to the Proving Grounds. Your first trial will now begin. Defeat each wave of illusions before time runs out.', 14, 0, 100, 0, 0, 38500, 75847, 222781, 352422, 0, 'Trial Master Rotun to Player');
INSERT INTO `creature_text` VALUES (61636, 3, 0, 'You have run out of time. Shall we try again?', 14, 0, 100, 0, 0, 38499, 75852, 317797, 502031, 0, 'Trial Master Rotun to Proving Grounds');
INSERT INTO `creature_text` VALUES (61636, 3, 0, 'А, иллюзорный метатель бананов, он будет обстреливать тебя. Уклоняйся от его атак и попытайся не дать ему убежать далеко.', 14, 0, 100, 0, 0, 38496, 75855, 1082000, 1255000, 0, 'Мастер испытаний Ротун to Арена испытаний');
INSERT INTO `creature_text` VALUES (61636, 3, 0, 'Великолепно! Увидимся на следующем испытании.', 14, 0, 100, 0, 0, 38554, 75851, 1074000, 1074000, 0, 'Мастер испытаний Ротун to Арена испытаний');
INSERT INTO `creature_text` VALUES (61636, 3, 0, 'Время вышло! Попробуешь еще раз?', 14, 0, 100, 0, 0, 38499, 75852, 221000, 559000, 0, 'Мастер испытаний Ротун to Арена испытаний');
INSERT INTO `creature_text` VALUES (61636, 3, 0, 'Иллюзорные вредители слабы и ходят группами. Чтобы быстро убить их, наноси урон по области.', 14, 0, 100, 0, 0, 38498, 75853, 109000, 914000, 0, 'Мастер испытаний Ротун to Арена испытаний');
INSERT INTO `creature_text` VALUES (61636, 3, 0, 'Иллюзорный мистик исцеляет раненые иллюзии. Прерывай его заклинания.', 14, 0, 100, 0, 0, 38495, 75856, 182000, 395000, 0, 'Мастер испытаний Ротун to Арена испытаний');
INSERT INTO `creature_text` VALUES (61636, 3, 0, 'Иллюзорный прядильщик призывает янтарные шары. Они взрываются при касании, оглушают и увеличивают получаемый урон. Приведи шары к иллюзиям!', 14, 0, 100, 0, 0, 38497, 75854, 102000, 315000, 0, 'Мастер испытаний Ротун to Арена испытаний');
INSERT INTO `creature_text` VALUES (61636, 3, 0, 'Иллюзорный стражник отражает все фронтальные атаки. Держись позади него!', 14, 0, 100, 0, 0, 38494, 75857, 54000, 859000, 0, 'Мастер испытаний Ротун to Арена испытаний');
INSERT INTO `creature_text` VALUES (61636, 3, 0, 'Как и раньше, тебе предстоит победить иллюзии, прежде чем выйдет время. Но на этот раз они будут сильнее.', 14, 0, 100, 0, 0, 38553, 75848, 21000, 234000, 0, 'Мастер испытаний Ротун to Player');
INSERT INTO `creature_text` VALUES (61636, 3, 0, 'Приготовься сразиться с самыми могучими иллюзиями. Одолей их за отведенное время.', 14, 0, 100, 0, 0, 38552, 75849, 1042000, 1214000, 0, 'Мастер испытаний Ротун to Player');
INSERT INTO `creature_text` VALUES (61636, 3, 0, 'Это Арена испытаний. Первое начнется прямо сейчас. Уничтожь иллюзии за отведенное время.', 14, 0, 100, 0, 0, 38500, 75847, 14000, 819000, 0, 'Мастер испытаний Ротун to Player');
INSERT INTO `creature_text` VALUES (61636, 3, 0, 'Этот иллюзорный убийца проверит силу твоей атаки. Не бойся, он не умеет нападать.', 12, 0, 100, 0, 0, 38492, 75859, 24000, 829000, 0, 'Мастер испытаний Ротун to Арена испытаний');
INSERT INTO `creature_text` VALUES (61641, 0, 0, 'Начнем с тебя, потом умрут остальные.', 12, 0, 100, 0, 0, 0, 64090, 0, 0, 0, 'Императорский антикватор to Player');
INSERT INTO `creature_text` VALUES (61641, 0, 0, 'Таким, как ты, не место в нашей долине.', 12, 0, 100, 0, 0, 0, 64088, 0, 0, 0, 'Императорский антикватор to Player');
INSERT INTO `creature_text` VALUES (61641, 0, 0, 'Ты заплатишь за свою дерзость!', 12, 0, 100, 0, 0, 0, 64084, 0, 0, 0, 'Императорский антикватор to Player');
INSERT INTO `creature_text` VALUES (61641, 3, 0, 'Дело не в том, что ты сделал. Тебе здесь не рады, Чэнь \"Буйный Портер\".', 12, 0, 100, 0, 0, 0, 64089, 36000, 36000, 0, 'Императорский антикватор to Player');
INSERT INTO `creature_text` VALUES (61641, 3, 0, 'Как ты смеешь входить в долину!', 12, 0, 100, 0, 0, 0, 64085, 77000, 77000, 0, 'Императорский антикватор to Player');
INSERT INTO `creature_text` VALUES (61641, 3, 0, 'Стыдись своей слабости!', 12, 0, 100, 0, 0, 0, 64094, 296000, 296000, 0, 'Императорский антикватор to Player');
INSERT INTO `creature_text` VALUES (61641, 3, 0, 'Ты пожалеешь о своем приходе!', 12, 0, 100, 0, 0, 0, 64086, 82000, 82000, 0, 'Императорский антикватор to Player');
INSERT INTO `creature_text` VALUES (61641, 3, 0, 'Ты покоришься или умрешь!', 12, 0, 100, 0, 0, 0, 64093, 255000, 255000, 0, 'Императорский антикватор to Player');
INSERT INTO `creature_text` VALUES (61647, 0, 0, 'Father!', 14, 0, 100, 1, 0, 0, 69734, 0, 0, 0, 'WuPeng - CompleteDialog');
INSERT INTO `creature_text` VALUES (61647, 1, 0, 'Yes! Our friend here brought me back!', 12, 0, 100, 1, 0, 0, 61137, 0, 0, 0, 'WuPeng - CompleteDialog');
INSERT INTO `creature_text` VALUES (61649, 0, 0, 'I felt someone. Such burning pain and rage in the mind ...', 12, 0, 100, 0, 0, 0, 61012, 0, 0, 0, 'Мастер клинков Мэй to Мастер клинков Мэй');
INSERT INTO `creature_text` VALUES (61649, 0, 0, 'Наконец-то я успокоилась. Что случилось?', 12, 0, 100, 0, 0, 0, 61009, 0, 0, 0, 'Мастер клинков Мэй to Мастер клинков Мэй');
INSERT INTO `creature_text` VALUES (61666, 0, 0, 'Он загнан в угол!', 14, 0, 100, 0, 0, 0, 61203, 0, 0, 0, 'Падший псарь to Player');
INSERT INTO `creature_text` VALUES (61666, 0, 0, 'Он у нас в руках!', 14, 0, 100, 0, 0, 0, 61204, 0, 0, 0, 'Падший псарь to Player');
INSERT INTO `creature_text` VALUES (61671, 3, 0, '%s хитро вам подмигивает.', 16, 0, 100, 0, 0, 0, 61224, 43000, 43000, 0, 'Хао Манн to Player');
INSERT INTO `creature_text` VALUES (61671, 3, 0, 'Лапушка! Мы вернулись из карьера! И у меня кое-что для тебя есть, радость моя.', 12, 0, 100, 22, 0, 32861, 61220, 9000, 9000, 0, 'Хао Манн to Player');
INSERT INTO `creature_text` VALUES (61671, 3, 0, 'Прости, что задержался, красавица. Я знаю, что ты беспокоилась. Вот кому я обязан своим спасением. Кто бы мог подумать?', 12, 0, 100, 1, 0, 32862, 61223, 38000, 38000, 0, 'Хао Манн to Player');
INSERT INTO `creature_text` VALUES (61671, 3, 0, 'Хо-хоооо! Вот это поездочка! Ты мне нравишься, |3-6($c). С тобой не соскучишься.', 12, 0, 100, 11, 0, 32871, 61219, 3000, 3000, 0, 'Хао Манн to Player');
INSERT INTO `creature_text` VALUES (61680, 0, 0, 'Thanks!', 12, 0, 100, 0, 0, 0, 112662, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61680, 0, 1, 'I thought I was going to die down here!', 12, 0, 100, 0, 0, 0, 61285, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61680, 0, 2, 'I didn\'t think anyone was coming for us.', 12, 0, 100, 0, 0, 0, 61287, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61683, 0, 0, 'You\'re looking for Ku-Yao? I haven\'t seen him in days. You don\'t think he\'s in trouble, do you?', 12, 0, 100, 0, 0, 0, 66373, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61685, 0, 0, 'This is Ha-Cha, Sentinel Ku-Yao\'s yak.', 41, 0, 100, 0, 0, 0, 66372, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61693, 0, 0, 'Спасите! Тут полно духов!', 14, 0, 100, 0, 0, 0, 60249, 0, 0, 0, 'Мускусная Лапа - младший to Player');
INSERT INTO `creature_text` VALUES (61693, 3, 0, 'Help! Sprites swarm!', 14, 0, 100, 0, 0, 0, 60249, 91172, 91172, 0, 'Muskpaw Jr. to Player');
INSERT INTO `creature_text` VALUES (61695, 3, 0, 'Надеюсь, я смогу его найти.', 12, 0, 100, 6, 0, 0, 66375, 6000, 6000, 0, 'Ку-Мо to Player');
INSERT INTO `creature_text` VALUES (61695, 3, 0, 'Спасибо за помощь! Я пойду искать своего отца.', 12, 0, 100, 6, 0, 0, 66374, 0, 0, 0, 'Ку-Мо to Player');
INSERT INTO `creature_text` VALUES (61699, 1, 0, '|TInterface\\Icons\\inv_misc_blackironbomb.blp:20|t |cFFdd7400Сик\'тик Желтокрыл|r передвигается, чтобы |cFFFF0000|Hspell:120559|h[бомбардировать]|h|r платформу!', 41, 0, 100, 0, 0, 0, 61466, 0, 0, 0, 'Сик\'тик Желтокрыл to Player');
INSERT INTO `creature_text` VALUES (61699, 4, 0, '%s остается без боеприпасов и улетает!', 41, 0, 100, 0, 0, 0, 61838, 15187, 15187, 0, 'Сик\'тик Желтокрыл to Сик\'тик Желтокрыл');
INSERT INTO `creature_text` VALUES (61706, 0, 0, 'Как я и думал. Спасибо вам обоим. $p, я буду ждать тебя в катакомбах.', 12, 0, 100, 0, 0, 0, 61988, 0, 0, 0, 'Тай Хо to Player');
INSERT INTO `creature_text` VALUES (61706, 3, 0, 'Как хочешь. Мы найдем твой артефакт. $p, встретимся в месте вторжения шра\'тиков.', 12, 0, 100, 0, 0, 0, 61978, 5000, 5000, 0, 'Тай Хо to Player');
INSERT INTO `creature_text` VALUES (61761, 3, 0, 'Молодец!', 12, 0, 100, 0, 0, 0, 85121, 459000, 1515000, 0, 'Макки');
INSERT INTO `creature_text` VALUES (61761, 3, 0, 'Садись!', 12, 0, 100, 0, 0, 0, 61248, 24000, 24000, 0, 'Макки');
INSERT INTO `creature_text` VALUES (61788, 0, 0, 'Thanks!', 12, 0, 100, 0, 0, 0, 112662, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61788, 0, 1, 'I thought I was going to die down here!', 12, 0, 100, 0, 0, 0, 61285, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61788, 0, 2, 'I didn\'t think anyone was coming for us.', 12, 0, 100, 0, 0, 0, 61287, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (61792, 3, 0, 'Мы хотим присоединиться к Альянсу. Просим аудиенции у вашего императора.', 12, 0, 100, 2, 0, 0, 61417, 22000, 22000, 0, 'Аиса Воспевающая Облака to Player');
INSERT INTO `creature_text` VALUES (61792, 3, 0, 'Отправляемся же, $p. Если мы хотим чего-нибудь добиться, нам понадобятся союзники.', 12, 0, 100, 0, 0, 27389, 61421, 1000, 1000, 0, 'Аиса Воспевающая Облака to Player');
INSERT INTO `creature_text` VALUES (61792, 3, 0, 'Смотри!', 14, 0, 100, 5, 0, 0, 83873, 47000, 47000, 0, 'Аиса Воспевающая Облака to Player');
INSERT INTO `creature_text` VALUES (61793, 0, 0, 'Давай просто... не останавливаться.', 12, 0, 100, 1, 0, 0, 61409, 0, 0, 0, 'Йо-Йо Железная Бровь to Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (61793, 0, 0, 'Что такое Свет?', 12, 0, 100, 6, 0, 0, 61416, 0, 0, 0, 'Йо-Йо Железная Бровь to Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'Did I make myself clear?', 12, 0, 100, 0, 0, 29587, 61440, 100000, 100000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'I understand your desire to join the Alliance.', 12, 0, 100, 0, 0, 29575, 61425, 9000, 9000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'А теперь, пандарен... давай посмотрим, на что годишься ты!', 12, 0, 100, 5, 0, 29569, 61454, 123000, 123000, 120411, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'Мне бесконечно жаль, но теперь они – по другую линию фронта. Я не потерплю дружбы с противниками, ибо она ставит под угрозу безопасность Альянса.', 12, 0, 100, 5, 0, 29586, 61439, 86000, 86000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'Нам нужны новые союзники, особенно сейчас, во время войны. Отказать вам было бы глупо.', 12, 0, 100, 1, 0, 29576, 61426, 14000, 14000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'Не все пандарены избрали тот же путь, что и вы трое.', 12, 0, 100, 0, 0, 29582, 61435, 59000, 59000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'Некоторые решили встать на сторону Орды.', 12, 0, 100, 0, 0, 29583, 61436, 65000, 65000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'Немногие знают, что когда-то я был гладиатором на потеху Орде. И у меня еще остались кое-какие бойцовские навыки.', 12, 0, 100, 1, 0, 29568, 66865, 112000, 112000, 120417, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'Но я хочу, чтобы вы кое-что уяснили для себя.', 12, 0, 100, 0, 0, 29577, 61427, 21000, 21000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'Первое: все расы Альянса помогают друг другу.', 12, 0, 100, 0, 0, 29578, 61429, 30000, 30000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'Пойдемте со мной. Вы все.', 12, 0, 100, 1, 0, 29574, 61424, 4000, 4000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'Присоединившись к варварским кланам, они стали вашими противниками.', 12, 0, 100, 1, 0, 29584, 61437, 70000, 70000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'Те, кого вы раньше считали друзьями или даже любили, стали отныне вашими заклятыми врагами.', 12, 0, 100, 274, 0, 29585, 61438, 77000, 77000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'Ха-ха-ха! Давненько я так не веселился! Не бойся, пандарен... со мной и не такое случалось. Такие бойцы, как ты, нужны Альянсу! А теперь давай вернемся к нашим делам...', 12, 0, 100, 11, 0, 29573, 61459, 169000, 169000, 0, 'Король Вариан Ринн to Король Вариан Ринн');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'Хорошо. Мы почти закончили.', 12, 0, 100, 0, 0, 29588, 61442, 110000, 110000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'Хорошо. Тогда перейдем ко второму пункту.', 12, 0, 100, 273, 0, 29581, 61434, 54000, 54000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'Это значит, вы должны помогать каждому, кто в этом нуждается, будь он человеком, гномом, дренеем... или пандареном.', 12, 0, 100, 1, 0, 29579, 61430, 35000, 35000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'Это очень простое, но важное правило. Вы согласны ему следовать?', 12, 0, 100, 0, 0, 29580, 61432, 44000, 44000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 4, 0, 'В чем дело? Это все, что ты можешь?', 12, 0, 100, 6, 0, 29571, 61456, 11000, 11000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 4, 0, 'Ну, пандарен! Ударь меня!', 14, 0, 100, 5, 0, 29572, 61458, 23000, 23000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61796, 4, 0, 'Поздно отступать! Нападай!', 12, 0, 100, 5, 0, 29570, 61455, 5000, 5000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (61818, 0, 0, 'Мое!', 12, 0, 100, 381, 0, 0, 61341, 0, 0, 0, 'Игловертыш');
INSERT INTO `creature_text` VALUES (61818, 3, 0, 'Если дурак потеряет меч, то это будет уже не меч дурака, нет!', 12, 0, 100, 381, 0, 0, 61337, 24000, 24000, 0, 'Игловертыш');
INSERT INTO `creature_text` VALUES (61818, 4, 0, 'Кто успел, тот и съел! Хватит глупостей.', 12, 0, 100, 381, 0, 0, 61340, 15000, 15000, 0, 'Игловертыш');
INSERT INTO `creature_text` VALUES (61819, 3, 0, 'Лао-Чинь! Мы же победили! Почему ты плачешь?', 12, 0, 100, 6, 0, 0, 61713, 9000, 9000, 0, 'Бань Медвежье Сердце to Player');
INSERT INTO `creature_text` VALUES (61820, 3, 0, 'Все эти бочки... мое лучшее пиво! Ну, по крайней мере под конец оно пользовалось взрывным успехом!', 12, 0, 100, 1, 0, 0, 61568, 14000, 14000, 0, 'Лао-Чинь Стальное Брюхо to Player');
INSERT INTO `creature_text` VALUES (61837, 3, 0, 'Добро пожаловать в Штормград!', 12, 0, 100, 3, 0, 0, 61410, 13000, 13000, 0, 'Лерия Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (61838, 3, 0, 'Правильное решение. По-моему, вышло удачно.', 12, 0, 100, 0, 0, 6594, 61408, 19000, 19000, 0, 'Гавин Малсбери to Player');
INSERT INTO `creature_text` VALUES (61838, 3, 0, 'Хе... пандарен. Наверное, хочешь присоединиться к Альянсу.', 12, 0, 100, 0, 0, 0, 61407, 14000, 14000, 0, 'Гавин Малсбери to Player');
INSERT INTO `creature_text` VALUES (61839, 3, 0, 'Тише, Нааней. Чужаки подходят.', 12, 0, 100, 1, 0, 0, 61412, 29000, 29000, 0, 'Лукас Северинг to Player');
INSERT INTO `creature_text` VALUES (61840, 3, 0, 'Смотри внимательно, Лукас. Свет в них силен. Особенно в $gнем:ней;.', 12, 0, 100, 1, 0, 0, 61414, 38000, 38000, 0, 'Нааней to Player');
INSERT INTO `creature_text` VALUES (61840, 3, 0, 'Чужаки – да, но с добрыми намерениями.', 12, 0, 100, 1, 0, 0, 61413, 34000, 34000, 0, 'Нааней to Player');
INSERT INTO `creature_text` VALUES (61841, 3, 0, 'Пандарен? Кажется, я брежу...', 12, 0, 100, 0, 0, 0, 61411, 19000, 19000, 0, 'Брунн Златопой to Player');
INSERT INTO `creature_text` VALUES (61850, 0, 0, 'Надеюсь, $n, наши пути снова пересекутся! А пока пусть твое путешествие будет спокойным и не пройдет зря.', 12, 0, 100, 1, 0, 0, 61333, 0, 0, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61850, 3, 0, 'I hope our paths cross again, $n! Until then, may your journey be safe and meaningful.', 12, 0, 100, 1, 0, 0, 61333, 2110, 2110, 0, 'Lorewalker Cho to Player');
INSERT INTO `creature_text` VALUES (61881, 0, 0, 'Я выдавлю из вас жизнь, мелкое отродье!', 14, 0, 100, 0, 0, 0, 66092, 0, 0, 0, 'Новобранец Фэн to Player');
INSERT INTO `creature_text` VALUES (61881, 3, 0, 'Да, лучше тебе убежать!', 14, 0, 100, 0, 0, 0, 66089, 7000, 7000, 0, 'Новобранец Фэн to Player');
INSERT INTO `creature_text` VALUES (61884, 0, 0, 'Useless, all of you! Even the guards you give me in tribute can\'t keep these lesser beings from my palace.', 14, 0, 100, 0, 0, 28353, 61394, 0, 0, 0, 'TALK02');
INSERT INTO `creature_text` VALUES (61884, 1, 0, 'Redeem, nay, PROVE yourselves now. Deal with these intruders. The clan who brings me their heads will have my favor!', 14, 0, 100, 0, 0, 28354, 61395, 0, 0, 0, 'TALK02');
INSERT INTO `creature_text` VALUES (61886, 4, 0, 'Прикончи его! Скинь его со стены!', 41, 0, 100, 0, 0, 0, 61723, 10000, 10000, 0, 'Опытный осул-лучник');
INSERT INTO `creature_text` VALUES (61895, 3, 0, 'Эй, смотри! Гноллы!', 12, 0, 100, 1, 0, 6594, 61401, 11000, 11000, 0, 'Марти to Player');
INSERT INTO `creature_text` VALUES (61896, 3, 0, 'Это не гноллы, глупыш. Протри глаза.', 12, 0, 100, 1, 0, 6594, 61402, 16000, 16000, 0, 'Джози to Player');
INSERT INTO `creature_text` VALUES (61896, 3, 0, 'Это фурболги.', 12, 0, 100, 1, 0, 6594, 61403, 20000, 20000, 0, 'Джози to Player');
INSERT INTO `creature_text` VALUES (61962, 0, 0, 'A pandaren legend is born! Sing praises to our new hero, $n!', 14, 0, 100, 0, 0, 38119, 75115, 0, 0, 0, 'Lorewalker Cho to Player');
INSERT INTO `creature_text` VALUES (61962, 3, 0, 'А, мой старый друг из Нефритового леса.', 12, 0, 100, 273, 0, 0, 75046, 7000, 7000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (61962, 3, 0, 'Встречайте легенду Пандарии! Воспевайте нашего нового героя!', 14, 0, 100, 22, 0, 38119, 75115, 155000, 155000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (62004, 3, 0, 'Ваше присутствие пробудило древнего духа!', 41, 0, 100, 0, 0, 0, 61566, 208484, 208484, 120782, 'Ghost Essence to Проклятая скульптура могу');
INSERT INTO `creature_text` VALUES (62081, 0, 0, 'Да, вождь.', 12, 0, 100, 2, 0, 27290, 61595, 0, 0, 0, 'Цзи Огненная Лапа to Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62081, 0, 0, 'Да... Да, конечно...', 12, 0, 100, 273, 0, 27291, 61603, 0, 0, 0, 'Цзи Огненная Лапа to Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62081, 0, 0, 'Император Адский Крик, я Цзи...', 12, 0, 100, 1, 0, 27289, 61633, 0, 0, 0, 'Цзи Огненная Лапа to Player');
INSERT INTO `creature_text` VALUES (62081, 0, 0, 'Смотри!', 12, 0, 100, 5, 0, 0, 83873, 0, 0, 0, 'Цзи Огненная Лапа to Player');
INSERT INTO `creature_text` VALUES (62087, 0, 0, 'Присоединиться к Орде – это правильное решение.', 12, 0, 100, 273, 0, 30746, 61586, 0, 0, 0, 'Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62087, 1, 0, 'Некоторые зовут нас варварами... \"дикими расами\" Азерота.', 12, 0, 100, 396, 0, 30747, 61587, 0, 0, 0, 'Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62087, 2, 0, 'Но они просто глупы и слепы. Посмотрите вокруг, пандарены!', 12, 0, 100, 274, 0, 30748, 61588, 0, 0, 0, 'Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62087, 3, 0, 'Орки – самые лучшие воины в мире.', 12, 0, 100, 397, 0, 30749, 61589, 0, 0, 0, 'Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62087, 4, 0, 'Таурены тоже доказали свою полезность. Посмотрите на этого шамана.', 12, 0, 100, 6, 0, 30750, 61590, 0, 0, 0, 'Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62087, 5, 0, 'Даже эльфы крови могут держать в руках меч.', 12, 0, 100, 396, 0, 30751, 61592, 0, 0, 0, 'Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62087, 6, 0, 'Каждый в моей Орде вносит вклад в общее дело, и вы не станете исключением.', 12, 0, 100, 396, 0, 30752, 61593, 0, 0, 0, 'Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62087, 7, 0, 'Вы понимаете это, пандарены?', 12, 0, 100, 6, 0, 30753, 61594, 0, 0, 0, 'Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62087, 8, 0, 'Хм. Хорошо.', 12, 0, 100, 0, 0, 30754, 61596, 0, 0, 0, 'Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62087, 9, 0, 'Идем дальше. Я знаю, что кроме вас остров покинули и другие пандарены.', 12, 0, 100, 396, 0, 30755, 61597, 0, 0, 0, 'Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62087, 10, 0, 'Некоторые из них решили присоединиться к Альянсу.', 12, 0, 100, 14, 0, 30756, 61598, 0, 0, 0, 'Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62087, 11, 0, 'Теперь они ваши враги.', 12, 0, 100, 397, 0, 30757, 61599, 0, 0, 0, 'Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62087, 12, 0, 'Среди них могут быть ваши друзья и родные, решившие противостоять Орде. Значит, больше они не друзья вам... и не члены вашей семьи.', 12, 0, 100, 396, 0, 30758, 61600, 0, 0, 0, 'Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62087, 13, 0, 'В тот момент, когда они напялили на себя накидку Альянса, они умерли для вас. Я не потерплю никаких связей с противниками. Тех, кто предаст Орду, ждет смерть!', 12, 0, 100, 274, 0, 30759, 61601, 0, 0, 0, 'Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62087, 14, 0, 'I clearly put it?', 12, 0, 100, 5, 0, 30760, 61602, 0, 0, 0, 'Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62087, 16, 0, 'Встретимся на Аллее Чести. У меня есть подарок для вас.', 12, 0, 100, 396, 0, 30762, 61605, 0, 0, 0, 'Гаррош Адский Крик');
INSERT INTO `creature_text` VALUES (62093, 0, 0, 'Две части нефти, одна часть пива... нет, нефти нужно больше...', 12, 0, 100, 0, 0, 0, 61503, 0, 0, 0, 'Лао-Чинь Стальное Брюхо to Player');
INSERT INTO `creature_text` VALUES (62107, 0, 0, 'Проверь, как идут дела у рекрутов. Им не помешает тренировка под руководством опытного воина.', 12, 0, 100, 0, 0, 0, 59069, 0, 0, 0, 'Здоровяк Кажаши to Player');
INSERT INTO `creature_text` VALUES (62149, 3, 0, 'Drinking is not guarding.', 12, 0, 100, 0, 0, 0, 62515, 188281, 1470688, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` VALUES (62149, 3, 0, 'Heartless? Is it too much to ask for a clean outhouse?', 12, 0, 100, 0, 0, 0, 62508, 124109, 1831453, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` VALUES (62149, 3, 0, 'Hmm. I never really liked that place. The outhouses were always dirty.', 12, 0, 100, 0, 0, 0, 62507, 116859, 1824125, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` VALUES (62149, 3, 0, 'I\'m sure they are just being overly superstitious.', 12, 0, 100, 0, 0, 0, 62514, 688063, 1614953, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` VALUES (62149, 3, 0, 'No. And no kafa either. Dark days are coming.', 12, 0, 100, 0, 0, 0, 62506, 339172, 2537078, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` VALUES (62149, 3, 0, 'Of course.', 12, 0, 100, 0, 0, 0, 138318, 899438, 2397141, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` VALUES (62149, 3, 0, 'So who is protecting the Burlap Trail?', 12, 0, 100, 0, 0, 0, 62513, 479469, 2468750, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` VALUES (62149, 3, 0, 'What?! Don\'t give me that look!', 12, 0, 100, 0, 0, 0, 62509, 58406, 1837141, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` VALUES (62149, 3, 0, 'What\'s a yaungol?', 12, 0, 100, 0, 0, 0, 62510, 892109, 2389922, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` VALUES (62149, 3, 0, 'Who says that?', 12, 0, 100, 0, 0, 0, 62505, 406516, 1683188, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` VALUES (62149, 3, 0, 'Так кто там защищает Джутовый путь?', 12, 0, 100, 0, 0, 0, 62513, 972000, 972000, 0, 'Юна');
INSERT INTO `creature_text` VALUES (62158, 3, 0, 'Has there been any word from Kota Basecamp?', 12, 0, 100, 0, 0, 0, 62482, 2528016, 2528016, 0, 'Zaiyu');
INSERT INTO `creature_text` VALUES (62158, 3, 0, 'Hozen are on the ravage again. I can feel it in my knees.', 12, 0, 100, 0, 0, 0, 62480, 1886610, 1886610, 0, 'Zaiyu');
INSERT INTO `creature_text` VALUES (62158, 3, 0, 'I haven\'t seen any Shado-Pan lately.', 12, 0, 100, 0, 0, 0, 62487, 2456282, 2595969, 0, 'Zaiyu');
INSERT INTO `creature_text` VALUES (62158, 3, 0, 'I heard the yaungol have taken over most of the farmlands in the south.', 12, 0, 100, 0, 0, 0, 62484, 2380953, 2380953, 0, 'Zaiyu');
INSERT INTO `creature_text` VALUES (62158, 3, 0, 'Uhh...', 12, 0, 100, 0, 0, 0, 62488, 2467063, 2467063, 0, 'Zaiyu');
INSERT INTO `creature_text` VALUES (62164, 0, 0, '|TInterface\\Icons\\spell_shaman_earthquake.blp:20|t Гаралон обнаруживает под собой |3-3($n) и начинает применять |cFFFF0000|Hspell:122774|h[Сокрушение]|h|r!', 41, 0, 100, 0, 0, 0, 62534, 0, 0, 0, 'Гаралон to Player');
INSERT INTO `creature_text` VALUES (62164, 1, 0, '|TInterface\\Icons\\ability_druid_ravage.blp:20|t Гаралону не удалось ударить по меньшей мере двух игроков, и он впадает в |cFFFF0000|Hspell:122754|h[неистовство]|h|r!', 41, 0, 100, 0, 0, 0, 66526, 0, 0, 0, 'Гаралон to 0');
INSERT INTO `creature_text` VALUES (62171, 4, 0, 'Жрица Пламенное Сердце предала нас! Мы потеряли храм!', 12, 0, 100, 0, 0, 0, 56983, 557388, 557388, 0, 'Воин школы Змеи');
INSERT INTO `creature_text` VALUES (62171, 4, 0, 'Нужно перестроиться, пока ша не атаковали снова!', 12, 0, 100, 0, 0, 0, 56982, 493797, 493797, 0, 'Воин школы Змеи');
INSERT INTO `creature_text` VALUES (62195, 3, 0, 'Именно они помогли Корге Крепкой Гриве сбежать из лап Альянса!', 12, 0, 100, 66, 0, 0, 61629, 17000, 17000, 0, 'Шан\'гок to Player');
INSERT INTO `creature_text` VALUES (62195, 3, 0, 'Смотрите-ка! Это же Цзи Огненная Лапа и $p!', 14, 0, 100, 66, 0, 0, 61628, 13000, 13000, 0, 'Шан\'гок to Player');
INSERT INTO `creature_text` VALUES (62202, 0, 0, 'I\'m... back.', 12, 0, 0, 1, 0, 0, 0, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (62203, 0, 0, '|3-6($C)... approach me...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62205, 0, 0, 'Да, вы далеко зашли. Вам удалось пробиться через моих воинов, но я... я убью вас и построю мост!', 14, 0, 100, 0, 0, 30359, 63348, 0, 0, 0, 'Командующий флангом Нер\'онок to 0');
INSERT INTO `creature_text` VALUES (62205, 1, 0, '%s поднимается в воздух и летит к другому концу моста!', 41, 0, 100, 0, 0, 30360, 62154, 0, 0, 0, 'Командующий флангом Нер\'онок to 0');
INSERT INTO `creature_text` VALUES (62205, 2, 0, 'I will do what Pa\'valak failed!', 14, 0, 100, 0, 0, 30360, 63347, 0, 0, 0, 'Командующий флангом Нер\'онок to 0');
INSERT INTO `creature_text` VALUES (62205, 5, 0, '...мост...', 14, 0, 100, 0, 0, 30361, 63349, 0, 0, 0, 'Командующий флангом Нер\'онок to Player');
INSERT INTO `creature_text` VALUES (62216, 0, 0, 'I will go ahead. See you on the other side ... hopefully.', 12, 0, 100, 5, 0, 0, 63517, 0, 0, 0, 'Суна Беззвучный Удар to Player');
INSERT INTO `creature_text` VALUES (62220, 0, 0, 'Demand?! You DEMAND?!', 14, 0, 100, 0, 0, 0, 61678, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62220, 1, 0, 'Who are YOU to make demands of the SHADO-PAN?', 14, 0, 100, 0, 0, 0, 61722, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62233, 3, 0, 'Просто покажи, куда надо идти. Я понесу груз.', 12, 0, 100, 0, 0, 0, 61718, 1000, 1000, 0, 'Силач Нюцзао to Player');
INSERT INTO `creature_text` VALUES (62270, 0, 0, '|TInterface\\Icons\\ability_warstomp.blp:20|t %s начинает использовать способность |cFFFF0000|Hspell:127480|h[Крушащий удар]|h|r! Убегайте!', 41, 0, 100, 0, 0, 0, 66237, 0, 0, 0, 'Воресс\'талик');
INSERT INTO `creature_text` VALUES (62271, 3, 0, 'Мы должны это узнать. Без Шадо-Пан тьма поглотит Пандарию...', 12, 0, 100, 0, 0, 0, 61710, 75000, 75000, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` VALUES (62271, 3, 0, 'Мы издавна служим своему народу, еще со времен Шаохао. Он был последним императором Пандарии и основателем Шадо-Пан.', 12, 0, 100, 0, 0, 0, 61703, 12000, 12000, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` VALUES (62271, 3, 0, 'Нами руководит Тажань Чжу, как до этого руководил его отец. Но он решил закрыть врата монастыря – интересно, зачем?', 12, 0, 100, 0, 0, 0, 61711, 64000, 64000, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` VALUES (62271, 3, 0, 'Наш священный долг – находить ша и отправлять их в заточение. Мы должны побеждать их, когда они захватывают сердца нашего народа.', 12, 0, 100, 0, 0, 0, 61706, 38000, 38000, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` VALUES (62271, 3, 0, 'Пандария не держит постоянной армии. Мы, Шадо-Пан, ее первая и последняя линия обороны.', 12, 0, 100, 0, 0, 0, 61708, 55000, 55000, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` VALUES (62271, 3, 0, 'Тебе незнакомы эти земли, а значит ты ничего не знаешь и о Шадо-Пан.', 12, 0, 100, 0, 0, 0, 61702, 6000, 6000, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` VALUES (62271, 3, 0, 'Темные силы дремлют в этих землях. Злость, ненависть, страх, сомнение...', 12, 0, 100, 0, 0, 0, 61704, 22000, 22000, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` VALUES (62271, 3, 0, 'Эти отрицательные эмоции могут обретать физическую форму. Мы называем их \"ша\".', 12, 0, 100, 0, 0, 0, 61705, 29000, 29000, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` VALUES (62271, 3, 0, 'Я занимаюсь этим уже много лет, но я не помню, чтобы когда-либо ша были так сильны, как после высадки ваших солдат.', 12, 0, 100, 0, 0, 0, 61707, 46000, 46000, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` VALUES (62301, 0, 0, 'How DARE you question her will!', 12, 0, 100, 0, 0, 0, 61731, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62301, 0, 1, 'I carry out her will! We shall crumble this wall and devour the lands beyond!', 12, 0, 100, 0, 0, 0, 66550, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62301, 0, 2, 'The Klaxxi have betrayed her majesty. Your corpses will be strung from the palace walls for this.', 12, 0, 100, 0, 0, 0, 61729, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62301, 0, 3, 'What\'s this?! Now they come over the wall to oppose us? The fools!', 12, 0, 100, 0, 0, 0, 61732, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62352, 0, 0, 'Loot and pillage, bwahahaha!', 14, 0, 100, 0, 0, 32500, 61953, 0, 0, 0, 'Chief Salyis to Galleon');
INSERT INTO `creature_text` VALUES (62366, 3, 0, 'Приближаются всадники!', 14, 0, 100, 0, 0, 0, 63441, 4000, 1321000, 0, 'Шоми to Player');
INSERT INTO `creature_text` VALUES (62377, 0, 0, 'First, go water my Bright Green Spinach!', 42, 0, 100, 0, 0, 0, 56163, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62377, 1, 0, 'Good enough! Now go water my Giant Carrots!', 42, 0, 100, 0, 0, 0, 56164, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62377, 2, 0, 'Keep going! Water my Cabbages!', 42, 0, 100, 0, 0, 0, 56165, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62377, 3, 0, 'Don\'t stop moving! Water those Sugar Beets!', 42, 0, 100, 0, 0, 0, 56166, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62377, 4, 0, 'Go water my Huge Melons, further down the path!', 42, 0, 100, 0, 0, 0, 56167, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62377, 5, 0, 'Keep it up! Now water my Red-Leaf Lettuce!', 42, 0, 100, 0, 0, 0, 56168, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62377, 6, 0, 'Looks like my Sugar Beets still need more water! Turn back!', 42, 0, 100, 0, 0, 0, 56169, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62377, 7, 0, 'Stop!', 42, 0, 100, 0, 0, 0, 34706, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62377, 8, 0, '$ R, you needt to pay for everything . If you need a hop - will have to work .', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Smart text');
INSERT INTO `creature_text` VALUES (62385, 0, 0, 'Ask about this Moon-Moon. He disposes of food .', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Smart text');
INSERT INTO `creature_text` VALUES (62385, 1, 0, 'Mina! You\'re home!', 12, 0, 0, 1, 0, 0, 56256, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (62397, 0, 0, 'Пандария падет под натиском Крыльев Императрицы!', 14, 0, 100, 0, 0, 0, 62854, 0, 0, 0, 'Повелитель ветров Мел\'джарак to Player');
INSERT INTO `creature_text` VALUES (62397, 1, 0, '|TInterface\\Icons\\inv_misc_uncutgemnormal.blp:20|t$n оказывается в |cFFF00000|Hspell:121881|h[Янтарной ловушке]|h|r!', 41, 0, 100, 0, 0, 0, 64448, 0, 0, 0, 'Повелитель ветров Мел\'джарак to Player');
INSERT INTO `creature_text` VALUES (62397, 3, 0, 'Мой клинок всегда попадает в цель!', 14, 0, 100, 0, 0, 0, 62858, 0, 0, 0, 'Повелитель ветров Мел\'джарак to 0');
INSERT INTO `creature_text` VALUES (62397, 4, 0, '|TInterface\\Icons\\ability_warrior_bladestorm.blp:20|tПовелитель ветров Мел\'джарак использует |cFFF00000|Hspell:121896|h[Крутящийся клинок]|h|r!\"', 41, 0, 100, 0, 0, 0, 63779, 0, 0, 0, 'Повелитель ветров Мел\'джарак to 0');
INSERT INTO `creature_text` VALUES (62397, 5, 0, 'Умереть за императрицу – это честь для нас!', 14, 0, 100, 0, 0, 0, 62863, 0, 0, 0, 'Повелитель ветров Мел\'джарак to Зар\'тик');
INSERT INTO `creature_text` VALUES (62397, 7, 0, 'Крылья Императрицы не подведут ее!', 14, 0, 100, 0, 0, 0, 62864, 0, 0, 0, 'Повелитель ветров Мел\'джарак to Шра\'тик');
INSERT INTO `creature_text` VALUES (62397, 8, 0, 'Вы заплатите за свои проступки!', 14, 0, 100, 0, 0, 0, 62865, 0, 0, 0, 'Повелитель ветров Мел\'джарак to Кор\'тик');
INSERT INTO `creature_text` VALUES (62397, 9, 0, 'Небеса принадлежат императрице!', 14, 0, 100, 0, 0, 0, 62859, 0, 0, 0, 'Повелитель ветров Мел\'джарак to 0');
INSERT INTO `creature_text` VALUES (62397, 10, 0, '|TInterface\\Icons\\inv_sword_2h_mantid_01.blp:20|tПовелитель ветров Мел\'джарак применяет|cFFF00000|Hspell:122407|h[Дождь клинков]|h|r!', 41, 0, 100, 0, 0, 0, 64425, 0, 0, 0, 'Повелитель ветров Мел\'джарак to 0');
INSERT INTO `creature_text` VALUES (62397, 13, 0, 'Императрица... я вас... подвел…', 14, 0, 100, 0, 0, 0, 62855, 0, 0, 0, 'Повелитель ветров Мел\'джарак to 26125');
INSERT INTO `creature_text` VALUES (62410, 0, 0, 'Мы сделали все, что смогли. Прощай, Гао-Жань. Я отправлю подкрепление, как только появится возможность.', 12, 0, 100, 1, 0, 0, 61770, 0, 0, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (62412, 0, 0, 'До перекрестка недалеко. Там мы встретим последнего союзника.', 12, 0, 100, 1, 0, 0, 61920, 0, 0, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (62436, 3, 0, 'Дальше я справлюсь одна. А ты бери стрелы и иди сражаться на мост!', 12, 0, 100, 1, 0, 0, 61778, 301000, 305000, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (62442, 0, 0, 'Здесь вам не место! Нужно защитить священные воды... Я прогоню вас или убью вас!', 14, 0, 100, 0, 0, 0, 63273, 0, 0, 0, 'Цулон to Player');
INSERT INTO `creature_text` VALUES (62442, 1, 0, '|TInterface\\Icons\\spell_holy_surgeoflight.blp:20|t%s произносит заклинание |cFFFF0000|Hspell:122789|h[Луч солнца]|h|r!', 41, 0, 100, 0, 0, 0, 62585, 0, 0, 0, 'Цулон to 0');
INSERT INTO `creature_text` VALUES (62442, 1, 0, 'You do not belong here! The waters must be protected. I\'ll cast you out, or slay you!', 14, 0, 100, 0, 0, 29350, 63273, 0, 0, 0, 'TSULONG_AGGRO');
INSERT INTO `creature_text` VALUES (62442, 2, 0, 'Protect.. the waters', 14, 0, 100, 0, 0, 29351, 63274, 0, 0, 0, 'TSULONG_DEATH_01');
INSERT INTO `creature_text` VALUES (62442, 2, 0, 'Покиньте это место или... умрите!', 14, 0, 100, 0, 0, 0, 63285, 0, 0, 0, 'Цулон to 0');
INSERT INTO `creature_text` VALUES (62442, 3, 0, 'Day falls to night!', 14, 0, 100, 0, 0, 29353, 63288, 0, 0, 0, 'VO_TES_SERPENT_EVENT_DAYTONIGHT_01');
INSERT INTO `creature_text` VALUES (62442, 3, 0, 'Умри во тьме.', 14, 0, 100, 0, 0, 0, 63276, 0, 0, 0, 'Цулон to Player');
INSERT INTO `creature_text` VALUES (62442, 3, 1, 'Darkness grows!', 14, 0, 100, 0, 0, 29354, 63289, 0, 0, 0, 'VO_TES_SERPENT_EVENT_DAYTONIGHT_02');
INSERT INTO `creature_text` VALUES (62442, 3, 2, 'Light.. fades!', 14, 0, 100, 0, 0, 29355, 63290, 0, 0, 0, 'VO_TES_SERPENT_EVENT_DAYTONIGHT_03');
INSERT INTO `creature_text` VALUES (62442, 4, 0, 'Night.. becomes day!', 14, 0, 100, 0, 0, 29356, 63291, 0, 0, 0, 'VO_TES_SERPENT_EVENT_NIGHTTODAY_01');
INSERT INTO `creature_text` VALUES (62442, 4, 0, 'Ночь окружает тебя...', 14, 0, 100, 0, 0, 0, 63277, 0, 0, 0, 'Цулон to Player');
INSERT INTO `creature_text` VALUES (62442, 4, 1, 'The sun.. shines through!', 14, 0, 100, 0, 0, 29357, 63292, 0, 0, 0, 'VO_TES_SERPENT_EVENT_NIGHTTODAY_02');
INSERT INTO `creature_text` VALUES (62442, 4, 2, 'Once more.. the dawn!', 14, 0, 100, 0, 0, 29358, 63293, 0, 0, 0, 'VO_TES_SERPENT_EVENT_NIGHTTODAY_03');
INSERT INTO `creature_text` VALUES (62442, 5, 0, 'Die in darkness!', 14, 0, 100, 0, 0, 29360, 63276, 0, 0, 0, 'VO_TES_SERPENT_SLAY_01_DARK');
INSERT INTO `creature_text` VALUES (62442, 5, 1, 'The night surrounds you!', 14, 0, 100, 0, 0, 29361, 63277, 0, 0, 0, 'VO_TES_SERPENT_SLAY_02_DARK');
INSERT INTO `creature_text` VALUES (62442, 6, 0, 'No!', 14, 0, 100, 0, 0, 29362, 63279, 0, 0, 0, 'VO_TES_SERPENT_SLAY_03_LIGHT');
INSERT INTO `creature_text` VALUES (62442, 6, 1, 'Forgive me!', 14, 0, 100, 0, 0, 29363, 63280, 0, 0, 0, 'VO_TES_SERPENT_SLAY_04_LIGHT');
INSERT INTO `creature_text` VALUES (62442, 7, 0, 'Overwhelming fear!', 14, 0, 100, 0, 0, 29364, 63284, 0, 0, 0, 'VO_TES_SERPENT_SPELL_NIGGHTMARE_01');
INSERT INTO `creature_text` VALUES (62442, 7, 0, 'Всепоглощающий... страх!', 14, 0, 100, 0, 0, 0, 63284, 0, 0, 0, 'Цулон to 0');
INSERT INTO `creature_text` VALUES (62442, 7, 1, 'Leave this place or die!', 14, 0, 100, 0, 0, 29365, 63285, 0, 0, 0, 'VO_TES_SERPENT_SPELL_NIGGHTMARE_02');
INSERT INTO `creature_text` VALUES (62442, 8, 0, 'I thank you strangers.. I have been freed.', 14, 0, 100, 0, 0, 29352, 63275, 0, 0, 0, 'TSULONG_DEATH_02');
INSERT INTO `creature_text` VALUES (62442, 9, 0, 'Ночь... становится... днем!', 14, 0, 100, 0, 0, 0, 63291, 0, 0, 0, 'Цулон to Цулон');
INSERT INTO `creature_text` VALUES (62442, 10, 0, '%s призывает |cFFFFFFFFвоплощение ужаса|r!', 41, 0, 100, 0, 0, 0, 62591, 0, 0, 0, 'Цулон to 0');
INSERT INTO `creature_text` VALUES (62442, 12, 0, '|TInterface\\Icons\\ability_warlock_everlastingaffliction.blp:20|t%s находится под действием заклинания |cFFFF0000|Hspell:123011|h[Запугивание]|h|r! %s нуждается в помощи, надо рассеять заклинание!', 41, 0, 100, 0, 0, 0, 63518, 0, 0, 0, 'Цулон to Воплощение ужаса');
INSERT INTO `creature_text` VALUES (62442, 13, 0, 'Нет!', 14, 0, 100, 0, 0, 0, 135514, 0, 0, 0, 'Цулон to Нестабильный ша');
INSERT INTO `creature_text` VALUES (62442, 15, 0, 'Простите меня!', 14, 0, 100, 0, 0, 0, 63280, 0, 0, 0, 'Цулон to Нестабильный ша');
INSERT INTO `creature_text` VALUES (62442, 24, 0, 'И снова... рассвет...', 14, 0, 100, 0, 0, 0, 63293, 0, 0, 0, 'Цулон to Цулон');
INSERT INTO `creature_text` VALUES (62442, 27, 0, 'Спасибо вам, незнакомцы. Я свободен.', 14, 0, 100, 0, 0, 0, 63275, 0, 0, 0, 'Цулон to 0');
INSERT INTO `creature_text` VALUES (62444, 3, 0, 'Готова, жду тебя.', 12, 0, 100, 396, 0, 0, 61828, 7000, 7000, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (62444, 3, 0, 'За баррикады!', 12, 0, 100, 0, 0, 0, 61831, 18000, 18000, 0, 'Таоши to Норвакесс');
INSERT INTO `creature_text` VALUES (62444, 3, 0, 'Похоже, перед нами стоит трудная задача.', 12, 0, 100, 396, 0, 0, 61827, 3000, 3000, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (62444, 4, 0, 'Прекрасно. Вернемся к господину Чжу. Здесь больше нечего делать.', 12, 0, 100, 396, 0, 0, 61829, 45000, 45000, 0, 'Таоши to Норвакесс');
INSERT INTO `creature_text` VALUES (62444, 4, 0, 'Укройся за баррикадами!', 12, 0, 100, 0, 0, 0, 61830, 4000, 4000, 0, 'Таоши to Норвакесс');
INSERT INTO `creature_text` VALUES (62511, 0, 0, 'А, вижу, вам удалось найти мои экспериментальные образцы. Подойдите поближе и узрите все величие моего гения!', 14, 0, 100, 0, 0, 0, 62227, 0, 0, 0, 'Ваятель янтаря Ун\'сок to Player');
INSERT INTO `creature_text` VALUES (62511, 1, 0, 'Наверное, вы сильны, раз зашли так далеко. Да, да, из вас выйдут достойные подопытные образцы.', 14, 0, 100, 0, 0, 0, 62226, 0, 0, 0, 'Ваятель янтаря Ун\'сок to 0');
INSERT INTO `creature_text` VALUES (62511, 2, 0, 'Смотрите, куда идете!', 14, 0, 100, 0, 0, 0, 62235, 0, 0, 0, 'Ваятель янтаря Ун\'сок to Amber Scalpel');
INSERT INTO `creature_text` VALUES (62511, 3, 0, 'Смерть порождает жизнь, а жизнь – смерть...', 14, 0, 100, 0, 0, 0, 62234, 0, 0, 0, 'Ваятель янтаря Ун\'сок to 0');
INSERT INTO `creature_text` VALUES (62511, 4, 0, 'У меня есть для вас подарок!', 14, 0, 100, 0, 0, 0, 62233, 0, 0, 0, 'Ваятель янтаря Ун\'сок to 0');
INSERT INTO `creature_text` VALUES (62511, 5, 0, 'Думаете, вы сильны? Какая самоуверенность! Узрите же мое величайшее творение!', 14, 0, 100, 0, 0, 0, 62229, 0, 0, 0, 'Ваятель янтаря Ун\'сок to 0');
INSERT INTO `creature_text` VALUES (62511, 11, 0, 'Что вы наделали? Нет, вы не можете положить конец моему делу! Я вам не позволю!', 14, 0, 100, 0, 0, 0, 62230, 0, 0, 0, 'Ваятель янтаря Ун\'сок to Янтарное чудовище');
INSERT INTO `creature_text` VALUES (62511, 12, 0, 'Источники янтаря поблизости начинают бешено пузыриться.', 41, 0, 100, 0, 0, 0, 62398, 0, 0, 0, 'Ваятель янтаря Ун\'сок to Янтарное чудовище');
INSERT INTO `creature_text` VALUES (62511, 17, 0, 'Простите меня, императрица...', 14, 0, 100, 0, 0, 0, 62228, 0, 0, 0, 'Ваятель янтаря Ун\'сок to Player');
INSERT INTO `creature_text` VALUES (62529, 3, 0, 'Возвращайся к Тажаню Чжу. Я скоро тебя догоню.', 12, 0, 100, 1, 0, 0, 61919, 315000, 315000, 0, 'Лао-Чинь Стальное Брюхо to Player');
INSERT INTO `creature_text` VALUES (62529, 4, 0, 'Вот, выпей. Сразу взбодришься.', 12, 0, 100, 1, 0, 0, 61910, 92000, 260000, 0, 'Лао-Чинь Стальное Брюхо');
INSERT INTO `creature_text` VALUES (62529, 4, 0, 'Вылей обратно!', 12, 0, 100, 1, 0, 0, 61912, 65000, 65000, 0, 'Лао-Чинь Стальное Брюхо');
INSERT INTO `creature_text` VALUES (62529, 4, 0, 'Мы почти победили!', 12, 0, 100, 1, 0, 0, 61917, 269000, 269000, 0, 'Лао-Чинь Стальное Брюхо to Огромный кор\'тик - раздиратель плоти');
INSERT INTO `creature_text` VALUES (62529, 4, 0, 'Отлично! Я рад, что мы пошли вместе!', 12, 0, 100, 1, 0, 0, 61918, 279000, 279000, 0, 'Лао-Чинь Стальное Брюхо to Огромный кор\'тик - раздиратель плоти');
INSERT INTO `creature_text` VALUES (62538, 1, 0, 'Wakener, your deeds have earned you the trust of the Klaxxi.', 12, 0, 100, 0, 0, 30312, 67848, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62538, 2, 0, 'You are to be rewarded. Walk with me.', 12, 0, 100, 0, 0, 30313, 67849, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62538, 3, 0, 'We mantid are an elder race. The pandaren you associate with - they are but children. They have their role to play.', 12, 0, 100, 0, 0, 30314, 67850, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62538, 4, 0, 'Each cycle, our young swarm their walls. The pandaren slay the weak. The strong return.', 12, 0, 100, 0, 0, 30315, 67851, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62538, 5, 0, 'With each generation, we grow ever stronger.', 12, 0, 100, 0, 0, 30316, 67852, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62538, 6, 0, 'Before your history began, our empire was vast. We shared this world with our sister kingdoms, Ahn\'Qiraj and Azjol-Nerub.', 12, 0, 100, 0, 0, 30317, 67853, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62538, 7, 0, 'Our Gods were many, and powerful. We mantid worshipped the seven heads of Y\'shaarj.', 12, 0, 100, 0, 0, 30318, 67854, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62538, 8, 0, 'Great was the Old One, and terrible was His wrath. He consumed hope and begat despair; He inhaled courage and breathed fear.', 12, 0, 100, 0, 0, 30319, 67855, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62538, 9, 0, 'When the usurpers came - the ones you call \"Titans\" - Y\'shaarj was destroyed.', 12, 0, 100, 0, 0, 30320, 67856, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62538, 10, 0, 'His last terrible breath has haunted this land ever since, but the shadows he left behind are mere whispers of his former glory.', 12, 0, 100, 0, 0, 30321, 67857, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62538, 11, 0, 'I tell you now, because you have earned this warning.', 12, 0, 100, 0, 0, 30322, 67858, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62538, 12, 0, 'Your gods are not our gods, outsider.', 12, 0, 100, 0, 0, 30323, 67859, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62538, 13, 0, 'If the Old Ones ever return, we mantid will once again stand by their side.', 12, 0, 100, 0, 0, 30324, 67860, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62538, 14, 0, 'The wisest among you will do the same.', 12, 0, 100, 0, 0, 30325, 67861, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62541, 0, 0, 'Give in. The more you fight, the more your hatred broils within you.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62541, 0, 1, 'Come, lambs... come and face your demise!', 14, 0, 100, 0, 0, 0, 62004, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62543, 0, 0, 'Сопляки!', 14, 0, 100, 0, 0, 0, 64302, 0, 0, 0, 'Повелитель клинков Та\'як to Player');
INSERT INTO `creature_text` VALUES (62543, 1, 0, 'Сопляки. Им надо было заниматься усерднее.', 14, 0, 100, 0, 0, 0, 64303, 0, 0, 0, 'Повелитель клинков Та\'як to 69943');
INSERT INTO `creature_text` VALUES (62543, 2, 0, 'Одной лишь техники недостаточно.', 14, 0, 100, 0, 0, 0, 64304, 0, 0, 0, 'Повелитель клинков Та\'як to Player');
INSERT INTO `creature_text` VALUES (62543, 3, 0, 'Они не прошли испытание. Сможете ли вы?', 14, 0, 100, 0, 0, 0, 64305, 0, 0, 0, 'Повелитель клинков Та\'як to Player');
INSERT INTO `creature_text` VALUES (62543, 4, 0, 'Они приближаются? Хорошо. Жаль, здесь нет моих учеников, им было бы полезно посмотреть на эту демонстрацию воинского искусства и поучиться.', 14, 0, 100, 0, 0, 0, 64306, 0, 0, 0, 'Повелитель клинков Та\'як to Player');
INSERT INTO `creature_text` VALUES (62543, 5, 0, 'К бою, чужаки. Вам предстоит сразиться со мной, повелителем клинков Та\'яком.', 14, 0, 100, 0, 0, 0, 62941, 0, 0, 0, 'Повелитель клинков Та\'як to 0');
INSERT INTO `creature_text` VALUES (62543, 6, 0, 'Повелитель клинков Та\'як выбирает |3-3($n) целью для |cFFFF0000|Hspell:122949|h[Невидимого удара]|h|r!', 41, 0, 100, 0, 0, 0, 63522, 0, 0, 0, 'Повелитель клинков Та\'як to Player');
INSERT INTO `creature_text` VALUES (62543, 8, 0, 'Успеваешь следить за моим клинком?', 14, 0, 100, 0, 0, 0, 66887, 0, 0, 0, 'Повелитель клинков Та\'як to 0');
INSERT INTO `creature_text` VALUES (62543, 9, 0, 'Мне всегда хотелось нанести кому-то такой удар, чтобы услышать этот звук. Но слышать его исходящим из собственной глотки... как-то глупо...', 14, 0, 100, 0, 0, 0, 62944, 0, 0, 0, 'Повелитель клинков Та\'як to Player');
INSERT INTO `creature_text` VALUES (62547, 3, 0, 'Что-то происходит с богомолами... и я подозреваю, что это связано с храмом на той стороне моста.', 12, 0, 100, 0, 0, 0, 62049, 2000, 2000, 0, 'Тай Хо to Player');
INSERT INTO `creature_text` VALUES (62547, 3, 0, 'Я встречу тебя в храме Нюцзао, $p. Наши поиски еще не окончены...', 12, 0, 100, 0, 0, 0, 62050, 7000, 7000, 0, 'Тай Хо to Player');
INSERT INTO `creature_text` VALUES (62550, 0, 0, 'Ha! You think you\'re ready to take me on?', 12, 0, 100, 1, 0, 0, 62603, 0, 0, 0, 'Gossip text');
INSERT INTO `creature_text` VALUES (62550, 1, 0, 'Let\'s see if you\'re still standing after this.', 12, 0, 100, 1, 0, 0, 62604, 0, 0, 0, 'Gossip text');
INSERT INTO `creature_text` VALUES (62574, 0, 0, 'Его панцирь расколот на спине. Надо бить по больному!', 14, 0, 100, 22, 0, 0, 61958, 0, 0, 0, 'Повелитель ястребов Нужун to Воресс\'талик');
INSERT INTO `creature_text` VALUES (62574, 0, 0, 'Лучше атаковать его сзади.', 14, 0, 100, 22, 0, 0, 61959, 0, 0, 0, 'Повелитель ястребов Нужун to Воресс\'талик');
INSERT INTO `creature_text` VALUES (62574, 0, 0, 'Только не попади под его удар!', 14, 0, 100, 22, 0, 0, 61963, 0, 0, 0, 'Повелитель ястребов Нужун to Воресс\'талик');
INSERT INTO `creature_text` VALUES (62579, 1, 0, 'Артиллерия выжжет землю, не оставив ничего!', 14, 0, 100, 0, 0, 0, 65687, 0, 0, 0, 'Корвекксис');
INSERT INTO `creature_text` VALUES (62579, 4, 0, 'I lost, but you will not be able to survive to tell about your victory.', 14, 0, 100, 0, 0, 0, 65686, 11000, 11000, 0, 'Корвекксис');
INSERT INTO `creature_text` VALUES (62580, 0, 0, 'I lost, but you will not be able to survive to tell about your victory.', 14, 0, 100, 0, 0, 0, 65686, 0, 0, 0, 'Серевекс');
INSERT INTO `creature_text` VALUES (62580, 3, 0, 'Эй, подкрепление, сюда! Убейте этих слабаков!', 14, 0, 100, 0, 0, 0, 65688, 3000, 3000, 0, 'Серевекс');
INSERT INTO `creature_text` VALUES (62581, 0, 0, 'Ха-ха, бескрылые глупцы!', 14, 0, 100, 0, 0, 0, 65689, 0, 0, 0, 'Ротек');
INSERT INTO `creature_text` VALUES (62581, 4, 0, 'I lost, but you will not be able to survive to tell about your victory.', 14, 0, 100, 0, 0, 0, 65686, 28000, 28000, 0, 'Ротек');
INSERT INTO `creature_text` VALUES (62581, 4, 0, 'Глупцы, вы умрете вместе со мной!', 14, 0, 100, 0, 0, 0, 65685, 8000, 8000, 0, 'Ротек');
INSERT INTO `creature_text` VALUES (62593, 0, 0, 'Хорошо.', 12, 0, 100, 0, 0, 0, 140215, 0, 0, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (62593, 3, 0, 'Выступай. Я пойду следом.', 12, 0, 100, 0, 0, 0, 62043, 2000, 2000, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (62595, 4, 0, 'Неплохо... неплохо...', 12, 0, 100, 0, 0, 0, 62048, 13000, 13000, 0, 'Лао-Чинь Стальное Брюхо to Player');
INSERT INTO `creature_text` VALUES (62600, 3, 0, 'Впечатляет!', 12, 0, 100, 0, 0, 0, 75251, 21000, 21000, 0, 'Повелитель ястребов Нужун to 0');
INSERT INTO `creature_text` VALUES (62707, 3, 0, 'Враг не проявит милосердие. Поступай с ним так же.', 12, 0, 100, 1, 0, 0, 62219, 39000, 39000, 0, 'Шоми');
INSERT INTO `creature_text` VALUES (62711, 0, 0, 'Amber Monster is cooking |cFFFF0000|Hspell:122398|h[amber explosion]|h|r!', 41, 0, 100, 0, 0, 0, 62391, 0, 0, 0, 'Янтарное чудовище to 0');
INSERT INTO `creature_text` VALUES (62711, 1, 0, 'От... ец...', 14, 0, 100, 0, 0, 0, 66020, 0, 0, 0, 'Янтарное чудовище to Player');
INSERT INTO `creature_text` VALUES (62730, 0, 0, 'Господин Чжу, мы скоро вернемся.', 12, 0, 100, 1, 0, 0, 61905, 0, 0, 0, 'Лао-Чинь Стальное Брюхо to Player');
INSERT INTO `creature_text` VALUES (62735, 0, 0, 'I hope your moves aren\'t too predictable.', 12, 0, 100, 1, 0, 0, 62637, 0, 0, 0, 'Gossip text');
INSERT INTO `creature_text` VALUES (62736, 3, 0, 'Полагаю, на мосту вы одержали победу?', 12, 0, 100, 6, 0, 0, 61927, 7000, 7000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (62773, 0, 0, 'Freedom...', 12, 0, 100, 0, 0, 0, 62574, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62773, 1, 0, 'For eight hundred and seventy-seven years, I waited in that crystal... watching shadows pass through the cracks, counting decades.', 12, 0, 100, 0, 0, 0, 62575, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62773, 2, 0, 'I am needed elsewhere.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62774, 1, 0, 'Who holds this swarm? Show yourself! Answer to the Klaxxi.', 12, 0, 100, 0, 0, 0, 67837, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62774, 2, 0, 'Speak your name puppet, that our annals will be complete.', 12, 0, 100, 0, 0, 0, 67839, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62774, 3, 0, 'Your \"Empress\" has fallen. The Klaxxi have pronounced judgment. Stand aide or be brushed away.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62778, 0, 0, 'NO... YOU WILL NOT KEEP ME INSIDE THIS ABHORRENT PRISON...', 12, 0, 100, 0, 0, 0, 68787, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62778, 1, 0, 'SET ME FREE... THE KLAXXI CALL...', 12, 0, 100, 0, 0, 0, 68785, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62778, 2, 0, 'THE HATCHING HOUR IS NIGH... I MUST ANSWER TO THE SWARM...', 12, 0, 100, 0, 0, 0, 68786, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62778, 3, 0, 'I... wah... where? What is going on?', 12, 0, 100, 0, 0, 0, 62567, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62778, 4, 0, 'Oh, that. Bah, I have been through worse. And who exactly are you?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62778, 5, 0, 'STORMSTOUT? No. You\'re foolin\'. Really? A Wandering Isle Stormstout, here, on Pandaria! Where\'s the rest of the family?', 12, 0, 100, 0, 0, 0, 62571, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62781, 0, 0, 'I accept your challenge!', 12, 0, 100, 0, 0, 0, 62298, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62782, 0, 0, 'Are you really as strong as the others say?', 12, 0, 100, 0, 0, 0, 62247, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62786, 0, 0, 'I\'m glad to fight next to you!', 12, 0, 100, 66, 0, 0, 61969, 0, 0, 0, 'Повелитель ястребов Нужун to Player');
INSERT INTO `creature_text` VALUES (62788, 0, 0, 'Если господин Чжу хочет нас видеть, лучше не заставлять его ждать. Идем.', 12, 0, 100, 1, 0, 0, 61970, 0, 0, 0, 'Повелитель ястребов Нужун to Player');
INSERT INTO `creature_text` VALUES (62802, 0, 0, 'Без тебя у нас ничего бы не вышло, $p. Спасибо!', 12, 0, 100, 396, 0, 0, 62042, 0, 0, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (62803, 4, 0, 'Настал тот час, ради которого вы тренировались всю свою жизнь!', 14, 0, 100, 0, 0, 0, 62029, 49000, 49000, 0, 'Тажань Чжу to Ша Ненависти');
INSERT INTO `creature_text` VALUES (62821, 0, 0, 'Вот так и сбываются мечты?', 12, 0, 100, 3, 0, 0, 63963, 0, 0, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` VALUES (62821, 3, 0, 'I\'ll ride through the gates of Mogu\'shan.', 12, 0, 100, 3, 0, 0, 63977, 4626, 4626, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` VALUES (62821, 3, 0, 'Yaki. Yaks never change.', 12, 0, 100, 3, 0, 0, 63951, 25554, 25554, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` VALUES (62821, 3, 0, 'Как будто от меня часть отгрызли.', 12, 0, 100, 3, 0, 0, 63989, 15582, 15582, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` VALUES (62821, 3, 0, 'Мне хватит и минуты. Нет, подожди, не то.', 12, 0, 100, 3, 0, 0, 63978, 9204, 9204, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` VALUES (62821, 3, 0, 'Ничто во всей Пандарии не выстоит против буйной ярости большого яка для путешествий. Молись, чтобы никогда не увидеть ее.', 12, 0, 100, 3, 0, 0, 63974, 50469, 50469, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` VALUES (62821, 3, 0, 'О горе. Ниже, ниже.', 12, 0, 100, 3, 0, 0, 63972, 2005875, 2005875, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` VALUES (62821, 3, 0, 'Овса не найдется?', 12, 0, 100, 3, 0, 0, 63948, 29315, 29315, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` VALUES (62821, 3, 0, 'По-моему, як куда-то пошел.', 12, 0, 100, 3, 0, 0, 63970, 23051, 23051, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` VALUES (62821, 3, 0, 'Проснись и пой.', 12, 0, 100, 3, 0, 0, 63949, 14003, 14003, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` VALUES (62821, 3, 0, 'Твой як в другом стойле.', 12, 0, 100, 3, 0, 0, 63952, 28931, 28931, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` VALUES (62821, 3, 0, 'У этих яунголов прямые мечи. Прямые. Мечи.', 12, 0, 100, 3, 0, 0, 63971, 12484, 12484, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` VALUES (62821, 3, 0, 'Я вовсе не собираюсь с тобой судиться.', 12, 0, 100, 3, 0, 0, 63975, 35035, 35035, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` VALUES (62821, 3, 0, 'Я езжу на яках. А это мой як.', 12, 0, 100, 3, 0, 0, 63973, 51189, 51189, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` VALUES (62822, 3, 0, 'I feel like minced meat.', 12, 0, 100, 6, 0, 0, 63988, 21035, 21035, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` VALUES (62822, 3, 0, 'I wanted to arrange a holiday and call Mystic Ptitsekrov, but because of you he left.', 12, 0, 100, 6, 0, 0, 63969, 11951, 11951, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` VALUES (62822, 3, 0, 'Да я сюда за репой пришел, честное слово!', 12, 0, 100, 6, 0, 0, 63967, 53713, 53713, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` VALUES (62822, 3, 0, 'Десять лет. Я преданно чищу яков целых десять лет.', 12, 0, 100, 6, 0, 0, 63965, 73193, 73193, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` VALUES (62822, 3, 0, 'Жалко класть свой зад на такую красоту.', 12, 0, 100, 6, 0, 0, 63961, 2004734, 2004734, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` VALUES (62822, 3, 0, 'Когда споришь с яком, есть две теории. Обе не работают.', 12, 0, 100, 6, 0, 0, 63966, 11207, 11207, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` VALUES (62822, 3, 0, 'Может, ты покатишь бочку?', 12, 0, 100, 6, 0, 0, 63955, 24043, 24043, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` VALUES (62822, 3, 0, 'Надо обновить журнал.', 12, 0, 100, 6, 0, 0, 63987, 8277, 8277, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` VALUES (62822, 3, 0, 'Не опасно ли идти в одиночку?', 12, 0, 100, 6, 0, 0, 63953, 15521, 15521, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` VALUES (62822, 3, 0, 'Не покажешь ли ты мне дорогу в Кунь-Лай?', 12, 0, 100, 6, 0, 0, 63954, 5054, 5054, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` VALUES (62822, 3, 0, 'Помогает ли яку хвост удерживать равновесие?', 12, 0, 100, 6, 0, 0, 63976, 8715, 8715, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` VALUES (62822, 3, 0, 'Чем больше положишь, тем больше войдет.', 12, 0, 100, 6, 0, 0, 63959, 9864, 9864, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` VALUES (62822, 3, 0, 'Я жалкое существо из мяса и пива.', 12, 0, 100, 6, 0, 0, 63957, 20495, 20495, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` VALUES (62825, 0, 0, 'Are you ready?', 12, 0, 100, 0, 0, 0, 105292, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62836, 0, 0, 'Death to all who dare challenge my empire!', 12, 0, 100, 0, 0, 0, 67111, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62836, 1, 0, 'Such is the fate of those who question authority.', 12, 0, 100, 0, 0, 0, 67347, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (62837, 0, 0, 'Закройте врата! Перегородите выходы! Уничтожьте чужаков!', 14, 0, 100, 1, 0, 0, 62890, 0, 0, 0, 'Великая императрица Шек\'зир to Player');
INSERT INTO `creature_text` VALUES (62837, 1, 0, 'I will kill anyone who dares to go against my empire!', 14, 0, 100, 0, 0, 0, 62891, 0, 0, 0, 'Великая императрица Шек\'зир to 0');
INSERT INTO `creature_text` VALUES (62837, 2, 0, 'Теперь ты будешь служить мне!', 14, 0, 100, 0, 0, 0, 62896, 0, 0, 0, 'Великая императрица Шек\'зир to Player');
INSERT INTO `creature_text` VALUES (62837, 3, 0, 'Ты больше не будешь дышать!', 14, 0, 100, 0, 0, 0, 62894, 0, 0, 0, 'Великая императрица Шек\'зир to Player');
INSERT INTO `creature_text` VALUES (62837, 4, 0, 'I\'m ... defeated ...', 14, 0, 100, 0, 0, 0, 62892, 0, 0, 0, 'Великая императрица Шек\'зир to Player');
INSERT INTO `creature_text` VALUES (62849, 3, 0, '%s растворяется в тенях.', 16, 0, 100, 0, 0, 0, 68774, 40000, 40000, 122789, 'Луч солнца');
INSERT INTO `creature_text` VALUES (62881, 4, 0, '|TInterface\\Icons\\ability_warlock_baneofhavoc:20|t%s начинает произносить |cFFFF0000|Hspell:126299|h[Освобождение душ]|h|r! Убегайте!', 41, 0, 100, 0, 0, 0, 65223, 18000, 18000, 0, 'Гаохунь Ловец Душ');
INSERT INTO `creature_text` VALUES (62970, 3, 0, 'Вечно ты недоволен, Хоу!', 12, 0, 100, 0, 0, 0, 63294, 29000, 29000, 0, 'Старейшина Чи to Player');
INSERT INTO `creature_text` VALUES (62971, 3, 0, 'Обещания, обещания! Ты никак не сможешь нам помочь. Зандалары не ведают жалости.', 12, 0, 100, 5, 0, 0, 63286, 23000, 23000, 0, 'Старейшина Хоу to Player');
INSERT INTO `creature_text` VALUES (62978, 0, 0, 'A challenge, huh? Let\'s see what you\'ve got.', 12, 0, 100, 1, 0, 0, 62636, 0, 0, 0, 'Gossip text');
INSERT INTO `creature_text` VALUES (62978, 1, 0, 'Oh, yeah. I remember you. Let\'s see what you\'ve learned.', 12, 0, 100, 1, 0, 0, 62635, 0, 0, 0, 'Gossip text');
INSERT INTO `creature_text` VALUES (62980, 0, 0, 'Мы – всего лишь вестники воли нашей императрицы.', 14, 0, 100, 0, 0, 0, 62452, 0, 0, 0, 'Императорский визирь Зор\'лок to Кор\'тик-кромсатель');
INSERT INTO `creature_text` VALUES (62980, 1, 0, 'Наше предназначение – служить ее божественному имени.', 14, 0, 100, 0, 0, 0, 62453, 0, 0, 0, 'Императорский визирь Зор\'лок to Кор\'тик-кромсатель');
INSERT INTO `creature_text` VALUES (62980, 2, 0, 'Мы не созерцаем. Мы не задумываемся. Мы действуем.', 14, 0, 100, 0, 0, 0, 62454, 0, 0, 0, 'Императорский визирь Зор\'лок to Кор\'тик-кромсатель');
INSERT INTO `creature_text` VALUES (62980, 3, 0, 'Мы сражаемся, трудимся и служим. Мы видим реальность ее глазами.', 14, 0, 100, 0, 0, 0, 62455, 0, 0, 0, 'Императорский визирь Зор\'лок to Кор\'тик-кромсатель');
INSERT INTO `creature_text` VALUES (62980, 4, 0, 'Наша награда – ее счастье. Ее грусть – наше поражение.', 14, 0, 100, 0, 0, 0, 62456, 0, 0, 0, 'Императорский визирь Зор\'лок to Кор\'тик-кромсатель');
INSERT INTO `creature_text` VALUES (62980, 5, 0, 'Мы без колебаний пожертвуем своими жизнями во имя императрицы. Она – свет. Без нее наши жизни будут затеряны во тьме.', 14, 0, 100, 0, 0, 0, 62457, 0, 0, 0, 'Императорский визирь Зор\'лок to Кор\'тик-кромсатель');
INSERT INTO `creature_text` VALUES (62980, 6, 0, 'Слабые мира сего, гонимые страхом, обивают наш порог. Ее императорский гнев сметет их.', 14, 0, 100, 0, 0, 0, 62458, 0, 0, 0, 'Императорский визирь Зор\'лок to Зар\'тик-молельщик');
INSERT INTO `creature_text` VALUES (62980, 7, 0, 'Они – лишь волны, которые разбиваются о неприступные скалы ее божественной воли. Они накатывают снова и снова, но всякий раз это ни к чему не приводит.', 14, 0, 100, 0, 0, 0, 62459, 0, 0, 0, 'Императорский визирь Зор\'лок to Порабощенный костолом');
INSERT INTO `creature_text` VALUES (62980, 8, 0, 'Мы непреклонны. Мы будем стоять твердо.', 14, 0, 100, 0, 0, 0, 62462, 0, 0, 0, 'Императорский визирь Зор\'лок to Зар\'тик-молельщик');
INSERT INTO `creature_text` VALUES (62980, 9, 0, 'Богиня хочет, чтобы мы выступили против чужаков.', 14, 0, 100, 0, 0, 0, 62460, 0, 0, 0, 'Императорский визирь Зор\'лок to Шра\'тик-щитоносец');
INSERT INTO `creature_text` VALUES (62980, 10, 0, 'Мы слышим ее божественный глас.', 14, 0, 100, 0, 0, 0, 62463, 0, 0, 0, 'Императорский визирь Зор\'лок to Зар\'тик-фанатик');
INSERT INTO `creature_text` VALUES (62980, 11, 0, 'Богиня избрала нас для выражения своей божественной воли нашими голосами смертных. Мы лишь орудия в ее руках.', 14, 0, 100, 0, 0, 0, 62465, 0, 0, 0, 'Императорский визирь Зор\'лок to 0');
INSERT INTO `creature_text` VALUES (62980, 12, 0, 'Императорский визирь Зор\'лок летит к одной из своих платформ!', 41, 0, 100, 0, 0, 0, 63770, 0, 0, 0, 'Императорский визирь Зор\'лок to 0');
INSERT INTO `creature_text` VALUES (62980, 13, 0, '|TInterface\\Icons\\warrior_disruptingshout.blp:20|tИмператорский визирь Зор\'лок издает вопль. В нем слышатся |cFFFF0000|Hspell:122713|h[сила и мощь]|h|r!', 41, 0, 100, 0, 0, 0, 63774, 0, 0, 0, 'Императорский визирь Зор\'лок to Императорский визирь Зор\'лок');
INSERT INTO `creature_text` VALUES (62980, 14, 0, 'Тебе здесь не место, грязный чужак. Богиня не станет терпеть твоего присутствия.', 14, 0, 100, 0, 0, 0, 62466, 0, 0, 0, 'Императорский визирь Зор\'лок to Player');
INSERT INTO `creature_text` VALUES (62980, 16, 0, 'Мы вдыхаем воздух. Она заставляет наши голоса звучать громче.', 14, 0, 100, 0, 0, 0, 62468, 0, 0, 0, 'Императорский визирь Зор\'лок to 0');
INSERT INTO `creature_text` VALUES (62980, 17, 0, '|TInterface\\Icons\\spell_druid_stamedingroar.blp:20|tИмператорский визирь Зор\'лок делает глубокий |cFFFF0000|Hspell:122852|h[вдох]|h|r!', 41, 0, 100, 0, 0, 0, 63772, 0, 0, 0, 'Императорский визирь Зор\'лок to Императорский визирь Зор\'лок');
INSERT INTO `creature_text` VALUES (62980, 21, 0, 'Ее воля важнее всех нас. Присоединяйся к нам или погибни.', 14, 0, 100, 0, 0, 0, 62471, 0, 0, 0, 'Императорский визирь Зор\'лок to 0');
INSERT INTO `creature_text` VALUES (62980, 22, 0, '|TInterface\\Icons\\spell_arcane_mindmastery.blp:20|tИмператорский визирь Зор\'лок |cFFFF0000|Hspell:122740|h[обращает]|h|r участников рейда на свою сторону!', 41, 0, 100, 0, 0, 0, 63776, 0, 0, 0, 'Императорский визирь Зор\'лок to Императорский визирь Зор\'лок');
INSERT INTO `creature_text` VALUES (62980, 23, 0, 'Услышь глас ее божественной ненависти.', 14, 0, 100, 0, 0, 0, 62469, 0, 0, 0, 'Императорский визирь Зор\'лок to 0');
INSERT INTO `creature_text` VALUES (62980, 25, 0, 'Императорский визирь Зор\'лок вдыхает феромоны истовости.', 41, 0, 100, 0, 0, 0, 63394, 0, 0, 0, 'Императорский визирь Зор\'лок to 0');
INSERT INTO `creature_text` VALUES (62980, 27, 0, 'Мы не погрузимся в отчаяние. Если она хочет, чтобы мы погибли – так и будет.', 14, 0, 100, 0, 0, 0, 62475, 0, 0, 0, 'Императорский визирь Зор\'лок to Player');
INSERT INTO `creature_text` VALUES (62983, 0, 0, 'Wh-what are you doing here!? G-go away!', 14, 0, 100, 0, 0, 29328, 63176, 0, 0, 0, 'LEI_SHI_AGGRO');
INSERT INTO `creature_text` VALUES (62983, 0, 0, 'Отражение Лэй Ши зловеще содрогается.', 41, 0, 100, 0, 0, 0, 62713, 0, 0, 0, 'Лэй Ши to Оскверненный защитник');
INSERT INTO `creature_text` VALUES (62983, 1, 0, 'I\'m hiding until you leave!', 14, 0, 100, 0, 0, 29333, 63198, 0, 0, 0, 'LEI_SHI_HIDE_01');
INSERT INTO `creature_text` VALUES (62983, 1, 0, 'Отражение содрогается вновь.', 41, 0, 100, 0, 0, 0, 62714, 0, 0, 0, 'Лэй Ши to Оскверненный защитник');
INSERT INTO `creature_text` VALUES (62983, 1, 1, 'I don\'t want to see you anymore!', 14, 0, 100, 0, 0, 29334, 63199, 0, 0, 0, 'LEI_SHI_HIDE_02');
INSERT INTO `creature_text` VALUES (62983, 2, 0, '%s появляется!', 41, 0, 100, 0, 0, 0, 62715, 0, 0, 0, 'Лэй Ши to Оскверненный защитник');
INSERT INTO `creature_text` VALUES (62983, 2, 0, 'Stay away from me!', 14, 0, 100, 0, 0, 29332, 63197, 0, 0, 0, 'LEI_SHI_GET_AWAY_01');
INSERT INTO `creature_text` VALUES (62983, 2, 1, 'Get AWAY!', 14, 0, 100, 0, 0, 29331, 63196, 0, 0, 0, 'LEI_SHI_GET_AWAY_02');
INSERT INTO `creature_text` VALUES (62983, 3, 0, 'S-sorry!', 14, 0, 100, 0, 0, 29329, 63194, 0, 0, 0, 'LEI_SHI_SLAY_01');
INSERT INTO `creature_text` VALUES (62983, 3, 0, 'Ч-что вы здесь делаете?! П-п-прочь!', 14, 0, 100, 0, 0, 0, 63176, 0, 0, 0, 'Лэй Ши to 0');
INSERT INTO `creature_text` VALUES (62983, 3, 1, 'It\'s your fault it happened!', 14, 0, 100, 0, 0, 29330, 63195, 0, 0, 0, 'LEI_SHI_SLAY_02');
INSERT INTO `creature_text` VALUES (62983, 4, 0, '%s призывает несколько элементалей, чтобы |cFFFF0000|Hspell:123250|h[защититься]|h|r!', 41, 0, 100, 0, 0, 0, 62685, 0, 0, 0, 'Лэй Ши to 0');
INSERT INTO `creature_text` VALUES (62983, 4, 0, 'I... ah... oh! Did I...? Was I...? It was... so... cloudy.', 14, 0, 100, 0, 0, 29325, 62834, 0, 0, 0, 'LEI_SHI_DEFEATED_01');
INSERT INTO `creature_text` VALUES (62983, 4, 1, 'I have to go now.', 14, 0, 100, 0, 0, 29326, 62835, 0, 0, 0, 'LEI_SHI_DEFEATED_02');
INSERT INTO `creature_text` VALUES (62983, 4, 2, 'I have to make the water not cloudy anymore. Will... will you help?', 14, 0, 100, 0, 0, 29327, 62836, 0, 0, 0, 'LEI_SHI_DEFEATED_03');
INSERT INTO `creature_text` VALUES (62983, 5, 0, '%s больше не окружена защитным пузырем.', 41, 0, 100, 0, 0, 0, 62740, 0, 0, 0, 'Лэй Ши to Оживленный защитник');
INSERT INTO `creature_text` VALUES (62983, 6, 0, '%s пытается |cFFFF0000|Hspell:123461|h[прогнать вас!]|h|r', 41, 0, 100, 0, 0, 0, 62671, 0, 0, 0, 'Лэй Ши to 0');
INSERT INTO `creature_text` VALUES (62983, 7, 0, 'Не подходите ко мне!', 14, 0, 100, 0, 0, 0, 63197, 0, 0, 0, 'Лэй Ши to 0');
INSERT INTO `creature_text` VALUES (62983, 10, 0, '%s |cFFFF0000|Hspell:123244|h[прячется]|h|r!', 41, 0, 100, 0, 0, 0, 62667, 0, 0, 0, 'Лэй Ши to 0');
INSERT INTO `creature_text` VALUES (62983, 11, 0, 'Я буду прятаться, пока вы не уйдете!', 14, 0, 100, 0, 0, 0, 63198, 0, 0, 0, 'Лэй Ши to 0');
INSERT INTO `creature_text` VALUES (62983, 15, 0, 'Я больше не хочу вас видеть!', 14, 0, 100, 0, 0, 0, 63199, 0, 0, 0, 'Лэй Ши to 0');
INSERT INTO `creature_text` VALUES (62983, 16, 0, 'Все это произошло из-за вас!', 14, 0, 100, 0, 0, 0, 63195, 0, 0, 0, 'Лэй Ши to Player');
INSERT INTO `creature_text` VALUES (62983, 20, 0, 'Отойдите!', 14, 0, 100, 0, 0, 0, 79377, 0, 0, 0, 'Лэй Ши to 0');
INSERT INTO `creature_text` VALUES (62983, 21, 0, 'I ... ah ... oh! Me? .. Everything was so ... muddy.', 14, 0, 100, 0, 0, 0, 62834, 0, 0, 0, 'Лэй Ши to 0');
INSERT INTO `creature_text` VALUES (62983, 22, 0, 'Мне нужно идти.', 14, 0, 100, 0, 0, 0, 62835, 0, 0, 0, 'Лэй Ши to 0');
INSERT INTO `creature_text` VALUES (62983, 23, 0, 'Я должна сделать так, чтобы священные воды больше не были мутными. Вы... Вы поможете мне?', 14, 0, 100, 0, 0, 0, 62836, 0, 0, 0, 'Лэй Ши to 0');
INSERT INTO `creature_text` VALUES (62983, 62, 0, 'П-п-простите!', 14, 0, 100, 0, 0, 29329, 63194, 0, 0, 0, 'Лэй Ши to Player');
INSERT INTO `creature_text` VALUES (62996, 3, 0, 'Добро пожаловать, $gпутник:путница;. Располагайся тут, если хочешь. Наверху ты найдешь банк и городские порталы. По всему зданию – рынок. Не стесняйся, ходи и смотри.', 12, 0, 100, 0, 0, 0, 66597, 7833000, 8278000, 0, 'Мадам Ви Ло to Player');
INSERT INTO `creature_text` VALUES (63013, 3, 0, 'Отведай-ка!', 12, 0, 100, 396, 0, 0, 113089, 15000, 15000, 0, 'Повар Дрожащая Тарелка to Player');
INSERT INTO `creature_text` VALUES (63025, 3, 0, 'Нет... священные воды... надо сопротивляться. Я не должен бояться...', 14, 0, 100, 0, 0, 0, 63272, 1404000, 1404000, 0, 'Цулон to Защитник Каолань');
INSERT INTO `creature_text` VALUES (63053, 4, 0, '|TInterface\\Icons\\spell_nature_healingwavelesser.blp:20|t Garalon |cFFFF0000|Hspell:123495|h[Mends]|h|r one of his legs!', 41, 0, 100, 0, 0, 0, 62837, 36000, 463000, 0, 'Garalon\'s Leg to Garalon');
INSERT INTO `creature_text` VALUES (63053, 4, 0, '|TInterface\\Icons\\spell_nature_healingwavelesser.blp:20|t Гаралон |cFFFF0000|Hspell:123495|h[восстанавливает]|h|r себе одну ногу!', 41, 0, 100, 0, 0, 0, 62837, 125000, 403000, 0, 'Нога Гаралона to Гаралон');
INSERT INTO `creature_text` VALUES (63071, 3, 0, 'Danach.', 12, 0, 100, 396, 0, 0, 66301, 89531, 1892406, 0, 'Skeer der Blutsucher to Player');
INSERT INTO `creature_text` VALUES (63071, 3, 0, 'Das ist das Ende unserer Unterhaltung.', 12, 0, 100, 396, 0, 0, 66303, 105531, 1908406, 0, 'Skeer der Blutsucher to Player');
INSERT INTO `creature_text` VALUES (63071, 3, 0, 'Was war der letzte Teil?', 12, 0, 100, 6, 0, 0, 66299, 76531, 1879406, 0, 'Skeer der Blutsucher to Player');
INSERT INTO `creature_text` VALUES (63071, 3, 0, 'Welche Sorte Tests?', 12, 0, 100, 6, 0, 0, 66297, 58531, 1861406, 0, 'Skeer der Blutsucher to Player');
INSERT INTO `creature_text` VALUES (63072, 3, 0, 'Blutsucher, Eure regenerativen Kräfte faszinieren mich. Ich frage mich, ob Ihr mit ein paar Tests einverstanden wärt.', 12, 0, 100, 396, 0, 0, 66296, 48531, 1851406, 0, 'Rik\'kal der Sezierer to Player');
INSERT INTO `creature_text` VALUES (63072, 3, 0, 'Gewebeproben?', 12, 0, 100, 6, 0, 0, 66300, 83531, 1886422, 0, 'Rik\'kal der Sezierer to Player');
INSERT INTO `creature_text` VALUES (63072, 3, 0, 'Nichts besonders Blutiges?', 12, 0, 100, 396, 0, 0, 66302, 98578, 1901422, 0, 'Rik\'kal der Sezierer to Player');
INSERT INTO `creature_text` VALUES (63072, 3, 0, 'Nur etwas Zellentraumainduktion, vielleicht die eine oder andere Gewebeprobe, etwas Mikroschallgensondierung. Nichts besonders Blutiges!', 12, 0, 100, 396, 0, 0, 66298, 64531, 1867406, 0, 'Rik\'kal der Sezierer to Player');
INSERT INTO `creature_text` VALUES (63075, 0, 0, 'Thros- ', 12, 6, 100, 0, 0, 0, 0, 0, 0, 0, 'Грейди Баннсон to Player');
INSERT INTO `creature_text` VALUES (63128, 0, 0, 'Come at me.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Gossip text');
INSERT INTO `creature_text` VALUES (63272, 3, 0, 'Ого! Это, эм, интересно!', 12, 0, 100, 1, 0, 0, 66023, 22448, 22448, 0, 'Тин Крепкий Желудок to Player');
INSERT INTO `creature_text` VALUES (63280, 3, 0, 'А поскорее можно? У меня уже руки устали!', 14, 0, 100, 22, 0, 0, 63434, 5000, 5000, 0, 'Палубный матрос');
INSERT INTO `creature_text` VALUES (63310, 3, 0, 'Nicht schlecht, $n. Nicht schlecht.', 12, 0, 100, 508, 0, 0, 37928, 400547, 400547, 0, 'Zabrax to Player');
INSERT INTO `creature_text` VALUES (63312, 0, 0, '%s злится!', 41, 0, 100, 0, 0, 0, 63362, 0, 0, 0, 'Джол\'Грум to Джол\'Грум');
INSERT INTO `creature_text` VALUES (63313, 0, 0, '%s начинает призывать |cFFFE9A2Eпитающих пламя духов|r! Уничтожайте их, чтобы остановить стену огня!', 41, 0, 100, 0, 0, 0, 68325, 0, 0, 0, 'Малышка Люян to Малышка Люян');
INSERT INTO `creature_text` VALUES (63315, 0, 0, 'Добро пожаловать на Арену Истребления, наше первое официально одобренное боевое состязание!', 14, 0, 100, 5, 0, 8574, 62842, 0, 0, 0, 'Гаргток to Player');
INSERT INTO `creature_text` VALUES (63315, 1, 0, 'Готовы ли вы продемонстрировать здесь, под взором Тигра, свое воинское искусство?', 14, 0, 100, 6, 0, 0, 63395, 0, 0, 0, 'Гаргток to Player');
INSERT INTO `creature_text` VALUES (63315, 2, 0, 'Тогда бейте в гонг и покажите этим монахам, из чего вы сделаны! Как вы это умеете, хе-хе.', 14, 0, 100, 1, 0, 0, 62847, 0, 0, 0, 'Гаргток to Player');
INSERT INTO `creature_text` VALUES (63315, 3, 0, 'Первое испытание мастерства и силы! Перед вами Потертый Панцирь, сильнейшая из драконьих черепах!', 14, 0, 100, 5, 0, 8574, 62915, 0, 0, 0, 'Гаргток to Потертый Панцирь');
INSERT INTO `creature_text` VALUES (63315, 4, 0, 'Мда, вот это каша! Передайте в монастырскую кухню: сегодня у нас на ужин будет черепаховый суп!', 14, 0, 100, 5, 0, 8573, 62916, 0, 0, 0, 'Гаргток to Потертый Панцирь');
INSERT INTO `creature_text` VALUES (63315, 5, 0, 'Готовы к новой битве? Бейте в гонг и В АТАКУ!', 14, 0, 100, 5, 0, 0, 63396, 0, 0, 0, 'Гаргток to Потертый Панцирь');
INSERT INTO `creature_text` VALUES (63315, 6, 0, 'Пришел час грозы груммелей, неистового лохматого голиафа, снежного ужаса вершины Кунь-Лай – Джол\'Грума!', 14, 0, 100, 5, 0, 8574, 62917, 0, 0, 0, 'Гаргток to Джол\'Грум');
INSERT INTO `creature_text` VALUES (63315, 7, 0, 'Ох-х! Да, с нашим снежным Джол\'Грумом смертоносная троица обошлась совсем не с-нежно! Кстати, о готовности...', 14, 0, 100, 5, 0, 8573, 62918, 0, 0, 0, 'Гаргток to Джол\'Грум');
INSERT INTO `creature_text` VALUES (63315, 9, 0, 'Она здесь для того, чтобы завести новых друзей... а затем поджечь их! Маленькая шутница, самая хорошенькая пироманьячка Пандарии – Люян!', 14, 0, 100, 5, 0, 8574, 62919, 0, 0, 0, 'Гаргток to Малышка Люян');
INSERT INTO `creature_text` VALUES (63315, 10, 0, 'Загасили, как свечку! Хоть кто-нибудь может противостоять этим троим?!', 14, 0, 100, 5, 0, 8573, 62920, 0, 0, 0, 'Гаргток to Малышка Люян');
INSERT INTO `creature_text` VALUES (63315, 12, 0, 'Кто сказал, что это разъяренный як?! Это мастер топора Чагань Огненное Копыто, странствующий воин Танлунских степей!', 14, 0, 100, 5, 0, 8574, 62921, 0, 0, 0, 'Гаргток to Чаган Огненное Копыто');
INSERT INTO `creature_text` VALUES (63315, 13, 0, 'Вот это битва! Наши бравые воители угостили Чагана здоровенной порцией шашлыка из яунголины.', 14, 0, 100, 5, 0, 8573, 62922, 0, 0, 0, 'Гаргток to Player');
INSERT INTO `creature_text` VALUES (63315, 15, 0, 'Он сквозь туман летит и смерть врагам сулит, он спокоен, но с ним не балуй: ассасин, урожденный цзинь-юй – Сатай Бюй!', 14, 0, 100, 5, 0, 8574, 65632, 0, 0, 0, 'Гаргток to Сатай Бюй');
INSERT INTO `creature_text` VALUES (63315, 16, 0, 'Да, сколько ни бился наш хвостатый убийца как рыба об лед, пришел ему конец.', 14, 0, 100, 5, 0, 8573, 65633, 0, 0, 0, 'Гаргток to Player');
INSERT INTO `creature_text` VALUES (63315, 17, 0, 'Давайте все вместе поприветствуем новых чемпионов Арены Истребления!', 14, 0, 100, 4, 0, 0, 65636, 0, 0, 0, 'Гаргток to Player');
INSERT INTO `creature_text` VALUES (63359, 0, 0, 'Skeer the Bloodseeker has returned! Unnngghhh...', 12, 0, 100, 0, 0, 0, 62832, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (63359, 1, 0, 'Yes, Klaxxi\'va. What does the Council command? Skeer hears. Skeer obeys.', 12, 0, 100, 0, 0, 0, 62833, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (63359, 2, 0, 'It appears that Empress Shek\'zeer has let our empire fall into chaos. This we must address. But first, I must regain my strength.', 12, 0, 100, 0, 0, 0, 66451, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (63367, 0, 0, 'Hey there. I don\'t think you want to go down that tunnel on foot. The saurok are out in force today and they are mighty territorial.', 12, 0, 100, 0, 0, 0, 63820, 0, 0, 0, 'Brewmaster Boof to Player');
INSERT INTO `creature_text` VALUES (63367, 1, 0, 'Ever been to Kun-Lai before? It\'s great. Long flowing farmlands. Beautiful mountains. And absolutely nothing dangerous whatsoever.', 12, 0, 100, 0, 0, 0, 63848, 0, 0, 0, 'Brewmaster Boof to Player');
INSERT INTO `creature_text` VALUES (63367, 2, 0, 'Well... yes. There are problems with the hozen.', 12, 0, 100, 0, 0, 0, 63849, 0, 0, 0, 'Brewmaster Boof to Player');
INSERT INTO `creature_text` VALUES (63367, 3, 0, 'Yes, and the tigers.', 12, 0, 100, 0, 0, 0, 63855, 0, 0, 0, 'Brewmaster Boof to Player');
INSERT INTO `creature_text` VALUES (63367, 4, 0, 'The spears are just for show. As long as we don\'t get out of the boat or make eye contact or get hit by a spear then we should be fine.', 12, 0, 100, 0, 0, 0, 63850, 0, 0, 0, 'Brewmaster Boof to Player');
INSERT INTO `creature_text` VALUES (63367, 5, 0, 'It\'s safe. I brought Lorewalker Cho and his strange companions up this way not long back.', 12, 0, 100, 0, 0, 0, 63851, 0, 0, 0, 'Brewmaster Boof to Player');
INSERT INTO `creature_text` VALUES (63367, 6, 0, 'You had that coming. Waving your incense about in his face... wait a minute. Something doesn\'t feel right about this.', 12, 0, 100, 0, 0, 0, 63852, 0, 0, 0, 'Brewmaster Boof to Player');
INSERT INTO `creature_text` VALUES (63367, 7, 0, 'Are those refugees? What\'s going on over there? Do I hear fighting?', 12, 0, 100, 0, 0, 0, 63853, 0, 0, 0, 'Brewmaster Boof to Player');
INSERT INTO `creature_text` VALUES (63367, 8, 0, 'These people are farmers, not fighters! They may need our help!', 12, 0, 100, 0, 0, 0, 63854, 0, 0, 0, 'Brewmaster Boof to Player');
