INSERT INTO `creature_text` VALUES (64040, 3, 0, 'Давненько мы не виделись с Твитс. Надеюсь, с ней все в порядке.', 12, 0, 100, 0, 0, 0, 66809, 129000, 10808000, 0, 'Тина Ван');
INSERT INTO `creature_text` VALUES (64040, 3, 0, 'Однажды я стану путешественницей.', 12, 0, 100, 0, 0, 0, 66808, 4174000, 8759000, 0, 'Тина Ван');
INSERT INTO `creature_text` VALUES (64040, 3, 0, 'Хотите чего-нибудь выпить?', 12, 0, 100, 0, 0, 0, 66812, 9575000, 10521000, 0, 'Тина Ван');
INSERT INTO `creature_text` VALUES (64040, 3, 0, 'Хочется когда-нибудь уехать отсюда, хотя и в Долине в принципе неплохо живется!', 12, 0, 100, 0, 0, 0, 66811, 1577000, 5371000, 0, 'Тина Ван');
INSERT INTO `creature_text` VALUES (64040, 3, 0, 'Эй, убери обувь со стола... И НОГИ ТОЖЕ!', 12, 0, 100, 0, 0, 0, 66810, 6892000, 6892000, 0, 'Тина Ван');
INSERT INTO `creature_text` VALUES (64068, 3, 0, 'Ни один чужак еще не подходил так близко к машине Налак\'ша.', 14, 0, 100, 0, 0, 0, 65930, 50000, 50000, 0, 'Хранитель двигателя Могу\'шан to Стражник Могу\'шан');
INSERT INTO `creature_text` VALUES (64068, 3, 0, 'Эти секреты умрут вместе с вами.', 14, 0, 100, 0, 0, 0, 65940, 53000, 53000, 128744, 'Хранитель двигателя Могу\'шан to Стражник Могу\'шан');
INSERT INTO `creature_text` VALUES (64071, 0, 0, 'Пожалуйста, отведай угощение, приготовленное Золотым Лотосом в знак признательности твоему народу за их помощь в защите Долины Четырех Ветров.', 12, 0, 100, 4, 0, 0, 66877, 0, 0, 0, 'Чжэнь Чжэнь Ван to Player');
INSERT INTO `creature_text` VALUES (64072, 0, 0, '%s is trying to escape in a panic!', 16, 0, 100, 0, 0, 0, 1150, 0, 0, 0, 'Омар Гонзалез');
INSERT INTO `creature_text` VALUES (64072, 0, 0, 'Как насчет еще одной игры?', 12, 0, 100, 0, 0, 0, 76713, 0, 0, 0, 'Омар Гонзалез');
