INSERT INTO `creature_text` VALUES (64072, 3, 0, 'Красиво сыграно, сэр.', 12, 0, 100, 0, 0, 0, 76712, 412000, 824000, 0, 'Омар Гонзалез');
INSERT INTO `creature_text` VALUES (64078, 3, 0, 'Еще стаканчик?', 12, 0, 100, 6, 0, 0, 66758, 25000, 25000, 0, 'Сарья Чайный Цветок');
INSERT INTO `creature_text` VALUES (64092, 3, 0, 'Здорово, дружище! Ну что, удалось запустить \"Громотанк XT 9000\"?', 12, 0, 100, 6, 0, 0, 66729, 39794, 256520, 0, 'Мерфи Злоторф');
INSERT INTO `creature_text` VALUES (64115, 0, 0, 'А вот это я называю хорошим матчем!', 12, 0, 100, 0, 0, 0, 76722, 0, 0, 0, 'Скотт Смит');
INSERT INTO `creature_text` VALUES (64115, 0, 0, 'Готовлю триумфальное возвращение!', 12, 0, 100, 0, 0, 0, 76720, 0, 0, 0, 'Скотт Смит');
INSERT INTO `creature_text` VALUES (64115, 0, 0, 'Лучшие две из трех!', 12, 0, 100, 0, 0, 0, 76719, 0, 0, 0, 'Скотт Смит');
INSERT INTO `creature_text` VALUES (64115, 0, 0, 'Хм. Неплохая карта.', 12, 0, 100, 0, 0, 0, 76716, 0, 0, 0, 'Скотт Смит');
INSERT INTO `creature_text` VALUES (64115, 3, 0, 'На этот раз я тебе начищу таз!', 12, 0, 100, 0, 0, 0, 76723, 757000, 757000, 0, 'Скотт Смит');
INSERT INTO `creature_text` VALUES (64115, 3, 0, 'Никогда такой карты не видел.', 12, 0, 100, 0, 0, 0, 76718, 208000, 208000, 0, 'Скотт Смит');
INSERT INTO `creature_text` VALUES (64115, 3, 0, 'Ну ладно, ладно, ты победил!', 12, 0, 100, 0, 0, 0, 76724, 232000, 232000, 0, 'Скотт Смит');
INSERT INTO `creature_text` VALUES (64140, 3, 0, 'Мне нужно собрать другую колоду…', 12, 0, 100, 0, 0, 0, 76706, 23000, 23000, 0, 'Вайна Стальная Лапа');
INSERT INTO `creature_text` VALUES (64140, 3, 0, 'Посмотрим…', 12, 0, 100, 0, 0, 0, 86872, 2038000, 2038000, 0, 'Вайна Стальная Лапа');
