INSERT INTO `creature_text` VALUES (64149, 0, 0, '$gКакой же ты худой:Какая же ты худая;, |3-6($r)! Тебе нужно что-то поесть. Садись, я принесу.', 12, 0, 100, 1, 0, 0, 64894, 0, 0, 0, 'Распорядительница Ви Вин to Player');
INSERT INTO `creature_text` VALUES (64149, 3, 0, 'Welcome to the Golden Lantern, the finest inn in the Vale. If anyone wants to argue with me, I will show them my fists.', 12, 0, 100, 1, 0, 0, 64896, 2516, 2516, 100340, 'Matron Vi Vinh to Player');
INSERT INTO `creature_text` VALUES (64149, 3, 0, 'В моей таверне ты почувствуешь себя как дома. А может, и лучше. Пожалуйста, располагайся поудобнее.', 12, 0, 100, 2, 0, 0, 64899, 2366000, 2366000, 0, 'Распорядительница Ви Вин to Player');
INSERT INTO `creature_text` VALUES (64149, 3, 0, 'Дай отдых усталым ногам, |3-6($r). Внутри тебя ждут хорошая еда и выпивка.', 12, 0, 100, 1, 0, 0, 64893, 0, 0, 0, 'Распорядительница Ви Вин to Player');
INSERT INTO `creature_text` VALUES (64149, 3, 0, 'Добро пожаловать в \"Золотой фонарь\", лучшую таверну в Доле. А кто со мной не согласен, познакомится с моим хуком слева.', 12, 0, 100, 1, 0, 0, 64896, 0, 0, 100340, 'Распорядительница Ви Вин to Player');
INSERT INTO `creature_text` VALUES (64149, 3, 0, 'Здесь, в \"Золотом фонаре\" краны никогда не закрываются, а новые бочки подкатывают без перерыва. Что тебе принести?', 12, 0, 100, 1, 0, 0, 64895, 7084406, 7084406, 100340, 'Распорядительница Ви Вин to Player');
INSERT INTO `creature_text` VALUES (64149, 3, 0, 'Мягче перин, чем у нас, в этих краях не сыскать. Хочешь вздремнуть?', 12, 0, 100, 1, 0, 5964, 64897, 0, 0, 0, 'Распорядительница Ви Вин to Player');
INSERT INTO `creature_text` VALUES (64149, 3, 0, 'Приветствую, $gстранник:странница;. Добро пожаловать в \"Золотой фонарь\".', 12, 0, 100, 2, 0, 0, 64892, 0, 0, 100340, 'Распорядительница Ви Вин to Player');
INSERT INTO `creature_text` VALUES (64149, 3, 0, 'Ты нормально питаешься, |3-6($r)? Побереги себя.', 12, 0, 100, 1, 0, 0, 64898, 24083797, 24083797, 100340, 'Распорядительница Ви Вин to Player');
