INSERT INTO `creature_text` VALUES (64164, 3, 0, 'Не бойся, если оружие вдруг дрогнет в твоей руке. Твое тело – вот истинное оружие, которое защитит тебя от любого врага.', 12, 0, 100, 1, 0, 0, 66774, 6000, 6000, 0, 'Ин Громовое Копье');
INSERT INTO `creature_text` VALUES (64191, 3, 0, 'До встречи, $n.', 12, 0, 100, 3, 0, 0, 111948, 15938, 15938, 0, 'Призрачный пандарен-ремесленник to Player');
INSERT INTO `creature_text` VALUES (64202, 1, 0, 'Уйди-ка ты с дороги, слышь?', 12, 0, 100, 1, 0, 0, 64656, 0, 0, 0, 'Мастер проклятий из племени Гурубаши to Player');
INSERT INTO `creature_text` VALUES (64202, 3, 0, 'Зандалары вернут прежние дни.', 12, 0, 100, 274, 0, 0, 64661, 5000, 5000, 0, 'Мастер проклятий из племени Гурубаши to Player');
INSERT INTO `creature_text` VALUES (64202, 3, 0, 'Зандалары снова поднимутся.', 12, 0, 100, 1, 0, 0, 64655, 232000, 232000, 0, 'Мастер проклятий из племени Гурубаши to Player');
INSERT INTO `creature_text` VALUES (64202, 3, 0, 'Попробуй магию зандаларов!', 12, 0, 100, 274, 0, 0, 64657, 5000, 5000, 0, 'Мастер проклятий из племени Гурубаши to Player');
INSERT INTO `creature_text` VALUES (64202, 3, 0, 'Ты будешь моей добычей.', 12, 0, 100, 1, 0, 0, 64660, 3000, 3000, 0, 'Мастер проклятий из племени Гурубаши to Player');
INSERT INTO `creature_text` VALUES (64202, 4, 0, 'Против зандаларов и могу сразу? Кишка тонка.', 12, 0, 100, 0, 0, 0, 64662, 56000, 56000, 0, 'Мастер проклятий из племени Гурубаши to Player');
INSERT INTO `creature_text` VALUES (64231, 3, 0, 'Если ты хочешь улучшить свои навыки в кулинарии, я могу поделиться с тобой своими знаниями.', 12, 0, 100, 0, 0, 0, 68293, 132000, 132000, 0, 'Суншинь Железная Лапа to Player');
INSERT INTO `creature_text` VALUES (64231, 3, 0, 'Эй, $gпутешественник:путешественница;! Хочешь научиться готовить?', 12, 0, 100, 0, 0, 0, 68288, 4869000, 4869000, 0, 'Суншинь Железная Лапа to Player');
INSERT INTO `creature_text` VALUES (64236, 0, 0, 'У всех есть кристаллы?', 12, 0, 100, 1, 0, 0, 61997, 0, 0, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (64240, 0, 0, 'Don\'t hold back.', 12, 0, 100, 1, 0, 0, 63750, 0, 0, 0, 'Gossip text');
