INSERT INTO `creature_text` VALUES (64243, 0, 0, 'По тайной лестнице сбегает саурок и тащит кое-какие сокровища!', 41, 0, 100, 0, 0, 0, 64421, 0, 0, 0, 'Глинтрок-разведчик to Player');
INSERT INTO `creature_text` VALUES (64259, 0, 0, 'Pick any of these fabolous rods and CAREFULLY tug the line. That should help set the hook into whatever\'s flailin\' around at the other end.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (64280, 0, 0, 'I call upon the waters of my native land ... Bring my enemies to me!', 14, 0, 100, 0, 0, 0, 64153, 0, 0, 0, 'Маки Водорез to Маки Водорез');
INSERT INTO `creature_text` VALUES (64281, 0, 0, 'Фум-м!', 12, 0, 100, 2, 0, 0, 65569, 0, 0, 0, 'Сатай Бюй to Player');
INSERT INTO `creature_text` VALUES (64283, 4, 0, '%s начинает преследовать |3-3($n)!', 41, 0, 100, 0, 0, 31564, 49578, 3418, 3418, 0, 'Янтарная сфера to Player');
INSERT INTO `creature_text` VALUES (64312, 0, 0, 'Hmmm, I reckon I don\'t know, $R. All I can understand is the part about the valley - that\'s right here! The Valley of the Four Winds!', 12, 0, 0, 1, 0, 0, 64254, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64313, 0, 0, 'The maiden, eh? Hmm, I remember that\'s what my father used to call that river to the north. Other than that, I wouldn\'t have the slightest clue.', 12, 0, 0, 1, 0, 0, 64252, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64315, 0, 0, 'Oh, I HAVE heard of the ancestors\' stair. That\'s what some folks call those mountains to the north.', 12, 0, 0, 1, 0, 0, 64255, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64327, 0, 0, 'What echoes and what\'s dark? It\'s simple! A cave!', 12, 0, 0, 1, 0, 0, 64253, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64330, 0, 0, 'Думаешь, сможешь одолеть меня в битве? Многие маленькие девочки боятся змей, а по-моему, они такие лапочки!', 12, 0, 100, 0, 0, 0, 64260, 0, 0, 0, 'Джулия Стивенс to Player');
INSERT INTO `creature_text` VALUES (64331, 3, 0, 'Нет, милый. Тебе нужно вырасти большим и сильным. Ты ведь хочешь стать таким же сильным, как я?', 12, 0, 100, 0, 0, 0, 64245, 5000, 5000, 0, 'Бри Лан');
INSERT INTO `creature_text` VALUES (64361, 0, 0, 'Дядюшка Гао, ты должен был присматривать за пивоварней. Что ты наделал?', 12, 0, 100, 0, 0, 0, 61478, 0, 0, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (64361, 3, 0, 'А в главном хранилище – гну-сини.', 12, 0, 100, 5, 0, 29774, 63022, 32000, 32000, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (64361, 3, 0, 'Мне кажется, хмелеварня горит.', 12, 0, 100, 1, 0, 29775, 63023, 51000, 51000, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (64361, 3, 0, 'Но какой ценой? Хмелеварня разорена, захвачена чужаками!', 12, 0, 100, 274, 0, 29773, 61479, 24000, 24000, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` VALUES (64367, 0, 0, 'Сущность Юй-лун тепло улыбается вам, растворяясь в небе.', 16, 0, 100, 0, 0, 0, 72870, 0, 0, 0, 'Невидимка to Невидимка');
INSERT INTO `creature_text` VALUES (64367, 1, 0, 'Сущность Сюэня издает триумфальный рык и превращается в небесную пыль.', 16, 0, 100, 0, 0, 0, 72876, 0, 0, 0, 'Невидимка to Невидимка');
INSERT INTO `creature_text` VALUES (64367, 2, 0, 'Сущность Чи-Цзи рассеивается, оставляя за собой след из звездной пыли.', 16, 0, 100, 0, 0, 0, 72869, 0, 0, 0, 'Невидимка to Невидимка');
INSERT INTO `creature_text` VALUES (64367, 3, 0, 'Сущность Нюцзао рокочет, принимая вашу помощь.', 16, 0, 100, 0, 0, 0, 72871, 0, 0, 0, 'Невидимка to Невидимка');
INSERT INTO `creature_text` VALUES (64451, 3, 0, 'Купи яблочко! Яблоко в день убережет от жрецов.', 12, 0, 100, 0, 0, 0, 64642, 16842000, 16842000, 0, 'Юнь Яблочная Бочка');
INSERT INTO `creature_text` VALUES (64467, 3, 0, 'Эй, $p, выход здесь.', 12, 0, 100, 0, 0, 0, 64860, 7000, 7000, 0, 'Лучница Ли to Player');
INSERT INTO `creature_text` VALUES (64473, 0, 0, 'Do not worry, I will go easy on you.', 12, 0, 100, 1, 0, 0, 62546, 0, 0, 0, 'Gossip text');
INSERT INTO `creature_text` VALUES (64482, 3, 0, 'Нам нужна пинта холодного пива! Сейчас же!', 14, 0, 100, 0, 0, 0, 66666, 116000, 116000, 0, 'Целительница Нань');
INSERT INTO `creature_text` VALUES (64482, 3, 0, 'Тише, жрец. Ты сейчас никому не сможешь помочь, у тебя сломаны обе руки! Я принесу тебе лекарство.', 12, 0, 100, 0, 0, 0, 66660, 110000, 110000, 0, 'Целительница Нань');
INSERT INTO `creature_text` VALUES (64492, 3, 0, 'Все готово? Отлично. Иди сюда.', 12, 0, 100, 273, 0, 30054, 64697, 2000, 2000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (64492, 3, 0, 'Для начала выпей это.', 12, 0, 100, 432, 0, 30056, 64702, 22000, 22000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (64492, 3, 0, 'Чтобы увидеть то, что может показать тебе хмель глубоких раздумий, тебе надо обрести внутреннюю гармонию.', 12, 0, 100, 0, 0, 0, 64699, 8000, 8000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (64506, 3, 0, 'Он на земле! Давай же прикончим его!', 12, 0, 100, 0, 0, 27382, 64768, 149000, 149000, 0, 'Аиса Воспевающая Облака to Чжао-жэнь');
INSERT INTO `creature_text` VALUES (64506, 3, 0, 'Получилось! Теперь атакуем его, пока он не взлетел!', 12, 0, 100, 0, 0, 27381, 64767, 78000, 78000, 0, 'Аиса Воспевающая Облака to Чжао-жэнь');
INSERT INTO `creature_text` VALUES (64506, 3, 0, 'Ты все правильно делаешь!', 12, 0, 100, 0, 0, 27380, 64766, 62000, 62000, 0, 'Аиса Воспевающая Облака to Чжао-жэнь');
INSERT INTO `creature_text` VALUES (64517, 0, 0, '$p, сюда! К мосту!', 12, 0, 100, 1, 0, 0, 64811, 0, 0, 0, 'Шадо-мастер Чум Киу to Player');
INSERT INTO `creature_text` VALUES (64528, 0, 0, 'Ah, another stranger from beyond the mists.', 12, 0, 100, 0, 0, 0, 65214, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (64528, 1, 0, 'Ten thousand years ago, the last Pandaren Emperor came seeking my advice. He, too, was filled with conflict. Let us see if you are correct, Taran. Let us see how this stranger deals with the turmoil of the soul.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (64528, 2, 0, 'We can agree it is noble to fight for a righteous cause: to defend home or family. But what of vengeance? What of anger? I sense these emotions run deep in this one.', 12, 0, 100, 0, 0, 0, 65273, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (64528, 3, 0, 'Well spoken, Zhi. The strangers from beyond the mists have no shortage of passions, but let us see if they indeed control them. Beyond anger lies hatred, the wellspring of all misery. What is this? Your hatred has a face!', 12, 0, 100, 0, 0, 0, 65326, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (64528, 4, 0, 'Well, this one has indeed proven mastery over anger. Between the young cub\'s words and the actions of this hero, I think I understand. Those from beyond the mists are hardened by battle. They are scarred, yes, but they have learned much. I trust them.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (64528, 5, 0, 'It is decided. I will open the gates. The Vale of Eternal Blossoms will be open for all.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (64528, 6, 0, 'I will consult the others. Meet me at the gates.', 12, 0, 100, 0, 0, 0, 65354, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (64536, 3, 0, 'Великий Тигр, чужеземцам, которые явились из-за туманов, нельзя верить. Тому доказательство руины в Нефритовом лесу.', 12, 0, 100, 396, 0, 0, 64886, 69000, 69000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (64536, 3, 0, 'Видите? Смотрите, как чужаки ненавидят друг друга! Они идут со своей ненавистью в бой и с ней же умирают!', 12, 0, 100, 396, 0, 0, 65327, 284000, 284000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (64536, 3, 0, 'Вы хотите победить только друг друга!', 12, 0, 100, 396, 0, 0, 65323, 205000, 205000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (64536, 3, 0, 'Жестокость. Видите? Они полны жестокости!', 12, 0, 100, 396, 0, 0, 65248, 134000, 134000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (64536, 3, 0, 'Мы уже видели вашу \"помощь\" в Нефритовом лесу. Мы видели \"Орду\" и \"Альянс\". Вы не умеете себя контролировать. Вы повсюду сеете несчастье.', 12, 0, 100, 396, 0, 0, 65217, 98000, 98000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (64536, 3, 0, 'Половина Дола уничтожена! Чужаки принесли больше бед, чем богомолы и могу, вместе взятые.', 12, 0, 100, 5, 0, 0, 75748, 56000, 56000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (64536, 3, 0, 'Скоро они наводнят весь материк. Как остановить их?', 12, 0, 100, 396, 0, 0, 65216, 85000, 85000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (64536, 3, 0, 'Ты совершаешь ужасную ошибку!', 12, 0, 100, 5, 0, 0, 65351, 375000, 375000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (64537, 3, 0, 'Как интересно. Они принесли много хорошего в эти земли. Но вместе с этим с ними придет и война.', 12, 0, 100, 5, 0, 0, 65329, 299000, 299000, 0, 'Чжи Безмятежный to Player');
INSERT INTO `creature_text` VALUES (64537, 3, 0, 'Не бывает жизни без гнева. Это то, с чем мы должны бороться. Преодолевая его, мы обретаем великую силу.', 12, 0, 100, 5, 0, 0, 65324, 211000, 211000, 0, 'Чжи Безмятежный to Player');
INSERT INTO `creature_text` VALUES (64537, 3, 0, 'Это так. Они сильны. И они многому могут научить нас.', 12, 0, 100, 5, 0, 0, 65258, 155000, 155000, 0, 'Чжи Безмятежный to Player');
INSERT INTO `creature_text` VALUES (64540, 3, 0, 'Да, гнев живет в наших душах, но мы ценим умение контролировать свои эмоции. Даже орки, которые когда-то были охвачены гневом и жаждой крови, смогли победить свою ярость.', 12, 0, 100, 396, 0, 0, 65322, 192000, 192000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (64540, 3, 0, 'Да... мы воины. Но мы пришли из жестоких земель. Мы сражались с демонами, мы победили смерть и даже спасли этот мир от гибели!', 12, 0, 100, 396, 0, 0, 65251, 142000, 142000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (64540, 3, 0, 'Дайте нам шанс все исправить.', 12, 0, 100, 396, 0, 0, 64889, 66000, 66000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (64540, 3, 0, 'Как похоже на... моего отца!', 12, 0, 100, 396, 0, 0, 65340, 282000, 282000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (64540, 3, 0, 'Клянусь, наступят мирные времена, и все распри уйдут в историю. Поверьте мне.', 12, 0, 100, 396, 0, 0, 65330, 306000, 306000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (64540, 3, 0, 'Мы хотим предложить вам помощь. Вместе мы восстановим дол.', 12, 0, 100, 396, 0, 0, 75749, 92000, 92000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (64540, 3, 0, 'Нет, это не так. В нас есть далеко не только это.', 12, 0, 100, 396, 0, 0, 65328, 293000, 293000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (64547, 3, 0, 'I heard the ruler wants to make us fight each other. But for this, it seems, and especially do not need to try.', 12, 0, 100, 5, 0, 0, 61320, 26000, 26000, 0, 'Гуртанский задира');
INSERT INTO `creature_text` VALUES (64547, 3, 0, 'Как ты думаешь, кого выберет король?', 12, 0, 100, 1, 0, 0, 61315, 41000, 41000, 120375, 'Гуртанский задира');
INSERT INTO `creature_text` VALUES (64547, 3, 0, 'Наш клан – самый великий на этой земле, он должен выбрать нас!', 12, 0, 100, 23, 0, 0, 61316, 18000, 18000, 120375, 'Гуртанский задира');
INSERT INTO `creature_text` VALUES (64548, 3, 0, 'Другие кланы, небось, и магией уже не владеют. Что от них толку королю?', 12, 0, 100, 11, 0, 0, 61317, 56000, 56000, 0, 'Рубака клана Каргеш');
INSERT INTO `creature_text` VALUES (64548, 3, 0, 'Как ты думаешь, кого выберет король?', 12, 0, 100, 1, 0, 0, 61315, 17000, 17000, 0, 'Рубака клана Каргеш');
INSERT INTO `creature_text` VALUES (64548, 3, 0, 'Когда в одном месте столько кланов, драки не избежать.', 12, 0, 100, 274, 0, 0, 61319, 39000, 39000, 0, 'Рубака клана Каргеш');
INSERT INTO `creature_text` VALUES (64549, 0, 0, 'На крышах появляются ледяные стрелки Шадо-Пан.', 41, 0, 100, 0, 0, 0, 64855, 0, 0, 0, 'Ледяной стрелок Шадо-Пан to Player');
INSERT INTO `creature_text` VALUES (64549, 1, 0, 'Стрелки Шадо-Пан исчезают!', 41, 0, 100, 0, 0, 0, 59331, 0, 0, 0, 'Ледяной стрелок Шадо-Пан to Player');
INSERT INTO `creature_text` VALUES (64561, 3, 0, 'Not today, little one. We have errands to run.', 12, 0, 100, 0, 0, 0, 65057, 6330000, 6330000, 0, 'Jong Ming-Yiu');
INSERT INTO `creature_text` VALUES (64561, 3, 0, 'Не сегодня, малыш. У нас еще куча поручений.', 12, 0, 100, 0, 0, 0, 65057, 4224000, 4224000, 0, 'Цзун Мин-Иу');
INSERT INTO `creature_text` VALUES (64562, 0, 0, 'Готовься к смерти, злодей!', 14, 0, 100, 0, 0, 0, 65253, 0, 0, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64562, 0, 0, 'Невыносимое зрелище.', 12, 0, 100, 274, 0, 30287, 65085, 0, 0, 0, 'Говорящий череп');
INSERT INTO `creature_text` VALUES (64562, 0, 0, 'Смерть Гандлингу! Умри!', 14, 0, 100, 0, 0, 0, 65149, 0, 0, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64562, 3, 0, 'Вот и все. Проклятие снято. Спасибо тебе, $p. Спасибо.', 12, 0, 100, 0, 0, 0, 65225, 387000, 387000, 0, 'Призрак лорда Алексея Барова to Player');
INSERT INTO `creature_text` VALUES (64562, 3, 0, 'Вот он! Убей его, $p!', 14, 0, 100, 1, 0, 29464, 65213, 309000, 309000, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64562, 3, 0, 'Вперед!', 12, 0, 100, 0, 0, 0, 131943, 247000, 247000, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64562, 3, 0, 'Говорящий Череп весело хихикает, глядя на гибель души инструктора.', 16, 0, 100, 11, 0, 29456, 65077, 91000, 91000, 0, 'Говорящий череп to Инструктор Ледяное Сердце');
INSERT INTO `creature_text` VALUES (64562, 3, 0, 'Говорящий Череп колеблется, входить ли в зал, но затем решается.', 16, 0, 100, 0, 0, 0, 65078, 112000, 112000, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64562, 3, 0, 'Говорящий Череп передвигается в следующий зал, не говоря ни слова.', 16, 0, 100, 0, 0, 30288, 65086, 179000, 179000, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64562, 3, 0, 'Его нужно остановить! Забудь о ней!', 14, 0, 100, 0, 0, 0, 65221, 340000, 340000, 0, 'Говорящий череп to Лилиан Восс');
INSERT INTO `creature_text` VALUES (64562, 3, 0, 'Мы идем за тобой, Ледяное Сердце! Время платить по счетам!', 14, 0, 100, 15, 0, 0, 65074, 40000, 40000, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64562, 3, 0, 'Наконец-то.', 12, 0, 100, 0, 0, 0, 136180, 363000, 363000, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64562, 3, 0, 'Нет! Вернись, трус!', 14, 0, 100, 15, 0, 0, 65203, 224000, 224000, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64562, 3, 0, 'Ну вот. Теперь постарайся дожить до конца.', 12, 0, 100, 1, 0, 29460, 65072, 34000, 34000, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64563, 3, 0, 'Вот и все. Проклятие снято. Спасибо тебе, $p. Спасибо.', 12, 0, 100, 1, 0, 0, 65225, 603000, 603000, 58538, 'Призрак лорда Алексея Барова to Player');
INSERT INTO `creature_text` VALUES (64563, 3, 0, 'Вот он! Убей его, $p!', 14, 0, 100, 1, 0, 29464, 65213, 408000, 408000, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64563, 3, 0, 'Вперед!', 12, 0, 100, 0, 0, 0, 131943, 322000, 322000, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64563, 3, 0, 'Говорящий Череп весело хихикает, глядя на гибель души инструктора.', 16, 0, 100, 11, 0, 29456, 65077, 106000, 106000, 0, 'Говорящий череп to Инструктор Ледяное Сердце');
