INSERT INTO `creature_text` VALUES (64563, 3, 0, 'Готовься к смерти, злодей!', 14, 0, 100, 0, 0, 27477, 65253, 550000, 550000, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64563, 3, 0, 'Его нужно остановить! Забудь о ней!', 14, 0, 100, 0, 0, 0, 65221, 460000, 460000, 0, 'Говорящий череп to Лилиан Восс');
INSERT INTO `creature_text` VALUES (64563, 3, 0, 'Мы идем за тобой, Ледяное Сердце! Время платить по счетам!', 14, 0, 100, 15, 0, 0, 65074, 31000, 31000, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64563, 3, 0, 'Наконец-то.', 12, 0, 100, 0, 0, 0, 136180, 530000, 530000, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64563, 3, 0, 'Невыносимое зрелище.', 12, 0, 100, 274, 0, 30287, 65085, 173000, 173000, 0, 'Говорящий череп to Джандис Барова');
INSERT INTO `creature_text` VALUES (64563, 3, 0, 'Нет! Вернись, трус!', 14, 0, 100, 15, 0, 27485, 65203, 275000, 275000, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64563, 3, 0, 'Ну вот. Теперь постарайся дожить до конца.', 12, 0, 100, 1, 0, 29460, 65072, 26000, 26000, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64563, 3, 0, 'Смерть Гандлингу! Умри!', 14, 0, 100, 0, 0, 29469, 65149, 223000, 223000, 0, 'Говорящий череп to Player');
INSERT INTO `creature_text` VALUES (64575, 3, 0, 'Боюсь, нам придется здесь задержаться. Орда может защитить нас.', 12, 0, 100, 0, 0, 0, 65064, 54757000, 72088000, 0, 'Чань Хой-Сань');
INSERT INTO `creature_text` VALUES (64578, 3, 0, 'I want to play with my toys. I want to go home.', 12, 0, 100, 0, 0, 0, 65062, 54935000, 54935000, 0, 'Цзун Цзюнь-Кит');
INSERT INTO `creature_text` VALUES (64578, 3, 0, 'А когда мы вернемся домой?', 12, 0, 100, 0, 0, 0, 65061, 54755000, 54755000, 0, 'Цзун Цзюнь-Кит');
INSERT INTO `creature_text` VALUES (64578, 3, 0, 'Может, вернемся на Кунь-Лай? Здесь так странно пахнет.', 12, 0, 100, 0, 0, 0, 65063, 72086000, 72086000, 0, 'Цзун Цзюнь-Кит');
INSERT INTO `creature_text` VALUES (64579, 3, 0, 'Can we go see the serpents?', 12, 0, 100, 0, 0, 0, 65056, 6327000, 6327000, 0, 'Jong Wik-Wung');
INSERT INTO `creature_text` VALUES (64579, 3, 0, 'Может, поиграем с гоблинами? Смотри, я выше них!', 12, 0, 100, 0, 0, 0, 65060, 7509000, 7509000, 0, 'Цзун Вик-Вун');
INSERT INTO `creature_text` VALUES (64579, 3, 0, 'Может, посмотрим женщину-нежить? Она чудная.', 12, 0, 100, 0, 0, 0, 65059, 26490000, 26490000, 0, 'Цзун Вик-Вун');
INSERT INTO `creature_text` VALUES (64579, 3, 0, 'Может, посмотрим змеев?', 12, 0, 100, 0, 0, 0, 65056, 5000, 5000, 0, 'Цзун Вик-Вун');
INSERT INTO `creature_text` VALUES (64579, 3, 0, 'Может, посмотрим тауренов? Они похожи на яунголов.', 12, 0, 100, 0, 0, 0, 65058, 4222000, 4222000, 0, 'Цзун Вик-Вун');
INSERT INTO `creature_text` VALUES (64596, 0, 0, 'Мэр Хан, мы должны действовать решительно! Нужно хотя бы собрать стражников!', 12, 0, 100, 5, 0, 0, 67702, 0, 0, 0, 'Тэн Яблоневый Цвет to Player');
INSERT INTO `creature_text` VALUES (64597, 0, 0, 'Mmmmm, I haven\'t tasted a brew of this complexity and clarity since... well... since the golden age of the Stormstout Brewery! Whoever made this has a real talent.', 12, 0, 100, 0, 0, 0, 65914, 0, 0, 0, 'Nana Mudclaw to player');
INSERT INTO `creature_text` VALUES (64616, 0, 0, 'Very good. Let`s talk.', 12, 0, 100, 1, 0, 29136, 65088, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 1, 0, 'Tong! Drinks, please.', 12, 0, 100, 1, 0, 29137, 65089, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 2, 0, 'My father, Deathwing, tried to destroy the whole of Azeroth.', 12, 0, 100, 1, 0, 29138, 65090, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 3, 0, 'He was misguided, of course, but he was right about one thing: Our world is... so fragile.', 12, 0, 100, 1, 0, 29139, 65091, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 4, 0, 'Wa are a point of light in a universe of shadow. A candle in a tempest.', 12, 0, 100, 1, 0, 29140, 65092, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 5, 0, 'Sometimes I think it was the very precariousness of our world that drove my father to madness.', 12, 0, 100, 1, 0, 29141, 65093, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 6, 0, 'Ah, thank you Tong.', 12, 0, 100, 1, 0, 29142, 65094, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 7, 0, 'Now to my point: I believe we are headed towards a reckoning. And no, I am not talking about the current conflict between the Aliance and the Horde.', 12, 0, 100, 1, 0, 29143, 65095, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 8, 0, 'Believe me - what Garrosh Hellscream achieved in Theramore is nothing compared to the horrors that are even now bearing down on our fragile home.', 12, 0, 100, 1, 0, 29144, 65096, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 9, 0, 'But the war deeply trubles me. Do you see my concern', 12, 0, 100, 1, 0, 29145, 65097, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 10, 0, 'A divided Azeroth cannot possibly stand against the darkness.', 12, 0, 100, 1, 0, 29146, 65098, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 11, 0, 'This war has to end. Soon. Before it consumes our Strength!', 12, 0, 100, 1, 0, 29147, 65099, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 12, 0, 'You can rest assured that my loyalties lie with Horde.', 12, 0, 100, 1, 0, 29149, 65101, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 13, 0, 'How do we bring a swift and decisive end to the conflict? I believe the answer lies with heroes like you.', 12, 0, 100, 1, 0, 29150, 65102, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 26, 0, 'You can rest assured that my loyalties lie with Aliance.', 12, 0, 100, 1, 0, 29148, 65100, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 28, 0, 'Tong! Something to eat, please.', 12, 0, 100, 1, 0, 34610, 70329, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 29, 0, 'Garrosh Hellscream! Warchief of the Horde.', 12, 0, 100, 1, 0, 34574, 70343, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 30, 0, 'He did not always command such power.', 12, 0, 100, 1, 0, 34575, 70344, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 31, 0, 'On Draenor, he was a miserable wretch. The actions of his father hung heavy on his heart.', 12, 0, 100, 1, 0, 34576, 70345, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 32, 0, 'I understand you, Garrosh. I know what it is like to bear the weight of your father\'s deeds.', 12, 0, 100, 1, 0, 34577, 70346, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 33, 0, 'A warrior like Garrosh comes to power with something to prove.', 12, 0, 100, 1, 0, 34578, 70347, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 34, 0, 'Your Horde finds itself at a turning point. This war will define you.', 12, 0, 100, 1, 0, 34579, 70348, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 35, 0, 'What IS the Horde?', 12, 0, 100, 1, 0, 34580, 70349, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 36, 0, 'Is it an instrument of vengeance, hellbent to carve out a future for its people?', 12, 0, 100, 1, 0, 34581, 70350, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 37, 0, 'Is it a refuge for those who need shelter, a commonwealth of the downtrodden, built for mutual support?', 12, 0, 100, 1, 0, 34582, 70351, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 38, 0, 'Or is it an alliance of convenience, a confederacy of unlikely partners free to pursue their own agendas?', 12, 0, 100, 1, 0, 34583, 70352, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 39, 0, 'I asked you: Who is the soul of the Horde?', 12, 0, 100, 1, 0, 34584, 70374, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 40, 0, 'We will find the answers here in Pandaria.', 12, 0, 100, 1, 0, 34585, 70354, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 41, 0, 'Show me what your Horde is made of, Champion!', 12, 0, 100, 1, 0, 34586, 70355, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 42, 0, 'Prove to me that your Horde is worthy of ruling over Azeroth.', 12, 0, 100, 1, 0, 34587, 70356, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 44, 0, 'The High King. Varian Wrynn - Leader of the Alliance.', 12, 0, 100, 1, 0, 34561, 70330, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 45, 0, 'Did you know, he was once a gladiator? Oh yes, I\'ve been doing my research.', 12, 0, 100, 1, 0, 34562, 70331, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 46, 0, 'Suffering from amnesia, he fought for his life in Horde arenas.', 12, 0, 100, 1, 0, 34563, 70332, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 47, 0, 'There, he earned the name \"Lo\'Gosh,\" or \"Ghost Wolf.\"', 12, 0, 100, 1, 0, 34564, 70333, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 48, 0, 'He IS a fighter. But... is he a leader?', 12, 0, 100, 1, 0, 34565, 70334, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 49, 0, 'Think about it! His enemies took advantage of my father\'s Cataclysm.', 12, 0, 100, 1, 0, 34566, 70335, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 50, 0, 'Horde soldiers thundered across the realm in the wake of the devastation.', 12, 0, 100, 1, 0, 34567, 70336, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 51, 0, 'Varian Wrynn didn\'t use the disaster to gain a military advantage.', 12, 0, 100, 1, 0, 34568, 70337, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 52, 0, 'Is this because he is weak...? Or because he is principled?', 12, 0, 100, 1, 0, 34569, 70338, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 53, 0, 'Here\'s the bigger question: Can principles win a war?', 12, 0, 100, 1, 0, 34570, 70339, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 54, 0, 'Make no mistake. Pandaria will challenge your King.', 12, 0, 100, 1, 0, 34571, 70340, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 55, 0, 'Let us see what he is truly made of.', 12, 0, 100, 1, 0, 34572, 70341, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 56, 0, 'Let us see if your Alliance has what it takes to rule Azeroth.', 12, 0, 100, 1, 0, 34573, 70342, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 57, 0, 'You have proven to be an exemplary hero. A champion for your cause.', 12, 0, 100, 1, 0, 34602, 70371, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 58, 0, 'Do you recall our earlier conversation?', 12, 0, 100, 1, 0, 34603, 70372, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 59, 0, 'We spoke of your Warchief. His inner conflicts and his outer conquests.', 12, 0, 100, 1, 0, 34604, 70373, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 61, 0, 'Your efforts on Padaria\'s southern shore and elsewhere have given me my answer.', 12, 0, 100, 1, 0, 34606, 70375, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 62, 0, 'YOU are the soul of the Horde.', 12, 0, 100, 1, 0, 34607, 70376, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 63, 0, 'Your actions in the coming weeks will come to define your people.', 12, 0, 100, 1, 0, 34608, 70377, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 64, 0, 'I want to make sure that you are properly equipped for the trials ahead.', 12, 0, 100, 1, 0, 34609, 70378, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 65, 0, 'Tong: fetch my things!', 12, 0, 100, 1, 0, 34597, 70366, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 66, 0, 'Your dedication to your cause deserves to be rewarded.', 12, 0, 100, 1, 0, 34598, 70367, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 67, 0, 'Like you, I continue to refine my skills here in Pandaria. I think you will appreciate the outcome.', 12, 0, 100, 1, 0, 34599, 70368, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 68, 0, 'Choose your reward!', 12, 0, 100, 1, 0, 34600, 70369, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 71, 0, 'We discussed the merits of your High King. I have been observing him closely.', 12, 0, 100, 1, 0, 34590, 70359, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 72, 0, 'It is true: he has the spirit of the wolf. And he fights like a wolf.', 12, 0, 100, 1, 0, 34591, 70360, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 73, 0, 'Having seen him in action, I now know what that means.', 12, 0, 100, 1, 0, 34592, 70361, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 74, 0, 'Wolves are pack animals.', 12, 0, 100, 1, 0, 34593, 70362, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 75, 0, 'Your King is calling his pack to him, assembling an Alliance in the truest sense of the word.', 12, 0, 100, 1, 0, 34594, 70363, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 76, 0, 'His journey is long. He has only taken the first steps.', 12, 0, 100, 1, 0, 34595, 70364, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64616, 77, 0, 'But with heroes like you in his pack, there is little your Alliance cannot accomplish.', 12, 0, 100, 1, 0, 34596, 70365, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64623, 3, 0, '|TInterface\\Icons\\ability_warlock_improvedsoulleech:20|tВы попали под действие заклинания |cFFFF0000|Hspell:126228|h[Покалеченная душа]|h|r! Убивать!', 42, 0, 100, 0, 0, 0, 65154, 0, 0, 126228, 'Severed Soul to Player');
INSERT INTO `creature_text` VALUES (64631, 3, 0, 'Колдунство троллей – самое сильное колдунство!', 12, 0, 100, 0, 0, 0, 10437, 3445000, 3445000, 0, 'Знахарь из племени Гурубаши to Player');
INSERT INTO `creature_text` VALUES (64631, 3, 0, 'Почувствуй большое страшное вуду!', 12, 0, 100, 0, 0, 0, 10438, 4397000, 4397000, 0, 'Знахарь из племени Гурубаши to Player');
INSERT INTO `creature_text` VALUES (64631, 3, 0, 'Я из тебя амулет сделаю!', 12, 0, 100, 0, 0, 0, 10435, 12854000, 12854000, 0, 'Знахарь из племени Гурубаши to Player');
INSERT INTO `creature_text` VALUES (64648, 3, 0, 'Лучники, в атаку! Пусть сгорит дотла!', 14, 0, 100, 0, 0, 0, 65347, 47000, 118000, 0, 'Капитан стражи Шадо-Пан to Кри\'чон');
INSERT INTO `creature_text` VALUES (64648, 3, 0, 'Тварь покрыта маслом, подожги его!', 14, 0, 100, 0, 0, 0, 65346, 94000, 94000, 0, 'Капитан стражи Шадо-Пан to Кри\'чон');
INSERT INTO `creature_text` VALUES (64738, 3, 0, 'Присев на колени, рыцарь в капюшоне осматривает и обыскивает тело ткача пламени.', 16, 0, 100, 0, 0, 0, 65386, 16000, 16000, 0, 'Рыцарь в капюшоне to Player');
INSERT INTO `creature_text` VALUES (64738, 3, 0, 'Там лежит немало тел. Отличная работа, $p.', 12, 0, 100, 21, 0, 0, 65384, 4000, 4000, 0, 'Рыцарь в капюшоне to Player');
INSERT INTO `creature_text` VALUES (64738, 3, 0, 'Удалось достать кодекс?', 12, 0, 100, 6, 0, 0, 65385, 21000, 21000, 0, 'Рыцарь в капюшоне to Player');
INSERT INTO `creature_text` VALUES (64757, 4, 0, 'Есть, свет!', 14, 0, 100, 0, 0, 0, 65504, 9000, 9000, 0, 'Шонуф to Шонуф');
INSERT INTO `creature_text` VALUES (64764, 0, 0, 'There are a lot of corpses out there.  Nicely done, $p.', 12, 0, 100, 21, 0, 0, 65384, 0, 0, 0, 'Hooded Crusader to Player');
INSERT INTO `creature_text` VALUES (64822, 0, 0, 'I often venture out here to think. The view is breathtaking, would you agree?', 12, 0, 100, 1, 0, 29124, 65109, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64822, 1, 0, 'Pandaria is indeed a jewel. Such beauty. It makes you want to protect her, yes?', 12, 0, 100, 1, 0, 29125, 65110, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64822, 2, 0, 'I feel the same way about Azeroth herself.', 12, 0, 100, 1, 0, 29126, 65111, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64822, 3, 0, 'You have done great things, friend. I can assure you this is but the beginning of our work together.', 12, 0, 100, 1, 0, 29127, 65112, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64822, 4, 0, 'Behold: The power of the foes you`ve vanquished.', 12, 0, 100, 1, 0, 29128, 65113, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64822, 5, 0, 'And the wisdom of those you`ve outsmarted.', 12, 0, 100, 1, 0, 29129, 65114, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64822, 6, 0, 'Here! The embodiment of the fear that you`ve conquered. May it now strike terror into the hearts of your enemies!', 12, 0, 100, 1, 0, 29130, 65115, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64822, 7, 0, 'Finally, a gift from me. Make no mistake: the Black Dragonflight lives on!', 12, 0, 100, 1, 0, 29131, 65116, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64822, 8, 0, 'There. My work is done. You can now greatly augment the power of any sha-touched weapon.', 12, 0, 100, 1, 0, 29132, 65117, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (64841, 3, 0, 'Готово. У тебя получилось, $p!', 12, 0, 100, 1, 0, 0, 65470, 6000, 6000, 0, 'Рыцарь в капюшоне to Player');
INSERT INTO `creature_text` VALUES (64841, 3, 0, 'Она достает клинки из тела Вайтмейн, и они меняют форму.', 16, 0, 100, 477, 0, 0, 65567, 17000, 17000, 0, 'Лилиан Восс to Player');
INSERT INTO `creature_text` VALUES (64841, 3, 0, 'Привет, меня зовут Лилиан. Не возражешь, если я возьму клинки?', 12, 0, 100, 477, 0, 0, 65472, 16000, 16000, 0, 'Лилиан Восс to Player');
INSERT INTO `creature_text` VALUES (64841, 3, 0, 'Рыцарь в капюшоне смотрит вниз на пронзенное мечом тело верховного инквизитора.', 16, 0, 100, 0, 0, 0, 65473, 10000, 10000, 0, 'Рыцарь в капюшоне to Player');
INSERT INTO `creature_text` VALUES (64841, 3, 0, 'Теперь можно и раскрыть карты.', 12, 0, 100, 463, 0, 0, 65471, 14000, 14000, 65313, 'Рыцарь в капюшоне to Player');
INSERT INTO `creature_text` VALUES (64875, 0, 0, 'Он открыл зонтик, воткнул в землю и сел в его прохладной тени.', 12, 0, 100, 6, 0, 0, 65613, 0, 0, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` VALUES (64875, 1, 0, 'Он закрыл глаза, и стал единым целым с землей. А потом – знаете, что случилось потом?', 12, 0, 100, 2, 0, 0, 65614, 0, 0, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` VALUES (64875, 2, 0, 'Его зонтик... пророс! У него выросли корни, он стал деревом, и на нем расцвели цветы!', 12, 0, 100, 5, 0, 0, 65615, 0, 0, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` VALUES (64875, 3, 0, 'Да, это правда. Можете пойти в Лес Посохов и увидеть его собственными глазами.', 12, 0, 100, 1, 0, 0, 65616, 0, 0, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` VALUES (64875, 4, 0, 'Окруженный посохами всех предков, что были до нас, посохами, выросшими в гигантские деревья.', 12, 0, 100, 274, 0, 0, 65617, 0, 0, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` VALUES (64875, 5, 0, 'Это не грустная история! Сам Лю Лан говорил: \"Не стоит сожалеть о жизни, прожитой не зря\".', 12, 0, 100, 1, 0, 0, 65618, 0, 0, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` VALUES (64875, 6, 0, 'А он, я думаю, прожил очень хорошую жизнь. Всем, что у нас есть, мы обязаны Лю Лану, первому пандарену-путешественнику.', 12, 0, 100, 273, 0, 0, 65619, 0, 0, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` VALUES (64875, 7, 0, 'Мы рассказываем его историю, чтобы память о нем не истерлась никогда.', 12, 0, 100, 2, 0, 0, 65620, 0, 0, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` VALUES (64875, 8, 0, 'Спасибо, Жуолинь! Это было прекрасно.', 12, 0, 100, 21, 0, 0, 65621, 0, 0, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` VALUES (64875, 9, 0, 'Можешь спеть ее еще раз, для тех, кто только что пришел?', 12, 0, 100, 6, 0, 0, 65622, 0, 0, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` VALUES (64875, 10, 0, 'Это песня о Лю Лане, первом пандарене-путешественнике.', 12, 0, 100, 1, 0, 0, 65583, 0, 0, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` VALUES (64875, 11, 0, 'Песня написана на древнем наречии, языке императоров и ученых. Вряд ли сейчас кто-то говорит на нем.', 12, 0, 100, 6, 0, 0, 65584, 0, 0, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` VALUES (64875, 12, 0, 'Она рассказывает о его приключениях.', 12, 0, 100, 273, 0, 0, 65585, 0, 0, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` VALUES (64875, 13, 0, 'Храбрый Лю Лан оседлал морскую черепаху и отправился исследовать мир.', 12, 0, 100, 1, 0, 0, 65586, 0, 0, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` VALUES (64875, 14, 0, 'Кто-нибудь помнит, как звали черепаху? Инь?', 12, 0, 100, 25, 0, 0, 65587, 0, 0, 0, 'Хранительница истории Амай');
INSERT INTO `creature_text` VALUES (64885, 0, 0, 'Здравствуй, $n! Хранитель истории внизу как раз начинает урок, может, ты хочешь послушать?', 12, 0, 100, 3, 0, 0, 65573, 0, 0, 0, 'Хранитель истории Цзань');
INSERT INTO `creature_text` VALUES (64916, 4, 0, '%s приходит в ярость, разлучаясь со своим призывателеи янтаря!', 41, 0, 100, 0, 0, 0, 65747, 10055, 10055, 0, 'Кор\'тик - страж роя');
INSERT INTO `creature_text` VALUES (65111, 4, 0, 'Артиллерия богомолов уже здесь! Торопитесь, герои!', 12, 0, 100, 397, 0, 0, 64838, 17781, 17781, 0, 'Защитник Змеиного Хребта to Крик\'тик-разведчик');
INSERT INTO `creature_text` VALUES (65153, 0, 0, 'Hold your blade, adventurer!', 14, 0, 0, 0, 0, 0, 47004, 0, 0, 0, 'General hold blade');
INSERT INTO `creature_text` VALUES (65153, 1, 0, 'This beast leads the Riverpaw gnoll gang and may be the key to ending gnoll aggression in Elwynn.', 12, 0, 0, 0, 0, 0, 47007, 0, 0, 0, 'General speech');
INSERT INTO `creature_text` VALUES (65153, 2, 0, 'We\'re taking him into custody in the name of King Varian Wrynn.', 12, 0, 0, 0, 0, 0, 47008, 0, 0, 0, 'General take custody');
INSERT INTO `creature_text` VALUES (65153, 3, 0, 'Take us to the Stockades, Andromath.', 12, 0, 0, 0, 0, 0, 47009, 0, 0, 0, 'General take custody');
INSERT INTO `creature_text` VALUES (65160, 3, 0, 'Wrathion\'s agents whisper quietly to one another.', 16, 0, 100, 0, 0, 0, 67584, 39000, 39000, 0, 'Blacktalon Watcher');
INSERT INTO `creature_text` VALUES (65160, 3, 0, 'Шпионы Гневиона перешептываются между собой.', 16, 0, 100, 0, 0, 0, 67584, 40000, 40000, 0, 'Страж Черного Когтя');
INSERT INTO `creature_text` VALUES (65173, 4, 0, '|TInterface\\Icons\\trade_archaeology_delicatemusicbox.blp:20|tИмператорский визирь Зор\'лок исполняет |cFFFF0000|Hspell:123791|h[песнь императрице]|h|r, наносящую урон всем игрокам до тех пор, пока они не вступят с ним в схватку!', 41, 0, 100, 0, 0, 0, 63775, 25397, 25397, 0, 'Эхо поглощения to Эхо поглощения');
INSERT INTO `creature_text` VALUES (65253, 0, 0, 'Hold off the Zan\'thik while Rik\'kal the Dissector gathers his strength.', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65315, 0, 0, 'На старт! 3...', 42, 0, 100, 0, 0, 0, 66167, 0, 0, 0, 'Roll Club to Player');
INSERT INTO `creature_text` VALUES (65315, 3, 0, '1...', 42, 0, 100, 0, 0, 0, 66169, 2000, 2000, 0, 'Roll Club to Player');
INSERT INTO `creature_text` VALUES (65315, 3, 0, '2...', 42, 0, 100, 0, 0, 0, 66168, 1000, 1000, 0, 'Roll Club to Player');
INSERT INTO `creature_text` VALUES (65315, 3, 0, 'Поехали!', 42, 0, 100, 0, 0, 0, 74371, 4000, 4000, 0, 'Roll Club to Player');
INSERT INTO `creature_text` VALUES (65376, 3, 0, 'Конечно! Выбирать группу, например!', 12, 0, 100, 1, 0, 28050, 63211, 394109, 394109, 0, 'Хмелевар-предок to Хмелевар-предок');
INSERT INTO `creature_text` VALUES (65407, 3, 0, 'Приветствую вас, чужеземцы! Мастер Снежный Вихрь ждет вас в школе. Не забудьте оказать ему подобающее почтение.', 12, 0, 100, 2, 0, 0, 66449, 55536, 55536, 0, 'Новообращенный Шадо-Пан to Player');
INSERT INTO `creature_text` VALUES (65450, 1, 0, '%s пробуждается ото сна, услышав звуки битвы!', 16, 0, 100, 0, 0, 0, 66497, 0, 0, 0, 'Древний страж to Древний страж');
INSERT INTO `creature_text` VALUES (65470, 0, 0, 'I have never seen a trainee with skills such as yours. I must tell the others.', 12, 0, 100, 0, 0, 0, 56445, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65470, 0, 1, 'My skills are no match for yours. I admit defeat.', 12, 0, 100, 0, 0, 0, 56441, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65470, 0, 2, 'Thank you for reminding me that I must train more diligently.', 12, 0, 100, 0, 0, 0, 56439, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65470, 0, 3, 'That was a good match. Thank you.', 12, 0, 100, 0, 0, 0, 56444, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65470, 0, 4, 'You are an honorable opponent.', 12, 0, 100, 0, 0, 0, 56438, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65470, 0, 5, 'You fight honorably, friend.', 12, 0, 100, 0, 0, 0, 56440, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65470, 0, 6, 'You fought well. I must learn more from you in the future.', 12, 0, 100, 0, 0, 0, 56443, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65470, 0, 7, 'Your skills are too great. I yield.', 12, 0, 100, 0, 0, 0, 56442, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65471, 0, 0, 'Моему искусству не сравниться с твоим. Я признаю поражение.', 12, 0, 100, 1, 0, 33646, 56441, 0, 0, 0, 'Ученик Тушуй');
INSERT INTO `creature_text` VALUES (65471, 0, 1, 'Прекрасный бой. Мне можно будет многому у тебя научиться.', 12, 0, 100, 1, 0, 33645, 56443, 0, 0, 0, 'Ученик Тушуй');
INSERT INTO `creature_text` VALUES (65471, 0, 2, 'Мне никогда не приходилось видеть таких способных новичков. Я должна рассказать остальным.', 12, 0, 100, 1, 0, 33643, 56445, 0, 0, 0, 'Ученик Тушуй');
INSERT INTO `creature_text` VALUES (65471, 0, 3, 'Достойный поединок, друг.', 12, 0, 100, 1, 0, 33646, 56440, 0, 0, 0, 'Ученик Тушуй');
INSERT INTO `creature_text` VALUES (65501, 0, 0, 'Гаралон, преврати их кости в пыль!', 14, 0, 100, 0, 0, 30351, 66436, 0, 0, 0, 'Повелитель ветров Мел\'джарак to Гаралон');
INSERT INTO `creature_text` VALUES (65558, 0, 0, 'Давай возьмемся за это вместе!', 12, 0, 100, 5, 0, 27320, 60609, 0, 0, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` VALUES (65558, 1, 0, 'Мне нравится твой стиль боя!', 12, 0, 100, 5, 0, 27322, 60610, 0, 0, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` VALUES (65558, 1, 1, 'Это все, на что ты способен?!', 12, 0, 100, 5, 0, 27316, 60620, 0, 0, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` VALUES (65558, 1, 2, 'Эта обезьяна нарывается!', 12, 0, 100, 5, 0, 27317, 60621, 0, 0, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` VALUES (65558, 1, 3, 'Еще одной мартышкой меньше. Неплохо!', 12, 0, 100, 5, 0, 27315, 60622, 0, 0, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` VALUES (65558, 2, 0, 'Увидимся у причала!', 12, 0, 100, 0, 0, 27319, 60618, 0, 0, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` VALUES (65576, 3, 0, 'Боюсь, что нет, сэр.', 12, 0, 100, 0, 0, 0, 66730, 46511, 262049, 0, 'Филип Люк to Мерфи Злоторф');
INSERT INTO `creature_text` VALUES (65577, 3, 0, 'Лучше потанцуй со мной, милашка!', 12, 0, 100, 0, 0, 0, 66763, 5361000, 5361000, 0, 'Коллин Гилберт');
INSERT INTO `creature_text` VALUES (65578, 3, 0, 'Hey there beautiful, I\'d like to make a withdrawal.', 12, 0, 100, 0, 0, 0, 66789, 306406, 306406, 0, 'Vinnie Morrison');
INSERT INTO `creature_text` VALUES (65578, 3, 0, 'Hey, Schätzchen, ich würde gerne was abheben.', 12, 0, 100, 0, 0, 0, 66789, 733766, 733766, 0, 'Winnie Morrison');
INSERT INTO `creature_text` VALUES (65578, 3, 0, 'Thank you very, very much.', 12, 0, 100, 0, 0, 0, 66791, 312875, 312875, 0, 'Vinnie Morrison');
INSERT INTO `creature_text` VALUES (65578, 3, 0, 'Vielen, vielen Dank.', 12, 0, 100, 0, 0, 0, 66791, 740250, 740250, 0, 'Winnie Morrison');
INSERT INTO `creature_text` VALUES (65578, 3, 0, 'Привет, красавица! Хочу снять деньги со счета.', 12, 0, 100, 0, 0, 0, 66789, 146000, 146000, 0, 'Винни Моррисон');
INSERT INTO `creature_text` VALUES (65578, 3, 0, 'Спасибо огромное!', 12, 0, 100, 0, 0, 0, 68657, 152000, 152000, 0, 'Винни Моррисон');
INSERT INTO `creature_text` VALUES (65586, 0, 0, 'Мне много чего довелось услышать, пока сидел в клетке. Жэнь-саю понравится то, что я ему расскажу.', 12, 0, 100, 1, 0, 0, 66735, 0, 0, 0, 'Беспокойный Лэн to Player');
INSERT INTO `creature_text` VALUES (65586, 0, 0, 'Спасибо. Я твой должник.', 12, 0, 100, 0, 0, 0, 66734, 0, 0, 0, 'Беспокойный Лэн to Player');
