INSERT INTO `creature_text` VALUES (65635, 3, 0, 'Сапог... много не бывает!', 12, 0, 100, 1, 0, 0, 66872, 35000, 35000, 0, 'Темный дух - плут to Player');
INSERT INTO `creature_text` VALUES (65635, 4, 0, 'Мне просто хотелось... еще... парочку...', 12, 0, 100, 1, 0, 0, 66871, 9000, 9000, 0, 'Темный дух - плут to Player');
INSERT INTO `creature_text` VALUES (65638, 3, 0, 'Доставьте рабов на вершину Кунь-Лай! Открыть портал!', 12, 0, 100, 0, 0, 0, 67044, 169000, 169000, 0, 'Надсмотрщик из Корцзя');
INSERT INTO `creature_text` VALUES (65638, 3, 0, 'Закрой рот, раб! Мертвый ты будешь стоить столько же, сколько и живой.', 12, 0, 100, 0, 0, 0, 67047, 96000, 146000, 0, 'Надсмотрщик из Корцзя');
INSERT INTO `creature_text` VALUES (65638, 3, 0, 'Мы возродимся, и тогда вы будете служить нам!', 12, 0, 100, 5, 0, 0, 57190, 162000, 162000, 0, 'Надсмотрщик из Корцзя');
INSERT INTO `creature_text` VALUES (65638, 3, 0, 'Открывайте портал на вершину Кунь-Лай! Живее, идиоты!', 12, 0, 100, 5, 0, 0, 67046, 2044000, 2044000, 0, 'Надсмотрщик из Корцзя');
INSERT INTO `creature_text` VALUES (65638, 3, 0, 'Умри же, как и все остальные, раб!', 12, 0, 100, 0, 0, 0, 67043, 2065000, 2065000, 0, 'Надсмотрщик из Корцзя to Player');
INSERT INTO `creature_text` VALUES (65638, 4, 0, 'Твоему народу суждено подчиниться.', 12, 0, 100, 0, 0, 0, 57194, 6000, 6000, 0, 'Надсмотрщик из Корцзя to Player');
INSERT INTO `creature_text` VALUES (65647, 0, 0, 'Did the Order of the Cloud Serpent send you? Bless them. Please help the others!', 12, 0, 100, 1, 0, 0, 66832, 0, 0, 0, 'ResqueText');
INSERT INTO `creature_text` VALUES (65648, 0, 0, 'Когда-то у меня была своя ферма, но с тех пор уже столько лет прошло. Мне нравятся заварушки, а тебе?', 12, 0, 100, 0, 0, 0, 66845, 0, 0, 0, 'Старик Макдональд to Player');
INSERT INTO `creature_text` VALUES (65651, 3, 0, 'Мне удалось поймать только пару кроликов. Зато теперь у меня их много!', 12, 0, 100, 0, 0, 0, 66836, 91884, 91884, 0, 'Линдси to Player');
INSERT INTO `creature_text` VALUES (65714, 0, 0, 'Идем, я покажу.', 12, 0, 100, 66, 0, 0, 61979, 0, 0, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (65746, 3, 0, 'Осторожнее, Цзюнь-Цзюнь! Тебя может унести в море мощным порывом ветра!', 12, 0, 100, 1, 0, 0, 67135, 2779000, 2779000, 0, 'Тэнфэй Прибрежная Вода');
INSERT INTO `creature_text` VALUES (65808, 3, 0, 'Каждая травма – это своеобразный жизненный урок. Каждое поражение – очередная ступень к совершенству.', 42, 0, 100, 0, 0, 0, 66941, 14000, 14000, 0, 'Святилище тела Искателя to Player');
INSERT INTO `creature_text` VALUES (65811, 3, 0, 'Когда ты обретешь внутренний покой, то сможешь ясно видеть все препятствия в своем сознании.', 42, 0, 100, 0, 0, 0, 66946, 10000, 10000, 0, 'Святилище дыхания Искателя to Player');
INSERT INTO `creature_text` VALUES (65812, 3, 0, 'Мудрость – это дар, который дается только тем, кто готов его принять.', 42, 0, 100, 0, 0, 0, 66948, 20000, 20000, 0, 'Святилище сердца Искателя to Player');
INSERT INTO `creature_text` VALUES (65868, 0, 0, 'I was relieving myself into one of the golden pools when they caught me. I guess you could call it a karma capture.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65868, 0, 1, 'I\'m getting rusty... and my lockpicks are too apparently. Do me a favor, don\'t let Ren know you found me here, kay?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65868, 0, 2, 'I\'ve made a mental note, mogu are light sleepers. Pass the word.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65868, 0, 3, 'It feels so good to stretch my limbs! It was cramped in there. They really should invest in more roomy cages!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65868, 0, 4, 'Ren bet me that I couldn\'t steal one of the mogu cannons. I didn\'t think they\'d be so heavy!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65868, 0, 5, 'Thanks!', 12, 0, 100, 0, 0, 0, 112662, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65868, 0, 6, 'That\'s the last time I\'ll let them catch me, promise.', 12, 0, 100, 0, 0, 0, 67058, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65868, 0, 7, 'What took you so long? I almost thought they weren\'t going to send someone for me this time... my heart frowned a little.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65868, 0, 8, 'You know, I\'ve been stuck in these cages watching the mogu a lot, but I\'m pretty sure I\'ve never seen a girl mogu. What\'s with that?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (65870, 3, 0, 'Догоняй, черепаха!', 12, 0, 100, 0, 0, 0, 67040, 8110000, 8110000, 0, 'Мэй Черная Бровь');
INSERT INTO `creature_text` VALUES (65871, 3, 0, 'Сейчас я тебя поймаю!', 12, 0, 100, 0, 0, 0, 67035, 8117000, 8117000, 0, 'Ло Черная Бровь');
INSERT INTO `creature_text` VALUES (65896, 3, 0, 'Бред какой-то! Не стоило тебе покидать деревню!', 12, 0, 100, 0, 0, 0, 67131, 96000, 1857000, 0, 'Жительница деревни Цзоучин');
INSERT INTO `creature_text` VALUES (65896, 3, 0, 'Делай, что хочешь. Придумываешь себе вопросы, на которые нет ответа!', 12, 0, 100, 0, 0, 0, 67133, 105000, 1866000, 0, 'Жительница деревни Цзоучин');
INSERT INTO `creature_text` VALUES (65896, 3, 0, 'Что за буквы? На крышке? Наверное, это чьи-то инициалы. Какая-нибудь ерунда.', 12, 0, 100, 0, 0, 0, 67128, 79000, 1840000, 0, 'Жительница деревни Цзоучин');
INSERT INTO `creature_text` VALUES (65896, 3, 0, 'Что за чушь? Обычная урна с жуками. По мне, так она вообще отвратительная.', 12, 0, 100, 0, 0, 0, 67130, 88000, 1849000, 0, 'Жительница деревни Цзоучин');
INSERT INTO `creature_text` VALUES (65897, 3, 0, 'I found this urn with bugs in a shack in the middle of Kun-Lai. At first I decided that it was rubbish, until I noticed some letters on it ...', 12, 0, 100, 6, 0, 0, 67125, 76000, 1836000, 0, 'Житель деревни Цзоучин');
INSERT INTO `creature_text` VALUES (65897, 3, 0, 'Должно быть, это послание! Может быть, ключ к великой тайне!', 12, 0, 100, 1, 0, 0, 67126, 84000, 1845000, 0, 'Житель деревни Цзоучин');
INSERT INTO `creature_text` VALUES (65897, 3, 0, 'Попробую-ка я разгадать эту загадку. Посмотрим, куда меня приведет эта надпись.', 12, 0, 100, 273, 0, 0, 67134, 101000, 1862000, 0, 'Житель деревни Цзоучин');
INSERT INTO `creature_text` VALUES (65897, 3, 0, 'Ты не понимаешь! Это же может быть сигналом конца света!', 12, 0, 100, 6, 0, 0, 67127, 92000, 1853000, 0, 'Житель деревни Цзоучин');
INSERT INTO `creature_text` VALUES (65910, 0, 0, 'Минуточку, Тэн. У нас... $gгость:гостья;.', 12, 0, 100, 1, 0, 0, 67703, 0, 0, 0, 'Сунк Хан to Player');
INSERT INTO `creature_text` VALUES (65911, 3, 0, 'Держись, приятель! Все будет хорошо.', 12, 0, 100, 0, 0, 0, 67775, 27220000, 27820000, 0, 'Лейтенант Даниэль');
INSERT INTO `creature_text` VALUES (65911, 3, 0, 'Мишка о тебе позаботится, слышишь?', 12, 0, 100, 0, 0, 0, 67779, 27525000, 27525000, 0, 'Лейтенант Даниэль');
INSERT INTO `creature_text` VALUES (65911, 3, 0, 'Пульс ровный, Мишка, но очень слабый.', 12, 0, 100, 0, 0, 0, 67776, 27962000, 28312000, 0, 'Лейтенант Даниэль');
INSERT INTO `creature_text` VALUES (65911, 3, 0, 'Следующий десяток орков я убью в твою честь.', 12, 0, 100, 0, 0, 0, 67780, 27412000, 27412000, 0, 'Лейтенант Даниэль');
INSERT INTO `creature_text` VALUES (65928, 1, 0, 'You dare challenge the yaungol?', 12, 0, 100, 0, 0, 0, 64550, 0, 0, 0, 'Ordo Raider to Player');
INSERT INTO `creature_text` VALUES (65928, 3, 0, 'Кто смеет бросать вызов яунголам?', 12, 0, 100, 0, 0, 0, 64550, 19000, 19000, 0, 'Рейдер Ордо to Player');
INSERT INTO `creature_text` VALUES (65928, 3, 0, 'Сдавайся!', 12, 0, 100, 0, 0, 0, 94374, 11000, 11000, 0, 'Рейдер Ордо to Player');
INSERT INTO `creature_text` VALUES (65928, 3, 0, 'Теперь это наши поля.', 12, 0, 100, 0, 0, 0, 64552, 32000, 32000, 0, 'Рейдер Ордо to Player');
INSERT INTO `creature_text` VALUES (65928, 4, 0, 'Еще одним рабом больше!', 12, 0, 100, 0, 0, 0, 64549, 32000, 32000, 0, 'Рейдер Ордо to Player');
INSERT INTO `creature_text` VALUES (65974, 0, 0, 'Can\'t wait to get back home.', 12, 0, 0, 1, 0, 0, 68660, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (65974, 0, 1, 'I suppose it is time I headed home.', 12, 0, 0, 1, 0, 0, 68653, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (65974, 0, 2, 'Thanks! I\'m outta here!', 12, 0, 0, 1, 0, 0, 68658, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (65974, 0, 3, 'Many thanks.', 12, 0, 0, 1, 0, 0, 68661, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (65974, 0, 4, 'Thank goodness!', 12, 0, 0, 1, 0, 0, 68657, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (65974, 0, 5, 'Thank you, $r', 12, 0, 0, 1, 0, 0, 0, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (65999, 0, 0, 'Their leader is still hiding. Find him!', 12, 0, 100, 0, 0, 0, 67750, 0, 0, 0, 'Генерал Назгрим to player');
INSERT INTO `creature_text` VALUES (65999, 0, 0, 'Их предводитель все еще прячется. Разыщите его!', 12, 0, 100, 0, 0, 0, 67750, 0, 0, 0, 'Генерал Назгрим to player');
INSERT INTO `creature_text` VALUES (66052, 0, 0, 'Don\'t you beasts know when to give up?', 12, 0, 100, 0, 0, 0, 67251, 0, 0, 0, 'Captain Doren to player');
INSERT INTO `creature_text` VALUES (66185, 0, 0, 'Отец!', 12, 0, 100, 0, 0, 0, 124735, 0, 0, 0, 'Лилиан Восс to 0');
INSERT INTO `creature_text` VALUES (66185, 1, 0, 'Заткнись!', 12, 0, 100, 0, 0, 0, 74918, 0, 0, 0, 'Лилиан Восс to 0');
INSERT INTO `creature_text` VALUES (66185, 2, 0, 'Ты воспитывал меня как убийцу. Ну, как я справляюсь, папочка?', 12, 0, 100, 0, 0, 0, 39141, 0, 0, 0, 'Лилиан Восс to 0');
INSERT INTO `creature_text` VALUES (66185, 3, 0, 'Погоди... кажется припоминаю. Ты учил меня убивать только нежить. Так что мне теперь делать – совершить самоубийство?', 12, 0, 100, 0, 0, 0, 39143, 0, 0, 0, 'Лилиан Восс to 0');
INSERT INTO `creature_text` VALUES (66185, 4, 0, 'И правда, зачем убивать себя, если я могу расправиться с тобой!', 12, 0, 100, 0, 0, 0, 39145, 0, 0, 0, 'Лилиан Восс to 0');
INSERT INTO `creature_text` VALUES (66209, 0, 0, 'Pandaria has not seen a full-scale war since the mists closed in. The Shado-Pan usually handle any trouble that crops up on the continent.', 12, 0, 100, 0, 0, 0, 67352, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (66209, 0, 1, 'Yes, that\'s the stuff! Our cider is prized throughout the Jade Forest. I hope I can carry them all.', 12, 0, 100, 0, 0, 0, 67351, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (66218, 3, 0, 'Нужно отнести хмель в безопасное место. Мое варево поддерживает дух всех горожан.', 12, 0, 100, 0, 0, 0, 67419, 14000, 14000, 0, 'Ло Донь to Player');
INSERT INTO `creature_text` VALUES (66220, 0, 0, 'Should we allow them to stay here? They have wounded to tend to.', 12, 0, 100, 0, 0, 0, 68135, 0, 0, 0, 'Мэр Медовая Роса to Player');
INSERT INTO `creature_text` VALUES (66223, 0, 0, 'They will bring more destruction to our village!', 12, 0, 100, 0, 0, 0, 68137, 0, 0, 0, 'Старейшина Медовая Лапа  to player');
INSERT INTO `creature_text` VALUES (66223, 1, 0, 'Well what about the bombs that landed in our farms from your warship!', 12, 0, 100, 0, 0, 0, 68136, 0, 0, 0, 'Старейшина Медовая Лапа  to player');
INSERT INTO `creature_text` VALUES (66223, 2, 0, 'A war we did not have until you arrived!', 12, 0, 100, 0, 0, 0, 68138, 0, 0, 0, 'Старейшина Медовая Лапа  to player');
INSERT INTO `creature_text` VALUES (66260, 3, 0, 'Мой помощник представит тебя мастеру Чэн. Удачи!', 12, 0, 100, 0, 0, 0, 68001, 0, 0, 0, 'Мастер Хайт to Player');
INSERT INTO `creature_text` VALUES (66261, 3, 0, 'Окружайте их! Не дайте им уйти!', 14, 0, 100, 5, 0, 0, 67395, 203000, 1209000, 0, 'Зандалар-защитник');
INSERT INTO `creature_text` VALUES (66261, 3, 0, 'Покажите им всю мощь троллей!', 14, 0, 100, 5, 0, 0, 67397, 3468, 3468, 0, 'Зандалар-защитник');
INSERT INTO `creature_text` VALUES (66261, 3, 0, 'Прикончите их побыстрее! У нас много дел.', 14, 0, 100, 0, 0, 0, 67398, 150000, 150000, 0, 'Зандалар-защитник');
INSERT INTO `creature_text` VALUES (66261, 3, 0, 'Разорвите их на куски!', 14, 0, 100, 0, 0, 0, 67396, 54436, 54436, 0, 'Зандалар-защитник');
INSERT INTO `creature_text` VALUES (66267, 1, 0, '$gНичтожный:Ничтожная:r; |3-6($r)...', 12, 0, 100, 0, 0, 0, 67567, 0, 0, 0, 'Гатрул\'лон - насылатель проклятий to Player');
INSERT INTO `creature_text` VALUES (66269, 1, 0, 'Ты труп, |3-6($r)!', 12, 0, 100, 0, 0, 0, 73020, 0, 0, 0, 'Рубака Двух Башен to Player');
INSERT INTO `creature_text` VALUES (66273, 1, 0, 'За Орду!', 12, 0, 100, 0, 0, 0, 143151, 0, 0, 0, 'Подрывник Двух Башен to Player');
INSERT INTO `creature_text` VALUES (66273, 1, 0, 'Пандария будет нашей!', 12, 0, 100, 0, 0, 0, 69203, 0, 0, 0, 'Подрывник Двух Башен to Player');
INSERT INTO `creature_text` VALUES (66274, 3, 0, 'Армия Адского Крика зальет тут все кровью!', 14, 0, 100, 0, 0, 0, 67574, 209000, 209000, 0, 'Далган to Player');
INSERT INTO `creature_text` VALUES (66275, 1, 0, 'I\'m not afraid of you, |3-6($r). But you should be afraid of me ...', 14, 0, 100, 0, 0, 0, 67576, 0, 0, 0, 'Белландра Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (66276, 0, 0, 'Alliance \"hero.\" Let\'s see how you fight without a flying warship to cover your back!', 12, 0, 100, 0, 0, 0, 67494, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (66276, 0, 1, 'They defied us - they defied Hellscream\'s Horde - I should\'ve executed the whole town!', 12, 0, 100, 0, 0, 0, 67496, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (66276, 0, 2, 'This land will be your grave.', 12, 0, 100, 0, 0, 0, 67498, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (66276, 1, 0, 'There\'s something about this land - you don\'t understand...', 14, 0, 100, 0, 0, 0, 67495, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (66276, 1, 1, 'Burn, Alliance scum! BURN!', 14, 0, 100, 0, 0, 0, 67497, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (66282, 0, 0, 'Lokand gez golgon ', 12, 1, 100, 0, 0, 0, 0, 0, 0, 0, 'Рубака лагеря Гаррош\'ар to Player');
INSERT INTO `creature_text` VALUES (66292, 3, 0, 'Спасибо за гостеприимство. Уверяю вас, вы все делаете правильно.', 12, 0, 100, 2, 0, 32549, 67711, 7000, 7000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (66296, 3, 0, 'Если вы пришли сюда с войной, то сама земля ответит на вашу жестокость и ваши страсти. И я не могу вам сказать, какими будут последствия. Никто этого не знает.', 12, 0, 100, 25, 0, 32577, 68790, 8000, 8000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (66300, 3, 0, 'Задай им трепку, $n! Кем бы они ни были...', 12, 0, 100, 0, 0, 0, 67651, 57000, 57000, 116219, 'Пехотинец \"Небесного огня\" to Player');
INSERT INTO `creature_text` VALUES (66321, 3, 0, 'УЗРИТЕ МОЮ СИЛУ!', 14, 0, 100, 0, 0, 32684, 110374, 0, 0, 0, 'Ксу\'даггаб to Player');
INSERT INTO `creature_text` VALUES (66349, 0, 0, '%s полностью исчезает в пламени, а в его взгляде появляется безумие!', 16, 0, 100, 0, 0, 0, 10425, 0, 0, 0, 'Летучий всадник Гурубаши');
INSERT INTO `creature_text` VALUES (66352, 3, 0, 'Глупая змея! Стреляй, говорю!', 12, 0, 100, 0, 0, 0, 67515, 325000, 325000, 0, 'Предатель Глак');
INSERT INTO `creature_text` VALUES (66366, 0, 0, 'I know the way home. Thanks, hero!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (66366, 0, 1, 'I... I thought I\'d be here forever...', 12, 0, 100, 0, 0, 0, 67545, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (66369, 3, 0, 'Адмирал! Приятно видеть, что с вами все в порядке.', 12, 0, 100, 3, 0, 32554, 67503, 4000, 4000, 0, 'Небесный адмирал Роджерс to Личный гирокоптер адмирала Роджерс');
INSERT INTO `creature_text` VALUES (66369, 3, 0, 'Боюсь, вам придется пробиваться туда без нас.', 12, 0, 100, 1, 0, 32555, 67504, 12000, 12000, 0, 'Небесный адмирал Роджерс to Личный гирокоптер адмирала Роджерс');
INSERT INTO `creature_text` VALUES (66369, 3, 0, 'Может быть, вам удастся завербовать кого-то из местных. Уверена, вы справитесь.', 12, 0, 100, 1, 0, 32558, 67507, 41000, 41000, 46598, 'Небесный адмирал Роджерс to Личный гирокоптер адмирала Роджерс');
INSERT INTO `creature_text` VALUES (66369, 3, 0, 'Только без паники, Тейлор. Я оставляю с вами агентов ШРУ. Проведите разведывательную операцию.', 12, 0, 100, 11, 0, 32557, 67506, 31000, 31000, 0, 'Небесный адмирал Роджерс to Личный гирокоптер адмирала Роджерс');
INSERT INTO `creature_text` VALUES (66369, 3, 0, 'У меня приказ обеспечить основной группе поддержку с воздуха. Через два месяца все силы флота Альянса будут у южных берегов.', 12, 0, 100, 1, 0, 32556, 67505, 16000, 16000, 0, 'Небесный адмирал Роджерс to Личный гирокоптер адмирала Роджерс');
INSERT INTO `creature_text` VALUES (66372, 3, 0, 'Эта земля многое пережила, но ей удалось оправиться от разрушений, который принес разрушитель миров. Сейчас ты увидишь, что мои спутники такие же стойкие, как и сама земля.', 12, 0, 100, 0, 0, 0, 67516, 9797, 9797, 0, 'Мерда Могучее Копыто to Player');
