INSERT INTO `creature_text` VALUES (66374, 0, 5, 'РђР№-СЏ-СЏР№! РћРіСЂРѕРјРЅС‹Р№ Р·Р»РѕР№ РјРѕРіСѓ! РЈ РјРµРЅСЏ СѓР¶Рµ РґСЂРѕР¶Р°С‚ РєРѕР»РµРЅРєРё!', 12, 0, 100, 0, 0, 0, 67520, 0, 0, 0, 'РђРЅР°С‚РѕРјРёС‡РµСЃРєРё С‚РѕС‡РЅС‹Р№ РјР°РЅРµРєРµРЅ to Player');
INSERT INTO `creature_text` VALUES (66374, 0, 6, 'Р РІРѕС‚ СЌС‚Рѕ РґРѕР»Р¶РЅРѕ Р±С‹Р»Рѕ СЃРґРµР»Р°С‚СЊ РјРЅРµ Р±РѕР»СЊРЅРѕ?', 12, 0, 100, 0, 0, 0, 67533, 0, 0, 0, 'РђРЅР°С‚РѕРјРёС‡РµСЃРєРё С‚РѕС‡РЅС‹Р№ РјР°РЅРµРєРµРЅ to Player');
INSERT INTO `creature_text` VALUES (66374, 0, 7, 'РҐРѕС‡РµС€СЊ РєСѓСЃРѕРє РѕС‚ РјРµРЅСЏ?', 12, 0, 100, 0, 0, 0, 67522, 0, 0, 0, 'РђРЅР°С‚РѕРјРёС‡РµСЃРєРё С‚РѕС‡РЅС‹Р№ РјР°РЅРµРєРµРЅ to Player');
INSERT INTO `creature_text` VALUES (66379, 3, 0, 'И что мне до тех пор делать? У меня нет людей! Мы не знаем, где сейчас силы Орды и сколько их!', 12, 0, 100, 5, 0, 32438, 67510, 24000, 24000, 0, 'Адмирал Тейлор to Небесный адмирал Роджерс');
INSERT INTO `creature_text` VALUES (66379, 3, 0, 'Спасибо, Кэтрин. Хорошо, что ты здесь. С поддержкой авианосца мы сможем пробиться к северо...', 12, 0, 100, 1, 0, 32436, 67508, 7000, 7000, 0, 'Адмирал Тейлор to Небесный адмирал Роджерс');
INSERT INTO `creature_text` VALUES (66379, 3, 0, 'Что?', 12, 0, 100, 6, 0, 32437, 127498, 15000, 15000, 0, 'Адмирал Тейлор to Небесный адмирал Роджерс');
INSERT INTO `creature_text` VALUES (66406, 3, 0, 'Клянусь вашей кровью, эта земля будет нашей!', 12, 0, 100, 0, 0, 32473, 67592, 1000, 1000, 130624, 'Га\'трул to Player');
INSERT INTO `creature_text` VALUES (66407, 3, 0, 'Чернокнижник! Он не мог далеко уйти – где-то тут должна быть еще одна база.', 12, 0, 100, 1, 0, 32666, 67593, 6000, 6000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (66422, 0, 0, 'Hey there $Ghot stuff:toots;, I\'m all revved up and ready to battle!', 12, 0, 100, 0, 0, 0, 68513, 0, 0, 0, 'Cassandra Kaboom to Player');
INSERT INTO `creature_text` VALUES (66466, 3, 0, 'Как дела, золотце?', 12, 0, 100, 0, 0, 0, 67641, 4306, 305716, 0, 'Ледяная Трикси to Player');
INSERT INTO `creature_text` VALUES (66501, 4, 0, 'Молодец, $n! Отправляйся в деревню за мостом. Я прикрою отход пехотинцев.', 12, 0, 100, 0, 0, 0, 67661, 3000, 3000, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (66581, 3, 0, 'I am not surprised. The horde is just monsters!', 12, 0, 100, 1, 0, 32677, 67707, 20000, 20000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (66581, 3, 0, 'Если оставить все как есть, Орда захватит эту землю. Вы не поможете нам сражаться с ними?', 12, 0, 100, 1, 0, 32678, 67709, 35000, 35000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (66581, 3, 0, 'Мы здесь с мирными целями. Вы поможете нам?', 12, 0, 100, 6, 0, 0, 68485, 24000, 24000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (66582, 3, 0, 'Ваша вражда будет иметь колоссальные последствия в этих землях. Не смейте осквернять их войной!', 12, 0, 100, 5, 0, 0, 67820, 54000, 54000, 0, 'Тажань Чжу to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (66582, 3, 0, 'Но в этой кровавой бойне мы участвовать не будем.', 12, 0, 100, 274, 0, 0, 67819, 49000, 49000, 0, 'Тажань Чжу to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (66582, 3, 0, 'Но вот уже много лет я не видел, чтобы ша были такими сильными.', 12, 0, 100, 25, 0, 0, 68486, 17000, 17000, 0, 'Тажань Чжу to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (66582, 3, 0, 'Сражений мы не боимся. Хоть наших воинов и немного, они очень сильны и опытны.', 12, 0, 100, 1, 0, 32570, 67757, 41000, 41000, 0, 'Тажань Чжу to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (66582, 3, 0, 'Твоего врага подчинила себе темная энергия, которую мы называем \"ша\". Ша черпают силы в отрицательных эмоциях, например, сомнениях.', 12, 0, 100, 1, 0, 32711, 67706, 8000, 8000, 0, 'Тажань Чжу to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (66582, 3, 0, 'Я видел вашу небесную крепость. У вашего народа очень странные представления о \"мире\".', 12, 0, 100, 1, 0, 32569, 67708, 28000, 28000, 0, 'Тажань Чжу to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (66583, 3, 0, 'Ваши солдаты могут расположиться здесь. Но мы не переходим ни на чью сторону.', 12, 0, 100, 25, 0, 32565, 67712, 6000, 6000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (66583, 3, 0, 'Их война – это не просто расовая междоусобица. Их ненависть затопит нашу землю. Мы не будем принимать в этом участия.', 12, 0, 100, 1, 0, 32566, 67713, 12000, 12000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (66583, 3, 0, 'Мне пора. Еще кто-то высадился на наших северных берегах.', 12, 0, 100, 1, 0, 32567, 67714, 23000, 23000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (66633, 4, 0, '|TInterface\\Icons\\ability_druid_tigersroar.blp:20|t %s прячется и готовиться к |cFFFF0000|Hspell:131271|h[прыжку из теней]|h|r!', 41, 0, 100, 0, 0, 0, 67913, 4000, 4000, 0, 'Руукти');
INSERT INTO `creature_text` VALUES (66643, 3, 0, 'Are you afraid to fight?', 12, 0, 100, 6, 0, 0, 67756, 45000, 45000, 0, 'General Nazgrim to player');
INSERT INTO `creature_text` VALUES (66643, 3, 0, 'But our orders stand - Hellscream will have his foothold here. We will NOT allow the Alliance to lay claim to this new world!', 12, 0, 100, 5, 0, 0, 67759, 74000, 74000, 0, 'General Nazgrim to player');
INSERT INTO `creature_text` VALUES (66643, 3, 0, 'Help us rout them from your land, and your kind can stand tall and proud with the Horde!', 12, 0, 100, 1, 0, 0, 68334, 31000, 31000, 0, 'General Nazgrim to player');
INSERT INTO `creature_text` VALUES (66643, 3, 0, 'This is a hostile land, and we have no reinforcements. We\'re going to have to improvise, champion.', 12, 0, 100, 1, 0, 0, 67758, 67000, 67000, 0, 'General Nazgrim to player');
INSERT INTO `creature_text` VALUES (66643, 3, 0, 'What is this... shadow? What is the Alliance playing at?', 12, 0, 100, 6, 0, 0, 67752, 6000, 6000, 0, 'General Nazgrim to player');
INSERT INTO `creature_text` VALUES (66643, 3, 0, 'You again. Do you see it now? Alliance arrogance!', 12, 0, 100, 1, 0, 0, 67754, 25000, 25000, 0, 'General Nazgrim to player');
INSERT INTO `creature_text` VALUES (66643, 3, 0, 'You finished him - well done!', 12, 0, 100, 5, 0, 0, 67751, 3000, 3000, 0, 'General Nazgrim to player');
INSERT INTO `creature_text` VALUES (66644, 3, 0, 'But we will not participate in this... race war... bloodbath of yours.', 12, 0, 100, 0, 0, 0, 67819, 57000, 57000, 0, 'Тажань Чжу to 0');
INSERT INTO `creature_text` VALUES (66644, 3, 0, 'It has been many years since I have seen it manifest so dramatically.', 12, 0, 100, 1, 0, 0, 68486, 18000, 18000, 0, 'Тажань Чжу to 0');
INSERT INTO `creature_text` VALUES (66644, 3, 0, 'My people have stood tall without you for thousands of years.', 12, 0, 100, 25, 0, 0, 67755, 38000, 38000, 0, 'Тажань Чжу to 0');
INSERT INTO `creature_text` VALUES (66644, 3, 0, 'We are not afraid to fight. Our warriors, while few in number, are extremely skilled.', 12, 0, 100, 1, 0, 0, 67757, 49000, 49000, 0, 'Тажань Чжу to 0');
INSERT INTO `creature_text` VALUES (66644, 3, 0, 'Your conflict will have immense consequences here. Do NOT bring your war to these shores!', 12, 0, 100, 0, 0, 0, 67820, 61000, 61000, 0, 'Тажань Чжу to 0');
INSERT INTO `creature_text` VALUES (66644, 3, 0, 'Your foe was overwhelmed by a dark energy we call the\"Sha.\" It thrives on negative emotions, such as doubt.', 12, 0, 100, 6, 0, 0, 67753, 10000, 10000, 0, 'Тажань Чжу to 0');
INSERT INTO `creature_text` VALUES (66655, 1, 0, 'Тут жить гну-сини!', 12, 0, 100, 0, 0, 0, 54876, 0, 0, 0, 'Кривоскок-соня to Player');
INSERT INTO `creature_text` VALUES (66666, 3, 0, 'К нам в деревню пожаловал еще один гость... Судя по виду, это |3-6($c). Проходи, но только не направляй на нас свое оружие, как это сделала Орда.', 12, 0, 100, 0, 0, 0, 68561, 9000, 9000, 127973, 'Защитник Лап\'дон to Player');
INSERT INTO `creature_text` VALUES (66693, 3, 0, 'Внимательно следите за наемниками, братья!', 14, 0, 100, 5, 0, 0, 67814, 1703000, 1703000, 0, 'Зандаларский предводитель');
INSERT INTO `creature_text` VALUES (66693, 3, 0, 'Держите свои блохастые лапы подальше от еды, никчемные животные!', 14, 0, 100, 5, 0, 0, 67813, 20000, 17662000, 0, 'Зандаларский предводитель');
INSERT INTO `creature_text` VALUES (66693, 3, 0, 'Работайте поживее, ленивые свиньи, или я вас сотру в порошок!', 14, 0, 100, 5, 0, 0, 67810, 3382000, 3382000, 0, 'Зандаларский предводитель');
INSERT INTO `creature_text` VALUES (66693, 3, 0, 'Сторожите припасы во все глаза, недоноски!', 14, 0, 100, 5, 0, 0, 67812, 143000, 143000, 0, 'Зандаларский предводитель');
INSERT INTO `creature_text` VALUES (66693, 3, 0, 'Только притроньтесь к припасам, и я вам головы оторву!', 14, 0, 100, 5, 0, 0, 67811, 34000, 103000, 0, 'Зандаларский предводитель');
INSERT INTO `creature_text` VALUES (66700, 3, 0, 'I feel the presence of spirits of the long dead can!', 14, 0, 100, 5, 0, 0, 67822, 1594000, 1594000, 0, 'Зандаларский прорицатель');
INSERT INTO `creature_text` VALUES (66700, 3, 0, 'В этом месте покоится скрытая сила. Мы должны найти ее!', 14, 0, 100, 5, 0, 0, 67821, 3878000, 3878000, 0, 'Зандаларский прорицатель');
INSERT INTO `creature_text` VALUES (66700, 3, 0, 'Защищайте руины любой ценой!', 14, 0, 100, 5, 0, 0, 67823, 5402000, 11382000, 0, 'Зандаларский прорицатель');
INSERT INTO `creature_text` VALUES (66700, 3, 0, 'Не подведите меня! Вскоре мы воссоединимся с древними союзниками-могу!', 14, 0, 100, 5, 0, 0, 67825, 18000, 18000, 0, 'Зандаларский прорицатель');
INSERT INTO `creature_text` VALUES (66700, 3, 0, 'Переверните каждый камень!', 14, 0, 100, 5, 0, 0, 67824, 7000, 7000, 0, 'Зандаларский прорицатель');
INSERT INTO `creature_text` VALUES (66731, 0, 0, 'Вот это да.', 12, 0, 100, 0, 0, 0, 76670, 0, 0, 0, 'Мистер Кризи');
INSERT INTO `creature_text` VALUES (66731, 3, 0, 'Вы попали прямо в расставленный мной капкан.', 12, 0, 100, 0, 0, 0, 76674, 49000, 49000, 0, 'Мистер Кризи');
INSERT INTO `creature_text` VALUES (66791, 1, 0, 'Treasonous Klaxxi... her magnificence was right about you.', 12, 0, 100, 0, 0, 0, 67838, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (66791, 2, 0, 'Grand Vizier Zor\'lok, voice of the Empress.', 12, 0, 100, 0, 0, 0, 67840, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (66791, 3, 0, 'The Klaxxi are to be disbanded, by the will of the Empress.', 12, 0, 100, 0, 0, 0, 67842, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (66791, 4, 0, 'The desperate Klaxxi have resorted to digging up fossils. Very well: let us finish this.', 12, 0, 100, 0, 0, 0, 67844, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (66791, 5, 0, '\"Unscathed\" indeed.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (66832, 3, 0, 'Добро пожаловать в Плодородные земли!', 12, 0, 100, 3, 0, 0, 67925, 67000, 67000, 0, 'Джина Грязный Коготь to Player');
INSERT INTO `creature_text` VALUES (66832, 3, 0, 'Если хочешь научиться лучше готовить, поговори с кем-нибудь из семейства Железная Лапа.', 12, 0, 100, 0, 0, 0, 68218, 31000, 31000, 0, 'Джина Грязный Коготь to Player');
INSERT INTO `creature_text` VALUES (66832, 3, 0, 'Знаешь, я обязана тебе своей шкурой за этот лук-шалот.', 12, 0, 100, 0, 0, 0, 67918, 12000, 12000, 0, 'Джина Грязный Коготь to Player');
INSERT INTO `creature_text` VALUES (66832, 3, 0, 'Обустраивайся и приходи на рынок в любое время, если тебе понадобится работа.', 12, 0, 100, 1, 0, 0, 67923, 62000, 62000, 0, 'Джина Грязный Коготь to Player');
INSERT INTO `creature_text` VALUES (66832, 3, 0, 'Пойдем со мной, $n. Я хочу нанести визит крестьянину Юну.', 12, 0, 100, 0, 0, 0, 0, 1000, 1000, 0, 'Джина Грязный Коготь to Player');
INSERT INTO `creature_text` VALUES (66832, 3, 0, 'Послушайте, раз уж вы так замечательно помогли мне, я отдам свой голос за то, чтобы вас приняли в земледельцы.', 12, 0, 100, 2, 0, 0, 67921, 46000, 46000, 0, 'Джина Грязный Коготь to Player');
INSERT INTO `creature_text` VALUES (66832, 3, 0, 'Тебе нужно получить еще четыре голоса. Но это вопрос времени.', 12, 0, 100, 0, 0, 0, 67922, 57000, 57000, 0, 'Джина Грязный Коготь to Player');
INSERT INTO `creature_text` VALUES (66832, 3, 0, 'Эй, фермер Юн!', 12, 0, 100, 3, 0, 0, 67920, 41000, 41000, 0, 'Джина Грязный Коготь to Player');
INSERT INTO `creature_text` VALUES (66845, 0, 0, 'How can you remain neutral after watching them blow us out of the sky?!', 12, 0, 100, 0, 0, 0, 68139, 0, 0, 0, ' Сержант Горрок to player');
INSERT INTO `creature_text` VALUES (66845, 1, 0, 'Those are the casualties of war!', 12, 0, 100, 0, 0, 0, 68140, 0, 0, 0, ' Сержант Горрок to player');
INSERT INTO `creature_text` VALUES (66905, 3, 0, 'Сюда, быстрее! Пушка пробила путь во двор!', 12, 0, 100, 1, 0, 29068, 68130, 44985, 44985, 0, 'Пандарен-артиллерист');
INSERT INTO `creature_text` VALUES (66949, 3, 0, 'Дипломатия уже сыграла свою роль. Теперь мы сможем доказать свои намерения только делами.', 12, 0, 100, 0, 0, 0, 61160, 67000, 67000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (66978, 3, 0, 'На этом корабле преследовали и сбили Смертокрыла. Думаете, у аванпоста Орды есть хоть какие-то шансы выстоять перед гордостью флота Альянса? Никаких!', 12, 0, 100, 0, 0, 32551, 68304, 11000, 11000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (66978, 3, 0, 'Ребята, вас готовили к этому. Вы – элита! Вы – команда \"Небесного огня\"!', 12, 0, 100, 0, 0, 32550, 68303, 3000, 3000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (67012, 3, 0, 'Лучше не говорить об этом здесь. Если не вдаваться в подробности, это воплощение твоих собственных сомнений.', 12, 0, 100, 1, 0, 32563, 67742, 9000, 9000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (67012, 3, 0, 'О, я все отлично понимаю. У меня же есть глаза. Но Пандария не похожа на те земли, откуда вы пришли. Она живая. Она дышит. Вы должны думать о том, какую энергию приносите сюда. А теперь уберите оружие!', 12, 0, 100, 1, 0, 32564, 67744, 26000, 26000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (67048, 0, 0, 'Адмирал, по многим признакам, этот берег обитаем. Богато украшенные строения...', 12, 0, 100, 0, 0, 32528, 68006, 0, 0, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (67048, 0, 0, 'Вы слышали приказ. Сосредоточить огонь на базе Орды. Начинайте заход для атаки.', 12, 0, 100, 0, 0, 32529, 68008, 0, 0, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (67048, 0, 0, 'Выпустить торпеды по кораблям.', 12, 0, 100, 0, 0, 32530, 68010, 0, 0, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (67048, 0, 0, 'Нет, адмирал. Никаких следов ни капитана Тейлора, ни его корабля.', 12, 0, 100, 0, 0, 32532, 68015, 0, 0, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (67048, 0, 0, 'Цель уничтожена. Отличный выстрел.', 12, 0, 100, 0, 0, 32531, 68011, 0, 0, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (67050, 0, 0, 'Это что за?..', 12, 0, 100, 0, 0, 27840, 68005, 0, 0, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (67051, 0, 0, 'Видно ли флагманский корабль?', 12, 0, 100, 0, 0, 32542, 68014, 0, 0, 0, 'Адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (67051, 0, 0, 'Задача выполнена. Всем вернуться. Приготовиться к наземному штурму.', 12, 0, 100, 0, 0, 32789, 68020, 0, 0, 0, 'Адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (67051, 0, 0, 'Отлично. Никого не щадить!', 12, 0, 100, 0, 0, 32540, 68012, 0, 0, 0, 'Адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (67051, 0, 0, 'Отомстите им! Не жалейте боеприпасов!', 12, 0, 100, 0, 0, 32543, 68019, 0, 0, 0, 'Адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (67051, 0, 0, 'Отставить разговоры. Все внимание на цель!', 12, 0, 100, 0, 0, 32539, 68007, 0, 0, 0, 'Адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (67051, 0, 0, 'Это за Терамор!', 12, 0, 100, 0, 0, 32541, 68013, 0, 0, 0, 'Адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (67053, 4, 0, '$n использовал ветряную бомбу!', 16, 0, 100, 0, 0, 30337, 68820, 5373, 5373, 0, 'Ветряная бомба to Player');
INSERT INTO `creature_text` VALUES (67069, 3, 0, 'Уничтожить...', 14, 0, 100, 0, 0, 0, 68518, 12000, 12000, 131867, 'Ксу\'даггаб to Player');
INSERT INTO `creature_text` VALUES (67090, 0, 0, 'Can\'t wait to get back home.', 12, 0, 0, 1, 0, 0, 68660, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (67090, 0, 1, 'I suppose it is time I headed home.', 12, 0, 0, 1, 0, 0, 68653, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (67090, 0, 2, 'Thanks! I\'m outta here!', 12, 0, 0, 1, 0, 0, 68658, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (67090, 0, 3, 'Many thanks.', 12, 0, 0, 1, 0, 0, 68661, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (67090, 0, 4, 'Thank goodness!', 12, 0, 0, 1, 0, 0, 68657, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (67090, 0, 5, 'Thank you, $r', 12, 0, 0, 1, 0, 0, 0, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (67132, 3, 0, 'I do not know why the fogs parted. But I know there is a reason for this.', 12, 0, 100, 0, 0, 28564, 63659, 31000, 31000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (67132, 3, 0, 'В Долине Четырех Ветров ты многое сможешь узнать об этой земле.', 12, 0, 100, 0, 0, 28567, 63662, 61000, 61000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (67132, 3, 0, 'Вот мы и снова встретились.', 12, 0, 100, 0, 0, 28561, 70590, 4000, 4000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (67132, 3, 0, 'Из-за стены тумана сюда прибыл еще один гость – его зовут Чэнь Буйный Портер. Он – пандарен, но нездешний.', 12, 0, 100, 0, 0, 28568, 63663, 69000, 69000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (67132, 3, 0, 'Извлеки из этого урок!', 12, 0, 100, 0, 0, 28563, 63658, 22000, 22000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (67132, 3, 0, 'Исследуй эти земли вместе с Чэнем. Подружись с живущим здесь народом. И со временем ты найдешь ответы.', 12, 0, 100, 0, 0, 28569, 63664, 80000, 80000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (67132, 3, 0, 'Но я уверена, что туманы рассеялись не просто так. Причина этому – ты. Тебе нужно понять, зачем ты здесь.', 12, 0, 100, 0, 0, 28566, 63661, 53000, 53000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (67132, 3, 0, 'Эта битва обошлась мне слишком дорого. Я смогу возродиться лишь через несколько лет.', 12, 0, 100, 0, 0, 28562, 63657, 11000, 11000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (67132, 3, 0, 'Я вижу, что ты здесь не для того, чтобы принести свою войну в эти земли. Тьма сокрыта в Пандарии, и теперь ты тоже знаешь об этом.', 12, 0, 100, 0, 0, 28565, 63660, 41000, 41000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (67134, 3, 0, 'Well played.', 12, 0, 100, 0, 0, 0, 76673, 5932000, 5932000, 0, 'Kat');
INSERT INTO `creature_text` VALUES (67134, 3, 0, 'Неплохо.', 12, 0, 100, 0, 0, 0, 88586, 113835000, 113835000, 0, 'Кэт');
INSERT INTO `creature_text` VALUES (67222, 3, 0, 'И \"кое-что\" – не для меня, Хао! Достопочтенный хранитель истории уже заждался... ой!', 12, 0, 100, 274, 0, 29538, 61225, 29000, 29000, 0, 'Штейгер Манн to Player');
INSERT INTO `creature_text` VALUES (67222, 3, 0, 'Медвежоночек! То есть... долго же тебя не было.', 12, 0, 100, 4, 0, 29536, 61221, 16000, 16000, 0, 'Штейгер Манн to Player');
INSERT INTO `creature_text` VALUES (67222, 3, 0, 'Не называй меня лапушкой! Я – то есть мы – тут чуть с ума не сошли!', 12, 0, 100, 274, 0, 29537, 61222, 24000, 24000, 0, 'Штейгер Манн to Player');
INSERT INTO `creature_text` VALUES (67260, 0, 0, 'Становится жарко, а?', 14, 0, 100, 0, 0, 0, 70130, 0, 0, 0, 'Босси Кософланж to Кусс');
INSERT INTO `creature_text` VALUES (67260, 1, 0, 'На арене появляется |3-6($r)-|3-6($c) по имени $n, $gнаш новый участник:наша новая участница; 1-го ранга! $gОн:Она; здесь пока новичок, так что полегче!', 14, 0, 100, 22, 0, 34757, 68842, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 2, 0, 'На арену выходит $gподающий:подающая:c; большие надежды |3-6($c) 2-го ранга – $n!', 14, 0, 100, 22, 0, 34757, 68888, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 3, 0, 'Все поприветствуйте |3-3($n), |3-3($c) 3-го ранга.', 14, 0, 100, 22, 0, 34757, 68889, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 4, 0, 'Вот $gон – наш претендент:она – наша претендентка;. Сегодня мы встречаем |3-3($n), |3-3($c) 4-го ранга.', 14, 0, 100, 22, 0, 34757, 68890, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 5, 0, 'На арену выходит невероятно $gталантливый:талантливая:c; |3-6($c). Поприветствуйте все |3-3($n), $gпретендента:претендентку; 5-го ранга!', 14, 0, 100, 22, 0, 34757, 68891, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 6, 0, 'Посмотрите-ка... На арену выходит смертельно $gопасный:опасная:c; |3-6($c) 6-го ранга по имени $n!', 14, 0, 100, 22, 0, 34761, 68892, 0, 0, 0, 'Босси Кософланж to Player');
INSERT INTO `creature_text` VALUES (67260, 7, 0, 'На арену выходит наш бессменный чемпион – |3-6($c) 7-го ранга $n!', 14, 0, 100, 22, 0, 34757, 68893, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 8, 0, 'На арену выходит... Вы все $gего:ее; знаете! Вы все $gего:ее; любите! Поприветствуйте бойца 8-го ранга |3-3($n)!!!', 14, 0, 100, 22, 0, 34757, 68894, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 9, 0, 'Официально подтверждаю – |3-2($n) удалось не умереть! Молодчина, что $gостался:осталась; в живых, $n!', 14, 0, 100, 0, 0, 34341, 68846, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 9, 1, 'Так-так-так! $n побеждает! Похоже, теперь я кое-кому должен денег.', 14, 0, 100, 0, 0, 34758, 68844, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 9, 2, '$gНаш победитель:Наша победительница; – $n!', 14, 0, 100, 0, 0, 34758, 68840, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 9, 3, 'Мои поздравления, $n! Постарайся не оставлять за пределами арены кровавых следов.', 14, 0, 100, 0, 0, 34758, 68845, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 9, 4, 'Давай еще, $n.', 14, 0, 100, 0, 0, 34758, 69895, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 9, 5, 'Блистательная победа, $n! А теперь возвращайся в очередь.', 14, 0, 100, 0, 0, 34758, 69890, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 9, 6, '$n побеждает! $n побеждает!', 14, 0, 100, 0, 0, 34758, 69896, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 10, 0, '$n, $n, $n. Постарайся в следующий раз не умирать так сильно.', 14, 0, 100, 0, 0, 34755, 68849, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 10, 1, 'Ну, похоже, смотреть тут больше не на что. Я прав?', 14, 0, 100, 0, 0, 0, 70145, 0, 0, 0, 'Бизмо to Малый бум');
INSERT INTO `creature_text` VALUES (67260, 10, 2, 'Извини, $n. А теперь не уберешь ли ты отсюда свое тело, чтобы освободить место для остальных?', 14, 0, 100, 0, 0, 34755, 69891, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 10, 3, '$gЕго:Ее; звали $n.', 14, 0, 100, 0, 0, 34755, 69889, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 10, 4, 'Все закончилось... не очень-то хорошо.', 14, 0, 100, 0, 0, 34755, 68848, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 10, 5, 'Ой... извини, $n. Возвращайся в очередь и попробуй еще раз.', 14, 0, 100, 0, 0, 34755, 68841, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 10, 6, 'Откровенно говоря, $n... Ты хотя бы $gпытался:пыталась; что-то сделать?', 14, 0, 100, 0, 0, 34755, 69892, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 11, 0, 'Похоже, для него Тыквовин не кончится никогда... Поприветствуем Уулисса!', 14, 0, 100, 22, 0, 0, 126125, 0, 0, 0, 'Босси Кософланж to 0');
INSERT INTO `creature_text` VALUES (67260, 12, 0, 'Эй, мы не можем торчать тут вечно. Поторапливайся!', 14, 0, 100, 0, 0, 0, 70139, 0, 0, 0, 'Бизмо to Ррррррар');
INSERT INTO `creature_text` VALUES (67260, 13, 0, 'Вы чувствуете? Запахло дымом!', 14, 0, 100, 0, 0, 0, 70142, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 14, 0, 'Поприветствуем любимца публики, нашего заиндевевшего знакомого... Тюку!', 14, 0, 100, 0, 0, 34760, 69855, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 15, 0, 'Напротив – Билл, мастер чистоты и смерти! Так, секундочку... Билл, а ты что здесь делаешь?', 14, 0, 100, 0, 0, 34760, 126126, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 16, 0, 'Санориак, повелевающий огнем, из недр Черной горы.', 14, 0, 100, 0, 0, 34760, 69945, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 17, 0, 'Опустите золотую монету, чтобы сразиться с мастером Паку!', 14, 0, 100, 0, 0, 34760, 126131, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 18, 0, 'А теперь встречайте огромного ламантина, за которым всегда остается последнее слово: РОКОВОЙ ЛАСТ!', 14, 0, 100, 0, 0, 34760, 126124, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 19, 0, 'Ветераны Морд\'Боя, возможно, помнят Блэта, самовоспроизводящуюся жижу. Сегодня я представляю вам его еще более злобную и страшную старшую сестру – Бульк!', 14, 0, 100, 0, 0, 34760, 74147, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 20, 0, 'В противоположном углу специалист по теням и интерьерному освещению: повелитель теней Аамин!', 14, 0, 100, 0, 0, 34760, 126128, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 21, 0, 'В другом углу – охотник, чьи крутость, удаль и великолепие просто восхищают! Джонни Потрясный!', 14, 0, 100, 0, 0, 34760, 126135, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 22, 0, 'Поприветствуем джинна, который в буквальном смысле слова растопит ваши сердца... Огнеус!', 14, 0, 100, 0, 0, 34760, 126137, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 23, 0, 'И... Мясыш!', 14, 0, 100, 0, 0, 34760, 70059, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 24, 0, 'Никто и предположить не мог, что они могут стать напарниками. На мой взгляд, в этом есть что-то нездоровое. Команда ГГ Инжиниринг!', 14, 0, 100, 0, 0, 34760, 69869, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 25, 0, 'Из самой чащи Сумеречного леса... Стежок!', 14, 0, 100, 0, 0, 34760, 126121, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 26, 0, 'А теперь настало время андерграунда! Встречайте Барабаха с подтанцовкой!', 14, 0, 100, 0, 0, 34760, 126139, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 27, 0, 'Он, наверное, самый смертоносный противник на арене Морд\'Бой. К сожалению, он слегка не в своей стихии. Все поднимаем стаканы за Щербохмыла!', 14, 0, 100, 0, 0, 34760, 73725, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 28, 0, 'Пятеро против одного... Конечно, не совсем честно, но ведь это же полумертвые гномы. Квинтет лепрогномов!', 14, 0, 100, 0, 0, 34760, 69852, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 29, 0, 'В другом углу: пират, которого трудно не подпустить на пушечный выстрел! Поднимите ваши крюки и поприветствуйте Черночеса!', 14, 0, 100, 0, 0, 34760, 126140, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 30, 0, 'В противоположном углу – огромный динозавр!', 14, 0, 100, 0, 0, 34760, 126130, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 31, 0, 'Следующий участник – возможно, самая хилая, страшненькая и тупая гномиха на свете. Это никчемная балда по имени Милли Ватт.', 14, 0, 100, 0, 0, 34760, 69860, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 32, 0, 'Другой наш претендент – червь, одержимый пироманией. Карл, ты что, не знаешь, что с огнем играть нельзя?', 14, 0, 100, 0, 0, 34760, 126134, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 34, 0, 'Ой... только посмотрите на эту милую змейку, Кусса! Дайте ему укусить вас за ногу. И посмотрите, что будет.', 14, 0, 100, 0, 0, 34760, 74141, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 35, 0, 'В другом углу – настолько древний претендент, что никто уже не помнит его настоящего имени! Будем звать его Древним Змеем!', 14, 0, 100, 0, 0, 34760, 126104, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 36, 0, 'А теперь претендент, который нам очень дорого обходится... Рей Д. Вайп!', 14, 0, 100, 0, 0, 34760, 126136, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (67260, 37, 0, 'Еще один боец. Всеобщий любимец – огроменный мертвец Фьолл! Я правильно произнес?', 14, 0, 100, 22, 0, 34756, 69954, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 40, 0, 'Многие из вас, старичков, его помнят. Может, он и Сломленный, но сегодня это не помешает ему сломить тебя! Акама!', 14, 0, 100, 22, 0, 34341, 69953, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 43, 0, 'А в противоположном углу у нас... большое облако ужасных летающих глаз? Это серьезно.', 14, 0, 100, 22, 0, 34756, 69959, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 48, 0, 'В противоположном углу... образчик гоблинского идиотизма омерзительного зеленого цвета верхом на своем огромном и тупом напарнике... Фрэн и Риддо! Всем бояться!', 14, 0, 100, 22, 0, 34756, 69943, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 57, 0, 'Время почти вышло!', 14, 0, 100, 0, 0, 0, 70141, 0, 0, 0, 'Бизмо to Маленький бумчик');
INSERT INTO `creature_text` VALUES (67260, 60, 0, 'Эй! Посмотри-ка, что мы приготовили для тебя сегодня... Это Блэт!', 14, 0, 100, 22, 0, 34756, 69940, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 69, 0, 'В противоположном углу – Виан, изрыгающий пламя последователь Властелина Грома!', 14, 0, 100, 22, 0, 34756, 69937, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 72, 0, 'Чтобы победить врага, ему не нужны ни меч, ни магия. Наш следующий соперник, Гексос, сражается с помощью потрясающих сил геометрии!', 14, 0, 100, 22, 0, 34756, 74136, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 75, 0, 'У него два режима. Оба называются \"УНИЧТОЖИТЬ\". Приветствуем Т440!', 14, 0, 100, 22, 0, 34756, 74138, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 87, 0, 'Что может быть лучше взрыва? Только много-много взрывов! В этом углу сидит Большущий Бумище!!!', 14, 0, 100, 22, 0, 34756, 74140, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 92, 0, 'Не разбив яйца, не сделаешь яичницы.', 14, 0, 100, 0, 0, 34755, 69893, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67260, 94, 0, 'Он воплощение справедливости, благородства и прочих добродетелей, которые едва ли ценит большинство из вас. Это дреней-паладин Агу\'ру!', 14, 0, 100, 22, 0, 34756, 73078, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 100, 0, 'Сегодня у нас особенный гость! Всеми любимый, никогда неунывающий Миллхаус Манашторм! Удачи, Милл!', 14, 0, 100, 22, 0, 34756, 69955, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 103, 0, 'А вот этот экземпляр мы отловили, когда он наматывал круги в Силитусе. Выглядит так, будто его уже кто-то отдубасил. Итак... Иккс!', 14, 0, 100, 22, 0, 34756, 69946, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 111, 0, 'Может, он и похож на огромного цыпленка, но его непросто победить. Это Тайсон Сандерс!', 14, 0, 100, 22, 0, 34756, 74137, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 126, 0, 'Наденьте кожаные доспехи, затяните ремни. На арене хранитель подземелья Вишас!', 14, 0, 100, 22, 0, 34756, 69939, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 129, 0, 'Ее улыбка заставляет улыбаться и меня. Она так очаровательна, когда произносит заклинания. Ее рога так... интригуют. Доминика!', 14, 0, 100, 22, 0, 34756, 69950, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 132, 0, 'Переходим к этому углу! Злой, смертельно опасный, но симпатичный. Прямо с пика Неутомимых... Давитель!', 14, 0, 100, 22, 0, 34756, 69948, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 147, 0, 'Никогда не думал, что доживу до этого дня! Наша прежняя чемпионка, Леона Ветер Земли, снова на арене! Аплодируйте ей, не жалейте ладоней!', 14, 0, 100, 22, 0, 34756, 70058, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 153, 0, 'Далее в программе Милли Ватт! Мы с ней встречались в инженерной школе!', 14, 0, 100, 22, 0, 34756, 69951, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (67260, 154, 0, 'А-ха-ха-ха-ха! Сколько крови! Чудесно.', 14, 0, 100, 0, 0, 34758, 69894, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (67267, 0, 0, 'Кровь и честь, |3-6($c).', 12, 0, 100, 66, 0, 0, 70088, 0, 0, 0, 'Рубака арены Морд\'Бой to Player');
