INSERT INTO `creature_text` VALUES (68118, 3, 0, 'Снаряд! Щит! Включай щит!', 14, 0, 100, 0, 0, 0, 70842, 75000, 75000, 0, 'Кор\'кронский пилот to Player');
INSERT INTO `creature_text` VALUES (68118, 3, 0, 'Те богомолы защищают башни, $p. Нужно их ликвидировать, иначе мы не сможем вести обстрел.', 14, 0, 100, 0, 0, 0, 71280, 78000, 78000, 0, 'Кор\'кронский пилот to Player');
INSERT INTO `creature_text` VALUES (68118, 3, 0, 'У нас впереди много дел, $p.', 14, 0, 100, 0, 0, 0, 69477, 12000, 12000, 0, 'Кор\'кронский пилот to Player');
INSERT INTO `creature_text` VALUES (68118, 3, 0, 'Цельтесь в янтарные бомбы. При взрыве они вызовут колоссальные разрушения!', 14, 0, 100, 0, 0, 0, 69483, 39000, 39000, 0, 'Кор\'кронский пилот to Player');
INSERT INTO `creature_text` VALUES (68118, 3, 0, 'Щита больше нет! Молодец, $p! Я отвезу тебя на береговой плацдарм.', 14, 0, 100, 0, 0, 0, 70891, 218000, 218000, 0, 'Кор\'кронский пилот to Player');
INSERT INTO `creature_text` VALUES (68137, 0, 0, 'I decided that $g was therefore a warlock:this warlock; can trust. $gHe:She; can move freely around the temple.', 12, 0, 100, 0, 0, 0, 69500, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (68137, 0, 0, 'Будь $gосторожен:осторожна;. Многие из ловушек все еще действуют. Может быть, какое-то из твоих демонических заклинаний поможет тебе разведать обстановку, прежде чем двигаться вперед.', 12, 0, 100, 0, 0, 0, 69503, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (68137, 0, 0, 'Здесь находится Святилище Потерянных Душ. В нем пребывают души тысяч моих погибших братьев.', 12, 0, 100, 0, 0, 0, 69502, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (68137, 0, 0, 'Пройдет еще много лет, прежде чем мы сможем устранить ущерб, нанесенный этому святому месту Магтериодном, Иллиданом и орками Скверны.', 12, 0, 100, 0, 0, 0, 69501, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (68137, 3, 0, '$n! Мои воины оказались в ловушке там, внизу. Времени мало. Помоги мне спасти их!', 14, 0, 100, 0, 0, 0, 69538, 8000, 8000, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (68137, 3, 0, 'Глупые чернокнижники! Я знал, что вы предпримете еще одну попытку разграбить наш храм!..', 12, 0, 100, 0, 0, 0, 69487, 1000, 1000, 134209, 'Акама to Player');
INSERT INTO `creature_text` VALUES (68137, 3, 0, 'Погоди... Ты не из тех чернокнижников, что были здесь раньше. Зачем ты здесь?', 12, 0, 100, 0, 0, 0, 69488, 6000, 6000, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (68137, 3, 0, 'Прошло уже много месяцев с тех пор, как двое чернокнижников проникли в храм. И я до сих пор не знаю, что им было нужно здесь...', 12, 0, 100, 0, 0, 0, 69498, 13000, 13000, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (68137, 3, 0, 'Следуй за мной. Они побывали и в Святилище Потерянных Душ, и на верхнем этаже храма.', 12, 0, 100, 0, 0, 0, 69499, 21000, 21000, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (68151, 0, 0, 'Узри же души, которые погубили твои собратья, чернокнижники!', 14, 0, 100, 0, 0, 0, 69535, 0, 0, 0, 'Пеплоуст-рабочий to Player');
INSERT INTO `creature_text` VALUES (68151, 1, 0, 'УМРИ!', 14, 0, 100, 0, 0, 0, 129619, 0, 0, 0, 'Пеплоуст-рабочий to Player');
INSERT INTO `creature_text` VALUES (68151, 2, 0, 'Ты так любишь демонов?.. Теперь у тебя будет возможность встретиться с ними!', 14, 0, 100, 0, 0, 0, 69537, 0, 0, 0, 'Пеплоуст-рабочий to Player');
INSERT INTO `creature_text` VALUES (68213, 0, 0, 'Дворфы завершили строительство!', 41, 0, 100, 0, 0, 0, 69553, 0, 0, 0, 'Работа окончена to Неуловимый светлячок');
INSERT INTO `creature_text` VALUES (68240, 3, 0, 'Вчерашний праздник заката, возможно, слегка затянулся. Думаю, мы переусердствовали... с медитацией.', 12, 0, 100, 1, 0, 0, 69778, 48000, 48000, 0, 'Мастер Бурый Камень to Player');
INSERT INTO `creature_text` VALUES (68240, 3, 0, 'Ищи неуловимых светлячков, $p! Они – ключевой компонент в этом напитке.', 12, 0, 100, 1, 0, 0, 71074, 126000, 126000, 0, 'Мастер Бурый Камень to Player');
INSERT INTO `creature_text` VALUES (68240, 3, 0, 'Мастер Бурый Камень и его бригада заснули!', 41, 0, 100, 0, 0, 0, 69922, 388000, 388000, 0, 'Мастер Бурый Камень to Падальщик Речного Клинка');
INSERT INTO `creature_text` VALUES (68240, 3, 0, 'Пожалуйста, $p, не дай мне заснуть. Если мы провалимся в сон, о результате можно будет забыть.', 12, 0, 100, 1, 0, 0, 71078, 424000, 424000, 0, 'Мастер Бурый Камень to Player');
INSERT INTO `creature_text` VALUES (68240, 3, 0, 'Тебе нужно всегда иметь при себе благословенную духами воду, $gдружок:подруга;. Без нее мы... начинаем клевать носом.', 12, 0, 100, 1, 0, 0, 71076, 390000, 390000, 0, 'Мастер Бурый Камень to Player');
INSERT INTO `creature_text` VALUES (68240, 3, 0, 'Ух. Мне кажется, ты $gвыбрал:выбрала; неудачное время для постройки укреплений.', 12, 0, 100, 274, 0, 0, 69777, 22000, 22000, 0, 'Мастер Бурый Камень to Player');
INSERT INTO `creature_text` VALUES (68305, 1, 0, 'I WILL CRUNCH YOU. YOU WILL DIE. DEAL WITH IT!', 14, 0, 100, 0, 0, 0, 69840, 0, 0, 0, 'Грох Топот Копыт');
INSERT INTO `creature_text` VALUES (68305, 2, 0, 'ЭТИ НАПЛЕЧНИКИ... ТАКИЕ ТЯЖЕЛЫЕ...', 14, 0, 100, 0, 0, 34758, 69839, 0, 0, 0, 'Грох Топот Копыт to Player');
INSERT INTO `creature_text` VALUES (68305, 4, 0, 'ХОЧЕШЬ ИСПОРТИТЬ МНЕ УДАЧУ? РАСТОПЧУ!', 14, 0, 100, 0, 0, 0, 69835, 11594, 11594, 0, 'Грох Топот Копыт to Гоблин удачи');
INSERT INTO `creature_text` VALUES (68312, 3, 0, 'Да, есть, Ваше Величество! Мы обнаружили артефакты древней могущественной расы!', 12, 0, 100, 5, 0, 0, 69723, 420000, 420000, 0, 'Хильда Хорнсвоггл to Player');
INSERT INTO `creature_text` VALUES (68312, 3, 0, 'Камень, много камня! Но руды мы поблизости не обнаружили.', 12, 0, 100, 396, 0, 0, 69721, 413000, 413000, 0, 'Хильда Хорнсвоггл to Player');
INSERT INTO `creature_text` VALUES (68312, 3, 0, 'Разрешите доложить!', 12, 0, 100, 113, 0, 0, 86151, 402000, 402000, 0, 'Хильда Хорнсвоггл to Player');
INSERT INTO `creature_text` VALUES (68323, 0, 0, 'На арену выходит... Вы все $gего:ее; знаете! Вы все $gего:ее; любите! Поприветствуйте бойца 8-го ранга |3-3($n)!!!', 14, 0, 100, 22, 0, 34757, 68894, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 1, 0, 'На арене появляется |3-6($r)-|3-6($c) по имени $n, $gнаш новый участник:наша новая участница; 1-го ранга! $gОн:Она; здесь пока новичок, так что полегче!', 14, 0, 100, 22, 0, 34757, 68842, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 2, 0, 'На арену выходит $gподающий:подающая:c; большие надежды |3-6($c) 2-го ранга – $n!', 14, 0, 100, 22, 0, 34757, 68888, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 3, 0, 'Все поприветствуйте |3-3($n), |3-3($c) 3-го ранга.', 14, 0, 100, 22, 0, 34757, 68889, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 4, 0, 'Вот $gон – наш претендент:она – наша претендентка;. Сегодня мы встречаем |3-3($n), |3-3($c) 4-го ранга.', 14, 0, 100, 22, 0, 34757, 68890, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 5, 0, 'На арену выходит невероятно $gталантливый:талантливая:c; |3-6($c). Поприветствуйте все |3-3($n), $gпретендента:претендентку; 5-го ранга!', 14, 0, 100, 22, 0, 34757, 68891, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 6, 0, 'Посмотрите-ка... На арену выходит смертельно $gопасный:опасная:c; |3-6($c) 6-го ранга по имени $n!', 14, 0, 100, 22, 0, 34761, 68892, 0, 0, 0, 'Босси Кософланж to Player');
INSERT INTO `creature_text` VALUES (68323, 7, 0, 'На арену выходит наш бессменный чемпион – |3-6($c) 7-го ранга $n!', 14, 0, 100, 22, 0, 34757, 68893, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 9, 0, 'Официально подтверждаю – |3-2($n) удалось не умереть! Молодчина, что $gостался:осталась; в живых, $n!', 14, 0, 100, 0, 0, 34341, 68846, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 9, 1, 'Так-так-так! $n побеждает! Похоже, теперь я кое-кому должен денег.', 14, 0, 100, 0, 0, 34758, 68844, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 9, 2, '$gНаш победитель:Наша победительница; – $n!', 14, 0, 100, 0, 0, 34758, 68840, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 9, 3, 'Мои поздравления, $n! Постарайся не оставлять за пределами арены кровавых следов.', 14, 0, 100, 0, 0, 34758, 68845, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 9, 4, 'Давай еще, $n.', 14, 0, 100, 0, 0, 34758, 69895, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 9, 5, 'Блистательная победа, $n! А теперь возвращайся в очередь.', 14, 0, 100, 0, 0, 34758, 69890, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 9, 6, '$n побеждает! $n побеждает!', 14, 0, 100, 0, 0, 34758, 69896, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 10, 0, '$n, $n, $n. Постарайся в следующий раз не умирать так сильно.', 14, 0, 100, 0, 0, 34755, 68849, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 10, 1, 'Ну, похоже, смотреть тут больше не на что. Я прав?', 14, 0, 100, 0, 0, 0, 70145, 0, 0, 0, 'Бизмо to Малый бум');
INSERT INTO `creature_text` VALUES (68323, 10, 2, 'Извини, $n. А теперь не уберешь ли ты отсюда свое тело, чтобы освободить место для остальных?', 14, 0, 100, 0, 0, 34755, 69891, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 10, 3, '$gЕго:Ее; звали $n.', 14, 0, 100, 0, 0, 34755, 69889, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 10, 4, 'Все закончилось... не очень-то хорошо.', 14, 0, 100, 0, 0, 34755, 68848, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 10, 5, 'Ой... извини, $n. Возвращайся в очередь и попробуй еще раз.', 14, 0, 100, 0, 0, 34755, 68841, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 10, 6, 'Откровенно говоря, $n... Ты хотя бы $gпытался:пыталась; что-то сделать?', 14, 0, 100, 0, 0, 34755, 69892, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 11, 0, 'Похоже, для него Тыквовин не кончится никогда... Поприветствуем Уулисса!', 14, 0, 100, 22, 0, 0, 126125, 0, 0, 0, 'Босси Кософланж to 0');
INSERT INTO `creature_text` VALUES (68323, 12, 0, 'Эй, мы не можем торчать тут вечно. Поторапливайся!', 14, 0, 100, 0, 0, 0, 70139, 0, 0, 0, 'Бизмо to Ррррррар');
INSERT INTO `creature_text` VALUES (68323, 13, 0, 'Вы чувствуете? Запахло дымом!', 14, 0, 100, 0, 0, 0, 70142, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 14, 0, 'Поприветствуем любимца публики, нашего заиндевевшего знакомого... Тюку!', 14, 0, 100, 0, 0, 34760, 69855, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 15, 0, 'Напротив – Билл, мастер чистоты и смерти! Так, секундочку... Билл, а ты что здесь делаешь?', 14, 0, 100, 0, 0, 34760, 126126, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 16, 0, 'Санориак, повелевающий огнем, из недр Черной горы.', 14, 0, 100, 0, 0, 34760, 69945, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 17, 0, 'Опустите золотую монету, чтобы сразиться с мастером Паку!', 14, 0, 100, 0, 0, 34760, 126131, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 18, 0, 'А теперь встречайте огромного ламантина, за которым всегда остается последнее слово: РОКОВОЙ ЛАСТ!', 14, 0, 100, 0, 0, 34760, 126124, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 19, 0, 'Ветераны Потасовочной, возможно, помнят Блэта, самовоспроизводящуюся жижу. Сегодня я представляю вам его еще более злобную и страшную старшую сестру – Бульк!', 14, 0, 100, 0, 0, 34760, 0, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 20, 0, 'В противоположном углу специалист по теням и интерьерному освещению: повелитель теней Аамин!', 14, 0, 100, 0, 0, 34760, 126128, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 21, 0, 'В другом углу – охотник, чьи крутость, удаль и великолепие просто восхищают! Джонни Потрясный!', 14, 0, 100, 0, 0, 34760, 126135, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 22, 0, 'Поприветствуем джинна, который в буквальном смысле слова растопит ваши сердца... Огнеус!', 14, 0, 100, 0, 0, 34760, 126137, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 23, 0, 'И... Мясыш!', 14, 0, 100, 0, 0, 34760, 70059, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 24, 0, 'Никто и предположить не мог, что они могут стать напарниками. На мой взгляд, в этом есть что-то нездоровое. Команда ГГ Инжиниринг!', 14, 0, 100, 0, 0, 34760, 69869, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 25, 0, 'Из самой чащи Сумеречного леса... Стежок!', 14, 0, 100, 0, 0, 34760, 126121, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 26, 0, 'А теперь настало время андерграунда! Встречайте Барабаха с подтанцовкой!', 14, 0, 100, 0, 0, 34760, 126139, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 27, 0, 'Он, наверное, самый смертоносный противник на арене Морд\'Бой. К сожалению, он слегка не в своей стихии. Все поднимаем стаканы за Щербохмыла!', 14, 0, 100, 0, 0, 34760, 73725, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 28, 0, 'Пятеро против одного... Конечно, не совсем честно, но ведь это же полумертвые гномы. Квинтет лепрогномов!', 14, 0, 100, 0, 0, 34760, 69852, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 29, 0, 'В другом углу: пират, которого трудно не подпустить на пушечный выстрел! Поднимите ваши крюки и поприветствуйте Черночеса!', 14, 0, 100, 0, 0, 34760, 126140, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 30, 0, 'В противоположном углу – огромный динозавр!', 14, 0, 100, 0, 0, 34760, 126130, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 31, 0, 'Следующий участник – возможно, самая хилая, страшненькая и тупая гномиха на свете. Это никчемная балда по имени Милли Ватт.', 14, 0, 100, 0, 0, 34760, 69860, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 32, 0, 'Другой наш претендент – червь, одержимый пироманией. Карл, ты что, не знаешь, что с огнем играть нельзя?', 14, 0, 100, 0, 0, 34760, 126134, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 33, 0, 'А в другом углу – эта кошмарная троица... Чинстон, Буравчик и Дюпри из команды \"Дозор огров\"!', 14, 0, 100, 0, 0, 34760, 126138, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 34, 0, 'Ой... только посмотрите на эту милую змейку, Кусса! Дайте ему укусить вас за ногу. И посмотрите, что будет.', 14, 0, 100, 0, 0, 34760, 74141, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 35, 0, 'В другом углу – настолько древний претендент, что никто уже не помнит его настоящего имени! Будем звать его Древним Змеем!', 14, 0, 100, 0, 0, 34760, 126104, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 36, 0, 'А теперь претендент, который нам очень дорого обходится... Рей Д. Вайп!', 14, 0, 100, 0, 0, 34760, 126136, 0, 0, 0, 'Бизмо to Блескотрон-3000');
INSERT INTO `creature_text` VALUES (68323, 37, 0, 'Еще один боец. Всеобщий любимец – огроменный мертвец Фьолл! Я правильно произнес?', 14, 0, 100, 22, 0, 34756, 69954, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 40, 0, 'Многие из вас, старичков, его помнят. Может, он и Сломленный, но сегодня это не помешает ему сломить тебя! Акама!', 14, 0, 100, 22, 0, 34341, 69953, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 41, 0, 'А-ха-ха-ха-ха! Сколько крови! Чудесно.', 14, 0, 100, 0, 0, 34758, 69894, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 43, 0, 'А в противоположном углу у нас... большое облако ужасных летающих глаз? Это серьезно.', 14, 0, 100, 22, 0, 34756, 69959, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 46, 0, 'Что может быть лучше взрыва? Только много-много взрывов! В этом углу сидит Большущий Бумище!!!', 14, 0, 100, 22, 0, 34756, 74140, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 48, 0, 'В противоположном углу... образчик гоблинского идиотизма омерзительного зеленого цвета верхом на своем огромном и тупом напарнике... Фрэн и Риддо! Всем бояться!', 14, 0, 100, 22, 0, 34756, 69943, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 51, 0, 'Никогда не думал, что доживу до этого дня! Наша прежняя чемпионка, Леона Ветер Земли, снова на арене! Аплодируйте ей, не жалейте ладоней!', 14, 0, 100, 22, 0, 34756, 70058, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 57, 0, 'Время почти вышло!', 14, 0, 100, 0, 0, 0, 70141, 0, 0, 0, 'Бизмо to Маленький бумчик');
INSERT INTO `creature_text` VALUES (68323, 58, 0, 'Не разбив яйца, не сделаешь яичницы.', 14, 0, 100, 0, 0, 34755, 69893, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 60, 0, 'Эй! Посмотри-ка, что мы приготовили для тебя сегодня... Это Блэт!', 14, 0, 100, 22, 0, 34756, 69940, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 63, 0, 'У него два режима. Оба называются \"УНИЧТОЖИТЬ\". Приветствуем Т440!', 14, 0, 100, 22, 0, 34756, 74138, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 66, 0, 'Огонь приближается!', 14, 0, 100, 0, 0, 0, 70143, 0, 0, 0, 'Бизмо to Player');
INSERT INTO `creature_text` VALUES (68323, 69, 0, 'В противоположном углу – Виан, изрыгающий пламя последователь Властелина Грома!', 14, 0, 100, 22, 0, 34756, 69937, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 72, 0, 'Чтобы победить врага, ему не нужны ни меч, ни магия. Наш следующий соперник, Гексос, сражается с помощью потрясающих сил геометрии!', 14, 0, 100, 22, 0, 34756, 74136, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 78, 0, 'А вот этот экземпляр мы отловили, когда он наматывал круги в Силитусе. Выглядит так, будто его уже кто-то отдубасил. Итак... Иккс!', 14, 0, 100, 22, 0, 34756, 69946, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 94, 0, 'Он воплощение справедливости, благородства и прочих добродетелей, которые едва ли ценит большинство из вас. Это дреней-паладин Агу\'ру!', 14, 0, 100, 22, 0, 34756, 73078, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 100, 0, 'Сегодня у нас особенный гость! Всеми любимый, никогда неунывающий Миллхаус Манашторм! Удачи, Милл!', 14, 0, 100, 22, 0, 34756, 69955, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 111, 0, 'Может, он и похож на огромного цыпленка, но его непросто победить. Это Тайсон Сандерс!', 14, 0, 100, 22, 0, 34756, 74137, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 126, 0, 'Наденьте кожаные доспехи, затяните ремни. На арене хранитель подземелья Вишас!', 14, 0, 100, 22, 0, 34756, 69939, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 129, 0, 'Ее улыбка заставляет улыбаться и меня. Она так очаровательна, когда произносит заклинания. Ее рога так... интригуют. Доминика!', 14, 0, 100, 22, 0, 34756, 69950, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 132, 0, 'Переходим к этому углу! Злой, смертельно опасный, но симпатичный. Прямо с пика Неутомимых... Давитель!', 14, 0, 100, 22, 0, 34756, 69948, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 141, 0, 'Вы готовы к танцам? На арене Дикен!', 14, 0, 100, 22, 0, 34756, 69957, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68323, 153, 0, 'Далее в программе Милли Ватт! Мы с ней встречались в инженерной школе!', 14, 0, 100, 22, 0, 34756, 69951, 0, 0, 0, 'Бизмо to 0');
INSERT INTO `creature_text` VALUES (68331, 3, 0, 'Держитесь, нас обходят сзади!', 14, 0, 100, 0, 0, 0, 70054, 720000, 720000, 0, 'Маршал Тротман to Player');
INSERT INTO `creature_text` VALUES (68363, 3, 0, '$n платит за все бойцовские зелья! Разбирайте, пока горячие!', 14, 0, 100, 0, 0, 80244, 124358, 0, 1137826, 0, 'Квакенбуш to Player');
INSERT INTO `creature_text` VALUES (68365, 0, 0, 'Делайте ваши ставки, угадывайте победителя боя!', 12, 0, 100, 0, 0, 34757, 69966, 0, 0, 0, 'Лорд Винифред Браун to Player');
INSERT INTO `creature_text` VALUES (68372, 0, 0, 'Бой начался, ставки больше не принимаются!', 12, 0, 100, 0, 0, 0, 69981, 0, 0, 0, 'Смотритель арены Сныкобакс to Player');
INSERT INTO `creature_text` VALUES (68377, 1, 0, 'Растения голодны...', 14, 0, 100, 0, 0, 0, 70046, 0, 0, 132735, 'Леона Ветер Земли');
INSERT INTO `creature_text` VALUES (68377, 2, 0, 'Мое тело возвращается в землю...', 14, 0, 100, 0, 0, 34758, 70050, 0, 0, 0, 'Леона Ветер Земли to Player');
INSERT INTO `creature_text` VALUES (68377, 3, 0, 'The plants are hungry...', 14, 0, 100, 0, 0, 0, 70046, 1000, 1000, 0, 'Leona Earthwind');
INSERT INTO `creature_text` VALUES (68377, 4, 0, 'Help me feed them.', 14, 0, 100, 0, 0, 0, 70052, 3000, 3000, 0, 'Leona Earthwind');
INSERT INTO `creature_text` VALUES (68377, 4, 0, 'My body returns to the earth...', 14, 0, 100, 0, 0, 0, 70050, 21000, 21000, 0, 'Leona Earthwind to Player');
INSERT INTO `creature_text` VALUES (68377, 4, 0, 'Помоги мне накормить их.', 14, 0, 100, 0, 0, 0, 70052, 3765, 3765, 134741, 'Леона Ветер Земли');
INSERT INTO `creature_text` VALUES (68377, 4, 0, 'Расскажи всем, что у этого кустика есть шипы!', 14, 0, 100, 0, 0, 34755, 70051, 41000, 41000, 0, 'Леона Ветер Земли');
INSERT INTO `creature_text` VALUES (68397, 0, 0, 'Теперь я разгневан! Сейчас вы почувствуете мою силу. Вы будете примером, глядя на который, другие будут трепетать в страхе!', 14, 0, 100, 0, 0, 0, 72509, 0, 0, 0, 'Лэй Шэнь to Страж молний');
INSERT INTO `creature_text` VALUES (68397, 1, 0, 'I am Lei Shen, the destroyer of gods and kings. You made a mistake.', 14, 0, 100, 0, 0, 0, 72510, 0, 0, 0, 'Лэй Шэнь to Player');
INSERT INTO `creature_text` VALUES (68397, 2, 0, '|TInterface\\Icons\\ability_thunderking_thunderstruck.blp:20|t%s begins to cast a spell |cFFFF0000|Hspell:135096|h[Громовой раскат]|h|r! Бегите!', 41, 0, 100, 0, 0, 0, 70328, 0, 0, 0, 'Лэй Шэнь to Разрушительный бросок');
INSERT INTO `creature_text` VALUES (68397, 3, 0, 'На колени! ', 14, 0, 100, 0, 0, 0, 96030, 0, 0, 0, 'Лэй Шэнь to Разрушительный бросок');
INSERT INTO `creature_text` VALUES (68397, 4, 0, 'Вы бросили мне вызов в моем же святилище? Какая глупость.', 14, 0, 100, 0, 0, 0, 72518, 0, 0, 0, 'Лэй Шэнь to 0');
INSERT INTO `creature_text` VALUES (68397, 5, 0, '%s готовится завершить |cFFFF0000Сверхзарядку|r!', 41, 0, 100, 0, 0, 0, 72282, 0, 0, 0, 'Лэй Шэнь to 0');
INSERT INTO `creature_text` VALUES (68397, 6, 0, 'Ха! Я сам вами займусь. Посмотрим, как вы справитесь с молнией.', 14, 0, 100, 0, 0, 0, 72519, 0, 0, 0, 'Лэй Шэнь to 0');
INSERT INTO `creature_text` VALUES (68397, 7, 0, '|TInterface\\Icons\\ability_thunderking_lightningwhip.blp:20|t%s begins to cast a spell |cFFFF0000|Hspell:136850|h[Хлыст молний]|h|r!', 41, 0, 100, 0, 0, 0, 71412, 0, 0, 0, 'Лэй Шэнь to Хлыст молний');
INSERT INTO `creature_text` VALUES (68397, 8, 0, 'Молния, найди их!', 14, 0, 100, 0, 0, 0, 72514, 0, 0, 0, 'Лэй Шэнь to Хлыст молний');
INSERT INTO `creature_text` VALUES (68397, 11, 0, 'Вы – НИКТО! В моих руках сила небес!', 14, 0, 100, 0, 0, 0, 72520, 0, 0, 0, 'Лэй Шэнь to 0');
INSERT INTO `creature_text` VALUES (68397, 13, 0, 'НЕТ! ВЫ НЕДОСТОЙНЫ! Я… ВЛАСТЕЛИН ГРОМА!', 14, 0, 100, 0, 0, 0, 72521, 0, 0, 0, 'Лэй Шэнь to 0');
INSERT INTO `creature_text` VALUES (68397, 14, 0, '|TInterface\\Icons\\spell_nature_lightningoverload.blp:20|t |cFFFF0000Северо-западные|r ворота начинают открываться, вызывая |cFFFF0000|Hspell:136889|h[жестокие штормовые ветра]|h|r!', 41, 0, 100, 0, 0, 0, 71497, 0, 0, 0, 'Лэй Шэнь to 0');
INSERT INTO `creature_text` VALUES (68397, 15, 0, 'Ветер, приди на мой зов!', 14, 0, 100, 0, 0, 0, 72515, 0, 0, 0, 'Лэй Шэнь to 0');
INSERT INTO `creature_text` VALUES (68397, 20, 0, '|TInterface\\Icons\\spell_nature_lightningoverload.blp:20|t |cFFFF0000Северо-восточные|r ворота начинают открываться, вызывая |cFFFF0000|Hspell:136889|h[жестокие штормовые ветра]|h|r!', 41, 0, 100, 0, 0, 0, 72228, 0, 0, 0, 'Лэй Шэнь to 0');
INSERT INTO `creature_text` VALUES (68397, 25, 0, 'Умрите!', 14, 0, 100, 0, 0, 0, 135011, 0, 0, 0, 'Лэй Шэнь to Разрушительный бросок');
INSERT INTO `creature_text` VALUES (68397, 26, 0, 'Я лишь хотел… завершить труд богов…', 14, 0, 100, 0, 0, 0, 72522, 0, 0, 0, 'Лэй Шэнь to Player');
INSERT INTO `creature_text` VALUES (68398, 3, 0, '|TInterface\\Icons\\spell_nature_unrelentingstorm.blp:20|t |cFFFF0000|Hspell:135680|h[Северный проводник]|h|r перегорел и вызвал |cFFFF0000|Hspell:137176|h[перегрузку цепи]|h|r!', 41, 0, 100, 0, 0, 0, 71761, 1121750, 1121750, 137150, 'Проводник статического шока');
INSERT INTO `creature_text` VALUES (68398, 3, 0, 'Проводник полностью заряжен, и Лэй Шэнь отсоединяется от него!', 41, 0, 100, 0, 0, 35584, 74238, 391000, 391000, 0, 'Проводник статического шока to Проводник статического шока');
INSERT INTO `creature_text` VALUES (68399, 0, 0, 'Ваша помощь очень кстати. Мне нужны ресурсы. Что вы здесь нашли?', 12, 0, 100, 1, 0, 0, 69720, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68399, 0, 0, 'Здесь надо построить башню.', 12, 0, 100, 0, 0, 0, 69716, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68399, 0, 0, 'Неплохо. Еще что-нибудь есть?', 12, 0, 100, 6, 0, 0, 69722, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68399, 0, 0, 'Предлагаю сделку, Хорнсвоггл. Вы организуете работу карьера, а мы защищаем ваши раскопки.', 12, 0, 100, 1, 0, 0, 69724, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68399, 0, 0, 'Разведка хорошо поработала. Это идеальное место для глубоководного порта.', 12, 0, 100, 1, 0, 0, 69715, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68399, 0, 0, 'Тротман, старый ты пес, я так и знал, что ты влипнешь.', 12, 0, 100, 1, 0, 0, 69714, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68399, 3, 0, 'Герой! Лишь благодаря твоей помощи в исследовании Пандарии мы заполучили этот плацдарм.', 12, 0, 100, 1, 0, 0, 69729, 24000, 24000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68399, 3, 0, 'Мы готовы. Из добытых ресурсов мы построим город на этом полуострове.', 12, 0, 100, 0, 0, 0, 69727, 10000, 10000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68399, 3, 0, 'Отсюда мы сможем установить контакт с коренными жителями и сдерживать агрессию Орды.', 12, 0, 100, 0, 0, 0, 69728, 18000, 18000, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68402, 0, 0, 'Have a merry time on the merry-go-round!', 12, 0, 100, 0, 0, 0, 70073, 0, 0, 0, 'Say line');
