INSERT INTO `creature_text` VALUES (68476, 5, 0, '|TInterface\\Icons\\ability_warrior_focusedrage.blp:20|t Увидев, как умирает бог войны Джалак, Хорридон впадает в |cFFFF0000|Hspell:136821|h[буйство]|h|r!', 41, 0, 100, 0, 0, 0, 71405, 0, 0, 0, 'Хорридон to Бог войны Джалак');
INSERT INTO `creature_text` VALUES (68492, 0, 0, 'Welcome to Darkmoon Island! Feel free to ride one of these lovely beasts down to the Faire.', 12, 0, 100, 396, 0, 0, 70148, 0, 0, 0, '\"Пыльный\" Брандом to Player');
INSERT INTO `creature_text` VALUES (68514, 0, 0, 'А Орда?', 12, 0, 100, 6, 0, 0, 69707, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68514, 0, 0, 'Вижу разведгруппу. Направляемся к ним!', 14, 0, 100, 25, 0, 0, 69704, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68514, 0, 0, 'Доложите обстановку.', 12, 0, 100, 547, 0, 0, 69705, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68519, 0, 0, 'Ваше Величество! Здесь есть все, что нужно: руда, лес, камень...', 12, 0, 100, 396, 0, 0, 69706, 0, 0, 0, 'Хильда Хорнсвоггл to Player');
INSERT INTO `creature_text` VALUES (68526, 0, 0, '$n платит за всех! Бесплатные напитки всем желающим в течение следующих двух минут!', 14, 0, 100, 0, 0, 80244, 127277, 0, 0, 0, 'Барменша Таунсли to Player');
INSERT INTO `creature_text` VALUES (68553, 0, 0, 'A |c00FFFF00|Hspell:178361|h[Капитан Орды]|h|r вступает в бой!', 41, 0, 100, 0, 0, 49299, 84106, 0, 0, 0, 'SLG Generic MoP (Large AOI) to Player');
INSERT INTO `creature_text` VALUES (68553, 1, 0, 'Альянс выиграл этот этап битвы!', 41, 0, 100, 0, 0, 48252, 82379, 0, 0, 0, 'SLG Generic MoP (Large AOI) to 0');
INSERT INTO `creature_text` VALUES (68553, 2, 0, 'Орда выиграла этот этап битвы!', 41, 0, 100, 0, 0, 48251, 82380, 0, 0, 0, 'SLG Generic MoP (Large AOI) to 0');
INSERT INTO `creature_text` VALUES (68553, 3, 0, 'Альянс захватил заставу Копья Войны!', 41, 0, 100, 0, 0, 46572, 89257, 0, 0, 0, 'SLG Generic MoP (Large AOI) to 0');
INSERT INTO `creature_text` VALUES (68553, 7, 0, 'Орда защитила заставу Копья Войны!', 41, 0, 100, 0, 0, 46569, 89344, 0, 0, 0, 'SLG Generic MoP (Large AOI) to 0');
INSERT INTO `creature_text` VALUES (68553, 9, 0, '|c00FFFF00|Hspell:178380|h[Kor\'lok, ogre king]|h|r Woke Up!', 41, 0, 100, 0, 0, 45968, 82979, 0, 0, 0, 'SLG Generic MoP (Large AOI) to Гладиатор Копья Войны');
INSERT INTO `creature_text` VALUES (68553, 15, 0, '|c00FFFF00|Hspell:178359|h[Cron]|h|r Rebell!', 41, 0, 100, 0, 0, 45967, 82980, 0, 0, 0, 'SLG Generic MoP (Large AOI) to Player');
INSERT INTO `creature_text` VALUES (68599, 3, 0, 'Благословенная духами вода подойдет для полива наших саженцев. Ее носят местные духи. Когда ведро наполнится, тебе не придется в него подливать.', 12, 0, 100, 1, 0, 0, 71150, 306000, 306000, 0, 'Ферра Жемчужина to Player');
INSERT INTO `creature_text` VALUES (68599, 3, 0, 'Во время поставок ресурсов следи за тем, чтобы положительные эффекты не исчезали.', 12, 0, 100, 1, 0, 34768, 71157, 107000, 222000, 0, 'Ферра Жемчужина to Player');
INSERT INTO `creature_text` VALUES (68599, 3, 0, 'Не оставляй нас без защиты, $p. Мы не умеем драться, и в случае нападения нам потребуется твоя помощь.', 12, 0, 100, 1, 0, 0, 71154, 354000, 354000, 0, 'Ферра Жемчужина to Player');
INSERT INTO `creature_text` VALUES (68599, 3, 0, 'О большем мы и мечтать не могли. Спасибо тебе за помощь!', 12, 0, 100, 1, 0, 0, 71142, 612000, 612000, 0, 'Ферра Жемчужина to Player');
INSERT INTO `creature_text` VALUES (68599, 3, 0, 'Пойдемте, дети мои. Пусть Элуна благословит наши нынешние деяния.', 12, 0, 100, 1, 0, 0, 70427, 3000, 3000, 0, 'Ферра Жемчужина to Player');
INSERT INTO `creature_text` VALUES (68599, 3, 0, 'Ты нам здорово $gпомог:помогла;, $p. Не прекращай нас поддерживать, пока мы не закончим.', 12, 0, 100, 1, 0, 0, 71143, 570000, 570000, 0, 'Ферра Жемчужина to Player');
INSERT INTO `creature_text` VALUES (68696, 3, 0, '|TInterface\\Icons\\spell_nature_unrelentingstorm.blp:20|t |cFFFF0000|Hspell:135681|h[Восточный проводник]|h|r перегорел и вызвал |cFFFF0000|Hspell:137176|h[перегрузку цепи]|h|r!', 41, 0, 100, 0, 0, 0, 71762, 304798, 304798, 0, 'Проводник цепного распространения');
INSERT INTO `creature_text` VALUES (68697, 3, 0, '|TInterface\\Icons\\spell_nature_unrelentingstorm.blp:20|t |cFFFF0000|Hspell:135682|h[Южный проводник]|h|r перегорел и вызвал |cFFFF0000|Hspell:137176|h[перегрузку цепи]|h|r!', 41, 0, 100, 0, 0, 0, 71763, 394823, 394823, 0, 'Проводник перегрузки');
INSERT INTO `creature_text` VALUES (68698, 0, 0, '|TInterface\\Icons\\spell_nature_unrelentingstorm.blp:20|t The |cFFFF0000|Hspell:135683|h[West Conduit]|h|r has burned out and caused |cFFFF0000|Hspell:137176|h[Overloaded Circuits]|h|r!', 41, 0, 100, 0, 0, 0, 71764, 0, 0, 0, 'Bouncing Bolt Conduit');
INSERT INTO `creature_text` VALUES (68698, 3, 0, '|TInterface\\Icons\\spell_nature_unrelentingstorm.blp:20|t |cFFFF0000|Hspell:135683|h[Западный проводник]|h|r перегорел и вызвал |cFFFF0000|Hspell:137176|h[перегрузку цепи]|h|r!', 41, 0, 100, 0, 0, 0, 71764, 382000, 382000, 137150, 'Проводник блуждающих разрядов');
INSERT INTO `creature_text` VALUES (68698, 3, 0, 'Проводник полностью заряжен, и Лэй Шэнь отсоединяется от него!', 41, 0, 100, 0, 0, 35584, 74238, 1557000, 1557000, 0, 'Проводник блуждающих разрядов to Проводник блуждающих разрядов');
INSERT INTO `creature_text` VALUES (68792, 3, 0, 'Вниманию всех бойцов! $n $gзаключил:заключила; сделку с целителями душ! Можете умирать теперь сколько душе угодно!', 14, 0, 100, 0, 0, 80244, 124363, 357031, 4230578, 0, 'Лидия Пейтон to Player');
INSERT INTO `creature_text` VALUES (68794, 0, 0, 'Вниманию всех бойцов! $n $gзаключил:заключила; сделку с целителями душ! Можете умирать теперь сколько душе угодно!', 14, 0, 100, 0, 0, 80244, 124363, 0, 0, 0, 'Эсме Солнечная Тень to Player');
INSERT INTO `creature_text` VALUES (68828, 3, 0, 'Как раз вовремя, $p! Нам нужна твоя помощь!', 14, 0, 100, 25, 0, 0, 70852, 2000, 2000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (68830, 3, 0, 'Надеюсь, ты снова шлепнешься в ил, Салли!', 14, 0, 100, 22, 0, 0, 70851, 20000, 20000, 0, 'Мишка');
INSERT INTO `creature_text` VALUES (68842, 0, 0, 'Я бы напала сразу. Многие бы погибли ради моего желания победить.', 12, 0, 100, 0, 0, 34279, 70307, 0, 0, 0, 'Тиранда Шелест Ветра to Зверь');
INSERT INTO `creature_text` VALUES (68842, 3, 0, 'Мы теряем время. Мы можем атаковать и убить их!', 12, 0, 100, 0, 0, 34286, 70302, 192000, 192000, 0, 'Тиранда Шелест Ветра');
INSERT INTO `creature_text` VALUES (68842, 3, 0, 'Почему мы не нападаем?', 12, 0, 100, 0, 0, 34288, 70304, 539000, 539000, 0, 'Тиранда Шелест Ветра');
INSERT INTO `creature_text` VALUES (68842, 3, 0, 'Ты колеблешься, Вариан. Будь решительным, и Альянс пойдет за тобой.', 12, 0, 100, 0, 0, 34287, 70303, 366000, 366000, 0, 'Тиранда Шелест Ветра');
INSERT INTO `creature_text` VALUES (68860, 3, 0, 'Враг взял тебя в цель! Щит! Включай щит!', 14, 0, 100, 0, 0, 0, 70846, 107000, 107000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (68860, 3, 0, 'Гладко сработано, $p! Продолжай сбрасывать бомбы.', 14, 0, 100, 0, 0, 0, 71290, 56000, 56000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (68860, 3, 0, 'Используйте силовой щит, чтобы защититься от управляемых снарядов.', 42, 0, 100, 0, 0, 0, 70844, 107000, 107000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (68860, 3, 0, 'Молодец, $p! Еле увернулись.', 14, 0, 100, 0, 0, 0, 71294, 112000, 204000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (68860, 3, 0, 'Нужно их уничтожить до того, как нас вытеснят войска Орды.', 14, 0, 100, 0, 0, 0, 70894, 37000, 37000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (68860, 3, 0, 'Остров охвачен звуковым щитом, $p.', 14, 0, 100, 0, 0, 0, 70892, 25000, 25000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (68860, 3, 0, 'Передаю координаты следующих башен.', 14, 0, 100, 0, 0, 0, 70897, 141000, 141000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (68860, 3, 0, 'По донесениям разведки этот щит поддерживает несколько генераторов, расположенных по всему острову.', 14, 0, 100, 0, 0, 0, 70893, 31000, 31000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (68860, 3, 0, 'Прием, $p, это небесный адмирал Роджерс!', 14, 0, 100, 0, 0, 0, 71141, 19000, 19000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (68860, 3, 0, 'Скорпиды перемещают башни! Уничтожь их!', 14, 0, 100, 0, 0, 0, 71281, 150000, 150000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (68860, 3, 0, 'Те богомолы защищают башни, $p. Нужно их ликвидировать, иначе мы не сможем вести обстрел.', 14, 0, 100, 0, 0, 0, 71280, 90000, 90000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (68860, 3, 0, 'Ты подходишь к первым башням! Уничтожь их!', 14, 0, 100, 0, 0, 0, 70895, 54000, 54000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (68860, 3, 0, 'Хорошо $gотстрелялся:отстрелялась;, $p! Щит ослабевает!', 14, 0, 100, 0, 0, 0, 70896, 81000, 81000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (68860, 3, 0, 'Целься в янтарные бомбы! Они нанесут колоссальный урон, если их подорвать!', 14, 0, 100, 0, 0, 0, 70885, 55000, 55000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (68860, 3, 0, 'Щит ликвидирован! Быстрее отправляйся на береговой плацдарм!', 14, 0, 100, 0, 0, 0, 70898, 217000, 217000, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (68904, 0, 0, 'Разожжем пламя! Давай, Лу\'линь… превратим их в пепел!', 14, 0, 100, 0, 0, 0, 72188, 0, 0, 0, 'Су\'линь to 0');
INSERT INTO `creature_text` VALUES (68904, 1, 0, 'Хахахаха!', 14, 0, 100, 0, 0, 0, 72189, 0, 0, 0, 'Су\'линь to 0');
INSERT INTO `creature_text` VALUES (68904, 3, 0, 'Я вижу свет…', 14, 0, 100, 0, 0, 0, 72191, 0, 0, 0, 'Су\'линь to 0');
INSERT INTO `creature_text` VALUES (68905, 0, 0, 'Как пожелаешь… ', 14, 0, 100, 0, 0, 0, 72179, 0, 0, 0, 'Лу\'линь to 0');
INSERT INTO `creature_text` VALUES (68930, 0, 0, 'Андуин! Ты жив!', 12, 0, 100, 5, 0, 0, 69735, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68930, 0, 0, 'Значит, мы защитим их – даже ценой наших жизней. Андуин, вместе мы сможем сделать это.', 12, 0, 100, 1, 0, 0, 69739, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68930, 0, 0, 'Маршал, вы отлично укрепили бухту. Дальше займемся...', 12, 0, 100, 1, 0, 0, 69731, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68930, 0, 0, 'Следопыты?.. Тиранда.', 12, 0, 100, 1, 0, 0, 69742, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68930, 0, 0, 'Тихо, тихо. Здесь мы ведем иную войну. Мы воюем не из ненависти к Орде. Мы просто пытаемся сделать то, что должны.', 12, 0, 100, 0, 0, 0, 69737, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68930, 0, 0, 'Что?..', 12, 0, 100, 6, 0, 0, 69733, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (68944, 3, 0, 'Вместе...', 12, 0, 100, 1, 0, 0, 114837, 45000, 45000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (68944, 3, 0, 'Отец, нет! Ненависть и злоба, порождаемые нашей войной, отравляют это место.', 12, 0, 100, 274, 0, 0, 69736, 9000, 9000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (68944, 3, 0, 'Отец!', 14, 0, 100, 22, 0, 0, 124735, 1000, 1000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (68944, 3, 0, 'Эти земли другие. Они просто сокровище.', 12, 0, 100, 1, 0, 0, 69738, 30000, 30000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (68945, 3, 0, 'Ваше величество! Орда взяла храм к западу отсюда. Следопыты эльфов окружили его и никого не выпускают живым...', 14, 0, 100, 5, 0, 0, 69741, 1000, 1000, 0, 'Хильда Хорнсвоггл to Player');
INSERT INTO `creature_text` VALUES (68963, 3, 0, 'Благодаря |3-2($n) бумзал потасовочной \"У Бизмо\" распахнул свои двери для всех бойцов!', 14, 0, 100, 0, 0, 80244, 127276, 1096860, 1096860, 0, 'Вышибала бумзала to Player');
INSERT INTO `creature_text` VALUES (68970, 3, 0, 'I will go, but only as a witness on behalf of my people.', 12, 0, 100, 1, 0, 0, 71275, 107000, 107000, 0, 'Вол\'джин to Player');
INSERT INTO `creature_text` VALUES (68970, 3, 0, 'Вопрос в том, что МЫ здесь делаем. Ты несешь войну на очередной материк, и я хочу знать, зачем.', 12, 0, 100, 6, 0, 0, 69599, 14000, 14000, 0, 'Вол\'джин to Player');
INSERT INTO `creature_text` VALUES (68970, 3, 0, 'Эта резня любого заставит подумать дважды о вступлении в Орду.', 12, 0, 100, 274, 0, 0, 69602, 56000, 56000, 0, 'Вол\'джин to Player');
INSERT INTO `creature_text` VALUES (68975, 0, 0, '%s и Рак\'гор Кровавая Бритва шепотом переговариваются между собой.', 16, 0, 100, 1, 0, 0, 71278, 0, 0, 0, 'Гаррош Адский Крик to Player');
INSERT INTO `creature_text` VALUES (68975, 0, 0, 'Видишь, герой? Моя Орда может добиться чего угодно. Это еще что?', 12, 0, 100, 1, 0, 0, 69596, 0, 0, 0, 'Гаррош Адский Крик to Player');
INSERT INTO `creature_text` VALUES (68975, 0, 0, 'Вол\'джин. Что ты здесь делаешь? Не сидится на своем острове в компании жаб?', 12, 0, 100, 547, 0, 0, 69597, 0, 0, 0, 'Гаррош Адский Крик to Player');
INSERT INTO `creature_text` VALUES (68975, 0, 0, 'Демонстрация силы на этих берегах заставит пандаренов подумать дважды о вступлении в Альянс.', 12, 0, 100, 5, 0, 0, 69601, 0, 0, 0, 'Гаррош Адский Крик to Player');
INSERT INTO `creature_text` VALUES (68975, 0, 0, 'Посмотри вокруг, Вол\'джин. Здесь есть все, что нам нужно: древесина, камень, руда, нефть. Воины.', 12, 0, 100, 1, 0, 0, 69600, 0, 0, 0, 'Гаррош Адский Крик to Player');
INSERT INTO `creature_text` VALUES (68975, 0, 0, 'Разница между мной и тобой, Вол\'джин, состоит в том, что я не даю СВОИМ умирать от голода в пустыне. Меня ничто, НИЧТО не остановит на пути к славному будущему для орков и всех, у кого хватит смелости пойти с нами. Жди здесь.', 12, 0, 100, 25, 0, 0, 69603, 0, 0, 0, 'Гаррош Адский Крик to Player');
INSERT INTO `creature_text` VALUES (68975, 0, 0, 'Тебе не хватает проницательности, дикарь. А еще тебе не хватает верности – потому я и не позвал с собой троллей Черного Копья.', 12, 0, 100, 1, 0, 0, 69598, 0, 0, 0, 'Гаррош Адский Крик to Player');
INSERT INTO `creature_text` VALUES (68975, 0, 0, 'Ты можешь кое-что сделать, чтобы доказать свою ценность для Орды, тролль. Я дам тебе задание в самом сердце этих земель.', 12, 0, 100, 1, 0, 0, 69604, 0, 0, 0, 'Гаррош Адский Крик to Player');
INSERT INTO `creature_text` VALUES (68990, 0, 0, '$n! $n! $n!', 14, 0, 100, 66, 0, 0, 70128, 0, 0, 0, 'Любитель боев to Player');
INSERT INTO `creature_text` VALUES (68990, 0, 0, 'Нет! Нет, нет, нет!', 14, 0, 100, 273, 0, 0, 70660, 0, 0, 0, 'Любитель боев');
INSERT INTO `creature_text` VALUES (68991, 0, 0, 'Нет! Нет, нет, нет!', 14, 0, 100, 0, 0, 0, 70660, 0, 0, 0, 'Любительница боев');
INSERT INTO `creature_text` VALUES (68991, 0, 0, 'ПРОИГРЫШ?! ЭТО ПРОСТО НЕВОЗМОЖНО!', 14, 0, 100, 396, 0, 0, 70661, 0, 0, 0, 'Любительница боев');
INSERT INTO `creature_text` VALUES (68991, 0, 0, 'ЧТО?! Как же так?', 14, 0, 100, 0, 0, 0, 70663, 0, 0, 0, 'Любительница боев');
INSERT INTO `creature_text` VALUES (68991, 0, 0, 'Эх! А я на этого бойца все деньги поставила!', 14, 0, 100, 0, 0, 0, 70659, 0, 0, 0, 'Любительница боев');
INSERT INTO `creature_text` VALUES (68992, 0, 0, '$n – $gлучший:лучшая;!', 14, 0, 100, 21, 0, 34746, 70129, 0, 0, 0, 'Любитель боев to Player');
INSERT INTO `creature_text` VALUES (68992, 3, 0, 'Замечательно, $n!', 14, 0, 100, 4, 0, 34341, 70126, 0, 865378, 0, 'Любитель боев to Player');
INSERT INTO `creature_text` VALUES (68992, 3, 0, 'Эх! А я на этого бойца все деньги поставил!', 14, 0, 100, 0, 0, 0, 70659, 2754266, 2754266, 0, 'Любитель боев');
INSERT INTO `creature_text` VALUES (68994, 3, 0, '$n! $n! $n!', 14, 0, 100, 21, 0, 34758, 70128, 2541828, 2541828, 0, 'Любительница боев to Player');
INSERT INTO `creature_text` VALUES (68994, 3, 0, 'Поздравляю!', 14, 0, 100, 4, 0, 0, 139463, 4718722, 4718722, 0, 'Любительница боев to Player');
INSERT INTO `creature_text` VALUES (68994, 4, 0, 'Эх! А я на этого бойца все деньги поставила!', 14, 0, 100, 0, 0, 0, 70659, 2547, 2547, 0, 'Любительница боев');
