INSERT INTO `creature_text` VALUES (69465, 5, 0, '|TInterface\\Icons\\ability_warrior_throwdown:20|t%s хватает |3-3($n) и готовится к |cFFFF0000|Hspell:137175|h[громовому броску]|h|r!', 41, 0, 100, 0, 0, 0, 72254, 0, 0, 0, 'Джин\'рок Разрушитель to Player');
INSERT INTO `creature_text` VALUES (69465, 6, 0, 'Ломать кости!', 14, 0, 100, 0, 0, 0, 71979, 0, 0, 0, 'Джин\'рок Разрушитель to Player');
INSERT INTO `creature_text` VALUES (69465, 22, 0, '|TInterface\\Icons\\spell_shaman_thunderstorm:20|t%s начинает призывать жестокую |cFFFF0000|Hspell:137313|h[грозу]|h|r!', 41, 0, 100, 0, 0, 0, 72255, 0, 0, 0, 'Джин\'рок Разрушитель to 0');
INSERT INTO `creature_text` VALUES (69465, 23, 0, 'Буря!', 14, 0, 100, 0, 0, 0, 76941, 0, 0, 0, 'Джин\'рок Разрушитель to 0');
INSERT INTO `creature_text` VALUES (69465, 35, 0, 'Как вы меня победили?..', 14, 0, 100, 0, 0, 0, 71974, 0, 0, 0, 'Джин\'рок Разрушитель to Player');
INSERT INTO `creature_text` VALUES (69468, 3, 0, 'Расчисти путь от корабля к воротам на верхнем уровне – нам придется идти пешком.', 12, 0, 100, 0, 0, 35862, 72406, 30484, 30484, 0, 'Таоши to Taoshi Line Bunny');
INSERT INTO `creature_text` VALUES (69473, 0, 0, 'For thousands of years I have been waiting in darkness, the silence of which was pierced only by the cries of torture victims. Nightmares have come true.', 14, 0, 100, 0, 0, 35756, 72237, 0, 0, 0, 'Ра-ден to Player');
INSERT INTO `creature_text` VALUES (69473, 1, 0, 'В ваших глазах я вижу надежду. Наивные! Думаете, вы меня спасли? Избавьтесь от этого жалкого сострадания и обратите его к себе, ибо скоро вы познаете мои муки.', 14, 0, 100, 0, 0, 35757, 72238, 0, 0, 0, 'Ра-ден to Player');
INSERT INTO `creature_text` VALUES (69473, 2, 0, 'Этот искаженный мир уже не спасти, ему не помогут обманутые герои. Скверну можно лишь уничтожить. И начну я прямо сейчас.', 14, 0, 100, 0, 0, 35758, 72239, 0, 0, 0, 'Ра-ден to Player');
INSERT INTO `creature_text` VALUES (69473, 3, 0, 'Покончив с вами, я не остановлюсь, пока не очищу весь этот мир. Умрите же!', 14, 0, 100, 0, 0, 35754, 72240, 0, 0, 0, 'Ра-ден to 0');
INSERT INTO `creature_text` VALUES (69473, 4, 0, 'Прикосновение творца… и разрушителя.', 14, 0, 100, 0, 0, 35763, 72236, 0, 0, 0, 'Ра-ден to 0');
INSERT INTO `creature_text` VALUES (69473, 5, 0, 'Это лишь крупица моей силы.', 14, 0, 100, 0, 0, 35765, 72233, 0, 0, 0, 'Ра-ден to Сущность анимы');
INSERT INTO `creature_text` VALUES (69473, 6, 0, 'Примиритесь с неизбежным.', 14, 0, 100, 0, 0, 35759, 72242, 0, 0, 0, 'Ра-ден to Player');
INSERT INTO `creature_text` VALUES (69473, 7, 0, 'Но внизу лежит бесконечная, всепоглощающая тьма. Не думаю, что вы сможете противостоять судьбе, смертные.', 14, 0, 100, 0, 0, 0, 72250, 0, 0, 0, 'Ра-ден to 0');
INSERT INTO `creature_text` VALUES (69473, 8, 0, 'Но… вы заслужили право попробовать. Прощайте.', 14, 0, 100, 0, 0, 0, 72251, 0, 0, 0, 'Ра-ден to 0');
INSERT INTO `creature_text` VALUES (69475, 3, 0, 'Они идут! К оружию, братья! Сбросим их тела в море!', 12, 0, 100, 0, 0, 0, 71676, 1000, 1000, 0, 'Чародей-ритуалист Тула\'чек to Player');
INSERT INTO `creature_text` VALUES (69475, 3, 0, 'Сокрушите захватчиков! Поддерживайте преграду!', 12, 0, 100, 0, 0, 0, 71678, 5000, 5000, 0, 'Чародей-ритуалист Тула\'чек to Player');
INSERT INTO `creature_text` VALUES (69475, 3, 0, 'Удерживайте руины! Убейте их на месте!', 12, 0, 100, 0, 0, 0, 71677, 8000, 8000, 0, 'Чародей-ритуалист Тула\'чек to Player');
INSERT INTO `creature_text` VALUES (69475, 3, 0, 'Ха! Вы еще не видели истинную силу зандаларов!', 12, 0, 100, 0, 0, 0, 71731, 26000, 26000, 0, 'Чародей-ритуалист Тула\'чек to Зеб\'тульский заклинатель зверей');
INSERT INTO `creature_text` VALUES (69475, 4, 0, 'Зандалар…', 12, 0, 100, 0, 0, 0, 71683, 59000, 59000, 0, 'Чародей-ритуалист Тула\'чек to Player');
INSERT INTO `creature_text` VALUES (69476, 4, 0, '%s begins to cast a spell \"Гимн тишине\"! Скройтесь за преградой!', 41, 0, 100, 0, 0, 0, 73044, 20859, 44000, 0, 'Маска духов');
INSERT INTO `creature_text` VALUES (69478, 3, 0, 'Братья и сестры! Враги окружают нас со всех сторон. Только от вашей отваги зависит, победим мы или проиграем.', 12, 0, 100, 0, 0, 0, 71674, 18000, 18000, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (69478, 3, 0, 'Роммат, выставь охрану. Этас, установи тотемы. Кетиэль, займись палатками и припасами.', 12, 0, 100, 0, 0, 0, 71673, 10000, 10000, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (69478, 3, 0, 'Слава син\'дорай! Орда снова одержала победу!', 12, 0, 100, 0, 0, 0, 71672, 5000, 5000, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (69481, 3, 0, 'Не сейчас, друг мой. Не сейчас.', 12, 0, 100, 0, 0, 0, 72414, 36000, 36000, 0, 'Лор\'темар Терон to Лор\'темар Терон');
INSERT INTO `creature_text` VALUES (69481, 3, 0, 'Прекрасно. Я знал, что Шадо-Пан будут отличными союзниками.', 12, 0, 100, 0, 0, 0, 72412, 13000, 13000, 0, 'Лор\'темар Терон to Надсмотрщик Шиаксу');
INSERT INTO `creature_text` VALUES (69481, 3, 0, 'Следопыты! Займите кузнечный двор! Сломайте ворота, чтобы их не закрыли!', 14, 0, 100, 0, 0, 0, 72411, 4000, 4000, 0, 'Лор\'темар Терон to Надсмотрщик Шиаксу');
INSERT INTO `creature_text` VALUES (69481, 3, 0, 'Тем не менее, мы нанесли Властелину Грома серьезный удар. Наши оружейники немедленно начнут изучать технологии могу.', 12, 0, 100, 0, 0, 0, 72413, 23000, 23000, 0, 'Лор\'темар Терон to Лор\'темар Терон');
INSERT INTO `creature_text` VALUES (69481, 3, 0, 'Эльсия ждет тебя.', 12, 0, 100, 0, 0, 0, 72410, 5000, 5000, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (69482, 2, 0, 'Вам… отсюда… не выбраться...', 12, 0, 100, 0, 0, 35325, 71846, 0, 0, 213738, 'Капитан Халу\'кал to Player');
INSERT INTO `creature_text` VALUES (69482, 3, 0, '%s мирно храпит.', 16, 0, 100, 0, 0, 35320, 71841, 229750, 229750, 0, 'Капитан Халу\'кал to Player');
INSERT INTO `creature_text` VALUES (69482, 4, 0, 'Хи-хи-хи, щекотно! Все, надоело с вами играть!', 12, 0, 100, 0, 0, 35323, 71844, 13109, 13109, 0, 'Капитан Халу\'кал');
INSERT INTO `creature_text` VALUES (69483, 2, 0, 'Никто… не….', 12, 0, 100, 0, 0, 35456, 71865, 0, 0, 213738, 'Ху\'шэнь, страж врат to Player');
INSERT INTO `creature_text` VALUES (69483, 3, 0, 'Хватит прятаться, жалкие твари!', 14, 0, 100, 0, 0, 35447, 71854, 431516, 431516, 140691, 'Ху\'шэнь, страж врат to Player');
INSERT INTO `creature_text` VALUES (69483, 4, 0, 'Ну все, вам конец!', 12, 0, 100, 0, 0, 35454, 91009, 14000, 14000, 140543, 'Ху\'шэнь, страж врат to Ху\'шэнь, страж врат');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Без твоей помощи я погибла бы в болоте, герой. Я обязана тебе жизнью.', 12, 0, 100, 0, 0, 0, 71666, 10000, 10000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Видишь центральную башню? Молнии бьют прямо в нее. Этас думает, что буря исходит из самого сердца крепости.', 12, 0, 100, 0, 0, 0, 71649, 43000, 43000, 0, 'Капитан разведчиков Эльсия to Кровавый дракондор Похитителей Солнца');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Вход перекрыт. Ломайте преграду!', 12, 0, 100, 0, 0, 0, 71658, 22000, 22000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Герой, ты в порядке?', 12, 0, 100, 0, 0, 0, 71709, 3000, 3000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Держись!', 12, 0, 100, 0, 0, 0, 99010, 80000, 80000, 0, 'Капитан разведчиков Эльсия to Кровавый дракондор Похитителей Солнца');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Захватим город – это наш единственный шанс выжить.', 12, 0, 100, 0, 0, 0, 71657, 5000, 5000, 0, 'Капитан разведчиков Эльсия to Зеб\'тульский заклинатель зверей');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Клянусь Cолнечным Колодцем! Небеса отвернулись от нас. Здесь мы в опасности! ', 12, 0, 100, 0, 0, 0, 71651, 69000, 69000, 0, 'Капитан разведчиков Эльсия to Кровавый дракондор Похитителей Солнца');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Мы нашли способ, как уберечь наших дракондоров от молний. Но лучше лететь разомкнутым строем, на всякий случай.', 12, 0, 100, 0, 0, 0, 71647, 17000, 17000, 0, 'Капитан разведчиков Эльсия to Кровавый дракондор Похитителей Солнца');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Наконец-то! Преграды больше нет! У нас получилось!', 12, 0, 100, 0, 0, 0, 71665, 6000, 6000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Не нравится мне этот тролль. Будь осторожнее.', 12, 0, 100, 0, 0, 0, 71659, 117000, 117000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Они отступают. Нельзя дать им уйти!', 12, 0, 100, 0, 0, 0, 71656, 2000, 2000, 0, 'Капитан разведчиков Эльсия to Зеб\'тульский заклинатель зверей');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Они устроили нам западню. Наши воины рассеяны по всему болоту, мы здесь сами по себе, в логове врага!', 12, 0, 100, 0, 0, 0, 71654, 7000, 7000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Плохи наши дела.', 12, 0, 100, 0, 0, 0, 71655, 23000, 23000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Прежде чем штурмовать дворец Властелина Грома, мы должны подготовить плацдарм.', 12, 0, 100, 0, 0, 0, 71771, 52000, 52000, 0, 'Капитан разведчиков Эльсия to Кровавый дракондор Похитителей Солнца');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Следопыты уже ждут нас впереди. Наша главная задача… Осторожно!', 12, 0, 100, 0, 0, 0, 71650, 60000, 60000, 0, 'Капитан разведчиков Эльсия to Кровавый дракондор Похитителей Солнца');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Смотри! Наши набеги на город и склепы приносят свои плоды. Могу и зандалары трепещут перед нашей мощью.', 12, 0, 100, 0, 0, 0, 71648, 32000, 32000, 0, 'Капитан разведчиков Эльсия to Кровавый дракондор Похитителей Солнца');
INSERT INTO `creature_text` VALUES (69485, 3, 0, 'Сюда, герой!', 12, 0, 100, 0, 0, 0, 75826, 14000, 14000, 0, 'Капитан разведчиков Эльсия to Кровавый дракондор Похитителей Солнца');
INSERT INTO `creature_text` VALUES (69501, 3, 0, 'За мной, герой! Мы должны идти вперед, а твои союзники нас прикроют.', 14, 0, 100, 14, 0, 35891, 71770, 134109, 134109, 0, 'Тажань Чжу to 208');
INSERT INTO `creature_text` VALUES (69501, 4, 0, 'Прикончи его!', 14, 0, 100, 0, 0, 35904, 124394, 42719, 42719, 0, 'Тажань Чжу to Шань-Бу');
INSERT INTO `creature_text` VALUES (69527, 4, 0, 'Ты $gслаб, приятель:слаба, подруга;.', 12, 0, 100, 0, 0, 0, 72146, 16156, 16156, 0, 'Зандаларский щитоносец to Тажань Чжу');
INSERT INTO `creature_text` VALUES (69530, 3, 0, 'Тролли, защищайте меня! Я почти закончил.', 14, 0, 100, 0, 0, 35771, 71775, 33016, 33016, 0, 'Шань-Бу to Player');
INSERT INTO `creature_text` VALUES (69534, 2, 0, 'I ... no ... Emperor! Emperor, I let you down!', 14, 0, 100, 0, 0, 36934, 71889, 0, 0, 213738, 'Шань-Бу to Player');
INSERT INTO `creature_text` VALUES (69534, 4, 0, 'Ваши крики услаждают слух повелителя!', 14, 0, 100, 0, 0, 35777, 71881, 17203, 17203, 138862, 'Шань-Бу');
INSERT INTO `creature_text` VALUES (69544, 3, 0, 'Таоши указывает на капитана Халу\'кала, а затем проводит пальцем по своему горлу.', 41, 0, 100, 25, 0, 0, 72265, 2640, 2640, 140639, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (69559, 3, 0, 'А ты $gдумал:думала;, тебе удастся пролететь тут так, чтобы никто тебя не заметил?!', 14, 0, 100, 25, 0, 0, 71738, 8000, 8000, 0, 'Повелитель зверей Хораки to Player');
INSERT INTO `creature_text` VALUES (69559, 3, 0, 'Глупцы! Я покажу вам, как это делается!', 14, 0, 100, 25, 0, 0, 72593, 0, 0, 0, 'Повелитель зверей Хораки to Player');
INSERT INTO `creature_text` VALUES (69559, 3, 0, 'Приготовься к смерти, $gчужак:чужеземка;!', 14, 0, 100, 53, 0, 0, 71740, 18000, 18000, 0, 'Повелитель зверей Хораки to Player');
INSERT INTO `creature_text` VALUES (69559, 3, 0, 'Что ж, ты там, куда $gстремился:стремилась;. Как жаль, что ты ничего здесь не увидишь, поскольку жить тебе осталось недолго!', 14, 0, 100, 5, 0, 0, 71739, 13000, 13000, 0, 'Повелитель зверей Хораки to Player');
INSERT INTO `creature_text` VALUES (69559, 4, 0, 'Хораки выпускает в небо тучу стрел!', 41, 0, 100, 0, 0, 0, 71742, 14000, 14000, 0, 'Повелитель зверей Хораки');
INSERT INTO `creature_text` VALUES (69559, 4, 0, 'Хораки готовится сделать замораживающий выстрел!', 41, 0, 100, 0, 0, 0, 71741, 10000, 10000, 0, 'Повелитель зверей Хораки');
INSERT INTO `creature_text` VALUES (69562, 3, 0, 'I am the son of thunder!', 14, 0, 100, 0, 0, 35732, 71355, 91734, 91734, 138776, 'Налак to 208');
INSERT INTO `creature_text` VALUES (69565, 3, 0, 'I will cover the flanks. Collect the explosives and take them to the wall. Faster!', 12, 0, 100, 1, 0, 0, 71639, 80000, 80000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (69565, 3, 0, 'Взрывчатка заложена, герой. Можешь лично взорвать эту стену.', 12, 0, 100, 1, 0, 0, 71737, 111000, 111000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (69565, 3, 0, 'Здесь творится настоящий хаос! Осада началась, но взрывчатку у стены так и не заложили.', 12, 0, 100, 5, 0, 0, 71638, 73000, 73000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (69565, 3, 0, 'Мой лорд, вам нельзя быть так близко к линии фронта.', 12, 0, 100, 5, 0, 0, 72681, 67000, 67000, 0, 'Капитан разведчиков Эльсия');
INSERT INTO `creature_text` VALUES (69565, 3, 0, 'Сюда! Зандалары отступают!', 14, 0, 100, 5, 0, 0, 72718, 154000, 154000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (69565, 3, 0, 'Что... что они сделали с этим троллем?', 12, 0, 100, 5, 0, 0, 72680, 2000, 2000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (69615, 3, 0, 'Они убили моего мужа!', 14, 0, 100, 5, 0, 35932, 71943, 142438, 142438, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (69616, 3, 0, 'Лицо Джайны смягчается.', 16, 0, 100, 0, 0, 0, 71947, 161453, 161453, 0, 'Леди Джайна Праудмур to Player');
INSERT INTO `creature_text` VALUES (69617, 1, 0, 'Проблемы будем решать по одной. Сейчас главное – зандалары!', 14, 0, 100, 0, 0, 35643, 71765, 0, 0, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (69617, 3, 0, 'Но?', 12, 0, 100, 547, 0, 35639, 71757, 19609, 19609, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (69619, 3, 0, 'Этас Похититель Солнца неловко мнется.', 16, 0, 100, 432, 0, 0, 71929, 82422, 82422, 0, 'Верховный маг Этас Похититель Солнца to Player');
INSERT INTO `creature_text` VALUES (69620, 3, 0, 'Но лорд?!', 14, 0, 100, 547, 0, 36387, 71940, 135578, 135578, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (69621, 3, 0, 'Но господин… вы тяжело ранены!', 12, 0, 100, 5, 0, 35821, 71923, 290109, 290109, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (69626, 0, 0, 'The eggs in one of the lower nests begin to hatch!', 16, 0, 100, 0, 0, 0, 71991, 0, 0, 0, 'Инкубатор to Инкубатор');
INSERT INTO `creature_text` VALUES (69626, 3, 0, 'The eggs in one of the upper nests begin to hatch!', 16, 0, 100, 0, 0, 0, 71996, 0, 0, 0, 'Инкубатор to Инкубатор');
INSERT INTO `creature_text` VALUES (69652, 0, 0, 'Закрыть ворота! Зандалары не удержали стену.', 14, 0, 100, 0, 0, 35768, 72728, 0, 0, 0, 'Шань-Бу');
INSERT INTO `creature_text` VALUES (69702, 1, 0, 'Мощь… ее нельзя так использовать! Вы играете с силами, суть которых не понимаете!', 14, 0, 100, 0, 0, 35722, 72612, 0, 0, 0, 'Ритуалист Рутан to Сфера анимы');
INSERT INTO `creature_text` VALUES (69702, 2, 0, 'Вы подписали себе приговор. Он на свободе…', 14, 0, 100, 0, 0, 35724, 72613, 0, 0, 0, 'Ритуалист Аутон');
INSERT INTO `creature_text` VALUES (69702, 3, 0, 'Остановите их! Ритуал не должен прерываться!', 14, 0, 100, 0, 0, 35725, 72611, 1550429, 1550429, 0, 'Ритуалист Аутон to Player');
INSERT INTO `creature_text` VALUES (69712, 0, 0, '|TInterface\\Icons\\ability_hunter_animalhandler.blp:20|tЦзи-Кунь |cFFFF0000|Hspell:138923|h[каркает]|h|r, поражая противников мощными звуковыми волнами!', 41, 0, 100, 0, 0, 0, 72812, 0, 0, 0, 'Цзи-Кунь to 0');
INSERT INTO `creature_text` VALUES (69712, 1, 0, '|TInterface\\Icons\\ability_hunter_pet_dragonhawk.blp:20|t|cFFFF0000|Hspell:134380|h[Перья]|h|r Цзи-Кунь летят во все стороны!', 41, 0, 100, 0, 0, 0, 72811, 0, 0, 0, 'Цзи-Кунь to Цзи-Кунь');
INSERT INTO `creature_text` VALUES (69712, 2, 0, '|TInterface\\Icons\\ability_druid_galewinds.blp:20|tПри помощи своих крыльев Цзи-Кунь совершает массовое |cFFFF0000|Hspell:134370|h[Сбрасывание]|h|r!', 41, 0, 100, 0, 0, 0, 72810, 0, 0, 0, 'Цзи-Кунь to Цзи-Кунь');
INSERT INTO `creature_text` VALUES (69717, 3, 0, 'Ладно, Таоши. Если все согласны, то приступаем.', 12, 0, 100, 1, 0, 35916, 71814, 1557000, 1557000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (69717, 3, 0, 'Но это неважно. Идея Таоши слишком опасна.', 12, 0, 100, 274, 0, 35915, 71809, 1520000, 1520000, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` VALUES (69742, 3, 0, 'А! Герой! Мы как раз говорили о тебе.', 12, 0, 100, 1, 0, 35669, 71817, 1515000, 1515000, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (69742, 3, 0, 'Мне нравится безрассудная смелость этого плана. Если мы прорвемся к пристани, то лишим могу дальнейшей поддержки со стороны зандаларов. ', 12, 0, 100, 1, 0, 35670, 71818, 1525000, 1525000, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (69742, 3, 0, 'Согласитесь, лорд Чжу. Только решительные действия приносят победу.', 12, 0, 100, 1, 0, 35671, 71820, 1551000, 1551000, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (69743, 3, 0, 'Проснись, Ху’шэнь! Ты отвечаешь за пристань. Никто не должен пройти за ворота.', 14, 0, 100, 463, 0, 35781, 71833, 72313, 72313, 0, 'Шань-Бу to Player');
INSERT INTO `creature_text` VALUES (69745, 3, 0, 'Лорд-регент пожелает выслушать полный доклад. Возвращайся в лагерь, тебе надо отдохнуть.', 12, 0, 100, 0, 0, 36395, 71873, 22782, 22782, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (69755, 0, 0, 'Уничтожьте их! Пусть боги выпьют их кровь!', 14, 0, 100, 0, 0, 35994, 72308, 0, 0, 0, 'Бог войны Ал\'чукла');
INSERT INTO `creature_text` VALUES (69755, 1, 0, 'Идите сюда! Узрите дар Властелина Грома!', 14, 0, 100, 1, 0, 0, 72723, 0, 0, 0, 'Бог войны Ал\'чукла to Player');
INSERT INTO `creature_text` VALUES (69755, 3, 0, 'Духи дают вам силу!', 14, 0, 100, 0, 0, 35993, 72307, 44000, 44000, 0, 'Бог войны Ал\'чукла');
INSERT INTO `creature_text` VALUES (69755, 3, 0, 'Заставьте их истекать кровью!', 14, 0, 100, 0, 0, 35995, 72309, 24000, 24000, 0, 'Бог войны Ал\'чукла');
INSERT INTO `creature_text` VALUES (69755, 4, 0, 'Вы будете наказаны! Ваша жизнь ничто, ничто!', 14, 0, 100, 1, 0, 0, 72726, 28000, 28000, 0, 'Бог войны Ал\'чукла');
INSERT INTO `creature_text` VALUES (69755, 4, 0, 'За Зандалар!', 14, 0, 100, 1, 0, 0, 72727, 36000, 36000, 0, 'Бог войны Ал\'чукла');
INSERT INTO `creature_text` VALUES (69755, 4, 0, 'Слишком много… духам не сдержать его силы…', 14, 0, 100, 1, 0, 0, 72725, 24000, 24000, 0, 'Бог войны Ал\'чукла');
INSERT INTO `creature_text` VALUES (69755, 4, 0, 'Я был избран! Моя кровь вскипает от его мощи!', 14, 0, 100, 1, 0, 0, 72724, 8000, 8000, 0, 'Бог войны Ал\'чукла to Player');
INSERT INTO `creature_text` VALUES (69756, 1, 0, 'Сфера взрывается!', 41, 0, 100, 0, 0, 0, 72051, 0, 0, 0, 'Сфера анимы');
INSERT INTO `creature_text` VALUES (69771, 3, 0, 'Я дам вам ледяную силу северных ветров!', 14, 0, 100, 0, 0, 29024, 73071, 452000, 452000, 0, 'Поблекнувшая проекция Сюэня to World Trigger');
INSERT INTO `creature_text` VALUES (69774, 3, 0, 'Я могу дать тебе немногое, но это все, что у меня есть. Пусть это поможет тебе в решающий час.', 14, 0, 100, 0, 0, 29620, 59968, 157000, 157000, 0, 'Поблекнувшая проекция Чи-Цзи to World Trigger');
INSERT INTO `creature_text` VALUES (69777, 3, 0, 'Громите этих варваров, не сдерживая ярости!', 14, 0, 100, 0, 0, 31044, 59694, 396000, 396000, 0, 'Поблекнувшая проекция Нюцзао to World Trigger');
INSERT INTO `creature_text` VALUES (69779, 3, 0, 'Тебе не придется сражаться в одиночку! Обрушь мой гнев на этих чужаков!', 14, 0, 100, 0, 0, 28560, 59679, 433000, 433000, 0, 'Поблекнувшая проекция Юй-лун to World Trigger');
INSERT INTO `creature_text` VALUES (69782, 0, 0, 'Властелин Грома завладел силой хранителя титанов. Мы должны отобрать ее!', 12, 0, 100, 1, 0, 0, 72375, 0, 0, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (69782, 0, 0, 'Принеси мне его черное, жестокое сердце. Мы окажем Пандарии услугу, а силу титанов обратим на благо всего Азерота.', 12, 0, 100, 25, 0, 0, 72376, 0, 0, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (69782, 3, 0, '... Why did you put this chip?', 12, 0, 100, 6, 0, 0, 72321, 292000, 8185000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (69782, 3, 0, 'Bring me his cruel, black heart. We will be doing the Pandaren a favor, and the powers that we liberate will once again be put to the service of Azeroth.', 12, 0, 100, 25, 0, 0, 72376, 35000, 35000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (69782, 3, 0, 'I have been looking forward to your return, champion.', 12, 0, 100, 1, 0, 0, 72374, 15000, 1266000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (69782, 3, 0, 'Something tells me our friend has returned triumphant.', 12, 0, 100, 6, 0, 0, 72378, 11000, 11000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (69782, 3, 0, 'The Thunder King took within himself the powers of a Titan keeper. We must take them back!', 12, 0, 100, 1, 0, 0, 72375, 26000, 26000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (69782, 3, 0, 'А я что тебе говорил? Мало кто из смертных обладает такой смелостью.', 12, 0, 100, 1, 0, 0, 72335, 2443000, 2443000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (69782, 3, 0, 'Полагаю, наш ордынский друг не станет сталкивать тебя со скалы.', 12, 0, 100, 6, 0, 0, 72339, 292000, 292000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (69782, 3, 0, 'Тун! Это наш друг?', 12, 0, 100, 0, 0, 0, 72332, 2437000, 2437000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (69782, 3, 0, 'Что-то мне говорит, что наш друг вернулся с победой.', 12, 0, 100, 6, 0, 36063, 72378, 223000, 223000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (69782, 3, 0, 'Чушь какая! Зачем тогда вообще играть?!', 12, 0, 100, 1, 0, 0, 72323, 300000, 8192000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (69782, 3, 0, 'Я ждал твоего возвращения, герой.', 12, 0, 100, 1, 0, 0, 72374, 8364000, 8364000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (69810, 3, 0, 'Эффектное появление, ничего не скажешь!', 12, 0, 100, 0, 0, 0, 72054, 13000, 13000, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (69824, 3, 0, 'Кузня Грома наша! Мы не пожалеем жизни ради ее защиты!', 14, 0, 100, 0, 0, 0, 73973, 0, 0, 0, 'Пиромант Шань\'цзэ');
INSERT INTO `creature_text` VALUES (69828, 3, 0, '|cFFFF2222[Наковальня грома]|r перегружена! Активируйте ее, чтобы |cFFFF2222|Hspell:138834|h[Разрядка]|h|r высвободила ее энергию!', 41, 0, 100, 0, 0, 0, 73026, 80000, 80000, 0, 'Небесный кузнец to Небесный кузнец');
INSERT INTO `creature_text` VALUES (69828, 4, 0, '|cFFFF2222[Наковальня грома]|r перегружена! Щелкните по ней, чтобы использовать |cFFFF2222|Hspell:138834|h[Раскат грома]|h|r!', 41, 0, 100, 0, 0, 0, 73003, 3000, 223000, 0, 'Небесный кузнец to Небесный кузнец');
INSERT INTO `creature_text` VALUES (69828, 4, 0, 'Небесный кузнец может умереть!', 41, 0, 100, 0, 0, 0, 72534, 8000, 8000, 0, 'Небесный кузнец');
INSERT INTO `creature_text` VALUES (69833, 3, 0, 'Обнаружены лазутчики! Необходимо защитить Кузню Грома!', 14, 0, 100, 0, 0, 0, 73972, 0, 0, 0, 'Воин Шань\'цзэ');
INSERT INTO `creature_text` VALUES (69835, 0, 0, 'Кузня Грома наша! Мы не пожалеем жизни ради ее защиты!', 14, 0, 100, 0, 0, 0, 73973, 0, 0, 0, 'Полководец Шань\'цзэ ');
INSERT INTO `creature_text` VALUES (69835, 0, 0, 'Обнаружены лазутчики! Необходимо защитить Кузню Грома!', 14, 0, 100, 0, 0, 0, 73972, 0, 0, 0, 'Полководец Шань\'цзэ ');
INSERT INTO `creature_text` VALUES (69837, 4, 0, 'Небесный защитник может умереть!', 41, 0, 100, 0, 0, 0, 72535, 60000, 248000, 0, 'Небесный защитник');
INSERT INTO `creature_text` VALUES (69837, 4, 0, 'Небесный защитник создает область |cFFFF2222|Hspell:140068|h[исцеляющей энергии]|h|r!', 41, 0, 100, 0, 0, 0, 72536, 14000, 38000, 0, 'Небесный защитник');
INSERT INTO `creature_text` VALUES (69837, 4, 0, 'Небесный защитник тяжело ранен и больше не способен сражаться!', 41, 0, 100, 0, 0, 0, 73001, 146000, 249000, 0, 'Небесный защитник');
INSERT INTO `creature_text` VALUES (69865, 1, 0, 'Я буду рад этой смерти.', 12, 0, 100, 0, 0, 0, 72150, 0, 0, 0, 'Зандаларский щитоносец to Капитан Похитителей Солнца');
INSERT INTO `creature_text` VALUES (69917, 3, 0, 'Все! Врата открыты!', 14, 0, 100, 0, 0, 0, 72071, 13000, 13000, 0, 'Таоши to Надсмотрщик Шиаксу');
INSERT INTO `creature_text` VALUES (69917, 3, 0, 'Еще немного! Я почти закончила!', 14, 0, 100, 0, 0, 0, 72070, 62000, 62000, 0, 'Таоши to Надсмотрщик Шиаксу');
INSERT INTO `creature_text` VALUES (69917, 3, 0, 'Из общего у нас с вами только враг, Правящий лорд.', 12, 0, 100, 2, 0, 0, 0, 96000, 96000, 0, 'Таоши to Лор\'темар Терон');
INSERT INTO `creature_text` VALUES (69917, 3, 0, 'Отвлеки повелителя рабов, а я открою замок.', 12, 0, 100, 0, 0, 0, 72068, 158000, 158000, 0, 'Таоши');
INSERT INTO `creature_text` VALUES (69917, 3, 0, 'Перейдем к делу.', 12, 0, 100, 396, 0, 0, 112256, 3000, 3000, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (69917, 3, 0, 'Со всем уважением – это проклятое оружие надо уничтожить!', 12, 0, 100, 5, 0, 0, 72416, 109000, 109000, 0, 'Таоши to Лор\'темар Терон');
INSERT INTO `creature_text` VALUES (69917, 3, 0, 'Ступай осторожно. Мы почти у ворот.', 12, 0, 100, 0, 0, 0, 72067, 155000, 155000, 0, 'Таоши');
INSERT INTO `creature_text` VALUES (69917, 3, 0, 'Этот замок оказался сложнее, чем я думала.', 14, 0, 100, 0, 0, 0, 72069, 2000, 2000, 0, 'Таоши to Надсмотрщик Шиаксу');
INSERT INTO `creature_text` VALUES (69917, 4, 0, 'I kill quickly and quietly, but your methods have their advantages.', 12, 0, 100, 0, 0, 0, 72064, 25000, 25000, 0, 'Таоши');
INSERT INTO `creature_text` VALUES (69917, 4, 0, 'Мы в самом сердце боевой машины могу.', 12, 0, 100, 0, 0, 0, 72061, 2000, 2000, 0, 'Таоши');
INSERT INTO `creature_text` VALUES (69917, 4, 0, 'Рабы, повсюду рабы. Теперь ты знаешь, против чего мы сражаемся.', 12, 0, 100, 0, 0, 0, 72058, 6000, 6000, 0, 'Таоши');
INSERT INTO `creature_text` VALUES (69917, 4, 0, 'Шадо-Пан нападает из тени, поэтому наши враги боятся темноты.', 12, 0, 100, 0, 0, 0, 72059, 10000, 17000, 0, 'Таоши');
INSERT INTO `creature_text` VALUES (69917, 4, 0, 'Я многое повидала, но при виде жестокости могу моя кровь стынет в жилах.', 12, 0, 100, 0, 0, 0, 72060, 11000, 11000, 0, 'Таоши');
INSERT INTO `creature_text` VALUES (69923, 1, 0, 'Что такое? Опять восстание?', 14, 0, 100, 0, 0, 0, 72430, 0, 0, 0, 'Надсмотрщик Шиаксу');
INSERT INTO `creature_text` VALUES (69923, 4, 0, 'Вы будете… наказаны…', 14, 0, 100, 0, 0, 0, 72436, 12000, 12000, 0, 'Надсмотрщик Шиаксу to Player');
INSERT INTO `creature_text` VALUES (69923, 4, 0, 'На колени перед хозяином!', 14, 0, 100, 0, 0, 0, 72435, 15000, 15000, 0, 'Надсмотрщик Шиаксу');
INSERT INTO `creature_text` VALUES (69923, 4, 0, 'Склонитесь предо мной!', 14, 0, 100, 0, 0, 0, 95127, 6000, 6000, 0, 'Надсмотрщик Шиаксу');
INSERT INTO `creature_text` VALUES (69923, 4, 0, 'Убегайте от оков, чтобы порвать цепи!', 41, 0, 100, 0, 0, 0, 73033, 15000, 15000, 0, 'Надсмотрщик Шиаксу');
INSERT INTO `creature_text` VALUES (69951, 1, 0, 'Вас преследует |3-6(%s)!', 42, 0, 100, 0, 0, 0, 72258, 0, 0, 0, 'Багровый след to Player');
INSERT INTO `creature_text` VALUES (69964, 0, 0, 'БОЙСЯ! Теперь я овладел всеми энергиями Скверны этого мира! Демоническая сила, которой я управляю… невероятна! Безгранична! Я ВСЕМОГУЩ!', 12, 0, 100, 0, 0, 0, 72452, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (69964, 1, 0, 'Думаешь, я слишком далеко зашел? Нет такого понятия. Мрачная Жатва поработит самых могущественных демонов и уничтожит всех, кто станет противостоять ей – в этом мире и на Азероте! И ТЫ хочешь остановить меня, кроха?', 12, 0, 100, 0, 0, 0, 72453, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (69964, 2, 0, 'Ну-ну, попытайся.', 12, 0, 100, 0, 0, 0, 72457, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (69964, 3, 0, 'Ха! Твои слабые попытки ранить меня никуда не годятся. Хочешь посмотреть на одного из моих новых питомцев?', 12, 0, 100, 0, 0, 0, 72456, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (69964, 4, 0, 'Разрушение у искоренителей в крови. Огромная сила и мощь делают их непобедимыми.', 12, 0, 100, 0, 0, 0, 72458, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (69964, 5, 0, 'ЧТО?! Вот теперь я зол!', 12, 0, 100, 0, 0, 0, 72459, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (69964, 6, 0, 'Часики тикают.', 12, 0, 100, 0, 0, 0, 72470, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (69964, 7, 0, 'Боль усиливается.', 12, 0, 100, 0, 0, 0, 72479, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (69964, 8, 0, 'Всем будет править хаос.', 12, 0, 100, 0, 0, 0, 72468, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (69964, 9, 0, '|TInterfaceIconsability_warlock_chaosbolt.blp:20|t%s начинает читать заклинание |cFFFF0000|Hspell:138559|h[Стрела Хаоса]|h|r!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (69964, 10, 0, 'МОЩЬ КАТАКЛИЗМА!', 12, 0, 100, 0, 0, 0, 72465, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (69964, 11, 0, '|TInterfaceIconsachievement_zone_cataclysm.blp:20|t%s начинает призывать чудовищный |cFFFF0000|Hspell:138564|h[Катаклизм]|h|r!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (69964, 12, 0, 'Один бес – хорошо, два – лучше, а ШЕСТЬДЕСЯТ – просто замечательно!', 12, 0, 100, 0, 0, 0, 72494, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (69964, 13, 0, 'Ха-ха-ха… у тебя была надежда на победу? Теперь меня НИЧТО не сможет убить, жалкое смертное существо!', 12, 0, 100, 0, 0, 0, 72609, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (69964, 14, 0, 'А теперь твоя сказка с грустным концом закончится!', 12, 0, 100, 0, 0, 0, 72501, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (69964, 15, 0, 'Джубека?! Что ты…', 12, 0, 100, 0, 0, 0, 72502, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (69964, 16, 0, 'АААААААААА!', 12, 0, 100, 0, 0, 0, 72503, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (70023, 4, 0, 'А ты не $gпытался:пыталась; в этом НЕ СТОЯТЬ?! Получалось бы куда лучше!', 12, 0, 100, 0, 0, 0, 72279, 3000, 3000, 138629, 'Надоедливый бес to Надоедливый бес');
INSERT INTO `creature_text` VALUES (70023, 4, 0, 'Когда у тебя остается мало маны, используй жезл. У тебя ВЕДЬ ЕСТЬ жезл, правда?', 12, 0, 100, 0, 0, 0, 72280, 13000, 13000, 138629, 'Надоедливый бес to Надоедливый бес');
INSERT INTO `creature_text` VALUES (70023, 4, 0, 'Надо спрыгнуть со стены Черного Храма и посмотреть, что там внизу.', 12, 0, 100, 0, 0, 0, 72267, 19000, 19000, 138629, 'Надоедливый бес to Надоедливый бес');
INSERT INTO `creature_text` VALUES (70023, 4, 0, 'Тебе уже говорили не сутулиться? Знаешь, как важно стоять прямо во время произнесения заклинаний?', 12, 0, 100, 0, 0, 0, 72272, 4000, 4000, 138629, 'Надоедливый бес to Надоедливый бес');
INSERT INTO `creature_text` VALUES (70032, 3, 0, 'Это цикл жизни. Свет создал троллей, тролли создали динозавров, динозавры едят людей, троллям достается Азерот.', 12, 0, 100, 1, 0, 0, 72297, 12000, 12000, 0, 'Доктор Ян Голдблум');
INSERT INTO `creature_text` VALUES (70035, 3, 0, 'Когда мы вернем себе контроль над экспедицией, все будет хорошо...', 12, 0, 100, 1, 0, 0, 72289, 5781, 5781, 0, 'Аттен Хэмлок');
INSERT INTO `creature_text` VALUES (70042, 3, 0, 'Агент Шадо-Пан встретит тебя у шахты. Удачи!', 14, 0, 100, 0, 0, 0, 72429, 31000, 31000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (70042, 3, 0, 'Найденное тобой существо не боится ударов молний, защищающих остров.', 12, 0, 100, 0, 0, 0, 72425, 8000, 8000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (70042, 3, 0, 'Оседлаешь его?', 12, 0, 100, 0, 0, 0, 72426, 13000, 13000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (70042, 3, 0, 'Отлично! Когда ты поднимешься в небо, зандалары бросятся за тобой.', 12, 0, 100, 0, 0, 0, 72427, 20000, 20000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (70042, 3, 0, 'Тебе придется сражаться в воздухе, перепрыгивай с одного летуна на другого!', 12, 0, 100, 0, 0, 0, 72428, 26000, 26000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (70055, 0, 0, 'Наступайте! Наша судьба решается сегодня!', 14, 0, 100, 0, 0, 35627, 72303, 0, 0, 0, 'Лор\'темар Терон');
INSERT INTO `creature_text` VALUES (70055, 0, 0, 'Прикрывайте друг друга! Не отступать.', 14, 0, 100, 0, 0, 35625, 72301, 0, 0, 0, 'Лор\'темар Терон');
INSERT INTO `creature_text` VALUES (70055, 3, 0, 'Вздор. Мое место здесь.', 12, 0, 100, 0, 0, 0, 72738, 197000, 197000, 0, 'Лор\'темар Терон');
INSERT INTO `creature_text` VALUES (70055, 3, 0, 'Все за тотемы! Враг попытается контратаковать, но мы будем готовы.', 12, 0, 100, 0, 0, 0, 72740, 207000, 207000, 0, 'Лор\'темар Терон');
INSERT INTO `creature_text` VALUES (70055, 3, 0, 'Прекрасно! Вперед! Тесните их! Никого не щадить!', 14, 0, 100, 0, 0, 0, 72677, 71000, 71000, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (70055, 3, 0, 'Следопыты, ко мне! Мы прорвали их строй!', 12, 0, 100, 0, 0, 0, 72737, 185000, 185000, 0, 'Лор\'темар Терон');
INSERT INTO `creature_text` VALUES (70055, 3, 0, 'Убейте их! Пусть они страшатся нашей мощи! ', 14, 0, 100, 0, 0, 35629, 72305, 0, 58000, 0, 'Лор\'темар Терон');
INSERT INTO `creature_text` VALUES (70056, 3, 0, 'С силой Властелина Грома зандалары возродятся!', 14, 0, 100, 53, 0, 35400, 72017, 105, 105, 0, 'Душелов Гара\'джал to Ледяной страж из племени Драккари');
INSERT INTO `creature_text` VALUES (70061, 0, 0, 'Вас преследует молния!', 41, 0, 100, 0, 0, 0, 72668, 0, 0, 0, 'Кузня Грома to Кузня Грома');
INSERT INTO `creature_text` VALUES (70068, 1, 0, 'Склонись перед судом.', 14, 0, 100, 0, 0, 34976, 74172, 0, 0, 142875, 'Агу\'ру');
INSERT INTO `creature_text` VALUES (70068, 3, 0, 'You are now being judged.', 14, 0, 100, 0, 0, 0, 74172, 1000, 1000, 0, 'Ahoo\'ru');
INSERT INTO `creature_text` VALUES (70068, 4, 0, 'I control ... Light!', 14, 0, 100, 0, 0, 0, 74173, 9829, 9829, 138811, 'Агу\'ру');
INSERT INTO `creature_text` VALUES (70068, 4, 0, 'I wield... the LIGHT!', 14, 0, 100, 0, 0, 0, 74173, 5000, 5000, 0, 'Ahoo\'ru');
INSERT INTO `creature_text` VALUES (70068, 4, 0, 'The Light is my shield.', 14, 0, 100, 0, 0, 0, 74175, 69000, 69000, 0, 'Ahoo\'ru');
INSERT INTO `creature_text` VALUES (70068, 4, 0, 'You fought... with... valor...', 14, 0, 100, 0, 0, 0, 74176, 38000, 38000, 0, 'Ahoo\'ru to Player');
INSERT INTO `creature_text` VALUES (70068, 4, 0, 'Свет станет моим щитом.', 14, 0, 100, 0, 0, 34755, 74175, 73063, 73063, 135945, 'Агу\'ру');
INSERT INTO `creature_text` VALUES (70070, 0, 0, 'Обнаружены лазутчики! Необходимо защитить Кузню Грома!', 14, 0, 100, 0, 0, 0, 73972, 0, 0, 0, 'Направляющий молнии Шань\'цзэ');
INSERT INTO `creature_text` VALUES (70072, 0, 0, '%s поглощает вашу способность \"Порабощение демона\".', 41, 0, 100, 0, 0, 0, 72505, 0, 0, 0, 'Охотник Скверны to 0');
INSERT INTO `creature_text` VALUES (70074, 4, 0, 'Главный кузнец Вул\'кон берет в руки свой молот и начинает размахивать им с огромной силой!', 41, 0, 100, 0, 0, 0, 72996, 36000, 36000, 0, 'Главный кузнец Вул\'кон');
INSERT INTO `creature_text` VALUES (70074, 4, 0, 'Главный кузнец Вул\'кон берет в руки свой посох и пропускает сквозь него разряды молнии!', 41, 0, 100, 0, 0, 0, 72995, 19000, 19000, 0, 'Главный кузнец Вул\'кон');
INSERT INTO `creature_text` VALUES (70081, 4, 0, 'For the Light!', 14, 0, 100, 0, 0, 0, 138560, 3000, 3000, 0, 'Avenging Angel to Player');
INSERT INTO `creature_text` VALUES (70081, 4, 0, 'Во имя Света!', 14, 0, 100, 0, 0, 0, 138560, 16765, 16765, 0, 'Ангел мести to 42717');
INSERT INTO `creature_text` VALUES (70099, 4, 0, 'Защитник Шадо-Пан создал |cFFFF2222|Hspell:132744|h[Исцеляющую сферу]|h|r!', 41, 0, 100, 0, 0, 0, 73015, 5000, 5000, 0, 'Защитник Шадо-Пан');
INSERT INTO `creature_text` VALUES (70099, 4, 0, 'Кузня в безопасности, нам нужно возвращаться. Удачи тебе в твоих дальнейших приключениях.', 12, 0, 100, 2, 0, 0, 72800, 47000, 47000, 0, 'Защитник Шадо-Пан to Гневион');
INSERT INTO `creature_text` VALUES (70099, 4, 0, 'Спасибо за помощь, герой. Мы поможем тебе защититься от могу, чем только сможем.', 14, 0, 100, 0, 0, 0, 72419, 27000, 27000, 0, 'Защитник Шадо-Пан to Гневион');
INSERT INTO `creature_text` VALUES (70100, 3, 0, '\"Выявлена аномалия.\" Какая еще аномалия?', 12, 0, 100, 6, 0, 36040, 72570, 435000, 435000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Бросай его прямо перед тем, как зверь умрет – ни секундой раньше, ни секундой позже!', 12, 0, 100, 6, 0, 0, 72583, 903000, 903000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Будь я дворфом, я бы стал нажимать все кнопки подряд. Но мы поступим иначе.', 12, 0, 100, 0, 0, 36028, 72558, 393000, 393000, 0, 'Гневион to Воин Шадо-Пан');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Все! Мы сделали это! Выковали настоящий шедевр!', 12, 0, 100, 1, 0, 0, 72579, 877000, 877000, 0, 'Гневион');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'И постарайся не умереть.', 12, 0, 100, 0, 0, 36055, 72585, 1405000, 1405000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Как интересно! Эта кузня выстроена поверх древнего механизма титанов. Ах, Лэй Шэнь, ты умен!', 12, 0, 100, 0, 0, 36027, 72557, 382000, 382000, 0, 'Гневион to Воин Шадо-Пан');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Копье выковано. Скорее брось его в ша!', 12, 0, 100, 22, 0, 36411, 72663, 864000, 864000, 0, 'Гневион');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Мне надо кое-что сделать на материке. Когда закончишь дело, приходи на Сокрытую лестницу.', 12, 0, 100, 25, 0, 0, 72584, 911000, 911000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Могу пользовались лишь частью открытых для них возможностей. Из них мало кто обладал мудростью Властелина Грома.', 12, 0, 100, 0, 0, 36039, 72569, 412000, 412000, 0, 'Гневион to Воин Шадо-Пан');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Может, если увеличить подачу энергии, проблема с ша решится? Давай попробуем.', 12, 0, 100, 0, 0, 36406, 72658, 694000, 694000, 0, 'Гневион to Небесный кузнец');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Наковальни заряжены. Теперь их можно использовать против ша, но каждую лишь один раз!', 12, 0, 100, 22, 0, 36410, 72662, 728000, 728000, 0, 'Гневион to Небесный кузнец');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Нет, нет, так не пойдет. Уходим отсюда!', 14, 0, 100, 0, 0, 36048, 72578, 668000, 668000, 0, 'Гневион to Исчадье ша');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Но учти – как только копье вонзится в его шкуру, тебе придется несладко.', 12, 0, 100, 274, 0, 0, 72582, 896000, 896000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Ой, кажется, я поторопился. Сила ша растет! Уничтожь его!', 12, 0, 100, 274, 0, 36408, 72660, 713000, 713000, 0, 'Гневион to Небесный кузнец');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Отлично! Получилось.', 12, 0, 100, 1, 0, 36407, 72659, 704000, 704000, 0, 'Гневион to Небесный кузнец');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Прекрасно. Материалы готовы. Следуй за мной.', 12, 0, 100, 4, 0, 36026, 72556, 347000, 347000, 0, 'Гневион to Воин Шадо-Пан');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Придется драться, если нас заметят.', 12, 0, 100, 0, 0, 36023, 72553, 70000, 70000, 0, 'Гневион');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Принесенный тобой триллий надо сначала очистить.', 12, 0, 100, 1, 0, 36021, 72551, 8000, 8000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Смотри-ка… технологии титанов притягивают ша как магнит!', 12, 0, 100, 1, 0, 36041, 72571, 441000, 441000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Тебе надо пронзить этим копьем бьющееся сердце Повелителя Гроз.', 12, 0, 100, 1, 0, 0, 72581, 889000, 889000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Тут мы закончили, но оружие еще надо закалить.', 12, 0, 100, 1, 0, 0, 72580, 885000, 885000, 0, 'Гневион');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Ты же не слишком сердишься, правда?', 12, 0, 100, 0, 0, 36409, 72661, 722000, 722000, 0, 'Гневион to Небесный кузнец');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Ты только посмотри… Лэй Шэню легко было выстроить империю, ибо он обладал силой создавать миры.', 12, 0, 100, 0, 0, 36030, 72560, 401000, 401000, 0, 'Гневион to Воин Шадо-Пан');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Ты уже наверняка знаешь, как с ними справиться. Убери их отсюда!', 12, 0, 100, 396, 0, 36042, 72572, 447000, 447000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Эта плавильня вполне подойдет. Мы усилим металл электрическим зарядом.', 12, 0, 100, 1, 0, 36405, 72657, 64000, 64000, 0, 'Гневион');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'Эта плавильня вполне подойдет. Помоги мне с формами.', 12, 0, 100, 25, 0, 36022, 72552, 16000, 16000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (70105, 3, 0, 'А я пропущу тебя через свои кинжалы!', 12, 0, 100, 45, 0, 35879, 71857, 53687, 53687, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (70126, 0, 0, 'I won a Brawler\'s Guild invitation just now! I\'ll surely be rank 8 by tonight! I sure hope I don\'t lose my ticket before I get there.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'WillyWilder - ICText');
INSERT INTO `creature_text` VALUES (70126, 1, 0, 'I guess I wasn\'t ready to be a brawler.', 12, 0, 100, 1, 0, 0, 72441, 0, 0, 0, 'WillyWilder - DeathText');
INSERT INTO `creature_text` VALUES (70155, 3, 0, 'Ахаха, давай все сюда, радость моя!$b$bЖуль снова будет при делах! Заведу новый лоток, понаделаю амулетов – и в путь!$b$bХочешь быть быстрее, выше, сильнее? Тогда не связывайся с груммелями, всегда обращайся только к старине Жулю!$b$bУ меня чудесные амулеты, дешевле только даром!', 12, 0, 100, 0, 0, 0, 72537, 10717000, 10717000, 0, 'Жуль');
INSERT INTO `creature_text` VALUES (70166, 3, 0, 'I did only what you yourself asked me before, Canretad. It’s your own fault.', 12, 0, 100, 1, 0, 0, 72496, 4000, 4000, 139186, 'Джубека Тенелом to Канретад Чернодрев');
INSERT INTO `creature_text` VALUES (70166, 3, 0, 'А теперь иди и используй полученные знания. Если когда-нибудь тебе захочется избавиться от энергии Скверны или вновь ощутить силу Канретада, ищи меня в Алтаре Проклятия в Долине Призрачной Луны. Там я буду присматривать за своим пленником.', 12, 0, 100, 0, 0, 0, 72499, 25000, 25000, 0, 'Джубека Тенелом to Канретад Чернодрев');
INSERT INTO `creature_text` VALUES (70166, 3, 0, 'Канретад настолько погрузился в энергию Скверны, что она полностью подчинила его. Но лишь глоток его силы не должен повредить. Испей ее.', 12, 0, 100, 0, 0, 0, 72498, 17000, 17000, 0, 'Джубека Тенелом to Канретад Чернодрев');
INSERT INTO `creature_text` VALUES (70166, 3, 0, 'НЕТ!', 12, 0, 100, 1, 0, 0, 135514, 0, 0, 0, 'Джубека Тенелом to Канретад Чернодрев');
INSERT INTO `creature_text` VALUES (70170, 3, 0, 'Остановись! Не здесь и не сейчас!', 12, 0, 100, 0, 0, 0, 72939, 354000, 354000, 0, 'Служитель солнца Дезко');
INSERT INTO `creature_text` VALUES (70202, 0, 0, 'THEY ARE NO MATCH FOR MANCHU!', 14, 0, 100, 0, 0, 35717, 72161, 0, 0, 0, 'Manchu');
INSERT INTO `creature_text` VALUES (70202, 4, 0, 'ВЫ МАНЧУ НЕ РОВНЯ!', 14, 0, 100, 0, 0, 0, 72161, 4000, 4000, 0, 'Манчу');
INSERT INTO `creature_text` VALUES (70205, 4, 0, 'Да, хозяин! Моя жизнь принадлежит тебе!', 14, 0, 100, 0, 0, 36017, 72160, 6032, 6032, 0, 'Вэйшэн');
INSERT INTO `creature_text` VALUES (70212, 1, 0, '|TInterface\\Icons\\inv_misc_volatilefire:20|tВас обжигают |cFFF00000|Hspell:139822|h[угли]|h|r!', 42, 0, 100, 0, 0, 0, 72966, 0, 0, 0, 'Пылающая голова to Player');
