INSERT INTO `creature_text` VALUES (70217, 0, 1, 'Thank you for the timely rescue, hero.', 12, 0, 50, 0, 0, 0, 64355, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (70217, 0, 2, 'Thank you.', 12, 0, 50, 0, 0, 0, 111253, 0, 0, 0, 'Smart Text');
