INSERT INTO `creature_text` VALUES (70228, 4, 0, 'Слияние ша begins to cast a spell begins to cast a spell', 41, 0, 100, 0, 0, 0, 72676, 5000, 136000, 0, 'Слияние ша');
INSERT INTO `creature_text` VALUES (70228, 4, 0, 'Слияние ша изгоняет небесного защитника. Защищайтесь!', 41, 0, 100, 0, 0, 0, 73000, 88000, 124000, 0, 'Слияние ша');
INSERT INTO `creature_text` VALUES (70229, 1, 0, '|TInterface\\Icons\\inv_misc_volatilefire:20|tВас обжигают |cFFF00000|Hspell:139822|h[угли]|h|r!', 42, 0, 100, 0, 0, 0, 72966, 0, 0, 0, 'Пылающая голова to Player');
INSERT INTO `creature_text` VALUES (70235, 4, 0, '|TInterface\\Icons\\spell_frost_piercing chill:20|tНа вас движется |cFFF00000|Hspell:139866|h[ледяной поток]|h|r!', 42, 0, 100, 0, 0, 0, 72965, 29562, 29562, 0, 'Ледяная голова to Player');
INSERT INTO `creature_text` VALUES (70283, 3, 0, 'Кузня Грома наполнила окружающее пространство электрическими разрядами. Подбегите к ней, чтобы |cFFFF2222|Hspell:139397|h[Перегрузка]|h|r пополнила ваши силы!', 41, 0, 100, 0, 0, 0, 72741, 30000, 30000, 0, 'Кузня Грома');
INSERT INTO `creature_text` VALUES (70297, 3, 0, 'Слушай меня, герой. Ночью с помощью прирученного тобой зверя мы долетим до пристани.', 12, 0, 100, 1, 0, 35849, 71811, 1536000, 1536000, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (70297, 3, 0, 'Спасибо, лорд Чжу. Я не подведу вас.', 12, 0, 100, 2, 0, 35851, 71815, 1563000, 1563000, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (70297, 3, 0, 'Там мы взорвем зандаларский корабль и, воспользовавшись суматохой, проскользнем во двор и откроем ворота изнутри.', 12, 0, 100, 1, 0, 35850, 71812, 1543000, 1543000, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (70309, 1, 0, 'Значит, ты $gрешил:решила; умереть?!', 12, 0, 100, 0, 0, 0, 71208, 0, 0, 0, 'Страж Кирин-Тора to Player');
INSERT INTO `creature_text` VALUES (70309, 3, 0, 'Вот тебе за Терамор!', 12, 0, 100, 0, 0, 0, 73024, 21000, 21000, 0, 'Страж Кирин-Тора to Player');
INSERT INTO `creature_text` VALUES (70320, 3, 0, 'Мы с трудом вытащили тебя оттуда. Тебе повезло!', 12, 0, 100, 396, 0, 0, 72983, 4000, 4000, 0, 'Таоши');
INSERT INTO `creature_text` VALUES (70337, 1, 0, 'Тебе не уйти из джунглей $gживым:живой;.', 12, 0, 100, 0, 0, 0, 71208, 0, 0, 0, 'Защитник Кирин-Тора to Player');
INSERT INTO `creature_text` VALUES (70347, 3, 0, 'Я брошу твой труп в яму с гнилью!', 14, 0, 100, 0, 0, 0, 72747, 3000, 3000, 0, 'Кромсатель Гнилого Клинка');
INSERT INTO `creature_text` VALUES (70348, 0, 0, 'Разорвать могу на кусочки!', 14, 0, 100, 0, 0, 0, 72746, 0, 0, 0, 'Застрельщик Гнилого Клинка');
INSERT INTO `creature_text` VALUES (70358, 3, 0, 'А, вот и ты! Правящий лорд желает видеть тебя. Отправляемся?', 12, 0, 100, 0, 0, 0, 0, 25000, 25000, 0, 'Капитан разведчиков Эльсия to Player');
INSERT INTO `creature_text` VALUES (70371, 3, 0, 'Война, которую наш вождь ведет в Калимдоре и Пандарии, разрывает Орду на части.', 12, 0, 100, 1, 0, 0, 72792, 23000, 23000, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (70371, 3, 0, 'Если дело дойдет до восстания, мы должны быть готовы.', 12, 0, 100, 1, 0, 0, 72795, 48000, 48000, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (70371, 3, 0, 'Мы должны сразиться с Властелином Грома – это дело чести. А Гаррош слишком занят Крепостью Покорителей, чтобы нам помогать.', 12, 0, 100, 1, 0, 0, 72793, 29000, 29000, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (70371, 3, 0, 'Победив этого врага, мы завладеем источником его великой силы. Мы сами завладеем ей, чтобы нам было что противопоставить... \"вождю\".', 12, 0, 100, 1, 0, 0, 72794, 38000, 38000, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (70371, 3, 0, 'Твоя верность Орде выдержала много испытаний, герой. Думаю, что могу тебе довериться.', 12, 0, 100, 1, 0, 0, 72791, 9000, 9000, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (70371, 3, 0, 'Этас, оставь нас ненадолго.', 12, 0, 100, 1, 0, 0, 72790, 3000, 3000, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (70437, 3, 0, 'Но на этом ваше вторжение заканчивается. Никому не позволено входить в мою крепость!', 14, 0, 100, 0, 0, 35588, 72508, 8203, 8203, 0, 'Лэй Шэнь to Player');
INSERT INTO `creature_text` VALUES (70476, 3, 0, 'Корона, что я дал тебе, будет твоим третьим глазом. Теперь я буду видеть то, что видишь ты. Вместе мы раскроем тайны титанов.', 12, 0, 100, 273, 0, 0, 72358, 51000, 51000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (70476, 3, 0, 'Мы знаем, что могу – создания титанов. Но многие вопросы остались без ответа. Зачем они были здесь? Почему в Пандарии?', 12, 0, 100, 1, 0, 0, 72356, 29000, 29000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (70476, 3, 0, 'Что могу должны были защитить? Или уничтожить? Нам не хватает последних кусочков головоломки, и ты поможешь мне найти их!', 12, 0, 100, 6, 0, 0, 72357, 40000, 40000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (70476, 3, 0, 'Я буду пристально следить за тобой!', 12, 0, 100, 0, 0, 0, 72359, 67000, 67000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (70480, 3, 0, 'В пандаренских легендах говорится о тиране Лэй Шэне, \"Властелине Грома\". Двенадцать тысяч лет назад он основал империю могу.', 12, 0, 100, 0, 0, 0, 72781, 3000, 3000, 0, 'Капитан разведчиков Эльсия to Кровавый дракондор Похитителей Солнца');
INSERT INTO `creature_text` VALUES (70480, 3, 0, 'На границе империи Властелин Грома построил величественную крепость. Скоро мы ее увидим.', 12, 0, 100, 0, 0, 0, 72783, 22000, 22000, 0, 'Капитан разведчиков Эльсия to Кровавый дракондор Похитителей Солнца');
INSERT INTO `creature_text` VALUES (70480, 3, 0, 'Он объединил их, поработил другие расы Пандарии и силами рабов построил великое государство.', 12, 0, 100, 0, 0, 0, 72782, 12000, 12000, 0, 'Капитан разведчиков Эльсия to Кровавый дракондор Похитителей Солнца');
INSERT INTO `creature_text` VALUES (70500, 3, 0, '|cFFFF2222[Копье молний]|r заряжено. Прикончите слияние ша!', 41, 0, 100, 0, 0, 0, 73027, 177000, 177000, 0, 'Lightning Spear Float Stalker');
INSERT INTO `creature_text` VALUES (70516, 0, 0, 'You\'ve found another stone! What have we learned? The \"curse of flesh\" made the mogu mortal. Ah - but later, they found a way to reverse the process. A clever race.', 15, 0, 100, 0, 0, 0, 72362, 0, 0, 0, 'Image of Wrathion to Player');
INSERT INTO `creature_text` VALUES (70516, 3, 0, 'The mogu served a Titan keeper. His name was \"Ra.\" In the mogu tongue, \"Ra-den\" means \"Master Ra.\" Fascinating! I must know more!', 15, 0, 100, 0, 0, 0, 72363, 9000, 9000, 0, 'Image of Wrathion to Player');
INSERT INTO `creature_text` VALUES (70516, 3, 0, 'А-а! Вот и последний кусочек головоломки! Властелин Грома присвоил силу Ра-дена, силу самих титанов. Он объединил могу в империю и пытался продолжить дело титанов. Понятно, почему он считал себя богом!', 15, 0, 100, 0, 0, 36423, 72372, 9000, 9000, 0, 'Проекция Гневиона to Player');
INSERT INTO `creature_text` VALUES (70516, 3, 0, 'Быстрее, возвращайся на Сокрытую лестницу. Нам нужно встретиться.', 15, 0, 100, 0, 0, 36424, 72373, 29000, 29000, 0, 'Проекция Гневиона to Player');
INSERT INTO `creature_text` VALUES (70516, 3, 0, 'Вот и еще один кусочек мозаики! Вырезан самими могу. Они были в отчаянии – тысячу лет они жили без приказов хозяина, а потом и их тела начали превращаться в плоть! Среди них начались разногласия.', 15, 0, 100, 0, 0, 0, 72370, 9000, 9000, 0, 'Проекция Гневиона to Player');
INSERT INTO `creature_text` VALUES (70516, 3, 0, 'Еще один рунный камень! Что мы узнаем из него? Проклятие плоти сделало могу смертными. Да, но потом они нашли способ обратить процесс. Они умны.', 15, 0, 100, 0, 0, 36413, 72362, 10000, 10000, 0, 'Проекция Гневиона to Player');
INSERT INTO `creature_text` VALUES (70516, 3, 0, 'Картина начинает проясняться. Могу охраняли Дол по воле их хозяев – титанов. Но от кого? Древние боги все еще представляли угрозу?', 15, 0, 100, 0, 0, 0, 72367, 9000, 9000, 0, 'Проекция Гневиона to Player');
INSERT INTO `creature_text` VALUES (70516, 3, 0, 'Могу служили хранителю титанов по имени \"Ра\". На языке титанов \"Ра-ден\" значит \"господин Ра\". Потрясающе! Надо узнать больше! ', 15, 0, 100, 0, 0, 0, 72363, 9000, 9000, 0, 'Проекция Гневиона to Player');
INSERT INTO `creature_text` VALUES (70516, 3, 0, 'Судя по написанному, могу и их хозяина отправили в Пандарию по следам великой тьмы… ах, тут не хватает фрагментов. Надо их найти.', 15, 0, 100, 0, 0, 0, 72364, 9000, 9000, 0, 'Проекция Гневиона to Player');
INSERT INTO `creature_text` VALUES (70516, 3, 0, 'Судя по этому рунному камню, бездушные могу были созданы титанами из камня. Так я и думал.', 15, 0, 100, 0, 0, 36412, 72361, 9000, 9000, 0, 'Проекция Гневиона to Player');
INSERT INTO `creature_text` VALUES (70516, 3, 0, 'Хмм… Этот рунный камень рассказывает о войнах могу. Они разделились на враждующие кланы. Но один из вождей оказался амбициознее других. Лэй Шэнь, Властелин Грома, решил, что может занять место спящего хозяина, хранителя титанов.', 15, 0, 100, 0, 0, 0, 72371, 9000, 9000, 0, 'Проекция Гневиона to Player');
INSERT INTO `creature_text` VALUES (70516, 3, 0, 'Этот фрагмент говорит о \"крылатых легионах И\'Шараджа\". Слуги древнего бога? Может, это богомолы?', 15, 0, 100, 0, 0, 0, 72368, 9000, 9000, 0, 'Проекция Гневиона to Player');
INSERT INTO `creature_text` VALUES (70542, 3, 0, 'The sound of grinding metal reverberates through the halls as the lock on the Nexus doors releases.', 41, 0, 100, 0, 0, 0, 76794, 1327000, 1327000, 0, 'Invisible Stalker to Player');
INSERT INTO `creature_text` VALUES (70542, 3, 0, 'По залам разносится металлический скрежет, и дверь Перекрестка открывается. ', 41, 0, 100, 0, 0, 0, 76794, 550000, 550000, 0, 'Невидимый охотник to Player');
INSERT INTO `creature_text` VALUES (70566, 1, 0, '|TInterface\\Icons\\spell_arcane_mindmastery.blp:20|t %s просыпается с чудовищным |cFFFF0000|Hspell:140432|h[похмельем]|h|r!', 41, 0, 100, 0, 0, 0, 73028, 0, 0, 140432, 'Захмелевший ветроступ to Капитан Халу\'кал');
INSERT INTO `creature_text` VALUES (70589, 4, 0, 'Пещерный буритель зовет ближайших сланцевых ловцов на помощь.', 16, 0, 100, 0, 0, 0, 73040, 13107, 13107, 0, 'Пещерный буритель');
INSERT INTO `creature_text` VALUES (70616, 3, 0, 'I’m archaeological of your skull, $gguy: girl ;!', 14, 0, 100, 0, 0, 0, 74159, 4000, 4000, 0, 'Мингус Коппс to Player');
INSERT INTO `creature_text` VALUES (70616, 4, 0, 'Похорони меня с моей лопатой...\nСерьёзно...', 14, 0, 100, 0, 0, 0, 74162, 96000, 96000, 0, 'Мингус Коппс to Player');
INSERT INTO `creature_text` VALUES (70659, 1, 0, 'Приготовься встретить свою судьбу.', 14, 0, 100, 0, 0, 0, 74177, 0, 0, 0, 'Гексос');
INSERT INTO `creature_text` VALUES (70659, 2, 0, 'Грк-к-к!', 14, 0, 100, 0, 0, 34758, 74179, 0, 0, 0, 'Гексос to Player');
INSERT INTO `creature_text` VALUES (70666, 1, 0, 'Пари как дракондор, жаль как скорпид!', 14, 0, 100, 22, 0, 0, 74152, 0, 0, 0, 'Ти\'тар');
INSERT INTO `creature_text` VALUES (70666, 4, 0, 'Какая невероятная скорость...', 14, 0, 100, 22, 0, 0, 74157, 39000, 39000, 0, 'Ти\'тар to Player');
INSERT INTO `creature_text` VALUES (70677, 3, 0, 'Ба-бах!', 12, 0, 100, 0, 0, 0, 131363, 0, 0, 0, 'Мастер Бум-Бум to 0');
INSERT INTO `creature_text` VALUES (70677, 4, 0, 'Time for the Boom Box!', 14, 0, 100, 0, 0, 0, 60595, 11000, 28000, 0, 'Master Boom Boom');
INSERT INTO `creature_text` VALUES (70677, 4, 0, 'Бугалу от бум-бум!', 12, 0, 100, 0, 0, 0, 60592, 12000, 12000, 0, 'Мастер Бум-Бум to Мастер Бум-Бум');
INSERT INTO `creature_text` VALUES (70677, 4, 0, 'Пора для Бум-коробка!', 14, 0, 100, 0, 0, 0, 60595, 6000, 19000, 0, 'Мастер Бум-Бум');
INSERT INTO `creature_text` VALUES (70677, 4, 0, 'Тебе бум-бум!', 12, 0, 100, 0, 0, 0, 60593, 2000, 23000, 0, 'Мастер Бум-Бум to Мастер Бум-Бум');
INSERT INTO `creature_text` VALUES (70678, 0, 0, 'Глупый тюк груммелей!', 14, 0, 100, 0, 0, 34341, 73115, 0, 0, 0, 'Дедушка Скрипучая Флейта to 0');
INSERT INTO `creature_text` VALUES (70678, 1, 0, 'Флейта-удачка!', 14, 0, 100, 0, 0, 0, 73110, 0, 0, 0, 'Дедушка Скрипучая Флейта to 0');
INSERT INTO `creature_text` VALUES (70678, 2, 0, 'Должно быть, у тебя хорошая удачка!', 14, 0, 100, 0, 0, 34758, 73114, 0, 0, 0, 'Дедушка Скрипучая Флейта to Player');
INSERT INTO `creature_text` VALUES (70694, 1, 0, '%s едва стоит, пошатываясь.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Большущий Бумище');
INSERT INTO `creature_text` VALUES (70733, 1, 0, 'Мои курицы... В атаку!', 14, 0, 100, 22, 0, 0, 73149, 0, 0, 0, 'Тайсон Сандерс');
INSERT INTO `creature_text` VALUES (70733, 2, 0, 'Гаук-к!', 14, 0, 100, 71, 0, 34758, 74181, 0, 0, 0, 'Тайсон Сандерс to Player');
INSERT INTO `creature_text` VALUES (70740, 1, 0, 'Подарок, значит, хочешь? Да у меня даже ноги не ходят!', 14, 0, 100, 0, 0, 34760, 73331, 0, 0, 142875, 'Блескотрон-3000');
INSERT INTO `creature_text` VALUES (70740, 3, 0, 'ELEC... TRICITY... GREATEST... WEAKNESS...', 14, 0, 100, 0, 0, 0, 73332, 76000, 76000, 0, 'Blingtron 3000 to Blingtron 2000');
INSERT INTO `creature_text` VALUES (70740, 3, 0, 'Ya want a present?! WELL, MY LEGS DON\'T EVEN WORK!!!', 14, 0, 100, 0, 0, 0, 73331, 1000, 1000, 0, 'Blingtron 3000');
INSERT INTO `creature_text` VALUES (70740, 4, 0, '%s, кажется, плачет.', 16, 0, 100, 18, 0, 34759, 74165, 99000, 99000, 135945, 'Блескотрон-3000');
INSERT INTO `creature_text` VALUES (70740, 4, 0, 'My bling is blung...', 14, 0, 100, 0, 0, 0, 74158, 20000, 20000, 0, 'Blingtron 3000 to Player');
INSERT INTO `creature_text` VALUES (70740, 4, 0, 'Who\'s the obsolete one now?', 14, 0, 100, 18, 0, 0, 74164, 72000, 72000, 0, 'Blingtron 3000');
INSERT INTO `creature_text` VALUES (70740, 4, 0, 'Мои глаза-бусинки рассыпались...', 14, 0, 100, 0, 0, 34758, 74158, 98531, 98531, 0, 'Блескотрон-3000 to Player');
INSERT INTO `creature_text` VALUES (70740, 4, 0, 'Ну и кто тут устаревший?', 14, 0, 100, 18, 0, 34759, 74164, 99000, 99000, 135945, 'Блескотрон-3000');
INSERT INTO `creature_text` VALUES (70740, 4, 0, 'Электри… чество… моя самая… большая слабость…', 14, 0, 100, 18, 0, 34755, 73332, 106266, 106266, 135945, 'Блескотрон-3000 to Блескотрон-2000');
INSERT INTO `creature_text` VALUES (70748, 1, 0, 'Гр-р-р!', 14, 0, 100, 0, 0, 0, 73288, 0, 0, 0, 'Аррр');
INSERT INTO `creature_text` VALUES (70749, 4, 0, '|TInterface\\Icons\\inv_inscription_scroll.blp:20|t %s выбирает |cFFFF0000бумагу|r! Вы |cFF00FF00выиграли|r!', 42, 0, 100, 0, 0, 0, 73245, 9000, 9000, 0, 'Ро-Шамбо to Player');
INSERT INTO `creature_text` VALUES (70794, 3, 0, 'Я найду тебя...', 14, 0, 100, 18, 0, 0, 74166, 1000, 1000, 0, 'Слепой герой');
INSERT INTO `creature_text` VALUES (70794, 4, 0, 'Звуки смерти... услада для моего слуха...', 14, 0, 100, 18, 0, 34755, 74167, 12000, 12000, 0, 'Слепой герой');
INSERT INTO `creature_text` VALUES (70794, 4, 0, 'Что это за ощущение? Это... свет?..', 14, 0, 100, 18, 0, 0, 74168, 42000, 42000, 0, 'Слепой герой to Player');
INSERT INTO `creature_text` VALUES (70824, 1, 0, 'Пламенный Гнев скоро |cFFFF2222|Hspell:141456|h[взорвется]|h|r!', 41, 0, 100, 0, 0, 0, 73320, 0, 0, 0, 'Пламенный Гнев');
INSERT INTO `creature_text` VALUES (70859, 3, 0, 'Don\'t worry. This hero will convince Ga\'nar to return. Your brother values strength, and my friend possesses that in abundance.', 12, 0, 100, 396, 0, 46371, 74290, 9403156, 9403156, 0, 'Thrall to Player');
INSERT INTO `creature_text` VALUES (70859, 3, 0, 'Thrall grins at his father\'s words.', 16, 0, 100, 273, 0, 0, 79136, 66281, 66281, 0, 'Thrall to Player');
INSERT INTO `creature_text` VALUES (70859, 3, 0, 'Не волнуйся. Я знаю, кто убедит Га\'нара вернуться. Твой брат уважает силу, а у героя ее в избытке.', 12, 0, 100, 396, 0, 46371, 74290, 22511, 22511, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (70859, 3, 0, 'Тралл ухмыляется, слыша слова отца.', 16, 0, 100, 273, 0, 0, 79136, 17331, 17331, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (70860, 0, 0, 'Увидишь Дреку… скажи ей, что я скоро буду дома.', 12, 0, 100, 1, 0, 45202, 79135, 0, 0, 0, 'Дуротан to Player');
INSERT INTO `creature_text` VALUES (70860, 1, 0, 'Ga\'nar\'s impatience could tear the Frostwolves apart. It is just like my brother to put his own needs above the clan.', 12, 0, 100, 396, 0, 45234, 74288, 0, 0, 0, 'Durotan to Player');
INSERT INTO `creature_text` VALUES (70861, 3, 0, 'Chieftain.', 12, 0, 100, 2, 0, 42439, 74327, 111797, 111797, 0, 'Ga\'nar to Player');
INSERT INTO `creature_text` VALUES (70861, 3, 0, 'Family looks to its own! Only I cared to avenge our father\'s death!', 12, 0, 100, 15, 0, 42439, 74323, 52094, 52094, 0, 'Ga\'nar to Player');
INSERT INTO `creature_text` VALUES (70861, 3, 0, 'I have slain the sons of the Iron Wolf. He drowns in the blood of his own.', 12, 0, 100, 1, 0, 44070, 74317, 9937, 9937, 0, 'Ga\'nar to Player');
INSERT INTO `creature_text` VALUES (70861, 3, 0, 'I killed the sons of the Iron Wolf. Let him drown in the blood of his kin.', 12, 0, 100, 1, 0, 44070, 74317, 8658, 8658, 0, 'Га\'нар to Player');
INSERT INTO `creature_text` VALUES (70861, 3, 0, 'You are no Chieftain of mine, brother. By my rights as second son, by the justice I have dealt today, I declare mak\'gora.', 12, 0, 100, 274, 0, 44071, 74319, 33890, 33890, 0, 'Ga\'nar to Player');
INSERT INTO `creature_text` VALUES (70861, 3, 0, 'Семья заботится друг о друге. Но лишь я хотел отомстить за отца!', 12, 0, 100, 15, 0, 44072, 74323, 52073, 52073, 0, 'Га\'нар to Player');
INSERT INTO `creature_text` VALUES (70861, 3, 0, 'Ты мне не вождь. По праву второго сына и восстановленной мной справедливости я объявляю мак\'гора.', 12, 0, 100, 274, 0, 44071, 74319, 33806, 33806, 0, 'Га\'нар to Player');
INSERT INTO `creature_text` VALUES (70878, 0, 0, 'Твой отец будет занят, оплакивая тебя.', 12, 0, 100, 61, 0, 44100, 77107, 0, 0, 0, 'Га\'нар to Player');
INSERT INTO `creature_text` VALUES (70878, 1, 0, 'Сообщите остальным: мы нашли сыновей Железного Волка. Лок\'тар огар!', 14, 0, 100, 53, 0, 44101, 77104, 0, 0, 0, 'Га\'нар to Player');
INSERT INTO `creature_text` VALUES (70878, 2, 0, 'Твой отец вряд ли сможет опознать то, что от тебя останется.', 12, 0, 100, 0, 0, 44088, 74366, 0, 0, 0, 'Га\'нар to Player');
INSERT INTO `creature_text` VALUES (70878, 3, 0, 'Продержитесь, пока не придет подкрепление. А мы тем временем вызовем на бой столько врагов, сколько сможем.', 12, 0, 100, 0, 0, 44089, 74462, 0, 0, 0, 'Га\'нар to Player');
INSERT INTO `creature_text` VALUES (70878, 4, 0, 'Громоборцы! Я Га\'нар из клана Северных Волков! Сын Гарада!', 14, 0, 100, 0, 0, 44090, 74396, 0, 0, 0, 'Га\'нар to Player');
INSERT INTO `creature_text` VALUES (70878, 5, 0, 'Ваш вождь убил моего отца, и я пришел за его сыновьями. Кто из вас осмелится остановить меня?', 14, 0, 100, 0, 0, 44091, 74397, 0, 0, 0, 'Га\'нар to Player');
INSERT INTO `creature_text` VALUES (70900, 3, 0, 'Wraaugh!', 14, 0, 100, 0, 0, 0, 74463, 1609, 1609, 0, 'Thunderlord Crag-Leaper to Player');
INSERT INTO `creature_text` VALUES (70900, 3, 0, 'Р-р-а-а-ар!', 14, 0, 100, 0, 0, 0, 74463, 1653, 1653, 144102, 'Скальный прыгун из клана Громоборцев to Player');
INSERT INTO `creature_text` VALUES (70941, 0, 0, 'Хаток, сын Железного Волка! Я пришел за тобой.', 14, 0, 100, 53, 0, 44104, 77215, 0, 0, 0, 'Га\'нар to Player');
INSERT INTO `creature_text` VALUES (70941, 1, 0, 'Убейте гронна! И найдите этого труса!', 14, 0, 100, 0, 0, 44105, 74262, 0, 0, 0, 'Га\'нар to Га\'нар');
INSERT INTO `creature_text` VALUES (70941, 2, 0, 'Хаток! Тебе не скрыться от меня!', 14, 0, 100, 22, 0, 44106, 77240, 0, 0, 0, 'Га\'нар to Груллок');
INSERT INTO `creature_text` VALUES (70956, 3, 0, 'Мы что-то обнаружили внизу, что-то ОГРОМНОЕ, и тут скалы напали на нас. Все как с ума посходили, ужас какой-то!', 12, 0, 100, 0, 0, 0, 73118, 6000, 6000, 0, 'Гриззл Скрипетол to Player');
INSERT INTO `creature_text` VALUES (70956, 3, 0, 'Пожалуйста, остановите это!', 12, 0, 100, 20, 0, 0, 73119, 15000, 15000, 0, 'Гриззл Скрипетол to Player');
INSERT INTO `creature_text` VALUES (70956, 3, 0, 'Слава законам физики, вы здесь!', 12, 0, 100, 5, 0, 0, 73242, 3000, 3000, 0, 'Гриззл Скрипетол to Player');
INSERT INTO `creature_text` VALUES (70959, 4, 0, 'Прислужники, уничтожьте этих букашек!', 14, 0, 100, 0, 0, 0, 73933, 26000, 26000, 0, 'Уртардж Разрушитель');
INSERT INTO `creature_text` VALUES (70966, 3, 0, 'Hey there! I\'m a Hydrolizer model two, dash zero or, H2-0 for short.', 12, 0, 100, 0, 0, 0, 73425, 1000, 1000, 0, 'H2-0 to Player');
INSERT INTO `creature_text` VALUES (70966, 3, 0, 'I am equipped with an additional reservoir of water, but you will have to get more fluid if you want to hydrolyze several times.', 15, 0, 100, 0, 0, 0, 73427, 8156, 8156, 0, 'H2-0 to Player');
INSERT INTO `creature_text` VALUES (70966, 3, 0, 'I\'ve been sent here to help you keep Anthracite under control.', 12, 0, 100, 0, 0, 0, 73426, 3000, 3000, 0, 'H2-0 to Player');
INSERT INTO `creature_text` VALUES (70966, 3, 0, 'Привет! Я гидролизатор модели два-дефис-ноль, иначе – Н2-0.', 12, 0, 100, 0, 0, 0, 73425, 1296, 1296, 0, 'H2-0 to Player');
INSERT INTO `creature_text` VALUES (70966, 3, 0, 'Я здесь, чтобы помочь тебе удерживать Антрацита под контролем.', 12, 0, 100, 0, 0, 0, 73426, 3344, 3344, 0, 'H2-0 to Player');
INSERT INTO `creature_text` VALUES (70983, 1, 0, 'Замечена враждебная цель.', 14, 0, 100, 0, 0, 0, 73121, 0, 0, 0, 'Двухфазный робот Т440');
INSERT INTO `creature_text` VALUES (70983, 4, 0, 'Вертикальные стабилизаторы неактивны. Питание отключено.', 14, 0, 100, 0, 0, 34758, 73122, 15640, 15640, 0, 'Двухфазный робот Т440 to Player');
INSERT INTO `creature_text` VALUES (70983, 4, 0, 'Цель недоступна. Завершение боевых действий.', 14, 0, 100, 0, 0, 34755, 73123, 106000, 106000, 0, 'Двухфазный робот Т440');
INSERT INTO `creature_text` VALUES (70984, 3, 0, 'Вол\'джин жив!', 14, 0, 100, 22, 0, 0, 73984, 1000, 1000, 0, 'Верный служитель Вол\'джина');
INSERT INTO `creature_text` VALUES (70989, 0, 0, 'I want to check if you are capable of being smart and agile in a moment of mortal danger.', 12, 0, 100, 2, 0, 0, 73754, 0, 0, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` VALUES (70989, 0, 0, 'Мудр тот, кто знает, что правильно. А иногда правильно бездействовать.', 12, 0, 100, 2, 0, 0, 73751, 0, 0, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` VALUES (70989, 0, 0, 'Подойди, и я благословлю тебя.', 12, 0, 100, 2, 0, 0, 73753, 0, 0, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` VALUES (70989, 0, 0, 'Поняв это, поймешь и суть мудрости.', 12, 0, 100, 2, 0, 0, 73752, 0, 0, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` VALUES (70989, 0, 0, 'Слова истинного потомка Аспектов, вечно стремящихся изменить мир.', 12, 0, 100, 2, 0, 0, 73750, 0, 0, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` VALUES (70989, 3, 0, 'Ah, the dragon child and the hero. Together you will achieve great things.', 12, 0, 100, 2, 0, 0, 73748, 35000, 35000, 0, 'Yu\'lon to Player');
INSERT INTO `creature_text` VALUES (70989, 3, 0, 'Do you see? Power without vision causes only random chaos and destruction.', 12, 0, 100, 2, 0, 0, 73783, 361000, 361000, 0, 'Yu\'lon to Player');
INSERT INTO `creature_text` VALUES (70989, 3, 0, 'Spoken like a true child of the Aspects. Always seeking to change the world.', 12, 0, 100, 2, 0, 0, 73750, 51000, 51000, 0, 'Yu\'lon to Player');
INSERT INTO `creature_text` VALUES (70989, 3, 0, 'Tell me, dragon child: What is the nature of wisdom?', 12, 0, 100, 2, 0, 0, 73749, 42000, 42000, 0, 'Yu\'lon to Player');
INSERT INTO `creature_text` VALUES (70989, 3, 0, 'А, юный дракон и герой. Вместе вы совершите великие дела.', 12, 0, 100, 2, 0, 0, 73748, 0, 0, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` VALUES (70989, 3, 0, 'Видишь? Слепая сила несет лишь хаос и разрушение.', 12, 0, 100, 2, 0, 0, 73783, 153000, 153000, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` VALUES (70989, 3, 0, 'Прошу тебя, юный принц, запомни этот урок.', 12, 0, 100, 2, 0, 0, 73882, 160000, 160000, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` VALUES (70989, 3, 0, 'Скажи мне, юный дракон: в чем суть мудрости?', 12, 0, 100, 2, 0, 0, 73749, 6000, 6000, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` VALUES (71002, 0, 0, 'Никто не сможет нам противостоять!', 12, 0, 100, 0, 0, 0, 74218, 0, 0, 0, 'Кор\'кронский поджигатель to Player');
INSERT INTO `creature_text` VALUES (71003, 0, 0, 'Делать то, что правильно. Любой ценой.', 12, 0, 100, 1, 0, 0, 73746, 0, 0, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71003, 0, 0, 'Когда же нужно бездействовать ради блага?', 12, 0, 100, 1, 0, 0, 73747, 0, 0, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71003, 3, 0, 'To do what is right, no matter the cost. That is wisdom.', 12, 0, 100, 1, 0, 0, 73746, 45000, 45000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71007, 3, 0, 'Никто не сможет нам противостоять!', 12, 0, 100, 0, 0, 0, 74218, 12000, 12000, 0, 'Кор\'кронский воспламенитель to Player');
INSERT INTO `creature_text` VALUES (71007, 3, 0, 'Победа или смерть!', 12, 0, 100, 0, 0, 0, 90976, 10961000, 10961000, 0, 'Кор\'кронский воспламенитель to Player');
INSERT INTO `creature_text` VALUES (71015, 3, 0, 'Вход только по приглашениям.', 12, 0, 100, 0, 0, 37518, 74120, 1800000, 1800000, 0, 'Смотритель зверинца');
INSERT INTO `creature_text` VALUES (71015, 3, 0, 'У вас нет приглашения.', 12, 0, 100, 0, 0, 28382, 74122, 300000, 300000, 0, 'Смотритель зверинца');
INSERT INTO `creature_text` VALUES (71015, 3, 0, 'Экспонаты руками не трогать.', 12, 0, 100, 0, 0, 37517, 74121, 900000, 1500000, 0, 'Смотритель зверинца');
INSERT INTO `creature_text` VALUES (71033, 0, 0, 'Эй, а ты ведь не тот вонючий баран. Чего тебе надо от Кил\'река?', 12, 0, 100, 0, 0, 0, 74119, 0, 0, 0, 'Злобный бес to Player');
INSERT INTO `creature_text` VALUES (71037, 0, 0, 'А-ха!', 12, 0, 100, 0, 0, 0, 74026, 0, 0, 0, 'Гневион');
INSERT INTO `creature_text` VALUES (71037, 0, 0, 'Гневион ничего не видит; нападайте сзади, чтобы нанести дополнительный урон!', 41, 0, 100, 0, 0, 0, 74011, 0, 0, 0, 'Гневион');
INSERT INTO `creature_text` VALUES (71037, 3, 0, '%s ворчит себе под нос, затягивая повязку на глазах.', 16, 0, 100, 1, 0, 0, 73516, 49000, 49000, 0, 'Гневион to Сюэнь');
INSERT INTO `creature_text` VALUES (71037, 3, 0, 'Интересно... Хотя исход вряд ли изменится.', 12, 0, 100, 6, 0, 0, 73513, 26000, 26000, 0, 'Гневион to Сюэнь');
INSERT INTO `creature_text` VALUES (71037, 3, 0, 'Но... это абсурд! Я убью своего же избранника!', 12, 0, 100, 274, 0, 0, 73512, 15000, 15000, 0, 'Гневион to Сюэнь');
INSERT INTO `creature_text` VALUES (71037, 3, 0, 'Слепой дракон. Какая нелепица!', 12, 0, 100, 1, 0, 0, 73517, 49000, 49000, 0, 'Гневион to Сюэнь');
INSERT INTO `creature_text` VALUES (71037, 3, 0, 'Хорошо. Прости, герой, но сегодня не мне предстоит усвоить урок.', 12, 0, 100, 1, 0, 0, 73514, 41000, 41000, 0, 'Гневион to Сюэнь');
INSERT INTO `creature_text` VALUES (71037, 4, 0, 'Глаза подводят тебя!', 14, 0, 100, 0, 0, 0, 73466, 81000, 81000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (71037, 4, 0, 'Как я и думал. Попробуем еще раз?', 12, 0, 100, 6, 0, 0, 73778, 40000, 40000, 0, 'Гневион to Сюэнь');
INSERT INTO `creature_text` VALUES (71037, 4, 0, 'Куда же ты? Вернись.', 12, 0, 100, 0, 0, 0, 74027, 6000, 6000, 0, 'Гневион');
INSERT INTO `creature_text` VALUES (71037, 4, 0, 'Найдешь ли ты меня теперь?', 14, 0, 100, 0, 0, 0, 73469, 94000, 94000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (71037, 4, 0, 'Неплохо!', 14, 0, 100, 0, 0, 0, 117372, 107000, 107000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (71037, 4, 0, 'Стой! Я же должен тебя убить!', 12, 0, 100, 0, 0, 0, 74025, 17000, 130000, 0, 'Гневион');
INSERT INTO `creature_text` VALUES (71095, 3, 0, 'Отражение Лэй Ши весело улыбается.', 16, 0, 100, 0, 0, 0, 73435, 95000, 95000, 0, 'Отражение Лэй Ши');
INSERT INTO `creature_text` VALUES (71095, 3, 0, 'Отражение Лэй Ши игриво колышется.', 16, 0, 100, 0, 0, 0, 73434, 32000, 32000, 117775, 'Отражение Лэй Ши');
INSERT INTO `creature_text` VALUES (71095, 3, 0, 'Отражение Лэй Ши тихо журчит.', 16, 0, 100, 0, 0, 0, 73436, 172000, 172000, 0, 'Отражение Лэй Ши');
INSERT INTO `creature_text` VALUES (71107, 0, 0, 'A black dragon, through and through. Very well, I will give you my blessing, but also some advice:', 12, 0, 100, 1, 0, 0, 73487, 0, 0, 0, 'Xuen to Player');
INSERT INTO `creature_text` VALUES (71107, 0, 0, 'Ah, the young black dragon graces my temple. The deeds of you and your champion are quickly becoming Pandaren legend.', 12, 0, 100, 1, 0, 0, 73478, 0, 0, 0, 'Xuen to Player');
INSERT INTO `creature_text` VALUES (71107, 0, 0, 'How so?', 12, 0, 100, 1, 0, 0, 73485, 0, 0, 0, 'Xuen to Player');
INSERT INTO `creature_text` VALUES (71107, 0, 0, 'Now. My challenge, should you accept it, will test your ability to stand right in the face of danger and persevere.', 12, 0, 100, 1, 0, 0, 73489, 0, 0, 0, 'Xuen to Player');
INSERT INTO `creature_text` VALUES (71107, 0, 0, 'Strength used in the service of others is twice as powerful as strength spent on our foes.', 12, 0, 100, 1, 0, 0, 73488, 0, 0, 0, 'Xuen to Player');
INSERT INTO `creature_text` VALUES (71107, 0, 0, 'Tell me, young prince: What is the nature of strength?', 12, 0, 100, 1, 0, 0, 73481, 0, 0, 0, 'Xuen to Player');
INSERT INTO `creature_text` VALUES (71107, 0, 0, 'You speak only in terms of one\'s enemies.', 12, 0, 100, 1, 0, 0, 73483, 0, 0, 0, 'Xuen to Player');
INSERT INTO `creature_text` VALUES (71107, 3, 0, 'А теперь я хотел бы проверить, способны ли вы встретить опасность лицом к лицу и уцелеть.', 12, 0, 100, 1, 0, 0, 73489, 100000, 100000, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (71107, 3, 0, 'А, в мой храм пожаловал юный черный дракон. Ты и твой спутник скоро станете легендой среди пандаренов.', 12, 0, 100, 1, 0, 0, 73478, 31000, 31000, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (71107, 3, 0, 'И как же?', 12, 0, 100, 1, 0, 0, 73485, 68000, 68000, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (71107, 3, 0, 'Истинный черный дракон. Хорошо, ты получишь мое благословение и небольшой совет:', 12, 0, 100, 1, 0, 0, 73487, 75000, 75000, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (71107, 3, 0, 'Надеюсь, теперь ты понимаешь, что слепая сила беспомощна. Одно дело – менять мир, но совсем другое – делать то, что правильно.', 12, 0, 100, 1, 0, 0, 73658, 3523000, 3523000, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (71107, 3, 0, 'Сила на службе у друзей намного мощнее той, что мы обращаем против своих врагов.', 12, 0, 100, 1, 0, 0, 73488, 90000, 90000, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (71107, 3, 0, 'Скажи мне, юный принц: что такое сила?', 12, 0, 100, 1, 0, 0, 73481, 49000, 49000, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (71107, 3, 0, 'Ты говоришь только о своих врагах.', 12, 0, 100, 1, 0, 0, 73483, 60000, 60000, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (71107, 3, 0, 'Юный принц, я надеюсь, ты запомнишь этот урок.', 12, 0, 100, 1, 0, 0, 73659, 3536000, 3536000, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (71108, 3, 0, '...by using it to crush their enemies!', 12, 0, 100, 5, 0, 0, 73486, 69000, 69000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71108, 3, 0, 'Certainly, strength can benefit one\'s allies.', 12, 0, 100, 273, 0, 0, 73484, 62000, 62000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71108, 3, 0, 'Great White Tiger! Hear our plea.', 12, 0, 100, 5, 0, 0, 73477, 21000, 21000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71108, 3, 0, 'I am looking forward to this: The White Tiger is the embodiment of strength in Pandaria!', 12, 0, 100, 1, 0, 0, 73470, 2000, 2000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71108, 3, 0, 'I look forward to this meeting: The White Tiger in Pandaria - the embodiment of power!', 12, 0, 100, 1, 0, 0, 73470, 1000, 1000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71108, 3, 0, 'I take great pride in the company I keep.', 12, 0, 100, 1, 0, 0, 73480, 42000, 42000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71108, 3, 0, 'My champion and I seek the blessing of your strength.', 12, 0, 100, 1, 0, 0, 73479, 25000, 25000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71108, 3, 0, 'The power to crush one\'s foes. That is strength.', 12, 0, 100, 1, 0, 0, 73482, 52000, 52000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71108, 3, 0, 'Белый Тигр! Услышь нас.', 12, 0, 100, 5, 0, 0, 73477, 20000, 20000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71108, 3, 0, 'Идти этим путем вместе – честь для меня.', 12, 0, 100, 1, 0, 0, 73480, 42000, 42000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71108, 3, 0, 'Мы просим у тебя благословения силы.', 12, 0, 100, 1, 0, 0, 73479, 24000, 24000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71108, 3, 0, 'Силу можно использовать и во благо друзей.', 12, 0, 100, 273, 0, 0, 73484, 61000, 61000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71108, 3, 0, 'Сокрушив их врагов!', 12, 0, 100, 5, 0, 0, 73486, 69000, 69000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71108, 3, 0, 'Это мощь, способная сломить противника.', 12, 0, 100, 1, 0, 0, 73482, 52000, 52000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71123, 4, 0, 'Моя кровь бурлит.', 14, 0, 100, 0, 0, 37206, 73594, 14000, 14000, 0, 'Эхо И\'Шараджа');
INSERT INTO `creature_text` VALUES (71123, 4, 0, 'Твоя агония питает меня.', 14, 0, 100, 0, 0, 37146, 73596, 24000, 24000, 0, 'Эхо И\'Шараджа to Зеркальное изображение');
INSERT INTO `creature_text` VALUES (71123, 4, 0, 'Это лишь сон.', 14, 0, 100, 0, 0, 37143, 73600, 28000, 28000, 0, 'Эхо И\'Шараджа to Player');
INSERT INTO `creature_text` VALUES (71136, 3, 0, 'Вперед!', 14, 0, 100, 1, 0, 0, 131943, 53000, 3315000, 0, 'Сюэнь to Гневион');
INSERT INTO `creature_text` VALUES (71136, 3, 0, 'Герой, обрати силу соперника против него самого. Я верю в тебя.', 12, 0, 100, 1, 0, 0, 73664, 114000, 114000, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (71136, 3, 0, 'Держись позади него. Он не видит тебя! Двигайся быстрее!', 14, 0, 100, 1, 0, 0, 73523, 56000, 3319000, 0, 'Сюэнь to Гневион');
INSERT INTO `creature_text` VALUES (71136, 3, 0, 'Довольно!', 14, 0, 100, 1, 0, 0, 129681, 104000, 719000, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (71136, 3, 0, 'Дракон сильнее тебя, герой! Не дай ему завершить атаку!', 14, 0, 100, 1, 0, 0, 73519, 449000, 449000, 0, 'Сюэнь to Гневион');
INSERT INTO `creature_text` VALUES (71136, 3, 0, 'Ищи истинного врага! Не дай фальшивым образам обмануть тебя!', 14, 0, 100, 1, 0, 0, 73880, 533000, 3413000, 0, 'Сюэнь to Гневион');
INSERT INTO `creature_text` VALUES (71136, 3, 0, 'Они бессмертны! Действуй быстрее, пока они снова не напали на тебя!', 14, 0, 100, 1, 0, 0, 73881, 500000, 3467000, 0, 'Сюэнь to Слеза Нелтариона');
INSERT INTO `creature_text` VALUES (71136, 3, 0, 'Отлично, сложите оружие!', 14, 0, 100, 1, 0, 0, 73657, 3474000, 3474000, 0, 'Сюэнь to Гневион');
INSERT INTO `creature_text` VALUES (71136, 3, 0, 'Тебе, Гневион, я завяжу глаза.', 12, 0, 100, 1, 0, 0, 73509, 21000, 3284000, 0, 'Сюэнь to Сюэнь');
INSERT INTO `creature_text` VALUES (71136, 3, 0, 'Тогда начнем! Герой, тебе придется одолеть Черного принца в ближнем бою.', 12, 0, 100, 1, 0, 0, 73508, 5000, 3269000, 0, 'Сюэнь to Сюэнь');
INSERT INTO `creature_text` VALUES (71136, 3, 0, 'Ты очень силен. Но сила бесполезна, если ты не видишь, куда ее направить.', 12, 0, 100, 1, 0, 0, 73510, 32000, 3295000, 0, 'Сюэнь to Сюэнь');
INSERT INTO `creature_text` VALUES (71136, 3, 0, 'Уклоняйся, используй его силу против него!', 14, 0, 100, 1, 0, 0, 73538, 102000, 102000, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (71142, 3, 0, 'Here we are, the home of Niuzao, the Black Ox. He is the patron spirit of Pandaren fortitude, the only celestial who chooses to live beyond the wall.', 12, 0, 100, 1, 0, 0, 73524, 2000, 2000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71142, 3, 0, 'Mighty Black Ox! Hear our plea.', 12, 0, 100, 1, 0, 0, 73525, 20000, 20000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71142, 3, 0, 'My champion and I seek your blessing of fortitude.', 12, 0, 100, 1, 0, 0, 73526, 24000, 24000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71142, 3, 0, 'Мы в обители Нюцзао, Черного Быка. Он – дух пандаренской стойкости, единственный небожитель, поселившийся за стеной.', 12, 0, 100, 1, 0, 0, 73524, 3000, 3000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71142, 3, 0, 'Мы просим у тебя благословения стойкости.', 12, 0, 100, 1, 0, 0, 73526, 23000, 23000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71142, 3, 0, 'Черный Бык! Услышь нас.', 12, 0, 100, 1, 0, 0, 73525, 19000, 19000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71142, 3, 0, 'Это сила, позволяющая преодолеть любые трудности.', 12, 0, 100, 1, 0, 0, 73527, 43000, 43000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71144, 3, 0, 'So enters the dragon, and his mortal champion. You are welcome here.', 12, 0, 100, 402, 0, 0, 73530, 47000, 47000, 0, 'Niuzao to Player');
INSERT INTO `creature_text` VALUES (71144, 3, 0, 'Вот идет дракон и избранный им смертный герой. Well Met.', 12, 0, 100, 402, 0, 0, 73530, 40000, 40000, 0, 'Нюцзао to Player');
INSERT INTO `creature_text` VALUES (71144, 3, 0, 'Скажи мне, Черный принц, что такое стойкость?', 12, 0, 100, 402, 0, 0, 73531, 48000, 48000, 0, 'Нюцзао to Player');
INSERT INTO `creature_text` VALUES (71144, 3, 0, 'Ты путаешь стойкость с силой, юный принц. Но сила – ничто без величия духа!', 12, 0, 100, 402, 0, 0, 73532, 60000, 60000, 0, 'Нюцзао to Player');
INSERT INTO `creature_text` VALUES (71152, 0, 0, 'Я выпью вашу кровь!', 14, 0, 100, 0, 0, 38674, 105369, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71152, 1, 0, 'Отведаем крови врагов!', 14, 0, 100, 0, 0, 38679, 75075, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71152, 2, 0, 'Отомстите за меня!', 14, 0, 100, 0, 0, 38675, 75077, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71153, 4, 0, 'Моя разведка определила эту цель как особо приоритетную.', 14, 0, 100, 0, 0, 38612, 75119, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71153, 5, 0, 'А... охотник... стал добычей..', 14, 0, 100, 0, 0, 38607, 75122, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71154, 6, 0, 'Вы так медлительны. Как вы ловите добычу?', 14, 0, 100, 0, 0, 38634, 75080, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71154, 7, 0, 'З-зу-у-у-у-ум-м-м!', 14, 0, 100, 0, 0, 38641, 75705, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71154, 8, 0, 'Будь проклят этот панцирь! Я недостаточно быстр!', 14, 0, 100, 0, 0, 38635, 75088, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71155, 9, 0, 'I am the First. Bow to the higher being.', 14, 0, 100, 0, 0, 38667, 75100, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71155, 10, 0, 'Узурпаторы покинули тебя.', 14, 0, 100, 0, 0, 38671, 75103, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71155, 11, 0, 'Древний дает нам силу.', 14, 0, 100, 0, 0, 38673, 75102, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71155, 12, 0, 'Верните меня... в янтарь..', 14, 0, 100, 0, 0, 38668, 75106, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71156, 20, 0, 'Сюда, мои куньчуны, пришло время еды!', 14, 0, 100, 0, 0, 38790, 75108, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71156, 21, 0, 'Ковок... ты мне нужен...', 14, 0, 100, 0, 0, 38643, 75114, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71157, 16, 0, 'Пробудитель, ты пришел дегустировать мои новые смеси? Уверяю, такого ты еще не пробовал.', 14, 0, 100, 0, 0, 38704, 75124, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71157, 17, 0, 'Такое разнообразие. Какую же смесь вам дать?', 14, 0, 100, 0, 0, 38709, 75125, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71157, 18, 0, 'Моя алхимия лучшая!', 14, 0, 100, 0, 0, 38708, 75127, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71157, 19, 0, 'I couldn’t ... understand how to stop ... death.', 14, 0, 100, 0, 0, 38705, 75129, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71158, 3, 0, 'Нужно было... использовать... генные... улучшения...', 14, 0, 100, 0, 0, 38914, 75142, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71160, 13, 0, 'Хаос – это высшая ступень порядка!', 14, 0, 100, 0, 0, 38618, 75091, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71160, 14, 0, 'Может, поиграем?', 14, 0, 100, 0, 0, 38623, 75092, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71160, 15, 0, 'Мои расчеты неверны? Может, еще через 800 лет.', 14, 0, 100, 0, 0, 38619, 75098, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71161, 22, 0, 'I\'ll take you one last time ... to death.', 14, 0, 100, 0, 0, 38654, 75131, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71161, 23, 0, 'Я воспарю над тобой!', 14, 0, 100, 0, 0, 38658, 75133, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71161, 24, 0, 'Пробудитель, мы еще встретимся...', 14, 0, 100, 0, 0, 38655, 75135, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71166, 3, 0, '... the fires that once burned the sky will return. It is inevitable. The Burning Legion WILL find Azeroth.', 12, 0, 100, 0, 0, 0, 73555, 73000, 73000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71166, 3, 0, '...Great Crane. You have not seen what I have seen.', 12, 0, 100, 0, 0, 0, 73554, 66000, 66000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71166, 3, 0, 'Great Red Crane! Hear our plea.', 12, 0, 100, 1, 0, 0, 73551, 27000, 27000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71166, 3, 0, 'Hope is... a belief in a better tomorrow.', 12, 0, 100, 1, 0, 0, 73553, 54000, 54000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71166, 3, 0, 'My champion and I seek your blessing of hope.', 12, 0, 100, 1, 0, 0, 73552, 31000, 31000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71166, 3, 0, 'Seas of blood, cities in ruin! Who are we - one divided world - to stand against a legion?', 12, 0, 100, 0, 0, 0, 73556, 82000, 82000, 0, 'Wrathion to Wrathion');
INSERT INTO `creature_text` VALUES (71166, 3, 0, 'The Red Crane of Hope has inspired the citizens of Pandaria from the very dawn of history. I very much want to speak with him.', 12, 0, 100, 1, 0, 0, 73550, 2000, 2000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71166, 3, 0, 'Великий Журавль, ты не видел того, что видел я.', 12, 0, 100, 0, 0, 0, 73554, 65000, 65000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71166, 3, 0, 'Красный Журавль надежды вдохновлял жителей Пандарии с начала времен. Я очень хочу поговорить с ним.', 12, 0, 100, 1, 0, 0, 73550, 2000, 2000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71166, 3, 0, 'Красный Журавль! Услышь нас.', 12, 0, 100, 1, 0, 0, 73551, 28000, 28000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71166, 3, 0, 'Мы просим у тебя благословения надежды.', 12, 0, 100, 1, 0, 0, 73552, 31000, 31000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71166, 3, 0, 'Надежда... это вера в лучшее будущее.', 12, 0, 100, 1, 0, 0, 73553, 55000, 55000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71166, 3, 0, 'Огонь снова поглотит небо. Это неизбежно. Пылающий Легион доберется до Азерота.', 12, 0, 100, 0, 0, 0, 73555, 73000, 73000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71166, 3, 0, 'Реки крови, города в руинах! Наш расколотый мир не устоит против демонов!', 12, 0, 100, 0, 0, 0, 73556, 81000, 81000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (71166, 3, 0, 'Ты говоришь о надежде... Поверь, только слабая надежда пережить катастрофу дает мне силу двигаться дальше.', 12, 0, 100, 0, 0, 0, 73557, 91000, 91000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (71169, 3, 0, 'My my, the child of the Worldbreaker, proof that none are beyond redemption. I am honored by your visit.', 12, 0, 100, 1, 0, 0, 73559, 65000, 65000, 0, 'Chi-Ji to Player');
INSERT INTO `creature_text` VALUES (71169, 3, 0, 'Tell me, son of the Earth-Warder: What is the nature of hope?', 12, 0, 100, 1, 0, 0, 73560, 76000, 76000, 0, 'Chi-Ji to Player');
INSERT INTO `creature_text` VALUES (71169, 3, 0, 'You speak, but doubt your own words.', 12, 0, 100, 1, 0, 0, 73561, 88000, 88000, 0, 'Chi-Ji to Player');
INSERT INTO `creature_text` VALUES (71169, 3, 0, 'You underestimate me.', 12, 0, 100, 1, 0, 0, 73562, 98000, 98000, 0, 'Chi-Ji to Player');
INSERT INTO `creature_text` VALUES (71169, 3, 0, 'Встань, сын Смертокрыла. Я дам тебе благословение, ибо ты нуждаешься в нем, как никто другой.', 12, 0, 100, 1, 0, 0, 73563, 413000, 413000, 0, 'Чи-Цзи to Player');
INSERT INTO `creature_text` VALUES (71169, 3, 0, 'Дитя Смертокрыла, доказательство того, что каждый достоин искупления. Это честь для меня.', 12, 0, 100, 1, 0, 0, 73559, 346000, 346000, 0, 'Чи-Цзи to Player');
INSERT INTO `creature_text` VALUES (71169, 3, 0, 'Кажется, ты сомневаешься...', 12, 0, 100, 1, 0, 0, 73561, 370000, 370000, 0, 'Чи-Цзи to Player');
INSERT INTO `creature_text` VALUES (71169, 3, 0, 'Не отчаивайся. Ты не повторишь путь отца.', 12, 0, 100, 1, 0, 0, 73808, 792000, 792000, 0, 'Чи-Цзи to Player');
INSERT INTO `creature_text` VALUES (71169, 3, 0, 'Но что еще важнее – твой герой не дал тебе погибнуть. Теперь понимаешь? Все возможно с помощью друзей.', 12, 0, 100, 1, 0, 0, 73809, 798000, 798000, 0, 'Чи-Цзи to Player');
INSERT INTO `creature_text` VALUES (71169, 3, 0, 'Скажи, сын Хранителя Земли: что такое надежда?', 12, 0, 100, 1, 0, 0, 73560, 357000, 357000, 0, 'Чи-Цзи to Player');
INSERT INTO `creature_text` VALUES (71169, 3, 0, 'Ты меня недооцениваешь.', 12, 0, 100, 1, 0, 0, 73562, 379000, 379000, 0, 'Чи-Цзи to Player');
INSERT INTO `creature_text` VALUES (71246, 3, 0, 'Блок питания включен.', 14, 0, 100, 0, 0, 37206, 73591, 130000, 130000, 0, 'Норусхен');
INSERT INTO `creature_text` VALUES (71246, 3, 0, 'Обшивка повреждена. Включена матрица активной защиты.', 14, 0, 100, 0, 0, 37205, 73589, 114000, 114000, 0, 'Норусхен to Хитрюга Амбициозный');
INSERT INTO `creature_text` VALUES (71270, 3, 0, 'Be aware of your surroundings.', 14, 0, 100, 2, 0, 0, 73771, 71000, 270000, 0, 'Yu\'lon to Player');
INSERT INTO `creature_text` VALUES (71270, 3, 0, 'Do not allow the elements to engage you!', 14, 0, 100, 2, 0, 0, 73772, 239000, 239000, 0, 'Yu\'lon to Wrathion');
INSERT INTO `creature_text` VALUES (71270, 3, 0, 'Let the challenge begin! Hero, you must defeat the Black Prince in combat.', 12, 0, 100, 2, 0, 0, 73765, 5000, 5000, 0, 'Yu\'lon to Yu\'lon');
INSERT INTO `creature_text` VALUES (71270, 3, 0, 'Seek out your true enemy! Do not be fooled by his false images!', 14, 0, 100, 2, 0, 0, 73880, 171000, 214000, 0, 'Yu\'lon to Wrathion');
INSERT INTO `creature_text` VALUES (71270, 3, 0, 'The elements are undying! Make use of your time before they rise again!', 14, 0, 100, 1, 0, 0, 73881, 124000, 286000, 0, 'Yu\'lon to Neltharion\'s Tears');
INSERT INTO `creature_text` VALUES (71270, 3, 0, 'Very good - that is enough!', 14, 0, 100, 2, 0, 0, 73781, 290000, 290000, 0, 'Yu\'lon to Wrathion');
INSERT INTO `creature_text` VALUES (71270, 3, 0, 'We will demonstrate the limits of power without vision to guide it.', 12, 0, 100, 2, 0, 0, 73767, 26000, 26000, 0, 'Yu\'lon to Yu\'lon');
INSERT INTO `creature_text` VALUES (71270, 3, 0, 'You, my dark prince, will be blindfolded.', 12, 0, 100, 2, 0, 0, 73766, 16000, 16000, 0, 'Yu\'lon to Yu\'lon');
INSERT INTO `creature_text` VALUES (71270, 3, 0, 'Довольно!', 14, 0, 100, 2, 0, 0, 129681, 107000, 107000, 0, 'Юй-лун to Player');
INSERT INTO `creature_text` VALUES (71270, 3, 0, 'Не отвлекайся на них!', 14, 0, 100, 2, 0, 0, 73772, 32000, 188000, 0, 'Юй-лун to Гневион');
INSERT INTO `creature_text` VALUES (71270, 3, 0, 'Они бессмертны! Действуй быстрее, пока они снова не напали на тебя!', 14, 0, 100, 1, 0, 0, 73881, 84000, 240000, 0, 'Юй-лун to Слеза Нелтариона');
INSERT INTO `creature_text` VALUES (71270, 3, 0, 'Отлично, хватит!', 14, 0, 100, 2, 0, 0, 73781, 250000, 250000, 0, 'Юй-лун to Гневион');
INSERT INTO `creature_text` VALUES (71270, 3, 0, 'Смотри по сторонам.', 14, 0, 100, 2, 0, 0, 73771, 38000, 247000, 0, 'Юй-лун to 0');
INSERT INTO `creature_text` VALUES (71279, 3, 0, '... interesting. But the outcome is unlikely to change.', 12, 0, 100, 6, 0, 0, 73513, 20000, 20000, 0, 'Wrathion to Yu\'lon');
INSERT INTO `creature_text` VALUES (71279, 3, 0, '%s mutters to himself as he ties the blindfold.', 16, 0, 100, 1, 0, 0, 73516, 38000, 38000, 0, 'Wrathion to Yu\'lon');
INSERT INTO `creature_text` VALUES (71279, 3, 0, '%s ворчит себе под нос, затягивая повязку на глазах.', 16, 0, 100, 0, 0, 0, 73516, 5000, 5000, 0, 'Гневион to Юй-лун');
INSERT INTO `creature_text` VALUES (71279, 3, 0, 'A blind dragon. Ridiculous!', 12, 0, 100, 1, 0, 0, 73517, 38000, 38000, 0, 'Wrathion to Yu\'lon');
INSERT INTO `creature_text` VALUES (71279, 3, 0, 'Very well. I am sorry, champion - But I will not be the one learning a lesson today.', 12, 0, 100, 1, 0, 0, 73514, 32000, 32000, 0, 'Wrathion to Yu\'lon');
INSERT INTO `creature_text` VALUES (71279, 3, 0, 'Wait - that\'s absurd. I would annihilate my own champion!', 12, 0, 100, 274, 0, 0, 73512, 12000, 12000, 0, 'Wrathion to Yu\'lon');
INSERT INTO `creature_text` VALUES (71279, 3, 0, 'Wrathion is blindfolded, so attack from behind for bonus damage!', 41, 0, 100, 0, 0, 0, 74011, 60000, 60000, 0, 'Wrathion');
INSERT INTO `creature_text` VALUES (71279, 3, 0, 'Гневион ничего не видит; нападайте сзади, чтобы нанести дополнительный урон!', 41, 0, 100, 0, 0, 0, 74011, 22000, 22000, 0, 'Гневион');
INSERT INTO `creature_text` VALUES (71279, 4, 0, 'Ahhh... Hah!', 12, 0, 100, 0, 0, 0, 73776, 71000, 196000, 0, 'Wrathion');
INSERT INTO `creature_text` VALUES (71279, 4, 0, 'But can you find me again?', 14, 0, 100, 0, 0, 0, 73469, 108000, 108000, 0, 'Wrathion to Wrathion');
INSERT INTO `creature_text` VALUES (71279, 4, 0, 'Stand still! I am trying to kill you here!', 12, 0, 100, 0, 0, 0, 74025, 7000, 188000, 0, 'Wrathion');
INSERT INTO `creature_text` VALUES (71279, 4, 0, 'Well done!', 14, 0, 100, 0, 0, 0, 117372, 151000, 151000, 0, 'Wrathion to Wrathion');
INSERT INTO `creature_text` VALUES (71279, 4, 0, 'Where did you go? Come back!', 12, 0, 100, 0, 0, 0, 74027, 16000, 225000, 0, 'Wrathion');
INSERT INTO `creature_text` VALUES (71279, 4, 0, 'Your eyes fail you yet!', 14, 0, 100, 0, 0, 0, 73466, 91000, 91000, 0, 'Wrathion to Wrathion');
INSERT INTO `creature_text` VALUES (71279, 4, 0, 'А-ха!', 12, 0, 100, 0, 0, 0, 74026, 23000, 23000, 0, 'Гневион');
INSERT INTO `creature_text` VALUES (71279, 4, 0, 'Куда же ты? Вернись.', 12, 0, 100, 0, 0, 0, 74027, 22000, 55000, 0, 'Гневион');
INSERT INTO `creature_text` VALUES (71279, 4, 0, 'Стой! Я же должен тебя убить!', 12, 0, 100, 0, 0, 0, 74025, 7000, 71000, 0, 'Гневион');
INSERT INTO `creature_text` VALUES (71280, 3, 0, '|TInterface\\Icons\\inv_misc_pocketwatch_01:20|t Начался дополнительный отcчет времени!', 41, 0, 100, 0, 0, 0, 74194, 25000, 25000, 0, 'Controller to Player');
INSERT INTO `creature_text` VALUES (71314, 3, 0, 'Исцеляй принца, но не забывай и о себе!', 14, 0, 100, 1, 0, 0, 73793, 140000, 632000, 0, 'Чи-Цзи to Видение Смертокрыла');
INSERT INTO `creature_text` VALUES (71314, 3, 0, 'Молодец! Ты справился!', 14, 0, 100, 1, 0, 0, 73830, 727000, 727000, 0, 'Чи-Цзи to Видение Смертокрыла');
INSERT INTO `creature_text` VALUES (71314, 3, 0, 'Начнем! Герой, используй свой дар лекаря, чтобы сохранить жизнь Черному принцу.', 12, 0, 100, 1, 0, 0, 73785, 4000, 4000, 0, 'Чи-Цзи to Чи-Цзи');
INSERT INTO `creature_text` VALUES (71314, 3, 0, 'Не страшись его ловушек. Твое мастерство лекаря сильнее его магии!', 14, 0, 100, 1, 0, 0, 73799, 228000, 566000, 0, 'Чи-Цзи to Видение Смертокрыла');
INSERT INTO `creature_text` VALUES (71314, 3, 0, 'Не упускай возможности исцелить Черного принца. ', 14, 0, 100, 1, 0, 0, 73794, 77000, 444000, 0, 'Чи-Цзи to Видение Смертокрыла');
INSERT INTO `creature_text` VALUES (71314, 3, 0, 'Но не впадай в отчаяние – твой дух сильнее, чем ты думаешь. Попробуй еще раз, я верю в тебя!', 12, 0, 100, 1, 0, 0, 73806, 357000, 357000, 0, 'Чи-Цзи to Player');
INSERT INTO `creature_text` VALUES (71314, 3, 0, 'Они бессмертны! Действуй быстрее, пока они снова не напали на тебя!', 14, 0, 100, 1, 0, 0, 73881, 114000, 683000, 0, 'Чи-Цзи to Слеза Нелтариона');
INSERT INTO `creature_text` VALUES (71314, 3, 0, 'Разве? Ты полон страхов и сомнений, юный принц.', 12, 0, 100, 1, 0, 0, 73786, 20000, 20000, 0, 'Чи-Цзи to Чи-Цзи');
INSERT INTO `creature_text` VALUES (71314, 3, 0, 'Твой герой поможет тебе. Вперед!', 14, 0, 100, 1, 0, 0, 73788, 41000, 408000, 0, 'Чи-Цзи to Чи-Цзи');
INSERT INTO `creature_text` VALUES (71314, 3, 0, 'Ты еще не примирился со своим прошлым. Сделай это сейчас.', 12, 0, 100, 1, 0, 0, 73787, 28000, 28000, 0, 'Чи-Цзи to Чи-Цзи');
INSERT INTO `creature_text` VALUES (71314, 3, 0, 'Хорошо. Испытание окончено.', 14, 0, 100, 1, 0, 0, 73805, 353000, 353000, 0, 'Чи-Цзи to Player');
INSERT INTO `creature_text` VALUES (71317, 0, 0, 'Славная работа, герой! Мы прошли испытание Небожителей и получили их благословение.', 12, 0, 100, 5, 0, 37430, 73916, 0, 0, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71317, 0, 0, 'Теперь возвращайся на Причуду Камнетеса – я подготовил для тебя приятный сюрприз!', 12, 0, 100, 5, 0, 37431, 73917, 0, 0, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71317, 3, 0, 'I am glad that our hero is still alive. Mighty tiger, thanks for the blessing.', 12, 0, 100, 0, 0, 0, 73661, 27000, 27000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71317, 3, 0, 'Ха! Это был нечестный бой.', 12, 0, 100, 1, 0, 0, 73782, 2000, 2000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71327, 0, 0, 'Огонь!', 14, 0, 100, 0, 0, 0, 137474, 0, 0, 0, 'Адмирал Ходжсон');
INSERT INTO `creature_text` VALUES (71327, 3, 0, 'Полундра! Эти крысята из Орды пробрались на корабль. Надо разобраться с ними!', 14, 0, 100, 0, 0, 0, 73954, 1000, 1000, 0, 'Адмирал Ходжсон to Player');
INSERT INTO `creature_text` VALUES (71328, 4, 0, 'Огонь!', 14, 0, 100, 0, 0, 0, 137474, 9000, 9000, 0, 'Лейтенант Бумфитиль to Бочка со взрывчаткой');
INSERT INTO `creature_text` VALUES (71329, 4, 0, 'Внимание!', 14, 0, 100, 0, 0, 0, 121748, 3000, 3000, 0, 'Лейтенант Стрелобам');
INSERT INTO `creature_text` VALUES (71330, 4, 0, '%s начинает пить |cFFFF2222|Hspell:141327|h[нестабильный отвар]|h|r.', 41, 0, 100, 0, 0, 0, 73492, 9000, 9000, 0, 'Лейтенант Шипокипун');
INSERT INTO `creature_text` VALUES (71330, 4, 0, 'Внимание!', 14, 0, 100, 0, 0, 0, 121748, 2000, 2000, 0, 'Лейтенант Шипокипун');
INSERT INTO `creature_text` VALUES (71330, 4, 0, 'Посмотрим, что получится.', 14, 0, 100, 0, 0, 0, 73953, 9000, 9000, 0, 'Лейтенант Шипокипун');
INSERT INTO `creature_text` VALUES (71352, 3, 0, 'I know our next step. Meet me back atop Mason\'s Folly - you\'ll be pleasantly surprised at what I have in store for you!', 12, 0, 100, 5, 0, 0, 73917, 19000, 19000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71352, 3, 0, 'PAH! A lopsided challenge.', 12, 0, 100, 1, 0, 0, 73782, 2000, 2000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71352, 3, 0, 'We have done it, hero! We have the blessings of the celestials, and we have completed their challenge.', 12, 0, 100, 5, 0, 0, 73916, 11000, 11000, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71352, 3, 0, 'Рад, что наш герой жив. Великая змея, спасибо за благословение.', 12, 0, 100, 1, 0, 0, 73784, 17000, 17000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71352, 3, 0, 'Ха! Это был нечестный бой.', 12, 0, 100, 1, 0, 0, 73782, 3000, 3000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71357, 3, 0, 'I am a black dragon. I do not need help.', 12, 0, 100, 1, 0, 0, 73816, 15000, 15000, 0, 'Гневион to Чи-Цзи');
INSERT INTO `creature_text` VALUES (71357, 3, 0, 'Прошу... я не хочу этого.', 12, 0, 100, 274, 0, 0, 73818, 39000, 39000, 0, 'Гневион to Чи-Цзи');
INSERT INTO `creature_text` VALUES (71357, 3, 0, 'Это... это... мой отец?', 12, 0, 100, 1, 0, 0, 73817, 33000, 33000, 0, 'Гневион to Чи-Цзи');
INSERT INTO `creature_text` VALUES (71357, 4, 0, 'Гневион побежден и без сил падает на землю!', 41, 0, 100, 1, 0, 0, 73970, 303000, 303000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (71357, 4, 0, 'Хватит! Довольно!', 14, 0, 100, 1, 0, 0, 73804, 303000, 303000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (71358, 3, 0, 'Вот это был взрыв! Пойдем, посмотрим, что стряслось.', 12, 0, 100, 0, 0, 0, 73959, 44000, 44000, 0, 'Хитрюга Амбициозный to Player');
INSERT INTO `creature_text` VALUES (71358, 3, 0, 'Нельзя терять ни минуты! Надо разнести эту стену на кусочки!', 12, 0, 100, 5, 0, 0, 73965, 38000, 38000, 0, 'Хитрюга Амбициозный to Player');
INSERT INTO `creature_text` VALUES (71358, 3, 0, 'Ну что, все понятно?', 12, 0, 100, 5, 0, 0, 73958, 36000, 36000, 0, 'Хитрюга Амбициозный to Player');
INSERT INTO `creature_text` VALUES (71358, 3, 0, 'Это какой-то громадный сундук. Наверняка он набит золотом! И все эти богатства станут нашими!', 12, 0, 100, 71, 0, 0, 73960, 52000, 52000, 0, 'Хитрюга Амбициозный to Player');
INSERT INTO `creature_text` VALUES (71363, 3, 0, 'I will tear this world apart!', 14, 0, 100, 0, 0, 0, 73792, 13000, 13000, 0, 'Видение Смертокрыла to Player');
INSERT INTO `creature_text` VALUES (71363, 3, 0, 'Ваши усилия ничего не изменят!', 14, 0, 100, 0, 0, 0, 73801, 11000, 11000, 0, 'Видение Смертокрыла');
INSERT INTO `creature_text` VALUES (71363, 4, 0, 'Видение Смертокрыла нацелилось на Гневиона! Его необходимо срочно исцелить!', 41, 0, 100, 0, 0, 0, 0, 30000, 30000, 0, 'Видение Смертокрыла');
INSERT INTO `creature_text` VALUES (71363, 4, 0, 'Защитите Гневиона от атак, прикрывая его своим телом.', 41, 0, 100, 1, 0, 0, 73930, 90000, 218000, 0, 'Видение Смертокрыла');
INSERT INTO `creature_text` VALUES (71363, 4, 0, 'От моей ярости не укрыться!', 14, 0, 100, 1, 0, 0, 73802, 90000, 218000, 0, 'Видение Смертокрыла');
INSERT INTO `creature_text` VALUES (71404, 3, 0, 'I understand. Thank you, great spirit.', 12, 0, 100, 0, 0, 0, 73811, 24000, 24000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71404, 3, 0, 'Это... безумие...', 12, 0, 100, 0, 0, 0, 73810, 2000, 2000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71409, 4, 0, '|TInterface\\Icons\\inv_misc_bomb_05:20|tYou have |cFFFF0000|Hspell:145996|h[Set to Blow]|h|r! Use |cFFFF0000|Hspell:146364|h[Throw Bomb]|h|r to throw them!', 42, 0, 100, 0, 0, 0, 77369, 16000, 26000, 0, 'Commander Zak\'tar to Player');
INSERT INTO `creature_text` VALUES (71409, 4, 0, '|TInterface\\Icons\\inv_misc_bomb_05:20|tВы использовали способность|cFFFF0000|Hspell:145996|h[Готовность к взрыву]|h|r! Используйте |cFFFF0000|Hspell:146364|h[Бросок бомбы]|h|r, чтобы метнуть заряд в цель.', 42, 0, 100, 0, 0, 0, 77369, 7000, 36000, 0, 'Командир Зак\'тар to Player');
INSERT INTO `creature_text` VALUES (71426, 0, 0, 'And from the Red Crane, a shining beacon of hope for a better world!', 12, 0, 100, 1, 0, 0, 74109, 0, 0, 0, 'Wrathion to Wrathion');
INSERT INTO `creature_text` VALUES (71426, 0, 0, 'Excellent work with the celestials. Can you believe how chatty they were?', 12, 0, 100, 1, 0, 0, 74102, 0, 0, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71426, 0, 0, 'I just wanted them to hand us our reward, but they were just, \"talk talk talk talk talk.\"', 12, 0, 100, 1, 0, 0, 74103, 0, 0, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71426, 0, 0, 'I\'m sorry - where were we? Oh yes! I have something for you:', 12, 0, 100, 1, 0, 0, 74104, 0, 0, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71426, 0, 0, 'My work is not quite finished. This cloak will grant you the power of the celestials.', 12, 0, 100, 1, 0, 0, 74130, 0, 0, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71426, 0, 0, 'Our world.', 12, 0, 100, 1, 0, 0, 74110, 0, 0, 0, 'Wrathion to Wrathion');
INSERT INTO `creature_text` VALUES (71426, 0, 0, 'The fortitude of the Black Ox.', 12, 0, 100, 1, 0, 0, 74108, 0, 0, 0, 'Wrathion to Wrathion');
INSERT INTO `creature_text` VALUES (71426, 0, 0, 'The strength of the White Tiger.', 12, 0, 100, 25, 0, 0, 74106, 0, 0, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71426, 0, 0, 'The very spirits of Pandaria are supporting us in our grand crusade! Behold:', 12, 0, 100, 1, 0, 0, 74105, 0, 0, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71426, 0, 0, 'The wisdom of the Jade Serpent.', 12, 0, 100, 1, 0, 0, 74107, 0, 0, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71426, 0, 0, 'There! It is ready.', 12, 0, 100, 1, 0, 0, 74111, 0, 0, 0, 'Wrathion to Wrathion');
INSERT INTO `creature_text` VALUES (71426, 0, 0, 'Very soon we will be able to augment your cloak with my own formidable draconic powers.', 12, 0, 100, 1, 0, 0, 74131, 0, 0, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71426, 0, 0, 'Welcome back, champion!', 12, 0, 100, 1, 0, 0, 74101, 0, 0, 0, 'Wrathion to Player');
INSERT INTO `creature_text` VALUES (71426, 3, 0, 'I was just waiting for a blessing, but no, I need to listen and listen to their speeches.', 12, 0, 100, 1, 0, 0, 74103, 12000, 12000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71426, 3, 0, 'Впрочем, мы отвлеклись! У меня для тебя кое-что есть.', 12, 0, 100, 1, 0, 0, 74104, 19000, 19000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71426, 3, 0, 'И от Красного Журавля – сияющий луч надежды для нового мира…', 12, 0, 100, 1, 0, 0, 74109, 55000, 55000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (71426, 3, 0, 'Моя работа еще не закончена. Этот плащ даст тебе мощь небожителей.', 12, 0, 100, 1, 0, 0, 74130, 85000, 85000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71426, 3, 0, 'Мудрость Нефритовой Змеи.', 12, 0, 100, 1, 0, 0, 74107, 43000, 43000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71426, 3, 0, 'Нашего мира.', 12, 0, 100, 1, 0, 0, 74110, 64000, 64000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (71426, 3, 0, 'Отличная работа! Эти небожители любят поболтать, да?', 12, 0, 100, 1, 0, 0, 74102, 6000, 6000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71426, 3, 0, 'Очень скоро мы сможем усилить его моей собственной драконьей мощью.', 12, 0, 100, 1, 0, 0, 74131, 91000, 91000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71426, 3, 0, 'С возвращением, герой!', 12, 0, 100, 1, 0, 0, 74101, 2000, 2000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71426, 3, 0, 'Сила Белого Тигра.', 12, 0, 100, 25, 0, 0, 74106, 37000, 37000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71426, 3, 0, 'Смотри же! Он готов.', 12, 0, 100, 1, 0, 0, 74111, 70000, 70000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (71426, 3, 0, 'Стойкость Черного Быка.', 12, 0, 100, 1, 0, 0, 74108, 49000, 49000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (71426, 3, 0, 'У нас есть поддержка духов Пандарии в этом великом деле. Итак…', 12, 0, 100, 1, 0, 0, 74105, 25000, 25000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (71436, 4, 0, 'Победа за нами! Залезай в пушку и пали!', 14, 0, 100, 0, 0, 0, 74129, 28000, 28000, 0, 'Адмирал Хагман to Admiral Yell Listener');
INSERT INTO `creature_text` VALUES (71436, 4, 0, 'Эти свиньи из Альянса запрыгивают к нам на палубу! Надо дать им отпор!', 14, 0, 100, 0, 0, 0, 73941, 3000, 10000, 0, 'Адмирал Хагман to Admiral Yell Listener');
INSERT INTO `creature_text` VALUES (71445, 0, 0, 'Поделом им! Залезай в пушку и целься в их ведущий корабль!', 14, 0, 100, 0, 0, 0, 73949, 0, 0, 0, 'Капитан Орды to Player');
INSERT INTO `creature_text` VALUES (71445, 3, 0, 'Еще одна группа. Кажется, это последние!', 14, 0, 100, 0, 0, 0, 74205, 12000, 12000, 0, 'Капитан Орды');
INSERT INTO `creature_text` VALUES (71445, 3, 0, 'Мы оттеснили врагов обратно на их корабль. Перепрыгни туда с помощью веревки, забери их взрывчатку и подорви их корыто!', 14, 0, 100, 0, 0, 0, 73948, 33000, 33000, 0, 'Капитан Орды to Player');
INSERT INTO `creature_text` VALUES (71445, 3, 0, 'Ну, вот и все! Спускайся в трюм и заложи взрывчатку.', 14, 0, 100, 0, 0, 0, 74135, 130000, 130000, 0, 'Капитан Орды to Player');
INSERT INTO `creature_text` VALUES (71445, 3, 0, 'Помоги нам прогнать псов Альянса!', 14, 0, 100, 0, 0, 0, 74182, 6000, 6000, 0, 'Капитан Орды to Player');
INSERT INTO `creature_text` VALUES (71454, 0, 0, 'Назгрим потерпел неудачу. Кор\'крон, уничтожить их!', 14, 0, 100, 0, 0, 0, 75161, 0, 0, 0, 'Малкорок to Player');
INSERT INTO `creature_text` VALUES (71454, 1, 0, 'I will not fail the leader!', 14, 0, 100, 0, 0, 0, 75162, 0, 0, 0, 'Малкорок to Player');
INSERT INTO `creature_text` VALUES (71454, 2, 0, 'Слабаки...', 14, 0, 100, 0, 0, 0, 75164, 0, 0, 0, 'Малкорок to Контроллер');
INSERT INTO `creature_text` VALUES (71454, 3, 0, '|TInterface\\Icons\\spell_shadow_shadesofdarkness.blp:20|t%s begins to cast a spell |cFFF00000|Hspell:142842|h[Дыхание И\'Шараджа]|h|r!', 41, 0, 100, 0, 0, 0, 75033, 0, 0, 0, 'Малкорок to Контроллер');
INSERT INTO `creature_text` VALUES (71454, 4, 0, 'Вы падете перед мощью нашего вождя!', 14, 0, 100, 0, 0, 0, 75167, 0, 0, 0, 'Малкорок to Контроллер');
INSERT INTO `creature_text` VALUES (71454, 5, 0, 'Вы бросаете вызов вождю?', 14, 0, 100, 0, 0, 0, 75165, 0, 0, 0, 'Малкорок to Контроллер');
INSERT INTO `creature_text` VALUES (71454, 9, 0, 'Вам НИКОГДА не остановить ИСТИННУЮ ОРДУ! ', 14, 0, 100, 0, 0, 0, 75168, 0, 0, 0, 'Малкорок to 0');
INSERT INTO `creature_text` VALUES (71454, 10, 0, '|TInterface\\Icons\\racial_orc_berserkerstrength.blp:20|t%s входит в состояние |cFFF00000|Hspell:142879|h[кровавой ярости]|h|r!', 41, 0, 100, 0, 0, 0, 75186, 0, 0, 0, 'Малкорок to 0');
INSERT INTO `creature_text` VALUES (71454, 11, 0, 'Этого требует вождь!', 14, 0, 100, 0, 0, 0, 75172, 0, 0, 0, 'Малкорок to Player');
INSERT INTO `creature_text` VALUES (71454, 12, 0, '|TInterface\\Icons\\racial_orc_berserkerstrength.blp:20|t%s: |cFFF00000|Hspell:142879|h[Кровавая ярость]|h|r идет на убыль!', 41, 0, 100, 0, 0, 0, 75189, 0, 0, 0, 'Малкорок to 0');
INSERT INTO `creature_text` VALUES (71454, 15, 0, 'Гаррош дает мне силу!', 14, 0, 100, 0, 0, 0, 75163, 0, 0, 0, 'Малкорок to Контроллер');
INSERT INTO `creature_text` VALUES (71454, 17, 0, 'Умереть за вождя... это... честь...', 14, 0, 100, 0, 0, 0, 75170, 0, 0, 0, 'Малкорок to Player');
INSERT INTO `creature_text` VALUES (71466, 0, 0, '%s переходит в режим осады!', 41, 0, 100, 0, 0, 0, 74585, 0, 0, 0, 'Железный исполин to 0');
