INSERT INTO `creature_text` VALUES (71466, 4, 0, '%s снова переходит в режим ближнего боя!', 41, 0, 100, 0, 0, 0, 74584, 0, 0, 0, 'Железный исполин to Железный исполин');
INSERT INTO `creature_text` VALUES (71475, 0, 0, 'Ты... Рук знает тебя...', 14, 0, 100, 396, 0, 38718, 76023, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71475, 1, 0, 'Рук... не знает... все... в тумане...', 14, 0, 100, 396, 0, 38720, 76024, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71475, 2, 0, 'Пожалуйста, помогите...', 14, 0, 100, 396, 0, 38722, 76026, 0, 0, 0, '0');
