INSERT INTO `creature_text` VALUES (71475, 5, 1, 'Рук... в беде...', 14, 0, 100, 0, 0, 38728, 74643, 0, 0, 0, 'Рук Каменная Ступня');
INSERT INTO `creature_text` VALUES (71475, 5, 2, 'Бейте... Рука!', 14, 0, 100, 0, 0, 38728, 74645, 0, 0, 0, 'Рук Каменная Ступня');
INSERT INTO `creature_text` VALUES (71475, 6, 0, '|TInterface/Icons/ability_priest_pathofthedevout:20|t%s begins to cast a spell |cFFFF0000|Hspell:143497|h[Узы Золотого Лотоса]|h|r.', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '0');
