INSERT INTO `creature_text` VALUES (71480, 0, 0, 'Наша судьба – безысходность. Это наше наказание.', 14, 0, 100, 396, 0, 38415, 76025, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71480, 1, 0, 'Получится ли у вас победить?', 14, 0, 100, 0, 0, 38413, 74635, 0, 0, 0, 'Рук Каменная Ступня');
INSERT INTO `creature_text` VALUES (71480, 2, 0, '|TInterface/Icons/ability_priest_halo_shadow:20|t%s begins to cast a spell |cFFFF0000|Hspell:143491|h[Катастрофа]|h|r!', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71480, 3, 0, 'Ощутите боль нашей неудачи.', 14, 0, 100, 0, 0, 38727, 74633, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71480, 4, 0, 'Взываю к вам, защитники!', 14, 0, 100, 0, 0, 38420, 74634, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71480, 5, 0, '|TInterface/Icons/ability_priest_pathofthedevout:20|t%s begins to cast a spell |cFFFF0000|Hspell:143497|h[Узы Золотого Лотоса]|h|r.', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71480, 6, 0, 'Мы вернемся туда, где сбились с пути. Вы... очень добры.', 14, 0, 100, 0, 0, 38414, 74637, 0, 0, 0, 'Сунь Доброе Сердце');
INSERT INTO `creature_text` VALUES (71501, 3, 0, 'Have a great day and don\'t let the door hit you on the way out.', 14, 0, 100, 0, 0, 38472, 75763, 7000, 7000, 0, 'Unsecured Stockpile of Pandaren Spoils to Secured Stockpile of Pandaren Spoils');
INSERT INTO `creature_text` VALUES (71501, 3, 0, 'Security system deactivated.  Welcome back Siegecrafter Blackfuse.', 14, 0, 100, 0, 0, 38478, 75761, 0, 0, 0, 'Unsecured Stockpile of Pandaren Spoils to Secured Stockpile of Pandaren Spoils');
INSERT INTO `creature_text` VALUES (71501, 3, 0, 'Приятного дня. Осторожнее на выходе, не врежьтесь в дверь.', 14, 0, 100, 0, 0, 0, 75763, 7000, 7000, 0, 'Неохраняемый запас пандаренских трофеев to Охраняемый запас пандаренских трофеев');
INSERT INTO `creature_text` VALUES (71501, 3, 0, 'Система безопасности отключена. С возвращением, мастер Черноплавс.', 14, 0, 100, 0, 0, 0, 75761, 0, 0, 0, 'Неохраняемый запас пандаренских трофеев to Охраняемый запас пандаренских трофеев');
INSERT INTO `creature_text` VALUES (71504, 0, 0, 'Совместим приятное с полезным', 14, 0, 100, 0, 0, 38445, 74627, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71504, 1, 0, 'Ах вы, недо...', 14, 0, 100, 0, 0, 38450, 75818, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71504, 2, 0, 'Автоматизированный защитный крошшер ЗК-5. Сделано на совесть!', 14, 0, 100, 0, 0, 0, 74669, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71504, 3, 0, 'Эй, вы за это заплатите...', 14, 0, 100, 0, 0, 38458, 75817, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71504, 4, 0, 'Еще один довольный заказчик.', 14, 0, 100, 0, 0, 38464, 74661, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71504, 5, 0, 'Не думайте что победили...', 14, 0, 100, 0, 0, 38447, 74670, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71504, 6, 0, 'Моё наследие...будет жить...', 14, 0, 100, 0, 0, 38448, 75819, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71515, 0, 0, 'Значит, так все обернулось. Мы вместе учились и росли все эти годы, а теперь сошлись на поле боя. Не ждите от меня пощады, и я не жду ее от вас. Главное, что все мы сражаемся ради одного. Ради Орды.', 14, 0, 100, 0, 0, 0, 75931, 0, 0, 0, 'Генерал Назгрим to Player');
