INSERT INTO `creature_text` VALUES (71828, 0, 0, 'Greetings. I am Sikari, master of jade lightning, chi, and healing mists. For this trial, you must protect me from the incoming illusions, while I heal you and dispatch our foes.', 14, 0, 100, 0, 0, 38439, 75966, 0, 0, 0, 'Sikari the Mistweaver to Player');
INSERT INTO `creature_text` VALUES (71828, 0, 0, 'I am Sikari, the master of jade lightning and healing mists. In this trial, you need to protect me from illusions while I heal you and kill the enemies.', 14, 0, 100, 0, 0, 38439, 75966, 0, 0, 0, 'Ткач туманов Сикари to Player');
INSERT INTO `creature_text` VALUES (71828, 0, 0, 'Тебе снова придется меня защищать. Ротун, заставь нас попотеть!', 14, 0, 100, 0, 0, 38437, 75968, 0, 0, 0, 'Ткач туманов Сикари to Player');
INSERT INTO `creature_text` VALUES (71828, 1, 0, 'Illusionary Foragers are attacking. Do not let their cute faces fool you. They can work together to cause terrible devastation.', 14, 0, 100, 0, 0, 38433, 75963, 0, 0, 0, 'Sikari the Mistweaver to Proving Grounds');
INSERT INTO `creature_text` VALUES (71828, 1, 0, 'Нас атакуют иллюзорные сборщики! Пусть тебя не обманывают их милые мордашки – вместе они страшная сила.', 14, 0, 100, 0, 0, 38433, 75963, 0, 0, 0, 'Ткач туманов Сикари to Арена испытаний');
INSERT INTO `creature_text` VALUES (71828, 3, 0, 'А, привет. Следующее испытание будет сложнее. Защищай меня!', 14, 0, 100, 0, 0, 38438, 75967, 15, 15, 144416, 'Ткач туманов Сикари to Player');
INSERT INTO `creature_text` VALUES (71828, 4, 0, 'Help! Get these things off of me!', 14, 0, 100, 0, 0, 38426, 75956, 10563, 41266, 144103, 'Sikari the Mistweaver to Proving Grounds');
INSERT INTO `creature_text` VALUES (71828, 4, 0, 'I am down. Maybe I should bring more healing herbs for next time.', 14, 0, 100, 4, 0, 38435, 75965, 28719, 28719, 142112, 'Sikari the Mistweaver to Proving Grounds');
INSERT INTO `creature_text` VALUES (71828, 4, 0, 'I can not heal you! Next time, put on a stronger armor.', 14, 0, 100, 4, 0, 38434, 75964, 448266, 448266, 144103, 'Ткач туманов Сикари to Арена испытаний');
INSERT INTO `creature_text` VALUES (71828, 4, 0, 'Illusionary Rippers approaching. They should pose little challenge; they are but mindless grunts.', 14, 0, 100, 0, 0, 38428, 75958, 2781, 2781, 144103, 'Sikari the Mistweaver to Proving Grounds');
INSERT INTO `creature_text` VALUES (71828, 4, 0, 'Берегись, иллюзорные ветростражи могут крыльями отбросить тебя. Если им это удастся, они нападут на меня! ', 14, 0, 100, 0, 0, 38431, 75961, 215875, 215875, 144077, 'Ткач туманов Сикари to Арена испытаний');
INSERT INTO `creature_text` VALUES (71828, 4, 0, 'Готовься, это иллюзорный завоеватель! В исступлении он так силен, что его никто не может остановить!', 14, 0, 100, 0, 0, 38429, 75959, 220719, 220719, 144077, 'Ткач туманов Сикари to Арена испытаний');
INSERT INTO `creature_text` VALUES (71828, 4, 0, 'Иллюзорные потрошители. С этими безмозглыми амбалами легко справиться.', 14, 0, 100, 0, 0, 38428, 75958, 3000, 3000, 144103, 'Ткач туманов Сикари to Арена испытаний');
INSERT INTO `creature_text` VALUES (71828, 4, 0, 'Иллюзорные разбойники! Быстрее, уведи их подальше, чтобы меня не задело этими ужасными бананами!', 14, 0, 100, 0, 0, 38432, 75962, 85656, 85656, 0, 'Ткач туманов Сикари to Арена испытаний');
INSERT INTO `creature_text` VALUES (71828, 4, 0, 'Эй! Сними их с меня!', 14, 0, 100, 0, 0, 38426, 75956, 10000, 12000, 144103, 'Ткач туманов Сикари to Арена испытаний');
INSERT INTO `creature_text` VALUES (71828, 4, 0, 'Это иллюзорные заклинатели пламени. Они бросаются огненными шарами и изрыгают лаву. Используй ее против самих иллюзий!', 14, 0, 100, 0, 0, 38430, 75960, 80875, 80875, 0, 'Ткач туманов Сикари to Арена испытаний');
INSERT INTO `creature_text` VALUES (71828, 4, 0, 'Я вышла из боя! Может, принести больше лекарственных трав?', 14, 0, 100, 4, 0, 38435, 75965, 28000, 28000, 142112, 'Ткач туманов Сикари to Арена испытаний');
