INSERT INTO `creature_text` VALUES (71865, 2, 0, 'Умрите с честью!', 14, 0, 100, 0, 0, 38075, 74711, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71865, 3, 0, 'Исцелите наши раны!', 14, 0, 100, 0, 0, 38072, 74710, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71865, 4, 0, 'Узрите силу оружия Истинной Орды!', 14, 0, 100, 0, 0, 38068, 74707, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71865, 5, 0, 'Мы очистим этот мир сталью и пламенем!', 14, 0, 100, 0, 0, 38070, 74708, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71865, 6, 0, 'Злоба. Ненависть. Страх! Вот орудия войны, вот слуги Вождя!', 14, 0, 100, 0, 0, 38048, 74610, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71865, 7, 0, 'Да... я вижу... вижу, какое будущее ждет этот мир... им будет править Орда... Моя Орда!', 14, 0, 100, 0, 0, 38051, 74616, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71865, 8, 0, 'I am full of power!', 14, 0, 100, 0, 0, 38076, 74714, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71865, 9, 0, 'Сила во мне уничтожит вас и весь ваш мир.', 14, 0, 100, 0, 0, 38077, 74715, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71865, 10, 0, 'Начнется правление Истинной Орды. Я видел это. Он показал мне горы черепов и реки крови. Мир... будет... моим!', 14, 0, 100, 0, 0, 38056, 74724, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71865, 11, 0, 'Ничтожество!', 14, 0, 100, 0, 0, 38065, 134193, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71865, 12, 0, 'Это не может... кончиться... так... Нет... я же видел...', 14, 0, 100, 0, 0, 38046, 75369, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71865, 13, 0, 'Вы окажетесь в ловушке навеки!', 14, 0, 100, 0, 0, 38055, 74723, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Good deal. Welcome to the spirit world. Kinda. Let\'s check out the chests for any goodies, and see if you can find my body somewhere!', 12, 0, 100, 0, 0, 0, 74489, 0, 0, 0, 'Zarhym to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'I am free, like a ghost in the sky!', 12, 0, 100, 0, 0, 0, 74470, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Sorry for ribbin\' ya so much.', 12, 0, 100, 0, 0, 0, 74473, 0, 0, 0, 'Zarhym to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Всегда хотел поиграть на органе.', 12, 0, 100, 0, 0, 0, 74481, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Говори что угодно, душа у меня в пятки не уйдет!', 12, 0, 100, 0, 0, 0, 74484, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Голова на плечах есть, а толку...', 12, 0, 100, 0, 0, 0, 74472, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Знаешь... У меня к этому сердце не лежит.', 12, 0, 100, 0, 0, 0, 74478, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Знаю, что у меня шутки с душком... Да и сам я не так давно разложился.', 12, 0, 100, 0, 0, 0, 74468, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Извини, что я тебя так часто подкалываю.', 12, 0, 100, 0, 0, 0, 74473, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Каждая косточка в моем теле тянется к тебе!', 12, 0, 100, 0, 0, 0, 74482, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Какое привидение живет под полом? Полтергейст!', 12, 0, 100, 0, 0, 0, 74467, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Кого ты надурить хочешь, призрак? Я тебя насквозь вижу!', 12, 0, 100, 0, 0, 0, 74469, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Не церемонься с наглыми привидениями, пересчитай им все кости!', 12, 0, 100, 0, 0, 0, 74474, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Отлично. Добро пожаловать в мир духов. Ну, или вроде того. Давай обыщем сундуки на предмет добычи. А может, ты где-нибудь найдешь и мое тело?', 12, 0, 100, 0, 0, 0, 74489, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Почему я не двигаю телом на тусовках? Так нечем двигать!', 12, 0, 100, 0, 0, 0, 74466, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Ты кого твердолобым назвал?', 12, 0, 100, 0, 0, 0, 74471, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'У меня кишка не тонка с тобой тягаться!', 12, 0, 100, 0, 0, 0, 74465, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Хочешь смешное слово? Копчик.', 12, 0, 100, 0, 0, 0, 74476, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Шевелюра у меня ни к черту, зато все шутки с бородой.', 12, 0, 100, 0, 0, 0, 74477, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Я ведь не такой уж и сварливый!', 12, 0, 100, 0, 0, 0, 74464, 0, 0, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 3, 0, 'I wanted to ask her out, but she didn’t have enough spirit.', 12, 0, 100, 0, 0, 0, 74486, 72000, 72000, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 3, 0, 'До смерти ты меня не заговоришь...', 12, 0, 100, 0, 0, 0, 74485, 102000, 102000, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 3, 0, 'Дух из меня так просто не вышибешь.', 12, 0, 100, 0, 0, 0, 74483, 162000, 252000, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 3, 0, 'Не знаю, хватит ли у меня на это духу...', 12, 0, 100, 0, 0, 0, 74487, 12000, 192000, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 3, 0, 'Призракам, наверное, очень холодно. Ветерок-то насквозь продувает!', 12, 0, 100, 0, 0, 0, 74480, 222000, 222000, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71869, 3, 0, 'Что-то я сегодня на ногах не держусь… Ой! У меня же нет ног!', 12, 0, 100, 0, 0, 0, 74479, 42000, 42000, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71876, 3, 0, 'Эй, ты! Подойди сюда! У меня ведь нет тела!', 12, 0, 100, 0, 0, 0, 74488, 147453, 147453, 0, 'Зарим to Player');
INSERT INTO `creature_text` VALUES (71889, 0, 0, 'Эй, записываем? Хорошо. Запускаю гоблинско-титанский блок управления. Все назад.', 14, 0, 100, 0, 0, 0, 75750, 0, 0, 0, 'Охраняемый запас пандаренских трофеев to Player');
INSERT INTO `creature_text` VALUES (71889, 1, 0, 'Проверяю данные. Подождете секунду?', 14, 0, 100, 0, 0, 0, 75751, 0, 0, 0, 'Охраняемый запас пандаренских трофеев to Player');
INSERT INTO `creature_text` VALUES (71889, 2, 0, 'Это вы зря. Совсем-совсем-совсем неверный код доступа.', 14, 0, 100, 0, 0, 0, 75752, 0, 0, 0, 'Охраняемый запас пандаренских трофеев to Player');
INSERT INTO `creature_text` VALUES (71889, 3, 0, 'Активирую защитные системы класса \"Армагеддон\" ГБ-11010. Кто-нибудь знает, это безопасно?', 14, 0, 100, 0, 0, 0, 75754, 0, 0, 0, 'Охраняемый запас пандаренских трофеев to Player');
INSERT INTO `creature_text` VALUES (71889, 4, 0, 'Запускаю перезагрузку системы. Первый модуль на 50%%. Эй! Для перезагрузки требуются оба модуля.', 14, 0, 100, 0, 0, 0, 75756, 0, 0, 0, 'Охраняемый запас пандаренских трофеев to Player');
INSERT INTO `creature_text` VALUES (71889, 5, 0, 'Первый модуль готов к перезагрузке системы.', 14, 0, 100, 0, 0, 0, 75757, 0, 0, 0, 'Охраняемый запас пандаренских трофеев to Player');
INSERT INTO `creature_text` VALUES (71889, 6, 0, 'Запускаю перезагрузку системы. Второй модуль на 50%%. Эй! Для перезагрузки требуются оба модуля.', 14, 0, 100, 0, 0, 0, 75758, 0, 0, 0, 'Охраняемый запас пандаренских трофеев to Player');
INSERT INTO `creature_text` VALUES (71889, 7, 0, 'Второй модуль готов к перезагрузке системы.', 14, 0, 100, 0, 0, 0, 75759, 0, 0, 0, 'Охраняемый запас пандаренских трофеев to Player');
INSERT INTO `creature_text` VALUES (71889, 8, 0, 'Система перезагружается. Не выключать питание, иначе возможен взрыв.', 14, 0, 100, 0, 0, 0, 75760, 0, 0, 0, 'Охраняемый запас пандаренских трофеев to Player');
INSERT INTO `creature_text` VALUES (71914, 0, 0, 'Железный исполин наводит на вас |cFFFF0404|Hspell:144918|h[режущий лазер]|h|r!', 42, 0, 100, 0, 0, 0, 75173, 0, 0, 0, 'Хвостовое орудие to Player');
INSERT INTO `creature_text` VALUES (71952, 0, 0, 'Перед лицом неведомых испытаний – на что вы надеетесь? Какого будущего ждете?', 14, 0, 100, 0, 0, 38026, 75897, 0, 0, 0, 'Чи-Цзи to 71954');
INSERT INTO `creature_text` VALUES (71952, 1, 0, 'Тогда начнем.', 14, 0, 100, 0, 0, 38024, 75898, 0, 0, 0, 'Чи-Цзи to 0');
INSERT INTO `creature_text` VALUES (71952, 2, 0, 'Будущее беспросветно, если в нем нет надежды!', 14, 0, 100, 0, 0, 38029, 75900, 0, 0, 0, 'Чи-Цзи to 0');
INSERT INTO `creature_text` VALUES (71952, 3, 0, 'Вас озаряет свет надежды, и он становится ярче, когда вы объединяетесь для свершений! Он будет светить вам даже в кромешной тьме.', 14, 0, 100, 0, 0, 38025, 75903, 0, 0, 0, 'Чи-Цзи to 0');
INSERT INTO `creature_text` VALUES (71953, 0, 0, 'Возвращайся вдвое сильнее!', 14, 0, 100, 0, 0, 38214, 75916, 0, 0, 0, 'Сюэнь to Player');
INSERT INTO `creature_text` VALUES (71953, 1, 0, 'Поверьте в свою силу!', 14, 0, 100, 0, 0, 38215, 75913, 0, 0, 0, 'Сюэнь to 0');
INSERT INTO `creature_text` VALUES (71953, 2, 0, 'Могущество, что предлагает тьма, – не истинная сила!', 14, 0, 100, 0, 0, 38217, 75915, 0, 0, 0, 'Сюэнь to 0');
INSERT INTO `creature_text` VALUES (71953, 3, 0, 'В ваших силах изменить свою судьбу!', 14, 0, 100, 0, 0, 38216, 75914, 0, 0, 0, 'Сюэнь to 0');
INSERT INTO `creature_text` VALUES (71953, 5, 0, 'Вы сильны, даже сильнее, чем представляете! Ступайте с этим знанием во тьму, и оно защитит вас.', 14, 0, 100, 0, 0, 38212, 75917, 0, 0, 0, 'Сюэнь to 0');
INSERT INTO `creature_text` VALUES (71954, 0, 0, 'Устоите ли вы на вершине вопреки непогоде, пока лес не иссохнет и горы не падут в море?', 14, 0, 100, 0, 0, 38757, 75890, 0, 0, 0, 'Нюцзао to 71955');
INSERT INTO `creature_text` VALUES (71954, 1, 0, 'Увидим.', 14, 0, 100, 0, 0, 38755, 112255, 0, 0, 0, 'Нюцзао to 0');
INSERT INTO `creature_text` VALUES (71954, 2, 0, 'Ветра сильны, а снег жалит кожу.', 14, 0, 100, 0, 0, 38759, 75892, 0, 0, 0, 'Нюцзао to 0');
INSERT INTO `creature_text` VALUES (71954, 3, 0, 'Вас будут окружать враги невероятной силы. Большей, чем вы можете представить. Но вы все выдержите благодаря своей стойкости. Помните об этом!', 14, 0, 100, 0, 0, 38756, 75896, 0, 0, 0, 'Нюцзао to 0');
INSERT INTO `creature_text` VALUES (71954, 6, 0, 'Вы – твердыня, которую ничто не может пошатнуть', 14, 0, 100, 0, 0, 38760, 75893, 0, 0, 0, 'Нюцзао to 0');
INSERT INTO `creature_text` VALUES (71955, 0, 0, 'Мои дорогие герои, истинная мудрость проявляется в вашем выборе. Столкнувшись с великой тьмой, сможете ли вы принять верное решение?', 14, 0, 100, 0, 0, 38224, 75904, 0, 0, 0, 'Юй-лун to 71955');
INSERT INTO `creature_text` VALUES (71955, 1, 0, 'Не позволяйте разуму затуманиваться.', 14, 0, 100, 0, 0, 38227, 75907, 0, 0, 0, 'Юй-лун to 0');
INSERT INTO `creature_text` VALUES (71955, 2, 0, 'Мудрость помогла вам пройти испытание. Пусть она всегда ведет вас к свету.', 14, 0, 100, 0, 0, 38223, 75910, 0, 0, 0, 'Юй-лун to 0');
INSERT INTO `creature_text` VALUES (71965, 0, 0, 'Вы выдержали испытание и доказали, что достойны. Но помните: внутри вас ждут куда более серьезные трудности. Как только будете готовы, можете войти.', 14, 0, 100, 0, 0, 38882, 74958, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71965, 1, 0, 'Порча усиливается. Фрагменты нужно уничтожить, иначе будет поздно.', 14, 0, 100, 0, 0, 38903, 74962, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71965, 2, 0, 'Не сработало. Он идет. Приготовьтесь. Будьте стойки душой и сердцем.', 14, 0, 100, 0, 0, 38904, 74963, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71965, 3, 0, 'Отбросьте высокомерие.', 14, 0, 100, 0, 0, 38901, 74966, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71965, 3, 1, 'Будьте скромнее.', 14, 0, 100, 0, 0, 38900, 74965, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71965, 4, 0, 'Вы... должны... удержать... его...', 14, 0, 100, 0, 0, 38902, 74964, 0, 0, 0, 'Ша Гордыни');
INSERT INTO `creature_text` VALUES (71967, 0, 0, 'Стойте!', 14, 0, 100, 0, 0, 38883, 75026, 0, 0, 0, 'Хранитель истории Чо');
INSERT INTO `creature_text` VALUES (71967, 1, 0, 'Порча больше не проникнет в покои сердца!', 14, 0, 100, 0, 0, 38884, 75027, 0, 0, 0, 'Хранитель истории Чо');
INSERT INTO `creature_text` VALUES (71967, 2, 0, 'Вы хотите победить порчу?', 14, 0, 100, 0, 0, 38885, 75028, 0, 0, 0, 'Хранитель истории Чо');
INSERT INTO `creature_text` VALUES (71967, 3, 0, 'Если вы пройдете через эти двери сейчас, то потерпите неудачу. Вы поддались коварной порче, и имя ей – Гордыня.', 14, 0, 100, 0, 0, 38886, 75029, 0, 0, 0, 'Хранитель истории Чо');
INSERT INTO `creature_text` VALUES (71967, 4, 0, 'Вы гордитесь своими победами, и в этом ваша слабость.', 14, 0, 100, 0, 0, 0, 75159, 0, 0, 0, 'Хранитель истории Чо');
INSERT INTO `creature_text` VALUES (71967, 5, 0, 'Чтобы победить порчу, сначала нужно убить ее в себе.', 14, 0, 100, 0, 0, 0, 75160, 0, 0, 0, 'Хранитель истории Чо');
INSERT INTO `creature_text` VALUES (71967, 6, 0, 'Скажите, когда будете готовы ко встрече со своими демонами.', 14, 0, 100, 0, 0, 38887, 75030, 0, 0, 0, 'Хранитель истории Чо');
INSERT INTO `creature_text` VALUES (71967, 7, 0, 'Хорошо, я создам поле для удерживания порчи.', 14, 0, 100, 0, 0, 38888, 75031, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71967, 8, 0, 'Докажите, что достойны, и я пропущу вас.', 14, 0, 100, 0, 0, 38889, 75032, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71967, 9, 0, 'Свет несет очищение, но принять его непросто. Приготовьтесь! ', 14, 0, 100, 0, 0, 38880, 75034, 0, 0, 0, 'Норусхен');
INSERT INTO `creature_text` VALUES (71967, 10, 0, 'Загляните в себя, изгоните тьму.', 14, 0, 100, 0, 0, 38892, 75035, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (71985, 3, 0, 'Вас убил |cFFFF0000|Hspell:135096|h[громовой раскат]|h|r. Убегайте от раскатов, чтобы не получать урона.', 42, 0, 100, 0, 0, 0, 72529, 903000, 903000, 0, 'Кор\'кронская железная звезда to Player');
INSERT INTO `creature_text` VALUES (71987, 1, 0, 'O OoO OOoO', 12, 0, 100, 0, 0, 0, 74561, 0, 0, 0, 'Призрачный пират to Player');
INSERT INTO `creature_text` VALUES (71987, 1, 0, 'oOO OOooO', 12, 0, 100, 0, 0, 0, 74562, 0, 0, 0, 'Призрачный пират to Player');
INSERT INTO `creature_text` VALUES (71992, 0, 0, '|cFFFF0000BEWARE:|r Moonfang Snarler now prowl the woods!', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'zonetalk');
INSERT INTO `creature_text` VALUES (71992, 1, 0, '|cFFFF0000BEWARE:|r Moonfang Dreadhowls now prowl the woods!', 41, 0, 100, 0, 0, 0, 74594, 0, 0, 0, 'zonetalk');
INSERT INTO `creature_text` VALUES (71992, 2, 0, '|cFFFF0000BEWARE:|r Den Mother Moonfang has appeared in the woods!', 41, 0, 100, 0, 0, 0, 74595, 0, 0, 0, 'zonetalk');
INSERT INTO `creature_text` VALUES (72048, 0, 0, 'RATTLESKEW!', 14, 0, 100, 1, 0, 0, 77118, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (72057, 0, 0, 'You will take my place on the eternal brazier.', 14, 0, 100, 0, 0, 0, 76160, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (72057, 1, 0, 'Burn!', 14, 0, 100, 0, 0, 0, 106132, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (72057, 2, 0, 'You will feel but a fraction of my agony.', 14, 0, 100, 0, 0, 0, 76163, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (72057, 3, 0, 'Your flesh will melt.', 14, 0, 100, 0, 0, 0, 76161, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (72057, 4, 0, 'Your pain will be endless.', 14, 0, 100, 0, 0, 0, 76162, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (72057, 5, 0, 'The eternal fire will never be extinguished.', 14, 0, 100, 0, 0, 0, 76166, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (72145, 0, 0, 'I will NOT burn like the rest! Face the demons you fear, traitors!', 14, 0, 100, 0, 0, 0, 74817, 0, 0, 0, 'Grol\'dar to Player');
INSERT INTO `creature_text` VALUES (72145, 3, 0, 'I will not burn with the rest! Now you will see those demons that you fear, traitors!', 14, 0, 100, 0, 0, 0, 74817, 11000, 11000, 0, 'Гроль\'дар to Player');
INSERT INTO `creature_text` VALUES (72151, 3, 0, 'Э... Ты же справишься, да?', 12, 0, 100, 547, 0, 0, 74605, 8547, 8547, 0, 'Финь Длинная Лапа to Player');
INSERT INTO `creature_text` VALUES (72156, 4, 0, 'Боррок Пожиратель голоден! Накормите его ограми из клана Жестокого Холода!', 41, 0, 100, 0, 0, 0, 78042, 3000, 3000, 133481, 'Боррок Пожиратель to Чернорабочий из клана Жестокого Холода');
INSERT INTO `creature_text` VALUES (72156, 4, 0, 'Довольный Боррок выплевывает огромный камень.', 41, 0, 100, 0, 0, 0, 78169, 149000, 149000, 151180, 'Боррок Пожиратель to Player');
INSERT INTO `creature_text` VALUES (72191, 0, 0, 'Вот и наши противники! Разрушители, огонь на поражение!', 14, 0, 100, 0, 0, 0, 74795, 0, 0, 0, 'Властитель Рунтак to Разрушитель Адского Крика');
INSERT INTO `creature_text` VALUES (72192, 0, 0, 'Нннфф... спасибо... тебе...', 12, 0, 100, 0, 0, 0, 74937, 0, 0, 0, 'Гамон to Player');
INSERT INTO `creature_text` VALUES (72192, 2, 0, 'Меня здорово потрепали, но внутри меня кипит ярость.', 12, 0, 0, 0, 0, 0, 74938, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (72192, 3, 0, 'Много раз орки избивали меня до полусмерти, но каждый раз я вставал вновь.', 12, 0, 0, 0, 0, 0, 74939, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (72192, 4, 0, 'Всё! Довольно!', 12, 0, 0, 0, 0, 0, 74940, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (72192, 5, 0, 'За каждый удар орка я раскрошу тысячу их черепов.', 12, 0, 0, 0, 0, 0, 74941, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (72192, 6, 0, 'Пришла расплата!', 14, 0, 0, 0, 0, 0, 74942, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (72192, 7, 0, 'I, Gamon, will save us all!', 14, 0, 0, 0, 0, 0, 90157, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (72192, 8, 0, 'Ты сражался с честью. Гамон позаботится о твоей доброй памяти. За Орду, друг мой.', 12, 0, 0, 0, 0, 0, 75888, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (72192, 9, 0, 'Назгрим, ты достойный орк. Я сожалею о том, что должен сделать.', 12, 0, 0, 0, 0, 0, 75887, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (72218, 0, 0, 'Hello. For this trial, you must keep us alive while we face the incoming illusions. I am Oto, and will protect you.', 14, 0, 100, 0, 0, 38599, 75861, 0, 0, 0, 'Oto the Protector to Player');
INSERT INTO `creature_text` VALUES (72218, 0, 0, 'Здравствуй. Твоя задача – поддерживать наше здоровье. Я Ото, и я защищу тебя от иллюзий.', 14, 0, 100, 0, 0, 38599, 75861, 0, 0, 0, 'Защитник Ото to Player');
INSERT INTO `creature_text` VALUES (72218, 1, 0, 'Ах, новое испытание – иллюзорные завоеватели! Лечи меня быстро, когда они разъярятся.', 14, 0, 100, 0, 0, 38597, 75865, 0, 0, 0, 'Защитник Ото to Арена испытаний');
INSERT INTO `creature_text` VALUES (72218, 1, 0, 'Иллюзорные певцы улья! Их звуковые волны накроют всех!', 14, 0, 100, 0, 0, 38596, 75864, 0, 0, 0, 'Защитник Ото to Арена испытаний');
INSERT INTO `creature_text` VALUES (72218, 4, 0, 'Ah, a new challenge, Illusionary Conquerors! When they become enraged, they strike with incredible power.', 14, 0, 100, 0, 0, 38597, 75865, 65000, 65000, 0, 'Oto the Protector to Proving Grounds');
INSERT INTO `creature_text` VALUES (72218, 4, 0, 'I am a wall of iron, but I cannot hold much longer!', 14, 0, 100, 0, 0, 38594, 75863, 7859, 7859, 145057, 'Oto the Protector to Proving Grounds');
INSERT INTO `creature_text` VALUES (72218, 4, 0, 'I did not survive, I\'m sorry! Next time I can handle it.', 14, 0, 100, 0, 0, 38598, 75866, 33950, 33950, 142112, 'Защитник Ото to Арена испытаний');
INSERT INTO `creature_text` VALUES (72218, 4, 0, 'I have fallen. I am sorry, I shall train harder for next time.', 14, 0, 100, 0, 0, 38598, 75866, 13438, 13438, 142112, 'Oto the Protector to Proving Grounds');
INSERT INTO `creature_text` VALUES (72218, 4, 0, 'Illusionary Hive-Singers! Their sonic waves will harm us all.', 14, 0, 100, 0, 0, 38596, 75864, 31000, 31000, 0, 'Oto the Protector to Proving Grounds');
INSERT INTO `creature_text` VALUES (72218, 4, 0, 'Глухая оборона!', 14, 0, 100, 0, 0, 38594, 75863, 13719, 17732, 145057, 'Защитник Ото to Арена испытаний');
INSERT INTO `creature_text` VALUES (72219, 0, 0, 'На этот раз нас ждет испытание посложнее: атаки иллюзий усилятся. Приготовь свои самые сильные исцеляющие заклинания.', 14, 0, 100, 0, 0, 38588, 75879, 0, 0, 0, 'Убийца Ки to Player');
INSERT INTO `creature_text` VALUES (72219, 1, 0, 'Ah, our first enemy! Illusionary Rippers are but simple warriors. Now, let us do this!', 14, 0, 100, 0, 0, 38584, 75875, 0, 0, 0, 'Ki the Assassin to Proving Grounds');
INSERT INTO `creature_text` VALUES (72219, 1, 0, 'Ага, наш первый противник – иллюзорные потрошители. Обычные воины, так, ничего особенного. Вперед!', 14, 0, 100, 0, 0, 38584, 75875, 0, 0, 0, 'Убийца Ки to Арена испытаний');
INSERT INTO `creature_text` VALUES (72219, 3, 0, 'The illusions will not hold back, this time. Their attacks will be relentless. Be prepared to use your strongest healing spells.', 14, 0, 100, 0, 0, 38588, 75879, 0, 0, 0, 'Ki the Assassin to Player');
INSERT INTO `creature_text` VALUES (72219, 3, 0, 'Тише, Каван. Уверена, будет лучше.', 14, 0, 100, 0, 0, 38581, 75873, 88831, 88831, 0, 'Убийца Ки to Арена испытаний');
INSERT INTO `creature_text` VALUES (72219, 4, 0, '|cFFFF8040Ki the Assassin |r|cFF71D5FF|Hspell:145427|h[Kick]|h|r|cFFFF8040s the $n.|r', 41, 0, 100, 0, 0, 0, 75595, 2000, 266000, 0, 'Ki the Assassin to Large Illusionary Flamecaller');
INSERT INTO `creature_text` VALUES (72219, 4, 0, '|cFFFF8040Убийца Ки |r|cFF71D5FF|Hspell:145427|h[бьет ногой]|h|r|cFFFF8040 |3-3($n).|r', 41, 0, 100, 0, 0, 0, 75595, 2106, 46750, 145420, 'Убийца Ки to Малый иллюзорный певец улья');
INSERT INTO `creature_text` VALUES (72219, 4, 0, 'I am sure they are trying, Kavan. Be nice.', 14, 0, 100, 0, 0, 38581, 75873, 43000, 43000, 0, 'Ki the Assassin to Proving Grounds');
INSERT INTO `creature_text` VALUES (72219, 4, 0, 'Illusionary Flamecallers! Watch your feet, everyone. Do not stand in the lava.', 14, 0, 100, 0, 0, 38585, 75876, 36000, 36000, 0, 'Ki the Assassin to Proving Grounds');
INSERT INTO `creature_text` VALUES (72219, 4, 0, 'Sooli, move!', 14, 0, 100, 0, 0, 38582, 75874, 30000, 30000, 0, 'Ki the Assassin to Proving Grounds');
INSERT INTO `creature_text` VALUES (72219, 4, 0, 'Иллюзорные заклинатели пламени! Смотрите под ноги, не стойте в лаве!', 14, 0, 100, 0, 0, 38585, 75876, 35725, 35725, 145420, 'Убийца Ки to Арена испытаний');
INSERT INTO `creature_text` VALUES (72220, 0, 0, 'Этим иллюзиям не видно конца, но мы будем сражаться до последнего!', 14, 0, 100, 0, 0, 38969, 75884, 0, 0, 0, 'Сули Выживающий to Player');
INSERT INTO `creature_text` VALUES (72220, 1, 0, 'О, иллюзорные копатели... Мы с Торопыгой уже встречали таких созданий. Осторожно, они могут закапываться в землю, а потом ка-ак выпрыгнут! Ха-ха-ха...', 14, 0, 100, 0, 0, 38967, 75882, 0, 0, 145759, 'Сули Выживающий to Арена испытаний');
INSERT INTO `creature_text` VALUES (72220, 3, 0, 'There is no end in sight to these illusions, but we shall hold out as long as we can.', 14, 0, 100, 0, 0, 38969, 75884, 0, 0, 0, 'Sooli the Survivalist to Player');
INSERT INTO `creature_text` VALUES (72220, 4, 0, 'Ahh, Illusionary Tunnelers! Speedy and I have faced these foes before. Beware, they may burrow into the ground and then suddenly pop up under a new target, and CHOMP! Hah ha hah ha.', 14, 0, 100, 0, 0, 38967, 75882, 80000, 80000, 0, 'Sooli the Survivalist to Proving Grounds');
INSERT INTO `creature_text` VALUES (72220, 4, 0, 'Hah ha! Wonderful job, everyone! Friend, see Rotun about beginning the next trial.', 14, 0, 100, 0, 0, 38970, 75885, 80000, 80000, 0, 'Sooli the Survivalist to Proving Grounds');
INSERT INTO `creature_text` VALUES (72220, 4, 0, 'Huh? Ohhhh, ohhh!', 14, 0, 100, 0, 0, 38965, 75881, 15000, 15000, 0, 'Sooli the Survivalist to Proving Grounds');
INSERT INTO `creature_text` VALUES (72220, 4, 0, 'Отлично! Все молодцы! Но Ротун подготовил новое испытание.', 14, 0, 100, 0, 0, 38970, 75885, 62695, 62695, 0, 'Сули Выживающий to Арена испытаний');
INSERT INTO `creature_text` VALUES (72221, 1, 0, 'Иллюзорные аквалиты будут накладывать темные чары. Раccеивай их скорее!', 14, 0, 100, 0, 0, 38961, 75869, 0, 0, 0, 'Чародейка Каван to Арена испытаний');
INSERT INTO `creature_text` VALUES (72221, 3, 0, 'Hello again. More illusions approach, and we will require your healing arts.', 14, 0, 100, 0, 0, 38963, 75871, 0, 0, 0, 'Kavan the Arcanist to Player');
INSERT INTO `creature_text` VALUES (72221, 3, 0, 'Привет! Иллюзии приближаются, нам снова потребуется целитель.', 14, 0, 100, 0, 0, 38963, 75871, 40, 40, 144416, 'Чародейка Каван to Player');
INSERT INTO `creature_text` VALUES (72221, 4, 0, '|cFFFF8040Kavan the Arcanist |r|cFF71D5FF|Hspell:145530|h[Counterspell]|h|r|cFFFF8040s the $n.|r', 41, 0, 100, 0, 0, 0, 75596, 2000, 64000, 0, 'Kavan the Arcanist to Small Illusionary Hive-Singer');
INSERT INTO `creature_text` VALUES (72221, 4, 0, '|cFFFF8040Чародейка Каван использует |r|cFF71D5FF|Hspell:145530|h[антимагию]|h|r|cFFFF8040 против |3-1($n).|r', 41, 0, 100, 0, 0, 0, 75596, 3875, 45665, 145530, 'Чародейка Каван to Большой иллюзорный певец улья');
INSERT INTO `creature_text` VALUES (72221, 4, 0, 'An Aqua Bomb has detonated! Dispel it faster, next time.', 14, 0, 100, 0, 0, 38959, 75868, 39000, 39000, 0, 'Kavan the Arcanist to Proving Grounds');
INSERT INTO `creature_text` VALUES (72221, 4, 0, 'I am down! Quick healing spells would be best for emergencies like that!', 14, 0, 100, 0, 0, 38962, 75870, 268000, 268000, 0, 'Kavan the Arcanist to Proving Grounds');
INSERT INTO `creature_text` VALUES (72221, 4, 0, 'These Illusionary Aqualytes will affect us with dark magic. Dispel it quickly.', 14, 0, 100, 0, 0, 38961, 75869, 61000, 61000, 0, 'Kavan the Arcanist to Proving Grounds');
INSERT INTO `creature_text` VALUES (72221, 4, 0, 'Водяная бомба взорвалась! Рассеивай их быстрее!', 14, 0, 100, 0, 0, 38959, 75868, 16758, 16758, 145437, 'Чародейка Каван to Арена испытаний');
INSERT INTO `creature_text` VALUES (72228, 0, 0, 'У тебя не хватит сил, чтобы одолеть его. Тебе не выстоять.', 15, 0, 100, 0, 0, 0, 75594, 0, 0, 0, 'Сердце И\'Шараджа to Player');
INSERT INTO `creature_text` VALUES (72228, 1, 0, 'Тебе не победить. Это безнадежно.', 15, 0, 100, 0, 0, 27753, 75592, 0, 0, 0, 'Сердце И\'Шараджа to Player');
INSERT INTO `creature_text` VALUES (72230, 0, 0, 'Крон приближается! За мной!', 12, 0, 100, 0, 0, 41798, 79609, 0, 0, 0, 'Дрека to Player');
INSERT INTO `creature_text` VALUES (72230, 3, 0, 'Берегись! Горены! ', 14, 0, 100, 0, 0, 41779, 77498, 1000, 1000, 0, 'Дрека');
INSERT INTO `creature_text` VALUES (72230, 3, 0, 'Дрек\'Тар! Прикрой меня.', 14, 0, 100, 0, 0, 41782, 77504, 91000, 91000, 0, 'Дрека to Предсказатель Дрек\'Тар');
INSERT INTO `creature_text` VALUES (72230, 3, 0, 'Так слушай быстрее, пока нас не растерзали!', 14, 0, 100, 0, 0, 41781, 77505, 45000, 45000, 0, 'Дрека to Предсказатель Дрек\'Тар');
INSERT INTO `creature_text` VALUES (72248, 0, 0, 'Направьте орудия на башню! Похороните их заживо!', 14, 0, 100, 0, 0, 38399, 75200, 0, 0, 0, 'Полководец Зела to Специалист-взрывотехник');
INSERT INTO `creature_text` VALUES (72248, 0, 0, 'Они ворвались в башню. Разрушить ее!', 14, 0, 100, 0, 0, 38397, 75198, 0, 0, 0, 'Полководец Зела to Специалист-взрывотехник');
INSERT INTO `creature_text` VALUES (72248, 0, 0, 'Следующий отряд!', 14, 0, 100, 0, 0, 38393, 75194, 0, 0, 0, 'Полководец Зела to Player');
INSERT INTO `creature_text` VALUES (72248, 3, 0, '%s is blasted off of her mount!', 41, 0, 100, 0, 0, 38154, 75343, 1219000, 1219000, 0, 'Warlord Zaela to Galakras');
INSERT INTO `creature_text` VALUES (72248, 3, 0, '%s от удара вылетает из седла!', 41, 0, 100, 0, 0, 38087, 75343, 601000, 601000, 146878, 'Полководец Зела to Галакрас');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'Ahhhhhhh!', 14, 0, 100, 0, 0, 38154, 97349, 1219000, 1219000, 0, 'Warlord Zaela to Galakras');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'Dragonmaw clan, retake the docks and push them into the sea!  In the name of Hellscream and the True Horde!', 14, 0, 100, 25, 0, 38391, 75193, 792000, 792000, 0, 'Warlord Zaela to Galakras');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'Dragonmaw, advance!', 14, 0, 100, 0, 0, 38394, 75195, 852000, 1126000, 0, 'Warlord Zaela to Player');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'For Hellscream!', 14, 0, 100, 5, 0, 38395, 75196, 907000, 1181000, 0, 'Warlord Zaela to Player');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'Galakras staggers, but recovers from the blast!', 41, 0, 100, 0, 0, 0, 75342, 1035000, 1035000, 0, 'Warlord Zaela to Dragonmaw Proto-Drake');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'Next squad, push forward!', 14, 0, 100, 0, 0, 38393, 75194, 1072000, 1072000, 0, 'Warlord Zaela to Player');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'The tower reinforcements will not abandon their post!', 41, 0, 100, 0, 0, 0, 75403, 47000, 47000, 0, 'Warlord Zaela to Dragonmaw Flameslinger');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'They\'ve breached the tower, bring it down!', 14, 0, 100, 0, 0, 38397, 75198, 927000, 927000, 0, 'Warlord Zaela to Demolitions Expert');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'Turn the siege weapons to attack that tower, and bury them alive!', 14, 0, 100, 0, 0, 38399, 75200, 937000, 937000, 0, 'Warlord Zaela to Demolitions Expert');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'You think you have won? We are orcs! Pure of blood, commanded by the greatest Warchief who ever lived! Your blood will fill this bay!', 14, 0, 100, 0, 0, 0, 75192, 344000, 344000, 0, 'Warlord Zaela to Galakras');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'Аргх!', 14, 0, 100, 0, 0, 38087, 128631, 601000, 601000, 146878, 'Полководец Зела to Галакрас');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'Воины Драконьей Пасти! Отбейте пристань и сбросьте врага в море! Во имя Истинной Орды!', 14, 0, 100, 25, 0, 38391, 75193, 42000, 42000, 0, 'Полководец Зела to Галакрас');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'Галакрас теряет высоту, но приходит в себя после выстрела!', 41, 0, 100, 0, 0, 0, 75342, 412000, 412000, 0, 'Полководец Зела to Протодракон клана Драконьей Пасти');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'Драконья Пасть, вперед!', 14, 0, 100, 0, 0, 38394, 75195, 101000, 541000, 0, 'Полководец Зела to Player');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'Думаете, вы победили? Мы – орки! Братья по крови, ведомые величайшим вождем! Ваша кровь наполнит залив!', 14, 0, 100, 0, 0, 0, 75192, 169000, 169000, 0, 'Полководец Зела to Галакрас');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'За Гарроша!', 14, 0, 100, 5, 0, 38395, 81860, 156000, 431000, 0, 'Полководец Зела to Player');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'Защитники башни не покинут свой пост!', 41, 0, 100, 0, 0, 0, 75403, 354000, 371000, 0, 'Полководец Зела to Рубака из клана Драконьей Пасти');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'Подкрепление на пристань! Лок\'тар огар!', 14, 0, 100, 0, 0, 38401, 75191, 16000, 16000, 0, 'Полководец Зела to Галакрас');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'Эту пристань удерживает Драконья Пасть! Вы сгорите вместе с кораблями!', 14, 0, 100, 0, 0, 38400, 75190, 19000, 19000, 0, 'Полководец Зела to Галакрас');
INSERT INTO `creature_text` VALUES (72274, 0, 0, 'Слышишь, Аша? Карг рядом. Значит, ты сможешь поиграть с Нероком!', 12, 0, 100, 0, 0, 0, 77524, 0, 0, 0, 'Локра to Player');
INSERT INTO `creature_text` VALUES (72277, 3, 0, 'I will fight by your side.', 12, 0, 100, 0, 0, 0, 129595, 9968, 9968, 159890, 'Frostwolf Warrior to Player');
INSERT INTO `creature_text` VALUES (72277, 3, 0, 'Эти головы станут отличными трофеями!', 12, 0, 100, 0, 0, 0, 79180, 168000, 168000, 0, 'Воин клана Северного Волка to Player');
INSERT INTO `creature_text` VALUES (72277, 4, 0, 'Отличный бой!', 12, 0, 100, 0, 0, 0, 119539, 19000, 19000, 0, 'Воин клана Северного Волка');
INSERT INTO `creature_text` VALUES (72277, 4, 0, 'Смерть нашим врагам!', 12, 0, 100, 0, 0, 0, 120576, 55000, 55000, 0, 'Воин клана Северного Волка');
INSERT INTO `creature_text` VALUES (72277, 4, 0, 'Ха, им от нас не уйти!', 12, 0, 100, 0, 0, 0, 81407, 52000, 52000, 0, 'Воин клана Северного Волка');
INSERT INTO `creature_text` VALUES (72294, 4, 0, 'Cindermaw submerges and tunnels north.', 42, 0, 100, 0, 0, 0, 80152, 54812, 54812, 0, 'Cindermaw to Player');
INSERT INTO `creature_text` VALUES (72294, 4, 0, 'Cindermaw submerges and tunnels southeast.', 42, 0, 100, 0, 0, 0, 80149, 27594, 27594, 89304, 'Cindermaw to Player');
INSERT INTO `creature_text` VALUES (72294, 4, 0, 'Магмочрев уходит под землю и прокладывает туннель на север.', 42, 0, 100, 0, 0, 0, 80152, 31559, 31559, 0, 'Магмочрев to Player');
INSERT INTO `creature_text` VALUES (72294, 4, 0, 'Магмочрев уходит под землю и прокладывает туннель на юго-восток.', 42, 0, 100, 0, 0, 0, 80149, 14477, 14477, 89304, 'Магмочрев to Player');
INSERT INTO `creature_text` VALUES (72302, 0, 0, 'Вот и они!', 14, 0, 100, 1, 0, 38099, 75207, 0, 0, 0, 'Леди Джайна Праудмур to Полководец Зела');
INSERT INTO `creature_text` VALUES (72302, 1, 0, 'Я вызвала снежную бурю, ведите их в нее!', 14, 0, 10, 0, 0, 38100, 75216, 0, 0, 0, 'Леди Джайна Праудмур to 0');
INSERT INTO `creature_text` VALUES (72302, 2, 0, 'Она направляет орудия на собственные башни!', 14, 0, 100, 0, 0, 38085, 75211, 0, 0, 0, 'Леди Джайна Праудмур to Полководец Зела');
INSERT INTO `creature_text` VALUES (72302, 3, 0, 'Осталась еще одна!', 14, 0, 100, 0, 0, 38086, 75212, 0, 0, 0, 'Леди Джайна Праудмур to Player');
INSERT INTO `creature_text` VALUES (72302, 4, 0, 'Получилось! Протодракон сбит!', 14, 0, 100, 0, 0, 38087, 75213, 0, 0, 0, 'Леди Джайна Праудмур to Галакрас');
INSERT INTO `creature_text` VALUES (72302, 5, 0, 'Это победа!', 14, 0, 0, 0, 0, 38088, 75214, 0, 0, 0, 'Леди Джайна Праудмур');
INSERT INTO `creature_text` VALUES (72302, 6, 0, 'Мы будем удерживать пристань до конца высадки. Идите к воротам Оргриммара и обменяйтесь новостями с Вол\'джином и его повстанцами.', 14, 0, 0, 0, 0, 38089, 75215, 0, 0, 0, 'Леди Джайна Праудмур');
INSERT INTO `creature_text` VALUES (72311, 0, 0, 'Посмотрим, удалось ли ему выполнить свою часть сделки.', 14, 0, 100, 1, 0, 38947, 75234, 0, 0, 0, 'Король Вариан Ринн to Леди Джайна Праудмур');
INSERT INTO `creature_text` VALUES (72311, 1, 0, 'Обе готовы! Стреляйте!', 14, 0, 100, 22, 0, 38944, 75231, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (72311, 2, 0, 'Ха-ха-ха! Большому кораблю...', 14, 0, 100, 153, 0, 38946, 75233, 0, 0, 0, 'Король Вариан Ринн');
INSERT INTO `creature_text` VALUES (72322, 0, 0, 'КОПАЙТЕ, ЧЕРВИ! КОПАЙТЕ!', 14, 0, 100, 0, 0, 0, 77950, 0, 0, 0, 'Надзиратель из клана Черной горы to 75006');
