INSERT INTO `creature_text` VALUES (72362, 1, 0, 'Дренеи падут! Все склонятся перед бездной!', 12, 0, 100, 0, 0, 0, 80105, 0, 0, 0, 'Пророк Бездны Ку\'тарг to Player');
INSERT INTO `creature_text` VALUES (72373, 0, 0, 'Локра! Любовь моя! Что ты здесь делаешь?!', 12, 0, 100, 0, 0, 0, 75473, 0, 0, 0, 'Карг Кровавое Безумие to Player');
INSERT INTO `creature_text` VALUES (72380, 0, 0, 'Отвратительно. Железный Волк не уважает ни зверей, ни своих же братьев, для него они все – лишь добыча.', 12, 0, 100, 274, 0, 0, 79330, 0, 0, 0, 'Дунгар Ледогрив to Player');
INSERT INTO `creature_text` VALUES (72380, 1, 0, 'Мой верный помощник, Рокнор, попытался прогнать одного из рилаков вилами – и его утащили в Костегар!', 12, 0, 100, 0, 0, 0, 74745, 0, 0, 0, 'Дунгар Ледогрив to Player');
INSERT INTO `creature_text` VALUES (72381, 0, 0, 'Одна из этих тварей схватила мою дочь Гану! Ее заковали в цепи, словно животное!', 12, 0, 100, 0, 0, 0, 74747, 0, 0, 0, 'Ротта Твердошкур to Player');
INSERT INTO `creature_text` VALUES (72383, 0, 0, 'Я давно не видела Маккара. Нам так не хватает его целительских навыков!', 12, 0, 100, 0, 0, 0, 74746, 0, 0, 0, 'Смотрительница Сума to Player');
INSERT INTO `creature_text` VALUES (72383, 1, 0, 'Хватит, дитя. Постарайся помолчать.', 12, 0, 100, 0, 0, 0, 77546, 0, 0, 0, 'Смотрительница Сума to Player');
