INSERT INTO `creature_text` VALUES (72405, 3, 0, 'Спасибо! Пока я здесь, не хочешь ли посмотреть на мои товары? Это новинки из Пандарии. Но дешево не отдам – война, $gсам:сама; понимаешь.', 12, 0, 100, 0, 0, 0, 74816, 857000, 857000, 0, 'Совик to Player');
