INSERT INTO `creature_text` VALUES (72413, 0, 0, 'These are the last of our forces. We\'ll stay here to hold the temple gates.', 12, 0, 100, 1, 0, 46723, 82580, 0, 0, 0, 'Exarch Akama to Player');
INSERT INTO `creature_text` VALUES (72413, 1, 0, 'Герой, тебе нужно пробиться к гавани.', 12, 0, 100, 1, 0, 46724, 82581, 0, 0, 0, 'Экзарх Акама to Player');
INSERT INTO `creature_text` VALUES (72426, 3, 0, 'И как я должна приготовить суп с секретным ингредиентом без этого самого секретного ингредиента?', 12, 0, 100, 0, 0, 0, 75283, 8596, 8596, 0, 'Линь Чао-Вэй');
INSERT INTO `creature_text` VALUES (72427, 4, 0, '%s is trying to escape in a panic!', 16, 0, 100, 0, 0, 0, 1150, 7000, 7000, 0, 'Оргриммарский батрак');
INSERT INTO `creature_text` VALUES (72432, 3, 0, 'Go with caution.  Hellscream has been assembling an arsenal down there - goblin mercenaries, pandaren artifacts, and some sort of darkness I can feel in my very soul.', 12, 0, 100, 0, 0, 0, 74824, 398000, 398000, 0, 'Eitrigg to Player');
INSERT INTO `creature_text` VALUES (72432, 3, 0, 'The Warchief ignored my council. I discovered he was building a compound beneath the caverns of Ragefire.', 12, 0, 100, 0, 0, 0, 74822, 447000, 447000, 0, 'Eitrigg to Player');
INSERT INTO `creature_text` VALUES (72432, 3, 0, 'Thrall and Saurfang have already gone down below.', 12, 0, 100, 0, 0, 0, 74823, 456000, 456000, 0, 'Eitrigg to Player');
INSERT INTO `creature_text` VALUES (72432, 3, 0, 'Будьте осторожны. Гаррош хорошо подготовился: гоблины-наемники, артефакты пандаренов и темная сила, которую я чувствую нутром.', 12, 0, 100, 0, 0, 0, 74824, 394000, 394000, 0, 'Эйтригг to Player');
INSERT INTO `creature_text` VALUES (72432, 3, 0, 'Вождь игнорировал мои советы. Я узнал, что он строит секретную базу под Огненной пропастью.', 12, 0, 100, 0, 0, 0, 74822, 378000, 378000, 0, 'Эйтригг to Player');
INSERT INTO `creature_text` VALUES (72432, 3, 0, 'Тралл и Саурфанг уже спустились вниз.', 12, 0, 100, 0, 0, 0, 74823, 388000, 388000, 0, 'Эйтригг to Player');
INSERT INTO `creature_text` VALUES (72438, 0, 0, 'Цзи, мне так жаль. Мы вернемся на черепаху, мы можем быть вместе. Я прошу тебя, Цзи, не умирай.', 12, 0, 0, 0, 0, 0, 74929, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (72438, 1, 0, 'I\'ll take him away from here.', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (72438, 2, 0, 'Я вытащу его отсюда.', 12, 0, 100, 0, 0, 0, 74930, 0, 0, 0, 'Аиса Воспевающая Облака to Player');
INSERT INTO `creature_text` VALUES (72449, 0, 0, 'Ты $gпотерялся:потерялась;? Что тебе тут нужно?', 12, 0, 100, 0, 0, 0, 77557, 0, 0, 0, 'Воин клана Северного Волка to Player');
INSERT INTO `creature_text` VALUES (72449, 1, 0, 'Карг? Конечно, знаю. Он недавно спас мне жизнь, в одиночку вышел против пяти поганых Громоборцев.', 12, 0, 100, 6, 0, 0, 74735, 0, 0, 0, 'Воин клана Северного Волка to Player');
INSERT INTO `creature_text` VALUES (72449, 2, 0, 'С ними он справился. А потом появился Железный Волк, верхом на самом здоровом и злобном рилаке из всех мною виденных. Схватил его и унес прочь.', 12, 0, 100, 397, 0, 0, 74736, 0, 0, 0, 'Воин клана Северного Волка to Player');
INSERT INTO `creature_text` VALUES (72449, 3, 0, 'И других тоже забрали. Утащили в каньон. Расспроси наших, узнай, кто исчез.', 12, 0, 100, 274, 0, 0, 74737, 0, 0, 0, 'Воин клана Северного Волка to Player');
INSERT INTO `creature_text` VALUES (72455, 0, 0, 'Есть что сказать, пандарен?', 14, 0, 100, 0, 0, 0, 74921, 0, 0, 0, 'Надзиратель Комак to Player');
INSERT INTO `creature_text` VALUES (72455, 1, 0, 'Видишь эту свечу? Когда она догорит, я хочу знать все о пандаренских артефактах! Начинай!', 14, 0, 100, 0, 0, 0, 74922, 0, 0, 0, 'Надзиратель Комак to Player');
