INSERT INTO `creature_text` VALUES (72560, 0, 0, 'Отлично, первый отряд высадился.', 12, 0, 100, 1, 0, 38167, 75245, 0, 0, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (72560, 1, 0, 'Тактика, Темная Госпожа. Два слова: береговая оборона.', 12, 0, 100, 1, 0, 38168, 75246, 0, 0, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (72560, 2, 0, 'Герои, захватите башни и сбейте этого дракона из пушек!', 12, 0, 100, 5, 0, 38169, 75247, 0, 0, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` VALUES (72560, 3, 0, 'Кто-то должен удерживать зону высадки.', 12, 0, 100, 1, 0, 38170, 75248, 0, 0, 0, 'Лор\'темар Терон to Player');
