INSERT INTO `creature_text` VALUES (72560, 7, 0, 'Наконец-то! Теперь придется выбивать пыль с одежды.', 14, 0, 100, 5, 0, 38155, 75254, 0, 0, 0, 'Лор\'темар Терон to Player');
