INSERT INTO `creature_text` VALUES (72637, 3, 0, 'It\'d be easier to guard you if you stayed out of sight, Khadgar.', 12, 0, 100, 396, 0, 44123, 75002, 446469, 446469, 0, 'Cordana Felsong to Player');
