INSERT INTO `creature_text` VALUES (72638, 3, 0, 'Look at that roast, Ufga! I bet our Iron Horde guests have never tasted anything like it.', 12, 0, 100, 0, 0, 0, 76849, 173516, 173516, 127974, 'Quartermaster Thurg to Player');
INSERT INTO `creature_text` VALUES (72638, 3, 0, 'Смотри, какое жаркое, Уфга! Готов спорить, наши гости из Железной Орды такого еще не пробовали.', 12, 0, 100, 0, 0, 0, 76849, 11000, 11000, 127974, 'Интендант Тург to Player');
