INSERT INTO `creature_text` VALUES (72640, 0, 0, 'Шаман клана Громового Молота ставит тотем сопротивления, чтобы помочь вам!', 41, 0, 100, 0, 0, 0, 74895, 0, 0, 0, 'Шаман клана Громового Молота to Player');
INSERT INTO `creature_text` VALUES (72646, 0, 0, 'Приветствую, командир. Рад снова видеть тебя.', 12, 0, 100, 0, 0, 45010, 75732, 0, 0, 0, 'Верховный маг Кадгар to Player');
