INSERT INTO `creature_text` VALUES (72646, 3, 0, 'Мы обязательно должны найти базу Совета и покончить с ними.', 12, 0, 100, 1, 0, 45013, 76056, 16000, 16000, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (72646, 3, 0, 'Приветствую, предсказатель. Мы прибыли по срочному делу. За нас поручится герой, спасший Вор\'гол.', 12, 0, 100, 2, 0, 45015, 75731, 31000, 31000, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (72646, 3, 0, 'Тром-ка, воины Северного Волка.', 12, 0, 100, 1, 0, 45012, 76054, 6000, 6000, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (72647, 1, 0, 'Тебе не остановить нас, |3-6($c)! Рано или поздно все орки отведают оскверненной крови!', 12, 0, 100, 0, 0, 0, 76127, 0, 0, 0, 'Губитель Кургот to Player');
