INSERT INTO `creature_text` VALUES (72694, 7, 0, 'Это за моего исполина!', 14, 0, 100, 0, 0, 0, 74660, 0, 0, 0, 'Мастер осады Черноплавс to Shockwave Missile Caster Stalker');
