INSERT INTO `creature_text` VALUES (72940, 1, 0, 'Ты представляешь, как выглядит живой гигант? Говорят, этот был последним, и клан Громоборцев победил его десятки лет назад.', 12, 0, 100, 0, 0, 0, 81403, 0, 0, 0, 'Локра to Player');
