INSERT INTO `creature_text` VALUES (72940, 7, 0, 'Это Аша… ты чувствуешь? Мне… так жаль, Нерок. Я не смогла ее защитить.', 12, 0, 100, 0, 0, 0, 77268, 0, 0, 0, 'Локра to Player');
INSERT INTO `creature_text` VALUES (72940, 8, 0, 'I considered myself invulnerable. Wrong. Back to the pack. The Iron Wolf recognizes the true power of the Frostwolves and is responsible for everything.', 12, 0, 100, 274, 0, 0, 77594, 0, 0, 0, 'Локра to Player');
