INSERT INTO `creature_text` VALUES (73097, 1, 0, 'Познакомься с моим топором!', 14, 0, 100, 0, 0, 0, 75530, 0, 0, 0, 'Карг Кровавое Безумие to Player');
INSERT INTO `creature_text` VALUES (73097, 2, 0, 'Надо было меня убить, пока был шанс. Где мой волк?!', 12, 0, 100, 397, 0, 0, 75535, 0, 0, 0, 'Карг Кровавое Безумие to Player');
