INSERT INTO `creature_text` VALUES (73097, 4, 0, 'Идем, брат. Ты теперь свободен.', 12, 0, 100, 0, 0, 0, 77265, 0, 0, 0, 'Карг Кровавое Безумие to Player');
INSERT INTO `creature_text` VALUES (73097, 5, 0, 'Мы потеряли так много братьев и сестер...', 12, 0, 100, 0, 0, 0, 77269, 0, 0, 0, 'Карг Кровавое Безумие to Player');
