INSERT INTO `creature_text` VALUES (73195, 0, 0, 'Бейте его током, пока не свалится! Он сеет хаос.', 14, 0, 100, 0, 0, 0, 75723, 0, 0, 0, 'Кор\'кронский тюремщик to Player');
INSERT INTO `creature_text` VALUES (73195, 0, 0, 'Он вырывается из клетки. Включить шоковый ошейник, усмирить зверя!', 14, 0, 100, 0, 0, 0, 75722, 0, 0, 0, 'Кор\'кронский тюремщик to Player');
