INSERT INTO `creature_text` VALUES (73195, 3, 0, 'Shock it until it can\'t move anymore!  It\'s causing a riot in here!', 14, 0, 100, 5, 0, 0, 75723, 39000, 39000, 0, 'Kor\'kron Jailer to Player');
INSERT INTO `creature_text` VALUES (73222, 3, 0, 'A soft hum ceases as the corruption creating new Puddles ebbs away with the death of the final Aqueous Defender.', 16, 0, 100, 0, 0, 0, 77144, 189000, 189000, 0, 'Ooze Controller (Stalker) to Player');
INSERT INTO `creature_text` VALUES (73222, 3, 0, 'Тихое жужжание замирает по мере того, как скверна исчезает после смерти последнего водяного защитника.', 16, 0, 100, 0, 0, 0, 77144, 150000, 150000, 0, 'Ooze Controller (Stalker) to Player');
INSERT INTO `creature_text` VALUES (73225, 3, 0, 'Почему трясется земля? На нас напали?', 12, 0, 100, 274, 0, 44144, 76145, 75000, 75000, 0, 'Жительница Тром\'вара to Player');
INSERT INTO `creature_text` VALUES (73243, 1, 0, 'Ты как раз вовремя, жертва!', 14, 0, 100, 0, 0, 0, 76689, 0, 0, 147436, 'Огзор Некромаг to Player');
INSERT INTO `creature_text` VALUES (73250, 3, 0, 'Let the intruders come this way! I look forward to crushing their skulls.', 14, 0, 100, 0, 0, 0, 75806, 516000, 516000, 0, 'Kor\'kron Wild Gun to Kor\'kron Iron Scorpion');
INSERT INTO `creature_text` VALUES (73250, 3, 0, 'Of course, those cowards will likely sneak by rather than face my wrath.', 14, 0, 100, 0, 0, 0, 75807, 523000, 523000, 0, 'Kor\'kron Wild Gun to Kor\'kron Iron Scorpion');
INSERT INTO `creature_text` VALUES (73250, 3, 0, 'Где эти чужаки? Пусть приходят! Очень уж хочется проломить пару голов.', 14, 0, 100, 0, 0, 0, 75806, 671000, 671000, 0, 'Кор\'кронский дикий стрелок to Кор\'кронский железный скорпион');
INSERT INTO `creature_text` VALUES (73250, 3, 0, 'Но, конечно, эти трусы скорее попрячутся в норы, чем решатся узреть мой гнев.', 14, 0, 100, 0, 0, 0, 75807, 678000, 678000, 0, 'Кор\'кронский дикий стрелок to Кор\'кронский железный скорпион');
INSERT INTO `creature_text` VALUES (73250, 3, 0, 'Сейчас вы умрете, трусы! Вы откроете эти врата только через мой труп!', 14, 0, 100, 0, 0, 0, 76089, 2727000, 2727000, 0, 'Кор\'кронский дикий стрелок to Player');
INSERT INTO `creature_text` VALUES (73250, 4, 0, 'Falling to me would probably be the greatest achievement they could hope to obtain.', 14, 0, 100, 0, 0, 0, 75809, 10000, 10000, 0, 'Kor\'kron Wild Gun to Kor\'kron Iron Scorpion');
INSERT INTO `creature_text` VALUES (73250, 4, 0, 'I will burn your corpses to ash!', 12, 0, 100, 0, 0, 0, 76076, 18000, 18000, 0, 'Kor\'kron Wild Gun to Kor\'kron Wild Gun');
INSERT INTO `creature_text` VALUES (73250, 4, 0, 'I will turn your bones to dust!', 12, 0, 100, 0, 0, 0, 76076, 13000, 13000, 0, 'Кор\'кронский дикий стрелок to Кор\'кронский дикий стрелок');
INSERT INTO `creature_text` VALUES (73250, 4, 0, 'What else would you expect from those without valor?', 14, 0, 100, 0, 0, 0, 75808, 2000, 2000, 0, 'Kor\'kron Wild Gun to Kor\'kron Iron Scorpion');
INSERT INTO `creature_text` VALUES (73250, 4, 0, 'Смерть от моей руки была бы их величайшим достижением!', 14, 0, 100, 0, 0, 0, 75809, 11000, 11000, 0, 'Кор\'кронский дикий стрелок to Кор\'кронский железный скорпион');
