INSERT INTO `creature_text` VALUES (73303, 0, 0, 'Красный Журавль увидел во мне надежду и велел заглянуть внутрь себя. Хоть я и чувствовал отчаяние, надежда помогла побороть его.', 14, 0, 100, 0, 0, 38038, 75935, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73303, 1, 0, 'Вам удалось пройти испытание надежды и путь Красного Журавля. Да направит он ваши стопы.', 14, 0, 100, 0, 0, 38042, 75939, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73303, 2, 0, 'Я был силен, но безрассуден. Белый Тигр обучил меня управлять собой. Так я поборол ненависть, злобу и жестокость.', 14, 0, 100, 0, 0, 38037, 75934, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73303, 3, 0, 'Вам удалось пройти испытание силы и путь Белого Тигра. Запомните этот урок навсегда.', 14, 0, 100, 0, 0, 38041, 75938, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73303, 4, 0, 'Давным-давно Нефритовая Змея сказала мне, что надо очистить дух и слиться с землей. Так я поборол сомнение.', 14, 0, 100, 0, 0, 38036, 75933, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73303, 5, 0, 'Вам удалось пройти испытание мудрости и путь Нефритовой Змеи. Извлеките из них урок, как я когда-то.', 14, 0, 100, 0, 0, 38040, 75937, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73303, 6, 0, 'Перед лицом великого страха Черный Бык учил меня стойкости. Так я превозмог ужас и тьму, и страх отступил.', 14, 0, 100, 0, 0, 38039, 75936, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73303, 7, 0, 'Вам удалось пройти испытание стойкости и путь Черного Быка. Да пребудет с вами его благословение!', 14, 0, 100, 0, 0, 38043, 75940, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73318, 0, 0, 'Сюда, герой!', 12, 0, 100, 0, 0, 39072, 75826, 0, 0, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (73318, 3, 0, 'Идем, герой. Вернемся в дол. Я думаю, кое-кому будет особенно интересно узнать о таком повороте событий. Если только мы сможем привлечь его внимание.', 12, 0, 100, 0, 0, 39075, 75829, 29000, 29000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (73318, 3, 0, 'Наконец-то тьма, которую оно источало, побеждена.', 12, 0, 100, 378, 0, 39074, 75828, 23000, 23000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (73318, 3, 0, 'Смотри, что осталось от чудовища, которое угнетало Пандарию все эти годы.', 12, 0, 100, 3, 0, 39073, 75827, 13000, 13000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (73319, 3, 0, '...ты …ты просто трактирщик!', 12, 0, 100, 25, 0, 0, 75986, 159000, 159000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (73319, 3, 0, 'I did everything I could. The whole world lay at his feet, you just had to take it!', 12, 0, 100, 5, 0, 0, 75837, 17000, 17000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (73319, 3, 0, 'I thought Hellscream would win, but he restored half the Horde against himself. And I changed my mind.', 12, 0, 100, 1, 0, 0, 75841, 40000, 40000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (73319, 3, 0, 'Больше никаких случайностей!', 12, 0, 100, 1, 0, 0, 75988, 173000, 173000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (73319, 3, 0, 'Как он допустил появление нового вождя? Он мог объединить мир под стягом Альянса. И почему я верил в него!', 12, 0, 100, 6, 0, 0, 75952, 60000, 60000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (73319, 3, 0, 'Король — глупец!', 12, 0, 100, 5, 0, 0, 75836, 13000, 13000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (73319, 3, 0, 'Надо было самому занять трон. Как тетушка Ониксия. Я бы навел порядок!', 12, 0, 100, 1, 0, 0, 75953, 70000, 70000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (73319, 3, 0, 'Неужели в Оргриммаре все произошло так, как ты говоришь?', 12, 0, 100, 25, 0, 0, 75834, 7000, 7000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (73319, 3, 0, 'Ни в чем!', 12, 0, 100, 5, 0, 0, 75989, 180000, 180000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (73319, 3, 0, 'Но этот идиот Ринн! \"Его Величество\"!', 12, 0, 100, 5, 0, 0, 75951, 55000, 55000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (73319, 3, 0, 'Ну конечно, еще год войны. Огромные потери при штурме Громового Утеса. Остальная Орда развалилась бы.', 12, 0, 100, 1, 0, 0, 75838, 23000, 23000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (73319, 3, 0, 'О, не удивляйся так сильно. Я – черный дракон. Я верен только себе.', 12, 0, 100, 274, 0, 0, 75944, 49000, 49000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (73319, 3, 0, 'Обещаю: я не остановлюсь ни перед чем, чтобы подготовить мир к предстоящей битве.', 12, 0, 100, 1, 0, 0, 75987, 165000, 165000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (73319, 3, 0, 'ТУН! ПИТЬ! ЖИВО!', 12, 0, 100, 25, 0, 0, 75839, 33000, 33000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (73319, 3, 0, 'Что? Да, я поддерживал Альянс.', 12, 0, 100, 273, 0, 0, 75840, 37000, 37000, 0, 'Гневион to Player');
INSERT INTO `creature_text` VALUES (73319, 3, 0, 'Э…?', 12, 0, 100, 6, 0, 0, 75985, 80000, 80000, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` VALUES (73320, 1, 0, 'I will gladly sacrifice you!', 12, 0, 100, 0, 0, 0, 76134, 0, 0, 0, 'Некрофит to Player');
INSERT INTO `creature_text` VALUES (73320, 1, 0, 'Быстрее! Убить чужаков!', 12, 0, 100, 0, 0, 0, 76132, 0, 0, 0, 'Некрофит to Player');
INSERT INTO `creature_text` VALUES (73320, 1, 0, 'Воля Совета – закон!', 12, 0, 100, 0, 0, 0, 76129, 0, 0, 0, 'Некрофит to Player');
INSERT INTO `creature_text` VALUES (73320, 1, 0, 'Готовься к смерти, |3-6($pc)!', 12, 0, 100, 0, 0, 0, 76130, 0, 0, 0, 'Некрофит to Player');
INSERT INTO `creature_text` VALUES (73320, 1, 0, 'Мы поглотим твою душу!', 12, 0, 100, 0, 0, 0, 76131, 0, 0, 0, 'Некрофит to Player');
INSERT INTO `creature_text` VALUES (73320, 1, 0, 'Тьма наступает!', 12, 0, 100, 0, 0, 0, 130980, 0, 0, 0, 'Некрофит to Player');
INSERT INTO `creature_text` VALUES (73320, 3, 0, 'Вы опоздали!', 12, 0, 100, 0, 0, 0, 76135, 64000, 64000, 0, 'Некрофит to Player');
INSERT INTO `creature_text` VALUES (73320, 3, 0, 'Смерть чужаку!', 12, 0, 100, 0, 0, 0, 76133, 113000, 113000, 0, 'Некрофит to Player');
INSERT INTO `creature_text` VALUES (73330, 0, 0, 'А, вот мы и снова путешествуем вместе. Правда, на этот раз обстоятельства куда более печальны.', 14, 0, 100, 0, 0, 38120, 74968, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73330, 1, 0, 'Земля изрезана шрамами, водоемы лишились влаги, деревья и травы сохнут и гибнут.', 14, 0, 100, 0, 0, 38122, 74970, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73330, 2, 0, 'Когда-то титаны создали все живое в Пандарии с помощью животворящих вод.', 14, 0, 100, 0, 0, 38124, 74972, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73330, 3, 0, 'Благодаря им наш Дол цвел круглый год. Они дают небывалое плодородие землям Долины Четырех Ветров!', 14, 0, 100, 0, 0, 38125, 74973, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73330, 4, 0, 'И теперь в этих водах явилось порождение злобы древнего бога.', 14, 0, 100, 0, 0, 38126, 74974, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73330, 5, 0, 'Мерзость... Какая мерзость... Уничтожьте ее, пока она не ушла в землю и не осквернила всю Пандарию.', 14, 0, 100, 0, 0, 38127, 74975, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73330, 6, 0, 'У вас получилось! Теперь воды снова чисты.', 14, 0, 100, 5, 0, 38128, 74976, 0, 0, 0, 'Зараженная лужа');
INSERT INTO `creature_text` VALUES (73330, 7, 0, 'Вы чувствуете в себе их жизненную энергию?', 14, 0, 100, 6, 0, 38129, 74977, 0, 0, 0, 'Зараженная лужа');
INSERT INTO `creature_text` VALUES (73330, 8, 0, 'Дол восстановится далеко не сразу. Но вы дали нам надежду!', 14, 0, 100, 1, 0, 38130, 74978, 0, 0, 0, 'Зараженная лужа');
INSERT INTO `creature_text` VALUES (73330, 9, 0, 'Неужели это?.. О нет, нет-нет-нет.', 14, 0, 100, 274, 0, 38131, 74979, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73330, 10, 0, 'Золотой Лотос... они жили ради защиты этого места.', 14, 0, 100, 1, 0, 38132, 74980, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73330, 11, 0, 'Да! Рук Каменная Ступня! Ты помнишь меня! Что c тобой?', 14, 0, 100, 5, 0, 38133, 74981, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73330, 12, 0, 'Clear. Sha imprisoned them here and makes them relive their failure again and again, feeding on their despair.', 14, 0, 100, 1, 0, 38134, 74982, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73330, 13, 0, 'Это гораздо страшнее смерти. Прошу вас, герои, освободите их!', 14, 0, 100, 1, 0, 38135, 74984, 0, 0, 0, '0');
INSERT INTO `creature_text` VALUES (73330, 14, 0, 'Покойтесь с миром.', 14, 0, 100, 1, 0, 38136, 88693, 0, 0, 0, 'Сунь Доброе Сердце');
INSERT INTO `creature_text` VALUES (73330, 15, 0, 'Пусть ваши души сольются с землей, за которую вы отдали жизни.', 14, 0, 100, 1, 0, 38137, 74986, 0, 0, 0, 'Сунь Доброе Сердце');
INSERT INTO `creature_text` VALUES (73342, 0, 0, 'Воды еще не очищены!', 14, 0, 100, 0, 0, 0, 76028, 0, 0, 0, 'Падший смотритель вод to 0');
INSERT INTO `creature_text` VALUES (73342, 1, 0, 'Ты $gдолжен:должна; защитить меня.', 14, 0, 100, 0, 0, 0, 76031, 0, 0, 0, 'Падший смотритель вод to Измученный адепт');
INSERT INTO `creature_text` VALUES (73342, 2, 0, 'Защита господина – твоя забота.', 14, 0, 100, 0, 0, 0, 76032, 0, 0, 0, 'Падший смотритель вод to Измученный адепт');
INSERT INTO `creature_text` VALUES (73346, 3, 0, '...Разрушил таверну и не дал на чай. Неприятный тип.', 12, 0, 100, 274, 0, 0, 75984, 190000, 190000, 0, 'Тун Устроитель to Тун Устроитель');
INSERT INTO `creature_text` VALUES (73346, 3, 0, 'Всегда говоришь. И никогда не слушаешь! Не видишь, чему учит Пандария! ', 12, 0, 100, 25, 0, 0, 75979, 86000, 86000, 0, 'Тун Устроитель to Гневион');
INSERT INTO `creature_text` VALUES (73346, 3, 0, 'Когда последний император укрыл Пандарию от остального мира, он уберег и землю наших древних врагов – богомолов. А почему?', 12, 0, 100, 1, 0, 0, 75981, 112000, 112000, 0, 'Тун Устроитель to Гневион');
INSERT INTO `creature_text` VALUES (73346, 3, 0, 'Одни разговоры!', 12, 0, 100, 5, 0, 0, 75978, 83000, 83000, 0, 'Тун Устроитель to Гневион');
INSERT INTO `creature_text` VALUES (73346, 3, 0, 'Он хотел сохранить целостность. Прожив рядом с богомолами десять тысяч лет, мы стали сильнее.', 12, 0, 100, 1, 0, 0, 75990, 125000, 125000, 0, 'Тун Устроитель to Гневион');
INSERT INTO `creature_text` VALUES (73346, 3, 0, 'Природа стремится к равновесию. Даже в нашем мехе есть черное и белое. Тьма и свет.', 12, 0, 100, 1, 0, 0, 75980, 95000, 95000, 0, 'Тун Устроитель to Гневион');
INSERT INTO `creature_text` VALUES (73346, 3, 0, 'Так дело обстоит и у Орды с Альянсом. Две стороны сильны не вопреки друг другу. Они сильны благодаря друг другу!', 12, 0, 100, 25, 0, 0, 75982, 135000, 135000, 0, 'Тун Устроитель to Тун Устроитель');
INSERT INTO `creature_text` VALUES (73346, 3, 0, 'Ты принимаешь вашу великую силу за слабость.', 12, 0, 100, 25, 0, 0, 75983, 149000, 149000, 0, 'Тун Устроитель to Тун Устроитель');
INSERT INTO `creature_text` VALUES (73346, 3, 0, 'Хватит!', 12, 0, 100, 274, 0, 0, 125689, 78000, 78000, 0, 'Тун Устроитель to Гневион');
INSERT INTO `creature_text` VALUES (73349, 0, 0, 'Мы должны защитить источники от скверны!', 14, 0, 100, 1, 0, 0, 76002, 0, 0, 0, 'Измученный адепт to Измученный адепт');
INSERT INTO `creature_text` VALUES (73360, 3, 0, 'Бейте сильно и уверенно. Будто разбиваете череп заклятого врага!', 12, 0, 100, 396, 0, 0, 77283, 0, 0, 0, 'Осадный кузнец из клана Черной горы to Ученица из клана Громоборцев');
INSERT INTO `creature_text` VALUES (73360, 3, 0, 'Держите ритм!', 12, 0, 100, 397, 0, 0, 77293, 26000, 26000, 0, 'Осадный кузнец из клана Черной горы to Ученик из клана Громоборцев');
INSERT INTO `creature_text` VALUES (73360, 3, 0, 'Каждый удар приближает нас к победе над врагами!', 12, 0, 100, 396, 0, 0, 77294, 1121000, 1121000, 0, 'Осадный кузнец из клана Черной горы to Ученица из клана Громоборцев');
INSERT INTO `creature_text` VALUES (73360, 3, 0, 'С таким металлом вам работать еще не доводилось. Он будет сопротивляться. Ваша воля должна быть сильнее!', 12, 0, 100, 396, 0, 0, 77282, 12000, 12000, 0, 'Осадный кузнец из клана Черной горы to Ученик из клана Громоборцев');
INSERT INTO `creature_text` VALUES (73360, 3, 0, 'Хорошо. Это хорошие удары.', 12, 0, 100, 273, 0, 0, 77295, 12000, 12000, 0, 'Осадный кузнец из клана Черной горы to Ученица из клана Громоборцев');
INSERT INTO `creature_text` VALUES (73371, 3, 0, 'Проваливай, это мое!', 12, 0, 100, 0, 0, 0, 76595, 109000, 109000, 0, 'Громила из клана Камнерогов to Громила из клана Камнерогов');
INSERT INTO `creature_text` VALUES (73371, 4, 0, 'Ох! Ну все, теперь я убью тебя!', 12, 0, 100, 0, 0, 0, 76598, 111000, 111000, 0, 'Громила из клана Камнерогов');
INSERT INTO `creature_text` VALUES (73395, 0, 0, 'No... Samaara!', 12, 0, 100, 5, 0, 45637, 77990, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (73395, 1, 0, 'Champion! Focus your attacks on that... THING! I will protect the others.', 12, 0, 100, 5, 0, 45638, 78040, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (73395, 2, 0, 'Great Prophet! Ner\'zhul is taking the captives to Shaz\'gul.', 12, 0, 100, 1, 0, 45639, 79693, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (73395, 3, 0, 'Герой, наару пришел нам на помощь. Защищай его, пока он набирает силу.', 12, 0, 100, 1, 0, 45628, 82583, 0, 0, 0, 'Ирель to 449');
INSERT INTO `creature_text` VALUES (73395, 4, 0, 'У нас получилось! Теперь закончим эту битву…', 12, 0, 100, 1, 0, 43657, 82584, 0, 0, 0, 'Ирель to 449');
INSERT INTO `creature_text` VALUES (73395, 5, 0, 'Пусть все узнают о нашей победе. Лети в гарнизон на волшебном дракончике. До встречи!', 12, 0, 100, 1, 0, 45634, 82762, 0, 0, 0, 'Ирель to 449');
INSERT INTO `creature_text` VALUES (73395, 6, 0, 'Герой, тебе неведом страх. Прими нашу благодарность.', 12, 0, 100, 1, 0, 45632, 82590, 0, 0, 0, 'Ирель to Player');
INSERT INTO `creature_text` VALUES (73395, 7, 0, 'Свет побеждает, ибо МЫ сражаемся против мрака.', 12, 0, 100, 1, 0, 45633, 88712, 0, 0, 0, 'Ирель to Player');
INSERT INTO `creature_text` VALUES (73412, 0, 0, 'Thank you, $n. I will meet you back at Twilight Glade.', 12, 0, 100, 1, 0, 45655, 78229, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (73426, 3, 0, 'Мы здесь позаботимся о раненых. Ступай, не медли. Разбуди верховного друида.', 12, 0, 100, 0, 0, 0, 95292, 496700, 496700, 0, 'Рилисса Медвежья Песнь to Player');
INSERT INTO `creature_text` VALUES (73426, 3, 0, 'Ничего бы этого не случилось, не будь твой тыл таким...<кхм>... УПИТАННЫМ.', 12, 0, 100, 0, 0, 0, 100540, 5499, 5499, 0, 'Рилисса Медвежья Песнь to Player');
INSERT INTO `creature_text` VALUES (73435, 3, 0, 'ЕДА И ГРОГ!', 12, 0, 100, 22, 0, 0, 77078, 7000, 7000, 0, 'Гримторн Рыжебород to Player');
INSERT INTO `creature_text` VALUES (73435, 3, 0, 'Идеальное сочетание северного и южного стилей!', 12, 0, 100, 5, 0, 0, 77077, 2276000, 2276000, 0, 'Гримторн Рыжебород to Player');
INSERT INTO `creature_text` VALUES (73435, 3, 0, 'Слабо попробовать Драконий П.И.Р.О.Г.?! Сначала волосы начнут расти на груди, а потом и везде. Понимаешь? ВЕЗДЕ!', 12, 0, 100, 4, 0, 0, 77080, 6000, 6000, 0, 'Гримторн Рыжебород to Player');
INSERT INTO `creature_text` VALUES (73435, 3, 0, 'Такого больше нигде не найти. Попробуйте!', 12, 0, 100, 1, 0, 0, 77079, 5000, 5000, 0, 'Гримторн Рыжебород to Player');
INSERT INTO `creature_text` VALUES (73443, 3, 0, 'В этих дюнах в некоторых животных вселилась демоническая сила.', 12, 0, 100, 0, 0, 44141, 75152, 12000, 12000, 0, 'Кордана Оскверненная Песнь to Player');
INSERT INTO `creature_text` VALUES (73443, 3, 0, 'Здесь точно замешан Совет Теней, но пока не удалось их обнаружить.', 12, 0, 100, 0, 0, 44142, 75153, 24000, 24000, 0, 'Кордана Оскверненная Песнь to Player');
INSERT INTO `creature_text` VALUES (73443, 3, 0, 'Кадгар присоединится к нам по дороге. Идем, Тром\'вар прямо за грядой.', 12, 0, 100, 273, 0, 44140, 75151, 3000, 3000, 0, 'Кордана Оскверненная Песнь to Player');
INSERT INTO `creature_text` VALUES (73443, 3, 0, 'Кадгар! Я как раз объясняла, что к чему.', 12, 0, 100, 0, 0, 44143, 75730, 39000, 39000, 0, 'Кордана Оскверненная Песнь to Player');
INSERT INTO `creature_text` VALUES (73458, 4, 0, 'I\'m gonna rearrange your nuts and bolts!', 12, 0, 100, 0, 0, 0, 80964, 27563, 27563, 0, 'Rukah the Machinist');
INSERT INTO `creature_text` VALUES (73458, 4, 0, 'I’ll rearrange the nuts and bolts in some places!', 12, 0, 100, 0, 0, 0, 80964, 13688, 13688, 0, 'Механик Рукха');
INSERT INTO `creature_text` VALUES (73458, 4, 0, 'Saddle up maggot, you\'re going for a ride!', 12, 0, 100, 0, 0, 0, 80965, 15469, 68782, 0, 'Rukah the Machinist');
INSERT INTO `creature_text` VALUES (73458, 4, 0, 'Давай в седло, червь, тебе пора в дорогу!', 12, 0, 100, 0, 0, 0, 80965, 1360, 26078, 0, 'Механик Рукха');
INSERT INTO `creature_text` VALUES (73465, 0, 0, 'I will gladly sacrifice you!', 12, 0, 100, 0, 0, 2992, 76134, 0, 0, 0, 'Темный прислужник to Player');
INSERT INTO `creature_text` VALUES (73465, 1, 0, 'Воля Совета – закон!', 12, 0, 100, 0, 0, 2992, 76129, 0, 0, 0, 'Темный прислужник to Player');
