INSERT INTO `creature_text` VALUES (73475, 3, 0, 'Мы должны поработить бледных орков и… (Чужак! Уходим!)', 12, 0, 100, 1, 0, 46610, 76952, 6000, 6000, 0, 'Проекция Чо\'Галла to Player');
